INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 1, 'بسم الله الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 1, 'الحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 1, 'الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 1, 'مالك يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 1, 'إياك نعبد وإياك نستعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 1, 'اهدنا الصراط المستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 1, 'صراط الذين أنعمت عليهم غير المغضوب عليهم ولا الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 2, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 2, 'ذلك الكتاب لا ريب فيه هدى للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 2, 'الذين يؤمنون بالغيب ويقيمون الصلاة ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 2, 'والذين يؤمنون بما أنزل إليك وما أنزل من قبلك وبالآخرة هم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 2, 'أولئك على هدى من ربهم وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 2, 'إن الذين كفروا سواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 2, 'ختم الله على قلوبهم وعلى سمعهم وعلى أبصارهم غشاوة ولهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 2, 'ومن الناس من يقول آمنا بالله وباليوم الآخر وما هم بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 2, 'يخادعون الله والذين آمنوا وما يخدعون إلا أنفسهم وما يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 2, 'في قلوبهم مرض فزادهم الله مرضا ولهم عذاب أليم بما كانوا يكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 2, 'وإذا قيل لهم لا تفسدوا في الأرض قالوا إنما نحن مصلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 2, 'ألا إنهم هم المفسدون ولكن لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 2, 'وإذا قيل لهم آمنوا كما آمن الناس قالوا أنؤمن كما آمن السفهاء ألا إنهم هم السفهاء ولكن لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 2, 'وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلوا إلى شياطينهم قالوا إنا معكم إنما نحن مستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 2, 'الله يستهزئ بهم ويمدهم في طغيانهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 2, 'أولئك الذين اشتروا الضلالة بالهدى فما ربحت تجارتهم وما كانوا مهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 2, 'مثلهم كمثل الذي استوقد نارا فلما أضاءت ما حوله ذهب الله بنورهم وتركهم في ظلمات لا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 2, 'صم بكم عمي فهم لا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 2, 'أو كصيب من السماء فيه ظلمات ورعد وبرق يجعلون أصابعهم في آذانهم من الصواعق حذر الموت والله محيط بالكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 2, 'يكاد البرق يخطف أبصارهم كلما أضاء لهم مشوا فيه وإذا أظلم عليهم قاموا ولو شاء الله لذهب بسمعهم وأبصارهم إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 2, 'يا أيها الناس اعبدوا ربكم الذي خلقكم والذين من قبلكم لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 2, 'الذي جعل لكم الأرض فراشا والسماء بناء وأنزل من السماء ماء فأخرج به من الثمرات رزقا لكم فلا تجعلوا لله أندادا وأنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 2, 'وإن كنتم في ريب مما نزلنا على عبدنا فأتوا بسورة من مثله وادعوا شهداءكم من دون الله إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 2, 'فإن لم تفعلوا ولن تفعلوا فاتقوا النار التي وقودها الناس والحجارة أعدت للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 2, 'وبشر الذين آمنوا وعملوا الصالحات أن لهم جنات تجري من تحتها الأنهار كلما رزقوا منها من ثمرة رزقا قالوا هذا الذي رزقنا من قبل وأتوا به متشابها ولهم فيها أزواج مطهرة وهم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 2, 'إن الله لا يستحيي أن يضرب مثلا ما بعوضة فما فوقها فأما الذين آمنوا فيعلمون أنه الحق من ربهم وأما الذين كفروا فيقولون ماذا أراد الله بهذا مثلا يضل به كثيرا ويهدي به كثيرا وما يضل به إلا الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 2, 'الذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض أولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 2, 'كيف تكفرون بالله وكنتم أمواتا فأحياكم ثم يميتكم ثم يحييكم ثم إليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 2, 'هو الذي خلق لكم ما في الأرض جميعا ثم استوى إلى السماء فسواهن سبع سماوات وهو بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 2, 'وإذ قال ربك للملائكة إني جاعل في الأرض خليفة قالوا أتجعل فيها من يفسد فيها ويسفك الدماء ونحن نسبح بحمدك ونقدس لك قال إني أعلم ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 2, 'وعلم آدم الأسماء كلها ثم عرضهم على الملائكة فقال أنبئوني بأسماء هؤلاء إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 2, 'قالوا سبحانك لا علم لنا إلا ما علمتنا إنك أنت العليم الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 2, 'قال يا آدم أنبئهم بأسمائهم فلما أنبأهم بأسمائهم قال ألم أقل لكم إني أعلم غيب السماوات والأرض وأعلم ما تبدون وما كنتم تكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 2, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى واستكبر وكان من الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 2, 'وقلنا يا آدم اسكن أنت وزوجك الجنة وكلا منها رغدا حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 2, 'فأزلهما الشيطان عنها فأخرجهما مما كانا فيه وقلنا اهبطوا بعضكم لبعض عدو ولكم في الأرض مستقر ومتاع إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 2, 'فتلقى آدم من ربه كلمات فتاب عليه إنه هو التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 2, 'قلنا اهبطوا منها جميعا فإما يأتينكم مني هدى فمن تبع هداي فلا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 2, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 2, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأوفوا بعهدي أوف بعهدكم وإياي فارهبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 2, 'وآمنوا بما أنزلت مصدقا لما معكم ولا تكونوا أول كافر به ولا تشتروا بآياتي ثمنا قليلا وإياي فاتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 2, 'ولا تلبسوا الحق بالباطل وتكتموا الحق وأنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 2, 'وأقيموا الصلاة وآتوا الزكاة واركعوا مع الراكعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 2, 'أتأمرون الناس بالبر وتنسون أنفسكم وأنتم تتلون الكتاب أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 2, 'واستعينوا بالصبر والصلاة وإنها لكبيرة إلا على الخاشعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 2, 'الذين يظنون أنهم ملاقو ربهم وأنهم إليه راجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 2, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأني فضلتكم على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 2, 'واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها شفاعة ولا يؤخذ منها عدل ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 2, 'وإذ نجيناكم من آل فرعون يسومونكم سوء العذاب يذبحون أبناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 2, 'وإذ فرقنا بكم البحر فأنجيناكم وأغرقنا آل فرعون وأنتم تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 2, 'وإذ واعدنا موسى أربعين ليلة ثم اتخذتم العجل من بعده وأنتم ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 2, 'ثم عفونا عنكم من بعد ذلك لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 2, 'وإذ آتينا موسى الكتاب والفرقان لعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 2, 'وإذ قال موسى لقومه يا قوم إنكم ظلمتم أنفسكم باتخاذكم العجل فتوبوا إلى بارئكم فاقتلوا أنفسكم ذلكم خير لكم عند بارئكم فتاب عليكم إنه هو التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 2, 'وإذ قلتم يا موسى لن نؤمن لك حتى نرى الله جهرة فأخذتكم الصاعقة وأنتم تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 2, 'ثم بعثناكم من بعد موتكم لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 2, 'وظللنا عليكم الغمام وأنزلنا عليكم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 2, 'وإذ قلنا ادخلوا هذه القرية فكلوا منها حيث شئتم رغدا وادخلوا الباب سجدا وقولوا حطة نغفر لكم خطاياكم وسنزيد المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 2, 'فبدل الذين ظلموا قولا غير الذي قيل لهم فأنزلنا على الذين ظلموا رجزا من السماء بما كانوا يفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 2, 'وإذ استسقى موسى لقومه فقلنا اضرب بعصاك الحجر فانفجرت منه اثنتا عشرة عينا قد علم كل أناس مشربهم كلوا واشربوا من رزق الله ولا تعثوا في الأرض مفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 2, 'وإذ قلتم يا موسى لن نصبر على طعام واحد فادع لنا ربك يخرج لنا مما تنبت الأرض من بقلها وقثائها وفومها وعدسها وبصلها قال أتستبدلون الذي هو أدنى بالذي هو خير اهبطوا مصرا فإن لكم ما سألتم وضربت عليهم الذلة والمسكنة وباءوا بغضب من الله ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون النبيين بغير الحق ذلك بما عصوا وكانوا يعتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 2, 'إن الذين آمنوا والذين هادوا والنصارى والصابئين من آمن بالله واليوم الآخر وعمل صالحا فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 2, 'وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 2, 'ثم توليتم من بعد ذلك فلولا فضل الله عليكم ورحمته لكنتم من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 2, 'ولقد علمتم الذين اعتدوا منكم في السبت فقلنا لهم كونوا قردة خاسئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 2, 'فجعلناها نكالا لما بين يديها وما خلفها وموعظة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 2, 'وإذ قال موسى لقومه إن الله يأمركم أن تذبحوا بقرة قالوا أتتخذنا هزوا قال أعوذ بالله أن أكون من الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 2, 'قالوا ادع لنا ربك يبين لنا ما هي قال إنه يقول إنها بقرة لا فارض ولا بكر عوان بين ذلك فافعلوا ما تؤمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 2, 'قالوا ادع لنا ربك يبين لنا ما لونها قال إنه يقول إنها بقرة صفراء فاقع لونها تسر الناظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 2, 'قالوا ادع لنا ربك يبين لنا ما هي إن البقر تشابه علينا وإنا إن شاء الله لمهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 2, 'قال إنه يقول إنها بقرة لا ذلول تثير الأرض ولا تسقي الحرث مسلمة لا شية فيها قالوا الآن جئت بالحق فذبحوها وما كادوا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 2, 'وإذ قتلتم نفسا فادارأتم فيها والله مخرج ما كنتم تكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 2, 'فقلنا اضربوه ببعضها كذلك يحيي الله الموتى ويريكم آياته لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 2, 'ثم قست قلوبكم من بعد ذلك فهي كالحجارة أو أشد قسوة وإن من الحجارة لما يتفجر منه الأنهار وإن منها لما يشقق فيخرج منه الماء وإن منها لما يهبط من خشية الله وما الله بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 2, 'أفتطمعون أن يؤمنوا لكم وقد كان فريق منهم يسمعون كلام الله ثم يحرفونه من بعد ما عقلوه وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 2, 'وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلا بعضهم إلى بعض قالوا أتحدثونهم بما فتح الله عليكم ليحاجوكم به عند ربكم أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 2, 'أولا يعلمون أن الله يعلم ما يسرون وما يعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 2, 'ومنهم أميون لا يعلمون الكتاب إلا أماني وإن هم إلا يظنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 2, 'فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا فويل لهم مما كتبت أيديهم وويل لهم مما يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 2, 'وقالوا لن تمسنا النار إلا أياما معدودة قل أتخذتم عند الله عهدا فلن يخلف الله عهده أم تقولون على الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 2, 'بلى من كسب سيئة وأحاطت به خطيئته فأولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 2, 'والذين آمنوا وعملوا الصالحات أولئك أصحاب الجنة هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 2, 'وإذ أخذنا ميثاق بني إسرائيل لا تعبدون إلا الله وبالوالدين إحسانا وذي القربى واليتامى والمساكين وقولوا للناس حسنا وأقيموا الصلاة وآتوا الزكاة ثم توليتم إلا قليلا منكم وأنتم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 2, 'وإذ أخذنا ميثاقكم لا تسفكون دماءكم ولا تخرجون أنفسكم من دياركم ثم أقررتم وأنتم تشهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 2, 'ثم أنتم هؤلاء تقتلون أنفسكم وتخرجون فريقا منكم من ديارهم تظاهرون عليهم بالإثم والعدوان وإن يأتوكم أسارى تفادوهم وهو محرم عليكم إخراجهم أفتؤمنون ببعض الكتاب وتكفرون ببعض فما جزاء من يفعل ذلك منكم إلا خزي في الحياة الدنيا ويوم القيامة يردون إلى أشد العذاب وما الله بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 2, 'أولئك الذين اشتروا الحياة الدنيا بالآخرة فلا يخفف عنهم العذاب ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 2, 'ولقد آتينا موسى الكتاب وقفينا من بعده بالرسل وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس أفكلما جاءكم رسول بما لا تهوى أنفسكم استكبرتم ففريقا كذبتم وفريقا تقتلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 2, 'وقالوا قلوبنا غلف بل لعنهم الله بكفرهم فقليلا ما يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 2, 'ولما جاءهم كتاب من عند الله مصدق لما معهم وكانوا من قبل يستفتحون على الذين كفروا فلما جاءهم ما عرفوا كفروا به فلعنة الله على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 2, 'بئسما اشتروا به أنفسهم أن يكفروا بما أنزل الله بغيا أن ينزل الله من فضله على من يشاء من عباده فباءوا بغضب على غضب وللكافرين عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 2, 'وإذا قيل لهم آمنوا بما أنزل الله قالوا نؤمن بما أنزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم قل فلم تقتلون أنبياء الله من قبل إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 2, 'ولقد جاءكم موسى بالبينات ثم اتخذتم العجل من بعده وأنتم ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 2, 'وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واسمعوا قالوا سمعنا وعصينا وأشربوا في قلوبهم العجل بكفرهم قل بئسما يأمركم به إيمانكم إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 2, 'قل إن كانت لكم الدار الآخرة عند الله خالصة من دون الناس فتمنوا الموت إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 2, 'ولن يتمنوه أبدا بما قدمت أيديهم والله عليم بالظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 2, 'ولتجدنهم أحرص الناس على حياة ومن الذين أشركوا يود أحدهم لو يعمر ألف سنة وما هو بمزحزحه من العذاب أن يعمر والله بصير بما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 2, 'قل من كان عدوا لجبريل فإنه نزله على قلبك بإذن الله مصدقا لما بين يديه وهدى وبشرى للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 2, 'من كان عدوا لله وملائكته ورسله وجبريل وميكال فإن الله عدو للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 2, 'ولقد أنزلنا إليك آيات بينات وما يكفر بها إلا الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 2, 'أوكلما عاهدوا عهدا نبذه فريق منهم بل أكثرهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 2, 'ولما جاءهم رسول من عند الله مصدق لما معهم نبذ فريق من الذين أوتوا الكتاب كتاب الله وراء ظهورهم كأنهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 2, 'واتبعوا ما تتلو الشياطين على ملك سليمان وما كفر سليمان ولكن الشياطين كفروا يعلمون الناس السحر وما أنزل على الملكين ببابل هاروت وماروت وما يعلمان من أحد حتى يقولا إنما نحن فتنة فلا تكفر فيتعلمون منهما ما يفرقون به بين المرء وزوجه وما هم بضارين به من أحد إلا بإذن الله ويتعلمون ما يضرهم ولا ينفعهم ولقد علموا لمن اشتراه ما له في الآخرة من خلاق ولبئس ما شروا به أنفسهم لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 2, 'ولو أنهم آمنوا واتقوا لمثوبة من عند الله خير لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 2, 'يا أيها الذين آمنوا لا تقولوا راعنا وقولوا انظرنا واسمعوا وللكافرين عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 2, 'ما يود الذين كفروا من أهل الكتاب ولا المشركين أن ينزل عليكم من خير من ربكم والله يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 2, 'ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها ألم تعلم أن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 2, 'ألم تعلم أن الله له ملك السماوات والأرض وما لكم من دون الله من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 2, 'أم تريدون أن تسألوا رسولكم كما سئل موسى من قبل ومن يتبدل الكفر بالإيمان فقد ضل سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 2, 'ود كثير من أهل الكتاب لو يردونكم من بعد إيمانكم كفارا حسدا من عند أنفسهم من بعد ما تبين لهم الحق فاعفوا واصفحوا حتى يأتي الله بأمره إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 2, 'وأقيموا الصلاة وآتوا الزكاة وما تقدموا لأنفسكم من خير تجدوه عند الله إن الله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 2, 'وقالوا لن يدخل الجنة إلا من كان هودا أو نصارى تلك أمانيهم قل هاتوا برهانكم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 2, 'بلى من أسلم وجهه لله وهو محسن فله أجره عند ربه ولا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 2, 'وقالت اليهود ليست النصارى على شيء وقالت النصارى ليست اليهود على شيء وهم يتلون الكتاب كذلك قال الذين لا يعلمون مثل قولهم فالله يحكم بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 2, 'ومن أظلم ممن منع مساجد الله أن يذكر فيها اسمه وسعى في خرابها أولئك ما كان لهم أن يدخلوها إلا خائفين لهم في الدنيا خزي ولهم في الآخرة عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 2, 'ولله المشرق والمغرب فأينما تولوا فثم وجه الله إن الله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 2, 'وقالوا اتخذ الله ولدا سبحانه بل له ما في السماوات والأرض كل له قانتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 2, 'بديع السماوات والأرض وإذا قضى أمرا فإنما يقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 2, 'وقال الذين لا يعلمون لولا يكلمنا الله أو تأتينا آية كذلك قال الذين من قبلهم مثل قولهم تشابهت قلوبهم قد بينا الآيات لقوم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 2, 'إنا أرسلناك بالحق بشيرا ونذيرا ولا تسأل عن أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 2, 'ولن ترضى عنك اليهود ولا النصارى حتى تتبع ملتهم قل إن هدى الله هو الهدى ولئن اتبعت أهواءهم بعد الذي جاءك من العلم ما لك من الله من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 2, 'الذين آتيناهم الكتاب يتلونه حق تلاوته أولئك يؤمنون به ومن يكفر به فأولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 2, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأني فضلتكم على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 2, 'واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها عدل ولا تنفعها شفاعة ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 2, 'وإذ ابتلى إبراهيم ربه بكلمات فأتمهن قال إني جاعلك للناس إماما قال ومن ذريتي قال لا ينال عهدي الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 2, 'وإذ جعلنا البيت مثابة للناس وأمنا واتخذوا من مقام إبراهيم مصلى وعهدنا إلى إبراهيم وإسماعيل أن طهرا بيتي للطائفين والعاكفين والركع السجود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 2, 'وإذ قال إبراهيم رب اجعل هذا بلدا آمنا وارزق أهله من الثمرات من آمن منهم بالله واليوم الآخر قال ومن كفر فأمتعه قليلا ثم أضطره إلى عذاب النار وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 2, 'وإذ يرفع إبراهيم القواعد من البيت وإسماعيل ربنا تقبل منا إنك أنت السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 2, 'ربنا واجعلنا مسلمين لك ومن ذريتنا أمة مسلمة لك وأرنا مناسكنا وتب علينا إنك أنت التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 2, 'ربنا وابعث فيهم رسولا منهم يتلو عليهم آياتك ويعلمهم الكتاب والحكمة ويزكيهم إنك أنت العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 2, 'ومن يرغب عن ملة إبراهيم إلا من سفه نفسه ولقد اصطفيناه في الدنيا وإنه في الآخرة لمن الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 2, 'إذ قال له ربه أسلم قال أسلمت لرب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 2, 'ووصى بها إبراهيم بنيه ويعقوب يا بني إن الله اصطفى لكم الدين فلا تموتن إلا وأنتم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 2, 'أم كنتم شهداء إذ حضر يعقوب الموت إذ قال لبنيه ما تعبدون من بعدي قالوا نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلها واحدا ونحن له مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 2, 'تلك أمة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسألون عما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 2, 'وقالوا كونوا هودا أو نصارى تهتدوا قل بل ملة إبراهيم حنيفا وما كان من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 2, 'قولوا آمنا بالله وما أنزل إلينا وما أنزل إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى وما أوتي النبيون من ربهم لا نفرق بين أحد منهم ونحن له مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 2, 'فإن آمنوا بمثل ما آمنتم به فقد اهتدوا وإن تولوا فإنما هم في شقاق فسيكفيكهم الله وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 2, 'صبغة الله ومن أحسن من الله صبغة ونحن له عابدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 2, 'قل أتحاجوننا في الله وهو ربنا وربكم ولنا أعمالنا ولكم أعمالكم ونحن له مخلصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 2, 'أم تقولون إن إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط كانوا هودا أو نصارى قل أأنتم أعلم أم الله ومن أظلم ممن كتم شهادة عنده من الله وما الله بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 2, 'تلك أمة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسألون عما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 2, 'سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها قل لله المشرق والمغرب يهدي من يشاء إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 2, 'وكذلك جعلناكم أمة وسطا لتكونوا شهداء على الناس ويكون الرسول عليكم شهيدا وما جعلنا القبلة التي كنت عليها إلا لنعلم من يتبع الرسول ممن ينقلب على عقبيه وإن كانت لكبيرة إلا على الذين هدى الله وما كان الله ليضيع إيمانكم إن الله بالناس لرءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 2, 'قد نرى تقلب وجهك في السماء فلنولينك قبلة ترضاها فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره وإن الذين أوتوا الكتاب ليعلمون أنه الحق من ربهم وما الله بغافل عما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 2, 'ولئن أتيت الذين أوتوا الكتاب بكل آية ما تبعوا قبلتك وما أنت بتابع قبلتهم وما بعضهم بتابع قبلة بعض ولئن اتبعت أهواءهم من بعد ما جاءك من العلم إنك إذا لمن الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 2, 'الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم وإن فريقا منهم ليكتمون الحق وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 2, 'الحق من ربك فلا تكونن من الممترين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 2, 'ولكل وجهة هو موليها فاستبقوا الخيرات أين ما تكونوا يأت بكم الله جميعا إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 2, 'ومن حيث خرجت فول وجهك شطر المسجد الحرام وإنه للحق من ربك وما الله بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 2, 'ومن حيث خرجت فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره لئلا يكون للناس عليكم حجة إلا الذين ظلموا منهم فلا تخشوهم واخشوني ولأتم نعمتي عليكم ولعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 2, 'كما أرسلنا فيكم رسولا منكم يتلو عليكم آياتنا ويزكيكم ويعلمكم الكتاب والحكمة ويعلمكم ما لم تكونوا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 2, 'فاذكروني أذكركم واشكروا لي ولا تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 2, 'يا أيها الذين آمنوا استعينوا بالصبر والصلاة إن الله مع الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 2, 'ولا تقولوا لمن يقتل في سبيل الله أموات بل أحياء ولكن لا تشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 2, 'ولنبلونكم بشيء من الخوف والجوع ونقص من الأموال والأنفس والثمرات وبشر الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 2, 'الذين إذا أصابتهم مصيبة قالوا إنا لله وإنا إليه راجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 2, 'أولئك عليهم صلوات من ربهم ورحمة وأولئك هم المهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 2, 'إن الصفا والمروة من شعائر الله فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما ومن تطوع خيرا فإن الله شاكر عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 2, 'إن الذين يكتمون ما أنزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب أولئك يلعنهم الله ويلعنهم اللاعنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 2, 'إلا الذين تابوا وأصلحوا وبينوا فأولئك أتوب عليهم وأنا التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 2, 'إن الذين كفروا وماتوا وهم كفار أولئك عليهم لعنة الله والملائكة والناس أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 2, 'خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 2, 'وإلهكم إله واحد لا إله إلا هو الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 2, 'إن في خلق السماوات والأرض واختلاف الليل والنهار والفلك التي تجري في البحر بما ينفع الناس وما أنزل الله من السماء من ماء فأحيا به الأرض بعد موتها وبث فيها من كل دابة وتصريف الرياح والسحاب المسخر بين السماء والأرض لآيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 2, 'ومن الناس من يتخذ من دون الله أندادا يحبونهم كحب الله والذين آمنوا أشد حبا لله ولو يرى الذين ظلموا إذ يرون العذاب أن القوة لله جميعا وأن الله شديد العذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 2, 'إذ تبرأ الذين اتبعوا من الذين اتبعوا ورأوا العذاب وتقطعت بهم الأسباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 2, 'وقال الذين اتبعوا لو أن لنا كرة فنتبرأ منهم كما تبرءوا منا كذلك يريهم الله أعمالهم حسرات عليهم وما هم بخارجين من النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 2, 'يا أيها الناس كلوا مما في الأرض حلالا طيبا ولا تتبعوا خطوات الشيطان إنه لكم عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 2, 'إنما يأمركم بالسوء والفحشاء وأن تقولوا على الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 2, 'وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما ألفينا عليه آباءنا أولو كان آباؤهم لا يعقلون شيئا ولا يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 2, 'ومثل الذين كفروا كمثل الذي ينعق بما لا يسمع إلا دعاء ونداء صم بكم عمي فهم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 2, 'يا أيها الذين آمنوا كلوا من طيبات ما رزقناكم واشكروا لله إن كنتم إياه تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 2, 'إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل به لغير الله فمن اضطر غير باغ ولا عاد فلا إثم عليه إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 2, 'إن الذين يكتمون ما أنزل الله من الكتاب ويشترون به ثمنا قليلا أولئك ما يأكلون في بطونهم إلا النار ولا يكلمهم الله يوم القيامة ولا يزكيهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 2, 'أولئك الذين اشتروا الضلالة بالهدى والعذاب بالمغفرة فما أصبرهم على النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 2, 'ذلك بأن الله نزل الكتاب بالحق وإن الذين اختلفوا في الكتاب لفي شقاق بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(177, 1, 2, 'ليس البر أن تولوا وجوهكم قبل المشرق والمغرب ولكن البر من آمن بالله واليوم الآخر والملائكة والكتاب والنبيين وآتى المال على حبه ذوي القربى واليتامى والمساكين وابن السبيل والسائلين وفي الرقاب وأقام الصلاة وآتى الزكاة والموفون بعهدهم إذا عاهدوا والصابرين في البأساء والضراء وحين البأس أولئك الذين صدقوا وأولئك هم المتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(178, 1, 2, 'يا أيها الذين آمنوا كتب عليكم القصاص في القتلى الحر بالحر والعبد بالعبد والأنثى بالأنثى فمن عفي له من أخيه شيء فاتباع بالمعروف وأداء إليه بإحسان ذلك تخفيف من ربكم ورحمة فمن اعتدى بعد ذلك فله عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(179, 1, 2, 'ولكم في القصاص حياة يا أولي الألباب لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(180, 1, 2, 'كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين بالمعروف حقا على المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(181, 1, 2, 'فمن بدله بعدما سمعه فإنما إثمه على الذين يبدلونه إن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(182, 1, 2, 'فمن خاف من موص جنفا أو إثما فأصلح بينهم فلا إثم عليه إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(183, 1, 2, 'يا أيها الذين آمنوا كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(184, 1, 2, 'أياما معدودات فمن كان منكم مريضا أو على سفر فعدة من أيام أخر وعلى الذين يطيقونه فدية طعام مسكين فمن تطوع خيرا فهو خير له وأن تصوموا خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(185, 1, 2, 'شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا أو على سفر فعدة من أيام أخر يريد الله بكم اليسر ولا يريد بكم العسر ولتكملوا العدة ولتكبروا الله على ما هداكم ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(186, 1, 2, 'وإذا سألك عبادي عني فإني قريب أجيب دعوة الداع إذا دعان فليستجيبوا لي وليؤمنوا بي لعلهم يرشدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(187, 1, 2, 'أحل لكم ليلة الصيام الرفث إلى نسائكم هن لباس لكم وأنتم لباس لهن علم الله أنكم كنتم تختانون أنفسكم فتاب عليكم وعفا عنكم فالآن باشروهن وابتغوا ما كتب الله لكم وكلوا واشربوا حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر ثم أتموا الصيام إلى الليل ولا تباشروهن وأنتم عاكفون في المساجد تلك حدود الله فلا تقربوها كذلك يبين الله آياته للناس لعلهم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(188, 1, 2, 'ولا تأكلوا أموالكم بينكم بالباطل وتدلوا بها إلى الحكام لتأكلوا فريقا من أموال الناس بالإثم وأنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(189, 1, 2, 'يسألونك عن الأهلة قل هي مواقيت للناس والحج وليس البر بأن تأتوا البيوت من ظهورها ولكن البر من اتقى وأتوا البيوت من أبوابها واتقوا الله لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(190, 1, 2, 'وقاتلوا في سبيل الله الذين يقاتلونكم ولا تعتدوا إن الله لا يحب المعتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(191, 1, 2, 'واقتلوهم حيث ثقفتموهم وأخرجوهم من حيث أخرجوكم والفتنة أشد من القتل ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم فيه فإن قاتلوكم فاقتلوهم كذلك جزاء الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(192, 1, 2, 'فإن انتهوا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(193, 1, 2, 'وقاتلوهم حتى لا تكون فتنة ويكون الدين لله فإن انتهوا فلا عدوان إلا على الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(194, 1, 2, 'الشهر الحرام بالشهر الحرام والحرمات قصاص فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم واتقوا الله واعلموا أن الله مع المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(195, 1, 2, 'وأنفقوا في سبيل الله ولا تلقوا بأيديكم إلى التهلكة وأحسنوا إن الله يحب المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(196, 1, 2, 'وأتموا الحج والعمرة لله فإن أحصرتم فما استيسر من الهدي ولا تحلقوا رءوسكم حتى يبلغ الهدي محله فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة أو نسك فإذا أمنتم فمن تمتع بالعمرة إلى الحج فما استيسر من الهدي فمن لم يجد فصيام ثلاثة أيام في الحج وسبعة إذا رجعتم تلك عشرة كاملة ذلك لمن لم يكن أهله حاضري المسجد الحرام واتقوا الله واعلموا أن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(197, 1, 2, 'الحج أشهر معلومات فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج وما تفعلوا من خير يعلمه الله وتزودوا فإن خير الزاد التقوى واتقون يا أولي الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(198, 1, 2, 'ليس عليكم جناح أن تبتغوا فضلا من ربكم فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام واذكروه كما هداكم وإن كنتم من قبله لمن الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(199, 1, 2, 'ثم أفيضوا من حيث أفاض الناس واستغفروا الله إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(200, 1, 2, 'فإذا قضيتم مناسككم فاذكروا الله كذكركم آباءكم أو أشد ذكرا فمن الناس من يقول ربنا آتنا في الدنيا وما له في الآخرة من خلاق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(201, 1, 2, 'ومنهم من يقول ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(202, 1, 2, 'أولئك لهم نصيب مما كسبوا والله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(203, 1, 2, 'واذكروا الله في أيام معدودات فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه لمن اتقى واتقوا الله واعلموا أنكم إليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(204, 1, 2, 'ومن الناس من يعجبك قوله في الحياة الدنيا ويشهد الله على ما في قلبه وهو ألد الخصام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(205, 1, 2, 'وإذا تولى سعى في الأرض ليفسد فيها ويهلك الحرث والنسل والله لا يحب الفساد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(206, 1, 2, 'وإذا قيل له اتق الله أخذته العزة بالإثم فحسبه جهنم ولبئس المهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(207, 1, 2, 'ومن الناس من يشري نفسه ابتغاء مرضات الله والله رءوف بالعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(208, 1, 2, 'يا أيها الذين آمنوا ادخلوا في السلم كافة ولا تتبعوا خطوات الشيطان إنه لكم عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(209, 1, 2, 'فإن زللتم من بعد ما جاءتكم البينات فاعلموا أن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(210, 1, 2, 'هل ينظرون إلا أن يأتيهم الله في ظلل من الغمام والملائكة وقضي الأمر وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(211, 1, 2, 'سل بني إسرائيل كم آتيناهم من آية بينة ومن يبدل نعمة الله من بعد ما جاءته فإن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(212, 1, 2, 'زين للذين كفروا الحياة الدنيا ويسخرون من الذين آمنوا والذين اتقوا فوقهم يوم القيامة والله يرزق من يشاء بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(213, 1, 2, 'كان الناس أمة واحدة فبعث الله النبيين مبشرين ومنذرين وأنزل معهم الكتاب بالحق ليحكم بين الناس فيما اختلفوا فيه وما اختلف فيه إلا الذين أوتوه من بعد ما جاءتهم البينات بغيا بينهم فهدى الله الذين آمنوا لما اختلفوا فيه من الحق بإذنه والله يهدي من يشاء إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(214, 1, 2, 'أم حسبتم أن تدخلوا الجنة ولما يأتكم مثل الذين خلوا من قبلكم مستهم البأساء والضراء وزلزلوا حتى يقول الرسول والذين آمنوا معه متى نصر الله ألا إن نصر الله قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(215, 1, 2, 'يسألونك ماذا ينفقون قل ما أنفقتم من خير فللوالدين والأقربين واليتامى والمساكين وابن السبيل وما تفعلوا من خير فإن الله به عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(216, 1, 2, 'كتب عليكم القتال وهو كره لكم وعسى أن تكرهوا شيئا وهو خير لكم وعسى أن تحبوا شيئا وهو شر لكم والله يعلم وأنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(217, 1, 2, 'يسألونك عن الشهر الحرام قتال فيه قل قتال فيه كبير وصد عن سبيل الله وكفر به والمسجد الحرام وإخراج أهله منه أكبر عند الله والفتنة أكبر من القتل ولا يزالون يقاتلونكم حتى يردوكم عن دينكم إن استطاعوا ومن يرتدد منكم عن دينه فيمت وهو كافر فأولئك حبطت أعمالهم في الدنيا والآخرة وأولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(218, 1, 2, 'إن الذين آمنوا والذين هاجروا وجاهدوا في سبيل الله أولئك يرجون رحمت الله والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(219, 1, 2, 'يسألونك عن الخمر والميسر قل فيهما إثم كبير ومنافع للناس وإثمهما أكبر من نفعهما ويسألونك ماذا ينفقون قل العفو كذلك يبين الله لكم الآيات لعلكم تتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(220, 1, 2, 'في الدنيا والآخرة ويسألونك عن اليتامى قل إصلاح لهم خير وإن تخالطوهم فإخوانكم والله يعلم المفسد من المصلح ولو شاء الله لأعنتكم إن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(221, 1, 2, 'ولا تنكحوا المشركات حتى يؤمن ولأمة مؤمنة خير من مشركة ولو أعجبتكم ولا تنكحوا المشركين حتى يؤمنوا ولعبد مؤمن خير من مشرك ولو أعجبكم أولئك يدعون إلى النار والله يدعو إلى الجنة والمغفرة بإذنه ويبين آياته للناس لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(222, 1, 2, 'ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فإذا تطهرن فأتوهن من حيث أمركم الله إن الله يحب التوابين ويحب المتطهرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(223, 1, 2, 'نساؤكم حرث لكم فأتوا حرثكم أنى شئتم وقدموا لأنفسكم واتقوا الله واعلموا أنكم ملاقوه وبشر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(224, 1, 2, 'ولا تجعلوا الله عرضة لأيمانكم أن تبروا وتتقوا وتصلحوا بين الناس والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(225, 1, 2, 'لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(226, 1, 2, 'للذين يؤلون من نسائهم تربص أربعة أشهر فإن فاءوا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(227, 1, 2, 'وإن عزموا الطلاق فإن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(228, 1, 2, 'والمطلقات يتربصن بأنفسهن ثلاثة قروء ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن إن كن يؤمن بالله واليوم الآخر وبعولتهن أحق بردهن في ذلك إن أرادوا إصلاحا ولهن مثل الذي عليهن بالمعروف وللرجال عليهن درجة والله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(229, 1, 2, 'الطلاق مرتان فإمساك بمعروف أو تسريح بإحسان ولا يحل لكم أن تأخذوا مما آتيتموهن شيئا إلا أن يخافا ألا يقيما حدود الله فإن خفتم ألا يقيما حدود الله فلا جناح عليهما فيما افتدت به تلك حدود الله فلا تعتدوها ومن يتعد حدود الله فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(230, 1, 2, 'فإن طلقها فلا تحل له من بعد حتى تنكح زوجا غيره فإن طلقها فلا جناح عليهما أن يتراجعا إن ظنا أن يقيما حدود الله وتلك حدود الله يبينها لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(231, 1, 2, 'وإذا طلقتم النساء فبلغن أجلهن فأمسكوهن بمعروف أو سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا ومن يفعل ذلك فقد ظلم نفسه ولا تتخذوا آيات الله هزوا واذكروا نعمت الله عليكم وما أنزل عليكم من الكتاب والحكمة يعظكم به واتقوا الله واعلموا أن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(232, 1, 2, 'وإذا طلقتم النساء فبلغن أجلهن فلا تعضلوهن أن ينكحن أزواجهن إذا تراضوا بينهم بالمعروف ذلك يوعظ به من كان منكم يؤمن بالله واليوم الآخر ذلكم أزكى لكم وأطهر والله يعلم وأنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(233, 1, 2, 'والوالدات يرضعن أولادهن حولين كاملين لمن أراد أن يتم الرضاعة وعلى المولود له رزقهن وكسوتهن بالمعروف لا تكلف نفس إلا وسعها لا تضار والدة بولدها ولا مولود له بولده وعلى الوارث مثل ذلك فإن أرادا فصالا عن تراض منهما وتشاور فلا جناح عليهما وإن أردتم أن تسترضعوا أولادكم فلا جناح عليكم إذا سلمتم ما آتيتم بالمعروف واتقوا الله واعلموا أن الله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(234, 1, 2, 'والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا فإذا بلغن أجلهن فلا جناح عليكم فيما فعلن في أنفسهن بالمعروف والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(235, 1, 2, 'ولا جناح عليكم فيما عرضتم به من خطبة النساء أو أكننتم في أنفسكم علم الله أنكم ستذكرونهن ولكن لا تواعدوهن سرا إلا أن تقولوا قولا معروفا ولا تعزموا عقدة النكاح حتى يبلغ الكتاب أجله واعلموا أن الله يعلم ما في أنفسكم فاحذروه واعلموا أن الله غفور حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(236, 1, 2, 'لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف حقا على المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(237, 1, 2, 'وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم إلا أن يعفون أو يعفو الذي بيده عقدة النكاح وأن تعفوا أقرب للتقوى ولا تنسوا الفضل بينكم إن الله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(238, 1, 2, 'حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(239, 1, 2, 'فإن خفتم فرجالا أو ركبانا فإذا أمنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(240, 1, 2, 'والذين يتوفون منكم ويذرون أزواجا وصية لأزواجهم متاعا إلى الحول غير إخراج فإن خرجن فلا جناح عليكم في ما فعلن في أنفسهن من معروف والله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(241, 1, 2, 'وللمطلقات متاع بالمعروف حقا على المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(242, 1, 2, 'كذلك يبين الله لكم آياته لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(243, 1, 2, 'ألم تر إلى الذين خرجوا من ديارهم وهم ألوف حذر الموت فقال لهم الله موتوا ثم أحياهم إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(244, 1, 2, 'وقاتلوا في سبيل الله واعلموا أن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(245, 1, 2, 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له أضعافا كثيرة والله يقبض ويبسط وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(246, 1, 2, 'ألم تر إلى الملإ من بني إسرائيل من بعد موسى إذ قالوا لنبي لهم ابعث لنا ملكا نقاتل في سبيل الله قال هل عسيتم إن كتب عليكم القتال ألا تقاتلوا قالوا وما لنا ألا نقاتل في سبيل الله وقد أخرجنا من ديارنا وأبنائنا فلما كتب عليهم القتال تولوا إلا قليلا منهم والله عليم بالظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(247, 1, 2, 'وقال لهم نبيهم إن الله قد بعث لكم طالوت ملكا قالوا أنى يكون له الملك علينا ونحن أحق بالملك منه ولم يؤت سعة من المال قال إن الله اصطفاه عليكم وزاده بسطة في العلم والجسم والله يؤتي ملكه من يشاء والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(248, 1, 2, 'وقال لهم نبيهم إن آية ملكه أن يأتيكم التابوت فيه سكينة من ربكم وبقية مما ترك آل موسى وآل هارون تحمله الملائكة إن في ذلك لآية لكم إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(249, 1, 2, 'فلما فصل طالوت بالجنود قال إن الله مبتليكم بنهر فمن شرب منه فليس مني ومن لم يطعمه فإنه مني إلا من اغترف غرفة بيده فشربوا منه إلا قليلا منهم فلما جاوزه هو والذين آمنوا معه قالوا لا طاقة لنا اليوم بجالوت وجنوده قال الذين يظنون أنهم ملاقو الله كم من فئة قليلة غلبت فئة كثيرة بإذن الله والله مع الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(250, 1, 2, 'ولما برزوا لجالوت وجنوده قالوا ربنا أفرغ علينا صبرا وثبت أقدامنا وانصرنا على القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(251, 1, 2, 'فهزموهم بإذن الله وقتل داوود جالوت وآتاه الله الملك والحكمة وعلمه مما يشاء ولولا دفع الله الناس بعضهم ببعض لفسدت الأرض ولكن الله ذو فضل على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(252, 1, 2, 'تلك آيات الله نتلوها عليك بالحق وإنك لمن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(253, 1, 2, 'تلك الرسل فضلنا بعضهم على بعض منهم من كلم الله ورفع بعضهم درجات وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس ولو شاء الله ما اقتتل الذين من بعدهم من بعد ما جاءتهم البينات ولكن اختلفوا فمنهم من آمن ومنهم من كفر ولو شاء الله ما اقتتلوا ولكن الله يفعل ما يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(254, 1, 2, 'يا أيها الذين آمنوا أنفقوا مما رزقناكم من قبل أن يأتي يوم لا بيع فيه ولا خلة ولا شفاعة والكافرون هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(255, 1, 2, 'الله لا إله إلا هو الحي القيوم لا تأخذه سنة ولا نوم له ما في السماوات وما في الأرض من ذا الذي يشفع عنده إلا بإذنه يعلم ما بين أيديهم وما خلفهم ولا يحيطون بشيء من علمه إلا بما شاء وسع كرسيه السماوات والأرض ولا يئوده حفظهما وهو العلي العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(256, 1, 2, 'لا إكراه في الدين قد تبين الرشد من الغي فمن يكفر بالطاغوت ويؤمن بالله فقد استمسك بالعروة الوثقى لا انفصام لها والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(257, 1, 2, 'الله ولي الذين آمنوا يخرجهم من الظلمات إلى النور والذين كفروا أولياؤهم الطاغوت يخرجونهم من النور إلى الظلمات أولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(258, 1, 2, 'ألم تر إلى الذي حاج إبراهيم في ربه أن آتاه الله الملك إذ قال إبراهيم ربي الذي يحيي ويميت قال أنا أحيي وأميت قال إبراهيم فإن الله يأتي بالشمس من المشرق فأت بها من المغرب فبهت الذي كفر والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(259, 1, 2, 'أو كالذي مر على قرية وهي خاوية على عروشها قال أنى يحيي هذه الله بعد موتها فأماته الله مائة عام ثم بعثه قال كم لبثت قال لبثت يوما أو بعض يوم قال بل لبثت مائة عام فانظر إلى طعامك وشرابك لم يتسنه وانظر إلى حمارك ولنجعلك آية للناس وانظر إلى العظام كيف ننشزها ثم نكسوها لحما فلما تبين له قال أعلم أن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(260, 1, 2, 'وإذ قال إبراهيم رب أرني كيف تحيي الموتى قال أولم تؤمن قال بلى ولكن ليطمئن قلبي قال فخذ أربعة من الطير فصرهن إليك ثم اجعل على كل جبل منهن جزءا ثم ادعهن يأتينك سعيا واعلم أن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(261, 1, 2, 'مثل الذين ينفقون أموالهم في سبيل الله كمثل حبة أنبتت سبع سنابل في كل سنبلة مائة حبة والله يضاعف لمن يشاء والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(262, 1, 2, 'الذين ينفقون أموالهم في سبيل الله ثم لا يتبعون ما أنفقوا منا ولا أذى لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(263, 1, 2, 'قول معروف ومغفرة خير من صدقة يتبعها أذى والله غني حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(264, 1, 2, 'يا أيها الذين آمنوا لا تبطلوا صدقاتكم بالمن والأذى كالذي ينفق ماله رئاء الناس ولا يؤمن بالله واليوم الآخر فمثله كمثل صفوان عليه تراب فأصابه وابل فتركه صلدا لا يقدرون على شيء مما كسبوا والله لا يهدي القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(265, 1, 2, 'ومثل الذين ينفقون أموالهم ابتغاء مرضات الله وتثبيتا من أنفسهم كمثل جنة بربوة أصابها وابل فآتت أكلها ضعفين فإن لم يصبها وابل فطل والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(266, 1, 2, 'أيود أحدكم أن تكون له جنة من نخيل وأعناب تجري من تحتها الأنهار له فيها من كل الثمرات وأصابه الكبر وله ذرية ضعفاء فأصابها إعصار فيه نار فاحترقت كذلك يبين الله لكم الآيات لعلكم تتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(267, 1, 2, 'يا أيها الذين آمنوا أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه واعلموا أن الله غني حميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(268, 1, 2, 'الشيطان يعدكم الفقر ويأمركم بالفحشاء والله يعدكم مغفرة منه وفضلا والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(269, 1, 2, 'يؤتي الحكمة من يشاء ومن يؤت الحكمة فقد أوتي خيرا كثيرا وما يذكر إلا أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(270, 1, 2, 'وما أنفقتم من نفقة أو نذرتم من نذر فإن الله يعلمه وما للظالمين من أنصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(271, 1, 2, 'إن تبدوا الصدقات فنعما هي وإن تخفوها وتؤتوها الفقراء فهو خير لكم ويكفر عنكم من سيئاتكم والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(272, 1, 2, 'ليس عليك هداهم ولكن الله يهدي من يشاء وما تنفقوا من خير فلأنفسكم وما تنفقون إلا ابتغاء وجه الله وما تنفقوا من خير يوف إليكم وأنتم لا تظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(273, 1, 2, 'للفقراء الذين أحصروا في سبيل الله لا يستطيعون ضربا في الأرض يحسبهم الجاهل أغنياء من التعفف تعرفهم بسيماهم لا يسألون الناس إلحافا وما تنفقوا من خير فإن الله به عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(274, 1, 2, 'الذين ينفقون أموالهم بالليل والنهار سرا وعلانية فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(275, 1, 2, 'الذين يأكلون الربا لا يقومون إلا كما يقوم الذي يتخبطه الشيطان من المس ذلك بأنهم قالوا إنما البيع مثل الربا وأحل الله البيع وحرم الربا فمن جاءه موعظة من ربه فانتهى فله ما سلف وأمره إلى الله ومن عاد فأولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(276, 1, 2, 'يمحق الله الربا ويربي الصدقات والله لا يحب كل كفار أثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(277, 1, 2, 'إن الذين آمنوا وعملوا الصالحات وأقاموا الصلاة وآتوا الزكاة لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(278, 1, 2, 'يا أيها الذين آمنوا اتقوا الله وذروا ما بقي من الربا إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(279, 1, 2, 'فإن لم تفعلوا فأذنوا بحرب من الله ورسوله وإن تبتم فلكم رءوس أموالكم لا تظلمون ولا تظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(280, 1, 2, 'وإن كان ذو عسرة فنظرة إلى ميسرة وأن تصدقوا خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(281, 1, 2, 'واتقوا يوما ترجعون فيه إلى الله ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(282, 1, 2, 'يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه وليكتب بينكم كاتب بالعدل ولا يأب كاتب أن يكتب كما علمه الله فليكتب وليملل الذي عليه الحق وليتق الله ربه ولا يبخس منه شيئا فإن كان الذي عليه الحق سفيها أو ضعيفا أو لا يستطيع أن يمل هو فليملل وليه بالعدل واستشهدوا شهيدين من رجالكم فإن لم يكونا رجلين فرجل وامرأتان ممن ترضون من الشهداء أن تضل إحداهما فتذكر إحداهما الأخرى ولا يأب الشهداء إذا ما دعوا ولا تسأموا أن تكتبوه صغيرا أو كبيرا إلى أجله ذلكم أقسط عند الله وأقوم للشهادة وأدنى ألا ترتابوا إلا أن تكون تجارة حاضرة تديرونها بينكم فليس عليكم جناح ألا تكتبوها وأشهدوا إذا تبايعتم ولا يضار كاتب ولا شهيد وإن تفعلوا فإنه فسوق بكم واتقوا الله ويعلمكم الله والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(283, 1, 2, 'وإن كنتم على سفر ولم تجدوا كاتبا فرهان مقبوضة فإن أمن بعضكم بعضا فليؤد الذي اؤتمن أمانته وليتق الله ربه ولا تكتموا الشهادة ومن يكتمها فإنه آثم قلبه والله بما تعملون عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(284, 1, 2, 'لله ما في السماوات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(285, 1, 2, 'آمن الرسول بما أنزل إليه من ربه والمؤمنون كل آمن بالله وملائكته وكتبه ورسله لا نفرق بين أحد من رسله وقالوا سمعنا وأطعنا غفرانك ربنا وإليك المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(286, 1, 2, 'لا يكلف الله نفسا إلا وسعها لها ما كسبت وعليها ما اكتسبت ربنا لا تؤاخذنا إن نسينا أو أخطأنا ربنا ولا تحمل علينا إصرا كما حملته على الذين من قبلنا ربنا ولا تحملنا ما لا طاقة لنا به واعف عنا واغفر لنا وارحمنا أنت مولانا فانصرنا على القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 3, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 3, 'الله لا إله إلا هو الحي القيوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 3, 'نزل عليك الكتاب بالحق مصدقا لما بين يديه وأنزل التوراة والإنجيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 3, 'من قبل هدى للناس وأنزل الفرقان إن الذين كفروا بآيات الله لهم عذاب شديد والله عزيز ذو انتقام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 3, 'إن الله لا يخفى عليه شيء في الأرض ولا في السماء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 3, 'هو الذي يصوركم في الأرحام كيف يشاء لا إله إلا هو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 3, 'هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب وأخر متشابهات فأما الذين في قلوبهم زيغ فيتبعون ما تشابه منه ابتغاء الفتنة وابتغاء تأويله وما يعلم تأويله إلا الله والراسخون في العلم يقولون آمنا به كل من عند ربنا وما يذكر إلا أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 3, 'ربنا لا تزغ قلوبنا بعد إذ هديتنا وهب لنا من لدنك رحمة إنك أنت الوهاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 3, 'ربنا إنك جامع الناس ليوم لا ريب فيه إن الله لا يخلف الميعاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 3, 'إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا وأولئك هم وقود النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 3, 'كدأب آل فرعون والذين من قبلهم كذبوا بآياتنا فأخذهم الله بذنوبهم والله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 3, 'قل للذين كفروا ستغلبون وتحشرون إلى جهنم وبئس المهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 3, 'قد كان لكم آية في فئتين التقتا فئة تقاتل في سبيل الله وأخرى كافرة يرونهم مثليهم رأي العين والله يؤيد بنصره من يشاء إن في ذلك لعبرة لأولي الأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 3, 'زين للناس حب الشهوات من النساء والبنين والقناطير المقنطرة من الذهب والفضة والخيل المسومة والأنعام والحرث ذلك متاع الحياة الدنيا والله عنده حسن المآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 3, 'قل أؤنبئكم بخير من ذلكم للذين اتقوا عند ربهم جنات تجري من تحتها الأنهار خالدين فيها وأزواج مطهرة ورضوان من الله والله بصير بالعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 3, 'الذين يقولون ربنا إننا آمنا فاغفر لنا ذنوبنا وقنا عذاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 3, 'الصابرين والصادقين والقانتين والمنفقين والمستغفرين بالأسحار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 3, 'شهد الله أنه لا إله إلا هو والملائكة وأولو العلم قائما بالقسط لا إله إلا هو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 3, 'إن الدين عند الله الإسلام وما اختلف الذين أوتوا الكتاب إلا من بعد ما جاءهم العلم بغيا بينهم ومن يكفر بآيات الله فإن الله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 3, 'فإن حاجوك فقل أسلمت وجهي لله ومن اتبعن وقل للذين أوتوا الكتاب والأميين أأسلمتم فإن أسلموا فقد اهتدوا وإن تولوا فإنما عليك البلاغ والله بصير بالعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 3, 'إن الذين يكفرون بآيات الله ويقتلون النبيين بغير حق ويقتلون الذين يأمرون بالقسط من الناس فبشرهم بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 3, 'أولئك الذين حبطت أعمالهم في الدنيا والآخرة وما لهم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 3, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يدعون إلى كتاب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 3, 'ذلك بأنهم قالوا لن تمسنا النار إلا أياما معدودات وغرهم في دينهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 3, 'فكيف إذا جمعناهم ليوم لا ريب فيه ووفيت كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 3, 'قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء بيدك الخير إنك على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 3, 'تولج الليل في النهار وتولج النهار في الليل وتخرج الحي من الميت وتخرج الميت من الحي وترزق من تشاء بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 3, 'لا يتخذ المؤمنون الكافرين أولياء من دون المؤمنين ومن يفعل ذلك فليس من الله في شيء إلا أن تتقوا منهم تقاة ويحذركم الله نفسه وإلى الله المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 3, 'قل إن تخفوا ما في صدوركم أو تبدوه يعلمه الله ويعلم ما في السماوات وما في الأرض والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 3, 'يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو أن بينها وبينه أمدا بعيدا ويحذركم الله نفسه والله رءوف بالعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 3, 'قل إن كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 3, 'قل أطيعوا الله والرسول فإن تولوا فإن الله لا يحب الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 3, 'إن الله اصطفى آدم ونوحا وآل إبراهيم وآل عمران على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 3, 'ذرية بعضها من بعض والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 3, 'إذ قالت امرأت عمران رب إني نذرت لك ما في بطني محررا فتقبل مني إنك أنت السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 3, 'فلما وضعتها قالت رب إني وضعتها أنثى والله أعلم بما وضعت وليس الذكر كالأنثى وإني سميتها مريم وإني أعيذها بك وذريتها من الشيطان الرجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 3, 'فتقبلها ربها بقبول حسن وأنبتها نباتا حسنا وكفلها زكريا كلما دخل عليها زكريا المحراب وجد عندها رزقا قال يا مريم أنى لك هذا قالت هو من عند الله إن الله يرزق من يشاء بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 3, 'هنالك دعا زكريا ربه قال رب هب لي من لدنك ذرية طيبة إنك سميع الدعاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 3, 'فنادته الملائكة وهو قائم يصلي في المحراب أن الله يبشرك بيحيى مصدقا بكلمة من الله وسيدا وحصورا ونبيا من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 3, 'قال رب أنى يكون لي غلام وقد بلغني الكبر وامرأتي عاقر قال كذلك الله يفعل ما يشاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 3, 'قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا واذكر ربك كثيرا وسبح بالعشي والإبكار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 3, 'وإذ قالت الملائكة يا مريم إن الله اصطفاك وطهرك واصطفاك على نساء العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 3, 'يا مريم اقنتي لربك واسجدي واركعي مع الراكعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 3, 'ذلك من أنباء الغيب نوحيه إليك وما كنت لديهم إذ يلقون أقلامهم أيهم يكفل مريم وما كنت لديهم إذ يختصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 3, 'إذ قالت الملائكة يا مريم إن الله يبشرك بكلمة منه اسمه المسيح عيسى ابن مريم وجيها في الدنيا والآخرة ومن المقربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 3, 'ويكلم الناس في المهد وكهلا ومن الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 3, 'قالت رب أنى يكون لي ولد ولم يمسسني بشر قال كذلك الله يخلق ما يشاء إذا قضى أمرا فإنما يقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 3, 'ويعلمه الكتاب والحكمة والتوراة والإنجيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 3, 'ورسولا إلى بني إسرائيل أني قد جئتكم بآية من ربكم أني أخلق لكم من الطين كهيئة الطير فأنفخ فيه فيكون طيرا بإذن الله وأبرئ الأكمه والأبرص وأحيي الموتى بإذن الله وأنبئكم بما تأكلون وما تدخرون في بيوتكم إن في ذلك لآية لكم إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 3, 'ومصدقا لما بين يدي من التوراة ولأحل لكم بعض الذي حرم عليكم وجئتكم بآية من ربكم فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 3, 'إن الله ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 3, 'فلما أحس عيسى منهم الكفر قال من أنصاري إلى الله قال الحواريون نحن أنصار الله آمنا بالله واشهد بأنا مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 3, 'ربنا آمنا بما أنزلت واتبعنا الرسول فاكتبنا مع الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 3, 'ومكروا ومكر الله والله خير الماكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 3, 'إذ قال الله يا عيسى إني متوفيك ورافعك إلي ومطهرك من الذين كفروا وجاعل الذين اتبعوك فوق الذين كفروا إلى يوم القيامة ثم إلي مرجعكم فأحكم بينكم فيما كنتم فيه تختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 3, 'فأما الذين كفروا فأعذبهم عذابا شديدا في الدنيا والآخرة وما لهم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 3, 'وأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم والله لا يحب الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 3, 'ذلك نتلوه عليك من الآيات والذكر الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 3, 'إن مثل عيسى عند الله كمثل آدم خلقه من تراب ثم قال له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 3, 'الحق من ربك فلا تكن من الممترين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 3, 'فمن حاجك فيه من بعد ما جاءك من العلم فقل تعالوا ندع أبناءنا وأبناءكم ونساءنا ونساءكم وأنفسنا وأنفسكم ثم نبتهل فنجعل لعنت الله على الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 3, 'إن هذا لهو القصص الحق وما من إله إلا الله وإن الله لهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 3, 'فإن تولوا فإن الله عليم بالمفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 3, 'قل يا أهل الكتاب تعالوا إلى كلمة سواء بيننا وبينكم ألا نعبد إلا الله ولا نشرك به شيئا ولا يتخذ بعضنا بعضا أربابا من دون الله فإن تولوا فقولوا اشهدوا بأنا مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 3, 'يا أهل الكتاب لم تحاجون في إبراهيم وما أنزلت التوراة والإنجيل إلا من بعده أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 3, 'ها أنتم هؤلاء حاججتم فيما لكم به علم فلم تحاجون فيما ليس لكم به علم والله يعلم وأنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 3, 'ما كان إبراهيم يهوديا ولا نصرانيا ولكن كان حنيفا مسلما وما كان من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 3, 'إن أولى الناس بإبراهيم للذين اتبعوه وهذا النبي والذين آمنوا والله ولي المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 3, 'ودت طائفة من أهل الكتاب لو يضلونكم وما يضلون إلا أنفسهم وما يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 3, 'يا أهل الكتاب لم تكفرون بآيات الله وأنتم تشهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 3, 'يا أهل الكتاب لم تلبسون الحق بالباطل وتكتمون الحق وأنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 3, 'وقالت طائفة من أهل الكتاب آمنوا بالذي أنزل على الذين آمنوا وجه النهار واكفروا آخره لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 3, 'ولا تؤمنوا إلا لمن تبع دينكم قل إن الهدى هدى الله أن يؤتى أحد مثل ما أوتيتم أو يحاجوكم عند ربكم قل إن الفضل بيد الله يؤتيه من يشاء والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 3, 'يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 3, 'ومن أهل الكتاب من إن تأمنه بقنطار يؤده إليك ومنهم من إن تأمنه بدينار لا يؤده إليك إلا ما دمت عليه قائما ذلك بأنهم قالوا ليس علينا في الأميين سبيل ويقولون على الله الكذب وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 3, 'بلى من أوفى بعهده واتقى فإن الله يحب المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 3, 'إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا أولئك لا خلاق لهم في الآخرة ولا يكلمهم الله ولا ينظر إليهم يوم القيامة ولا يزكيهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 3, 'وإن منهم لفريقا يلوون ألسنتهم بالكتاب لتحسبوه من الكتاب وما هو من الكتاب ويقولون هو من عند الله وما هو من عند الله ويقولون على الله الكذب وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 3, 'ما كان لبشر أن يؤتيه الله الكتاب والحكم والنبوة ثم يقول للناس كونوا عبادا لي من دون الله ولكن كونوا ربانيين بما كنتم تعلمون الكتاب وبما كنتم تدرسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 3, 'ولا يأمركم أن تتخذوا الملائكة والنبيين أربابا أيأمركم بالكفر بعد إذ أنتم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 3, 'وإذ أخذ الله ميثاق النبيين لما آتيتكم من كتاب وحكمة ثم جاءكم رسول مصدق لما معكم لتؤمنن به ولتنصرنه قال أأقررتم وأخذتم على ذلكم إصري قالوا أقررنا قال فاشهدوا وأنا معكم من الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 3, 'فمن تولى بعد ذلك فأولئك هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 3, 'أفغير دين الله يبغون وله أسلم من في السماوات والأرض طوعا وكرها وإليه يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 3, 'قل آمنا بالله وما أنزل علينا وما أنزل على إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى والنبيون من ربهم لا نفرق بين أحد منهم ونحن له مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 3, 'ومن يبتغ غير الإسلام دينا فلن يقبل منه وهو في الآخرة من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 3, 'كيف يهدي الله قوما كفروا بعد إيمانهم وشهدوا أن الرسول حق وجاءهم البينات والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 3, 'أولئك جزاؤهم أن عليهم لعنة الله والملائكة والناس أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 3, 'خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 3, 'إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 3, 'إن الذين كفروا بعد إيمانهم ثم ازدادوا كفرا لن تقبل توبتهم وأولئك هم الضالون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 3, 'إن الذين كفروا وماتوا وهم كفار فلن يقبل من أحدهم ملء الأرض ذهبا ولو افتدى به أولئك لهم عذاب أليم وما لهم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 3, 'لن تنالوا البر حتى تنفقوا مما تحبون وما تنفقوا من شيء فإن الله به عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 3, 'كل الطعام كان حلا لبني إسرائيل إلا ما حرم إسرائيل على نفسه من قبل أن تنزل التوراة قل فأتوا بالتوراة فاتلوها إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 3, 'فمن افترى على الله الكذب من بعد ذلك فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 3, 'قل صدق الله فاتبعوا ملة إبراهيم حنيفا وما كان من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 3, 'إن أول بيت وضع للناس للذي ببكة مباركا وهدى للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 3, 'فيه آيات بينات مقام إبراهيم ومن دخله كان آمنا ولله على الناس حج البيت من استطاع إليه سبيلا ومن كفر فإن الله غني عن العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 3, 'قل يا أهل الكتاب لم تكفرون بآيات الله والله شهيد على ما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 3, 'قل يا أهل الكتاب لم تصدون عن سبيل الله من آمن تبغونها عوجا وأنتم شهداء وما الله بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 3, 'يا أيها الذين آمنوا إن تطيعوا فريقا من الذين أوتوا الكتاب يردوكم بعد إيمانكم كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 3, 'وكيف تكفرون وأنتم تتلى عليكم آيات الله وفيكم رسوله ومن يعتصم بالله فقد هدي إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 3, 'يا أيها الذين آمنوا اتقوا الله حق تقاته ولا تموتن إلا وأنتم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 3, 'واعتصموا بحبل الله جميعا ولا تفرقوا واذكروا نعمت الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا وكنتم على شفا حفرة من النار فأنقذكم منها كذلك يبين الله لكم آياته لعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 3, 'ولتكن منكم أمة يدعون إلى الخير ويأمرون بالمعروف وينهون عن المنكر وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 3, 'ولا تكونوا كالذين تفرقوا واختلفوا من بعد ما جاءهم البينات وأولئك لهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 3, 'يوم تبيض وجوه وتسود وجوه فأما الذين اسودت وجوههم أكفرتم بعد إيمانكم فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 3, 'وأما الذين ابيضت وجوههم ففي رحمة الله هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 3, 'تلك آيات الله نتلوها عليك بالحق وما الله يريد ظلما للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 3, 'ولله ما في السماوات وما في الأرض وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 3, 'كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ولو آمن أهل الكتاب لكان خيرا لهم منهم المؤمنون وأكثرهم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 3, 'لن يضروكم إلا أذى وإن يقاتلوكم يولوكم الأدبار ثم لا ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 3, 'ضربت عليهم الذلة أين ما ثقفوا إلا بحبل من الله وحبل من الناس وباءوا بغضب من الله وضربت عليهم المسكنة ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون الأنبياء بغير حق ذلك بما عصوا وكانوا يعتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 3, 'ليسوا سواء من أهل الكتاب أمة قائمة يتلون آيات الله آناء الليل وهم يسجدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 3, 'يؤمنون بالله واليوم الآخر ويأمرون بالمعروف وينهون عن المنكر ويسارعون في الخيرات وأولئك من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 3, 'وما يفعلوا من خير فلن يكفروه والله عليم بالمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 3, 'إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا وأولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 3, 'مثل ما ينفقون في هذه الحياة الدنيا كمثل ريح فيها صر أصابت حرث قوم ظلموا أنفسهم فأهلكته وما ظلمهم الله ولكن أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 3, 'يا أيها الذين آمنوا لا تتخذوا بطانة من دونكم لا يألونكم خبالا ودوا ما عنتم قد بدت البغضاء من أفواههم وما تخفي صدورهم أكبر قد بينا لكم الآيات إن كنتم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 3, 'ها أنتم أولاء تحبونهم ولا يحبونكم وتؤمنون بالكتاب كله وإذا لقوكم قالوا آمنا وإذا خلوا عضوا عليكم الأنامل من الغيظ قل موتوا بغيظكم إن الله عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 3, 'إن تمسسكم حسنة تسؤهم وإن تصبكم سيئة يفرحوا بها وإن تصبروا وتتقوا لا يضركم كيدهم شيئا إن الله بما يعملون محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 3, 'وإذ غدوت من أهلك تبوئ المؤمنين مقاعد للقتال والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 3, 'إذ همت طائفتان منكم أن تفشلا والله وليهما وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 3, 'ولقد نصركم الله ببدر وأنتم أذلة فاتقوا الله لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 3, 'إذ تقول للمؤمنين ألن يكفيكم أن يمدكم ربكم بثلاثة آلاف من الملائكة منزلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 3, 'بلى إن تصبروا وتتقوا ويأتوكم من فورهم هذا يمددكم ربكم بخمسة آلاف من الملائكة مسومين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 3, 'وما جعله الله إلا بشرى لكم ولتطمئن قلوبكم به وما النصر إلا من عند الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 3, 'ليقطع طرفا من الذين كفروا أو يكبتهم فينقلبوا خائبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 3, 'ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 3, 'ولله ما في السماوات وما في الأرض يغفر لمن يشاء ويعذب من يشاء والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 3, 'يا أيها الذين آمنوا لا تأكلوا الربا أضعافا مضاعفة واتقوا الله لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 3, 'واتقوا النار التي أعدت للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 3, 'وأطيعوا الله والرسول لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 3, 'وسارعوا إلى مغفرة من ربكم وجنة عرضها السماوات والأرض أعدت للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 3, 'الذين ينفقون في السراء والضراء والكاظمين الغيظ والعافين عن الناس والله يحب المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 3, 'والذين إذا فعلوا فاحشة أو ظلموا أنفسهم ذكروا الله فاستغفروا لذنوبهم ومن يغفر الذنوب إلا الله ولم يصروا على ما فعلوا وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 3, 'أولئك جزاؤهم مغفرة من ربهم وجنات تجري من تحتها الأنهار خالدين فيها ونعم أجر العاملين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 3, 'قد خلت من قبلكم سنن فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 3, 'هذا بيان للناس وهدى وموعظة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 3, 'ولا تهنوا ولا تحزنوا وأنتم الأعلون إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 3, 'إن يمسسكم قرح فقد مس القوم قرح مثله وتلك الأيام نداولها بين الناس وليعلم الله الذين آمنوا ويتخذ منكم شهداء والله لا يحب الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 3, 'وليمحص الله الذين آمنوا ويمحق الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 3, 'أم حسبتم أن تدخلوا الجنة ولما يعلم الله الذين جاهدوا منكم ويعلم الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 3, 'ولقد كنتم تمنون الموت من قبل أن تلقوه فقد رأيتموه وأنتم تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 3, 'وما محمد إلا رسول قد خلت من قبله الرسل أفإن مات أو قتل انقلبتم على أعقابكم ومن ينقلب على عقبيه فلن يضر الله شيئا وسيجزي الله الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 3, 'وما كان لنفس أن تموت إلا بإذن الله كتابا مؤجلا ومن يرد ثواب الدنيا نؤته منها ومن يرد ثواب الآخرة نؤته منها وسنجزي الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 3, 'وكأين من نبي قاتل معه ربيون كثير فما وهنوا لما أصابهم في سبيل الله وما ضعفوا وما استكانوا والله يحب الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 3, 'وما كان قولهم إلا أن قالوا ربنا اغفر لنا ذنوبنا وإسرافنا في أمرنا وثبت أقدامنا وانصرنا على القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 3, 'فآتاهم الله ثواب الدنيا وحسن ثواب الآخرة والله يحب المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 3, 'يا أيها الذين آمنوا إن تطيعوا الذين كفروا يردوكم على أعقابكم فتنقلبوا خاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 3, 'بل الله مولاكم وهو خير الناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 3, 'سنلقي في قلوب الذين كفروا الرعب بما أشركوا بالله ما لم ينزل به سلطانا ومأواهم النار وبئس مثوى الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 3, 'ولقد صدقكم الله وعده إذ تحسونهم بإذنه حتى إذا فشلتم وتنازعتم في الأمر وعصيتم من بعد ما أراكم ما تحبون منكم من يريد الدنيا ومنكم من يريد الآخرة ثم صرفكم عنهم ليبتليكم ولقد عفا عنكم والله ذو فضل على المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 3, 'إذ تصعدون ولا تلوون على أحد والرسول يدعوكم في أخراكم فأثابكم غما بغم لكيلا تحزنوا على ما فاتكم ولا ما أصابكم والله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 3, 'ثم أنزل عليكم من بعد الغم أمنة نعاسا يغشى طائفة منكم وطائفة قد أهمتهم أنفسهم يظنون بالله غير الحق ظن الجاهلية يقولون هل لنا من الأمر من شيء قل إن الأمر كله لله يخفون في أنفسهم ما لا يبدون لك يقولون لو كان لنا من الأمر شيء ما قتلنا هاهنا قل لو كنتم في بيوتكم لبرز الذين كتب عليهم القتل إلى مضاجعهم وليبتلي الله ما في صدوركم وليمحص ما في قلوبكم والله عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 3, 'إن الذين تولوا منكم يوم التقى الجمعان إنما استزلهم الشيطان ببعض ما كسبوا ولقد عفا الله عنهم إن الله غفور حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 3, 'يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا لإخوانهم إذا ضربوا في الأرض أو كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا ليجعل الله ذلك حسرة في قلوبهم والله يحيي ويميت والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 3, 'ولئن قتلتم في سبيل الله أو متم لمغفرة من الله ورحمة خير مما يجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 3, 'ولئن متم أو قتلتم لإلى الله تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 3, 'فبما رحمة من الله لنت لهم ولو كنت فظا غليظ القلب لانفضوا من حولك فاعف عنهم واستغفر لهم وشاورهم في الأمر فإذا عزمت فتوكل على الله إن الله يحب المتوكلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 3, 'إن ينصركم الله فلا غالب لكم وإن يخذلكم فمن ذا الذي ينصركم من بعده وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 3, 'وما كان لنبي أن يغل ومن يغلل يأت بما غل يوم القيامة ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 3, 'أفمن اتبع رضوان الله كمن باء بسخط من الله ومأواه جهنم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 3, 'هم درجات عند الله والله بصير بما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 3, 'لقد من الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 3, 'أولما أصابتكم مصيبة قد أصبتم مثليها قلتم أنى هذا قل هو من عند أنفسكم إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 3, 'وما أصابكم يوم التقى الجمعان فبإذن الله وليعلم المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 3, 'وليعلم الذين نافقوا وقيل لهم تعالوا قاتلوا في سبيل الله أو ادفعوا قالوا لو نعلم قتالا لاتبعناكم هم للكفر يومئذ أقرب منهم للإيمان يقولون بأفواههم ما ليس في قلوبهم والله أعلم بما يكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 3, 'الذين قالوا لإخوانهم وقعدوا لو أطاعونا ما قتلوا قل فادرءوا عن أنفسكم الموت إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 3, 'ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 3, 'فرحين بما آتاهم الله من فضله ويستبشرون بالذين لم يلحقوا بهم من خلفهم ألا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 3, 'يستبشرون بنعمة من الله وفضل وأن الله لا يضيع أجر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 3, 'الذين استجابوا لله والرسول من بعد ما أصابهم القرح للذين أحسنوا منهم واتقوا أجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 3, 'الذين قال لهم الناس إن الناس قد جمعوا لكم فاخشوهم فزادهم إيمانا وقالوا حسبنا الله ونعم الوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 3, 'فانقلبوا بنعمة من الله وفضل لم يمسسهم سوء واتبعوا رضوان الله والله ذو فضل عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 3, 'إنما ذلكم الشيطان يخوف أولياءه فلا تخافوهم وخافون إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 3, 'ولا يحزنك الذين يسارعون في الكفر إنهم لن يضروا الله شيئا يريد الله ألا يجعل لهم حظا في الآخرة ولهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(177, 1, 3, 'إن الذين اشتروا الكفر بالإيمان لن يضروا الله شيئا ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(178, 1, 3, 'ولا يحسبن الذين كفروا أنما نملي لهم خير لأنفسهم إنما نملي لهم ليزدادوا إثما ولهم عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(179, 1, 3, 'ما كان الله ليذر المؤمنين على ما أنتم عليه حتى يميز الخبيث من الطيب وما كان الله ليطلعكم على الغيب ولكن الله يجتبي من رسله من يشاء فآمنوا بالله ورسله وإن تؤمنوا وتتقوا فلكم أجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(180, 1, 3, 'ولا يحسبن الذين يبخلون بما آتاهم الله من فضله هو خيرا لهم بل هو شر لهم سيطوقون ما بخلوا به يوم القيامة ولله ميراث السماوات والأرض والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(181, 1, 3, 'لقد سمع الله قول الذين قالوا إن الله فقير ونحن أغنياء سنكتب ما قالوا وقتلهم الأنبياء بغير حق ونقول ذوقوا عذاب الحريق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(182, 1, 3, 'ذلك بما قدمت أيديكم وأن الله ليس بظلام للعبيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(183, 1, 3, 'الذين قالوا إن الله عهد إلينا ألا نؤمن لرسول حتى يأتينا بقربان تأكله النار قل قد جاءكم رسل من قبلي بالبينات وبالذي قلتم فلم قتلتموهم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(184, 1, 3, 'فإن كذبوك فقد كذب رسل من قبلك جاءوا بالبينات والزبر والكتاب المنير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(185, 1, 3, 'كل نفس ذائقة الموت وإنما توفون أجوركم يوم القيامة فمن زحزح عن النار وأدخل الجنة فقد فاز وما الحياة الدنيا إلا متاع الغرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(186, 1, 3, 'لتبلون في أموالكم وأنفسكم ولتسمعن من الذين أوتوا الكتاب من قبلكم ومن الذين أشركوا أذى كثيرا وإن تصبروا وتتقوا فإن ذلك من عزم الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(187, 1, 3, 'وإذ أخذ الله ميثاق الذين أوتوا الكتاب لتبيننه للناس ولا تكتمونه فنبذوه وراء ظهورهم واشتروا به ثمنا قليلا فبئس ما يشترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(188, 1, 3, 'لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(189, 1, 3, 'ولله ملك السماوات والأرض والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(190, 1, 3, 'إن في خلق السماوات والأرض واختلاف الليل والنهار لآيات لأولي الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(191, 1, 3, 'الذين يذكرون الله قياما وقعودا وعلى جنوبهم ويتفكرون في خلق السماوات والأرض ربنا ما خلقت هذا باطلا سبحانك فقنا عذاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(192, 1, 3, 'ربنا إنك من تدخل النار فقد أخزيته وما للظالمين من أنصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(193, 1, 3, 'ربنا إننا سمعنا مناديا ينادي للإيمان أن آمنوا بربكم فآمنا ربنا فاغفر لنا ذنوبنا وكفر عنا سيئاتنا وتوفنا مع الأبرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(194, 1, 3, 'ربنا وآتنا ما وعدتنا على رسلك ولا تخزنا يوم القيامة إنك لا تخلف الميعاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(195, 1, 3, 'فاستجاب لهم ربهم أني لا أضيع عمل عامل منكم من ذكر أو أنثى بعضكم من بعض فالذين هاجروا وأخرجوا من ديارهم وأوذوا في سبيلي وقاتلوا وقتلوا لأكفرن عنهم سيئاتهم ولأدخلنهم جنات تجري من تحتها الأنهار ثوابا من عند الله والله عنده حسن الثواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(196, 1, 3, 'لا يغرنك تقلب الذين كفروا في البلاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(197, 1, 3, 'متاع قليل ثم مأواهم جهنم وبئس المهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(198, 1, 3, 'لكن الذين اتقوا ربهم لهم جنات تجري من تحتها الأنهار خالدين فيها نزلا من عند الله وما عند الله خير للأبرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(199, 1, 3, 'وإن من أهل الكتاب لمن يؤمن بالله وما أنزل إليكم وما أنزل إليهم خاشعين لله لا يشترون بآيات الله ثمنا قليلا أولئك لهم أجرهم عند ربهم إن الله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(200, 1, 3, 'يا أيها الذين آمنوا اصبروا وصابروا ورابطوا واتقوا الله لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 4, 'بسم الله الرحمن الرحيم يا أيها الناس اتقوا ربكم الذي خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا الله الذي تساءلون به والأرحام إن الله كان عليكم رقيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 4, 'وآتوا اليتامى أموالهم ولا تتبدلوا الخبيث بالطيب ولا تأكلوا أموالهم إلى أموالكم إنه كان حوبا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 4, 'وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ذلك أدنى ألا تعولوا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 4, 'وآتوا النساء صدقاتهن نحلة فإن طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 4, 'ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 4, 'وابتلوا اليتامى حتى إذا بلغوا النكاح فإن آنستم منهم رشدا فادفعوا إليهم أموالهم ولا تأكلوها إسرافا وبدارا أن يكبروا ومن كان غنيا فليستعفف ومن كان فقيرا فليأكل بالمعروف فإذا دفعتم إليهم أموالهم فأشهدوا عليهم وكفى بالله حسيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 4, 'للرجال نصيب مما ترك الوالدان والأقربون وللنساء نصيب مما ترك الوالدان والأقربون مما قل منه أو كثر نصيبا مفروضا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 4, 'وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 4, 'وليخش الذين لو تركوا من خلفهم ذرية ضعافا خافوا عليهم فليتقوا الله وليقولوا قولا سديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 4, 'إن الذين يأكلون أموال اليتامى ظلما إنما يأكلون في بطونهم نارا وسيصلون سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 4, 'يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك وإن كانت واحدة فلها النصف ولأبويه لكل واحد منهما السدس مما ترك إن كان له ولد فإن لم يكن له ولد وورثه أبواه فلأمه الثلث فإن كان له إخوة فلأمه السدس من بعد وصية يوصي بها أو دين آباؤكم وأبناؤكم لا تدرون أيهم أقرب لكم نفعا فريضة من الله إن الله كان عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 4, 'ولكم نصف ما ترك أزواجكم إن لم يكن لهن ولد فإن كان لهن ولد فلكم الربع مما تركن من بعد وصية يوصين بها أو دين ولهن الربع مما تركتم إن لم يكن لكم ولد فإن كان لكم ولد فلهن الثمن مما تركتم من بعد وصية توصون بها أو دين وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت فلكل واحد منهما السدس فإن كانوا أكثر من ذلك فهم شركاء في الثلث من بعد وصية يوصى بها أو دين غير مضار وصية من الله والله عليم حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 4, 'تلك حدود الله ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار خالدين فيها وذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 4, 'ومن يعص الله ورسوله ويتعد حدوده يدخله نارا خالدا فيها وله عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 4, 'واللاتي يأتين الفاحشة من نسائكم فاستشهدوا عليهن أربعة منكم فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 4, 'واللذان يأتيانها منكم فآذوهما فإن تابا وأصلحا فأعرضوا عنهما إن الله كان توابا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 4, 'إنما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فأولئك يتوب الله عليهم وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 4, 'وليست التوبة للذين يعملون السيئات حتى إذا حضر أحدهم الموت قال إني تبت الآن ولا الذين يموتون وهم كفار أولئك أعتدنا لهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 4, 'يا أيها الذين آمنوا لا يحل لكم أن ترثوا النساء كرها ولا تعضلوهن لتذهبوا ببعض ما آتيتموهن إلا أن يأتين بفاحشة مبينة وعاشروهن بالمعروف فإن كرهتموهن فعسى أن تكرهوا شيئا ويجعل الله فيه خيرا كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 4, 'وإن أردتم استبدال زوج مكان زوج وآتيتم إحداهن قنطارا فلا تأخذوا منه شيئا أتأخذونه بهتانا وإثما مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 4, 'وكيف تأخذونه وقد أفضى بعضكم إلى بعض وأخذن منكم ميثاقا غليظا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 4, 'ولا تنكحوا ما نكح آباؤكم من النساء إلا ما قد سلف إنه كان فاحشة ومقتا وساء سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 4, 'حرمت عليكم أمهاتكم وبناتكم وأخواتكم وعماتكم وخالاتكم وبنات الأخ وبنات الأخت وأمهاتكم اللاتي أرضعنكم وأخواتكم من الرضاعة وأمهات نسائكم وربائبكم اللاتي في حجوركم من نسائكم اللاتي دخلتم بهن فإن لم تكونوا دخلتم بهن فلا جناح عليكم وحلائل أبنائكم الذين من أصلابكم وأن تجمعوا بين الأختين إلا ما قد سلف إن الله كان غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 4, 'والمحصنات من النساء إلا ما ملكت أيمانكم كتاب الله عليكم وأحل لكم ما وراء ذلكم أن تبتغوا بأموالكم محصنين غير مسافحين فما استمتعتم به منهن فآتوهن أجورهن فريضة ولا جناح عليكم فيما تراضيتم به من بعد الفريضة إن الله كان عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 4, 'ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات والله أعلم بإيمانكم بعضكم من بعض فانكحوهن بإذن أهلهن وآتوهن أجورهن بالمعروف محصنات غير مسافحات ولا متخذات أخدان فإذا أحصن فإن أتين بفاحشة فعليهن نصف ما على المحصنات من العذاب ذلك لمن خشي العنت منكم وأن تصبروا خير لكم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 4, 'يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم ويتوب عليكم والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 4, 'والله يريد أن يتوب عليكم ويريد الذين يتبعون الشهوات أن تميلوا ميلا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 4, 'يريد الله أن يخفف عنكم وخلق الإنسان ضعيفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 4, 'يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم ولا تقتلوا أنفسكم إن الله كان بكم رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 4, 'ومن يفعل ذلك عدوانا وظلما فسوف نصليه نارا وكان ذلك على الله يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 4, 'إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 4, 'ولا تتمنوا ما فضل الله به بعضكم على بعض للرجال نصيب مما اكتسبوا وللنساء نصيب مما اكتسبن واسألوا الله من فضله إن الله كان بكل شيء عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 4, 'ولكل جعلنا موالي مما ترك الوالدان والأقربون والذين عقدت أيمانكم فآتوهم نصيبهم إن الله كان على كل شيء شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 4, 'الرجال قوامون على النساء بما فضل الله بعضهم على بعض وبما أنفقوا من أموالهم فالصالحات قانتات حافظات للغيب بما حفظ الله واللاتي تخافون نشوزهن فعظوهن واهجروهن في المضاجع واضربوهن فإن أطعنكم فلا تبغوا عليهن سبيلا إن الله كان عليا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 4, 'وإن خفتم شقاق بينهما فابعثوا حكما من أهله وحكما من أهلها إن يريدا إصلاحا يوفق الله بينهما إن الله كان عليما خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 4, 'واعبدوا الله ولا تشركوا به شيئا وبالوالدين إحسانا وبذي القربى واليتامى والمساكين والجار ذي القربى والجار الجنب والصاحب بالجنب وابن السبيل وما ملكت أيمانكم إن الله لا يحب من كان مختالا فخورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 4, 'الذين يبخلون ويأمرون الناس بالبخل ويكتمون ما آتاهم الله من فضله وأعتدنا للكافرين عذابا مهينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 4, 'والذين ينفقون أموالهم رئاء الناس ولا يؤمنون بالله ولا باليوم الآخر ومن يكن الشيطان له قرينا فساء قرينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 4, 'وماذا عليهم لو آمنوا بالله واليوم الآخر وأنفقوا مما رزقهم الله وكان الله بهم عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 4, 'إن الله لا يظلم مثقال ذرة وإن تك حسنة يضاعفها ويؤت من لدنه أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 4, 'فكيف إذا جئنا من كل أمة بشهيد وجئنا بك على هؤلاء شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 4, 'يومئذ يود الذين كفروا وعصوا الرسول لو تسوى بهم الأرض ولا يكتمون الله حديثا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 4, 'يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم إن الله كان عفوا غفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 4, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يشترون الضلالة ويريدون أن تضلوا السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 4, 'والله أعلم بأعدائكم وكفى بالله وليا وكفى بالله نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 4, 'من الذين هادوا يحرفون الكلم عن مواضعه ويقولون سمعنا وعصينا واسمع غير مسمع وراعنا ليا بألسنتهم وطعنا في الدين ولو أنهم قالوا سمعنا وأطعنا واسمع وانظرنا لكان خيرا لهم وأقوم ولكن لعنهم الله بكفرهم فلا يؤمنون إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 4, 'يا أيها الذين أوتوا الكتاب آمنوا بما نزلنا مصدقا لما معكم من قبل أن نطمس وجوها فنردها على أدبارها أو نلعنهم كما لعنا أصحاب السبت وكان أمر الله مفعولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 4, 'إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى إثما عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 4, 'ألم تر إلى الذين يزكون أنفسهم بل الله يزكي من يشاء ولا يظلمون فتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 4, 'انظر كيف يفترون على الله الكذب وكفى به إثما مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 4, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت ويقولون للذين كفروا هؤلاء أهدى من الذين آمنوا سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 4, 'أولئك الذين لعنهم الله ومن يلعن الله فلن تجد له نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 4, 'أم لهم نصيب من الملك فإذا لا يؤتون الناس نقيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 4, 'أم يحسدون الناس على ما آتاهم الله من فضله فقد آتينا آل إبراهيم الكتاب والحكمة وآتيناهم ملكا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 4, 'فمنهم من آمن به ومنهم من صد عنه وكفى بجهنم سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 4, 'إن الذين كفروا بآياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب إن الله كان عزيزا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 4, 'والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا لهم فيها أزواج مطهرة وندخلهم ظلا ظليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 4, 'إن الله يأمركم أن تؤدوا الأمانات إلى أهلها وإذا حكمتم بين الناس أن تحكموا بالعدل إن الله نعما يعظكم به إن الله كان سميعا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 4, 'يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر ذلك خير وأحسن تأويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 4, 'ألم تر إلى الذين يزعمون أنهم آمنوا بما أنزل إليك وما أنزل من قبلك يريدون أن يتحاكموا إلى الطاغوت وقد أمروا أن يكفروا به ويريد الشيطان أن يضلهم ضلالا بعيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 4, 'وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول رأيت المنافقين يصدون عنك صدودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 4, 'فكيف إذا أصابتهم مصيبة بما قدمت أيديهم ثم جاءوك يحلفون بالله إن أردنا إلا إحسانا وتوفيقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 4, 'أولئك الذين يعلم الله ما في قلوبهم فأعرض عنهم وعظهم وقل لهم في أنفسهم قولا بليغا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 4, 'وما أرسلنا من رسول إلا ليطاع بإذن الله ولو أنهم إذ ظلموا أنفسهم جاءوك فاستغفروا الله واستغفر لهم الرسول لوجدوا الله توابا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 4, 'فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 4, 'ولو أنا كتبنا عليهم أن اقتلوا أنفسكم أو اخرجوا من دياركم ما فعلوه إلا قليل منهم ولو أنهم فعلوا ما يوعظون به لكان خيرا لهم وأشد تثبيتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 4, 'وإذا لآتيناهم من لدنا أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 4, 'ولهديناهم صراطا مستقيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 4, 'ومن يطع الله والرسول فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 4, 'ذلك الفضل من الله وكفى بالله عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 4, 'يا أيها الذين آمنوا خذوا حذركم فانفروا ثبات أو انفروا جميعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 4, 'وإن منكم لمن ليبطئن فإن أصابتكم مصيبة قال قد أنعم الله علي إذ لم أكن معهم شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 4, 'ولئن أصابكم فضل من الله ليقولن كأن لم تكن بينكم وبينه مودة يا ليتني كنت معهم فأفوز فوزا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 4, 'فليقاتل في سبيل الله الذين يشرون الحياة الدنيا بالآخرة ومن يقاتل في سبيل الله فيقتل أو يغلب فسوف نؤتيه أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 4, 'وما لكم لا تقاتلون في سبيل الله والمستضعفين من الرجال والنساء والولدان الذين يقولون ربنا أخرجنا من هذه القرية الظالم أهلها واجعل لنا من لدنك وليا واجعل لنا من لدنك نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 4, 'الذين آمنوا يقاتلون في سبيل الله والذين كفروا يقاتلون في سبيل الطاغوت فقاتلوا أولياء الشيطان إن كيد الشيطان كان ضعيفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 4, 'ألم تر إلى الذين قيل لهم كفوا أيديكم وأقيموا الصلاة وآتوا الزكاة فلما كتب عليهم القتال إذا فريق منهم يخشون الناس كخشية الله أو أشد خشية وقالوا ربنا لم كتبت علينا القتال لولا أخرتنا إلى أجل قريب قل متاع الدنيا قليل والآخرة خير لمن اتقى ولا تظلمون فتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 4, 'أينما تكونوا يدرككم الموت ولو كنتم في بروج مشيدة وإن تصبهم حسنة يقولوا هذه من عند الله وإن تصبهم سيئة يقولوا هذه من عندك قل كل من عند الله فمال هؤلاء القوم لا يكادون يفقهون حديثا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 4, 'ما أصابك من حسنة فمن الله وما أصابك من سيئة فمن نفسك وأرسلناك للناس رسولا وكفى بالله شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 4, 'من يطع الرسول فقد أطاع الله ومن تولى فما أرسلناك عليهم حفيظا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 4, 'ويقولون طاعة فإذا برزوا من عندك بيت طائفة منهم غير الذي تقول والله يكتب ما يبيتون فأعرض عنهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 4, 'أفلا يتدبرون القرآن ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 4, 'وإذا جاءهم أمر من الأمن أو الخوف أذاعوا به ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم ولولا فضل الله عليكم ورحمته لاتبعتم الشيطان إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 4, 'فقاتل في سبيل الله لا تكلف إلا نفسك وحرض المؤمنين عسى الله أن يكف بأس الذين كفروا والله أشد بأسا وأشد تنكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 4, 'من يشفع شفاعة حسنة يكن له نصيب منها ومن يشفع شفاعة سيئة يكن له كفل منها وكان الله على كل شيء مقيتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 4, 'وإذا حييتم بتحية فحيوا بأحسن منها أو ردوها إن الله كان على كل شيء حسيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 4, 'الله لا إله إلا هو ليجمعنكم إلى يوم القيامة لا ريب فيه ومن أصدق من الله حديثا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 4, 'فما لكم في المنافقين فئتين والله أركسهم بما كسبوا أتريدون أن تهدوا من أضل الله ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 4, 'ودوا لو تكفرون كما كفروا فتكونون سواء فلا تتخذوا منهم أولياء حتى يهاجروا في سبيل الله فإن تولوا فخذوهم واقتلوهم حيث وجدتموهم ولا تتخذوا منهم وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 4, 'إلا الذين يصلون إلى قوم بينكم وبينهم ميثاق أو جاءوكم حصرت صدورهم أن يقاتلوكم أو يقاتلوا قومهم ولو شاء الله لسلطهم عليكم فلقاتلوكم فإن اعتزلوكم فلم يقاتلوكم وألقوا إليكم السلم فما جعل الله لكم عليهم سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 4, 'ستجدون آخرين يريدون أن يأمنوكم ويأمنوا قومهم كل ما ردوا إلى الفتنة أركسوا فيها فإن لم يعتزلوكم ويلقوا إليكم السلم ويكفوا أيديهم فخذوهم واقتلوهم حيث ثقفتموهم وأولئكم جعلنا لكم عليهم سلطانا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 4, 'وما كان لمؤمن أن يقتل مؤمنا إلا خطأ ومن قتل مؤمنا خطأ فتحرير رقبة مؤمنة ودية مسلمة إلى أهله إلا أن يصدقوا فإن كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة وإن كان من قوم بينكم وبينهم ميثاق فدية مسلمة إلى أهله وتحرير رقبة مؤمنة فمن لم يجد فصيام شهرين متتابعين توبة من الله وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 4, 'ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 4, 'يا أيها الذين آمنوا إذا ضربتم في سبيل الله فتبينوا ولا تقولوا لمن ألقى إليكم السلام لست مؤمنا تبتغون عرض الحياة الدنيا فعند الله مغانم كثيرة كذلك كنتم من قبل فمن الله عليكم فتبينوا إن الله كان بما تعملون خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 4, 'لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين درجة وكلا وعد الله الحسنى وفضل الله المجاهدين على القاعدين أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 4, 'درجات منه ومغفرة ورحمة وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 4, 'إن الذين توفاهم الملائكة ظالمي أنفسهم قالوا فيم كنتم قالوا كنا مستضعفين في الأرض قالوا ألم تكن أرض الله واسعة فتهاجروا فيها فأولئك مأواهم جهنم وساءت مصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 4, 'إلا المستضعفين من الرجال والنساء والولدان لا يستطيعون حيلة ولا يهتدون سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 4, 'فأولئك عسى الله أن يعفو عنهم وكان الله عفوا غفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 4, 'ومن يهاجر في سبيل الله يجد في الأرض مراغما كثيرا وسعة ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت فقد وقع أجره على الله وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 4, 'وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا إن الكافرين كانوا لكم عدوا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 4, 'وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك وليأخذوا أسلحتهم فإذا سجدوا فليكونوا من ورائكم ولتأت طائفة أخرى لم يصلوا فليصلوا معك وليأخذوا حذرهم وأسلحتهم ود الذين كفروا لو تغفلون عن أسلحتكم وأمتعتكم فيميلون عليكم ميلة واحدة ولا جناح عليكم إن كان بكم أذى من مطر أو كنتم مرضى أن تضعوا أسلحتكم وخذوا حذركم إن الله أعد للكافرين عذابا مهينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 4, 'فإذا قضيتم الصلاة فاذكروا الله قياما وقعودا وعلى جنوبكم فإذا اطمأننتم فأقيموا الصلاة إن الصلاة كانت على المؤمنين كتابا موقوتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 4, 'ولا تهنوا في ابتغاء القوم إن تكونوا تألمون فإنهم يألمون كما تألمون وترجون من الله ما لا يرجون وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 4, 'إنا أنزلنا إليك الكتاب بالحق لتحكم بين الناس بما أراك الله ولا تكن للخائنين خصيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 4, 'واستغفر الله إن الله كان غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 4, 'ولا تجادل عن الذين يختانون أنفسهم إن الله لا يحب من كان خوانا أثيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 4, 'يستخفون من الناس ولا يستخفون من الله وهو معهم إذ يبيتون ما لا يرضى من القول وكان الله بما يعملون محيطا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 4, 'ها أنتم هؤلاء جادلتم عنهم في الحياة الدنيا فمن يجادل الله عنهم يوم القيامة أم من يكون عليهم وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 4, 'ومن يعمل سوءا أو يظلم نفسه ثم يستغفر الله يجد الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 4, 'ومن يكسب إثما فإنما يكسبه على نفسه وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 4, 'ومن يكسب خطيئة أو إثما ثم يرم به بريئا فقد احتمل بهتانا وإثما مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 4, 'ولولا فضل الله عليك ورحمته لهمت طائفة منهم أن يضلوك وما يضلون إلا أنفسهم وما يضرونك من شيء وأنزل الله عليك الكتاب والحكمة وعلمك ما لم تكن تعلم وكان فضل الله عليك عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 4, 'لا خير في كثير من نجواهم إلا من أمر بصدقة أو معروف أو إصلاح بين الناس ومن يفعل ذلك ابتغاء مرضات الله فسوف نؤتيه أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 4, 'ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم وساءت مصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 4, 'إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد ضل ضلالا بعيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 4, 'إن يدعون من دونه إلا إناثا وإن يدعون إلا شيطانا مريدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 4, 'لعنه الله وقال لأتخذن من عبادك نصيبا مفروضا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 4, 'ولأضلنهم ولأمنينهم ولآمرنهم فليبتكن آذان الأنعام ولآمرنهم فليغيرن خلق الله ومن يتخذ الشيطان وليا من دون الله فقد خسر خسرانا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 4, 'يعدهم ويمنيهم وما يعدهم الشيطان إلا غرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 4, 'أولئك مأواهم جهنم ولا يجدون عنها محيصا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 4, 'والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا وعد الله حقا ومن أصدق من الله قيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 4, 'ليس بأمانيكم ولا أماني أهل الكتاب من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 4, 'ومن يعمل من الصالحات من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة ولا يظلمون نقيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 4, 'ومن أحسن دينا ممن أسلم وجهه لله وهو محسن واتبع ملة إبراهيم حنيفا واتخذ الله إبراهيم خليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 4, 'ولله ما في السماوات وما في الأرض وكان الله بكل شيء محيطا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 4, 'ويستفتونك في النساء قل الله يفتيكم فيهن وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تؤتونهن ما كتب لهن وترغبون أن تنكحوهن والمستضعفين من الولدان وأن تقوموا لليتامى بالقسط وما تفعلوا من خير فإن الله كان به عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 4, 'وإن امرأة خافت من بعلها نشوزا أو إعراضا فلا جناح عليهما أن يصلحا بينهما صلحا والصلح خير وأحضرت الأنفس الشح وإن تحسنوا وتتقوا فإن الله كان بما تعملون خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 4, 'ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم فلا تميلوا كل الميل فتذروها كالمعلقة وإن تصلحوا وتتقوا فإن الله كان غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 4, 'وإن يتفرقا يغن الله كلا من سعته وكان الله واسعا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 4, 'ولله ما في السماوات وما في الأرض ولقد وصينا الذين أوتوا الكتاب من قبلكم وإياكم أن اتقوا الله وإن تكفروا فإن لله ما في السماوات وما في الأرض وكان الله غنيا حميدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 4, 'ولله ما في السماوات وما في الأرض وكفى بالله وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 4, 'إن يشأ يذهبكم أيها الناس ويأت بآخرين وكان الله على ذلك قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 4, 'من كان يريد ثواب الدنيا فعند الله ثواب الدنيا والآخرة وكان الله سميعا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 4, 'يا أيها الذين آمنوا كونوا قوامين بالقسط شهداء لله ولو على أنفسكم أو الوالدين والأقربين إن يكن غنيا أو فقيرا فالله أولى بهما فلا تتبعوا الهوى أن تعدلوا وإن تلووا أو تعرضوا فإن الله كان بما تعملون خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 4, 'يا أيها الذين آمنوا آمنوا بالله ورسوله والكتاب الذي نزل على رسوله والكتاب الذي أنزل من قبل ومن يكفر بالله وملائكته وكتبه ورسله واليوم الآخر فقد ضل ضلالا بعيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 4, 'إن الذين آمنوا ثم كفروا ثم آمنوا ثم كفروا ثم ازدادوا كفرا لم يكن الله ليغفر لهم ولا ليهديهم سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 4, 'بشر المنافقين بأن لهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 4, 'الذين يتخذون الكافرين أولياء من دون المؤمنين أيبتغون عندهم العزة فإن العزة لله جميعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 4, 'وقد نزل عليكم في الكتاب أن إذا سمعتم آيات الله يكفر بها ويستهزأ بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره إنكم إذا مثلهم إن الله جامع المنافقين والكافرين في جهنم جميعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 4, 'الذين يتربصون بكم فإن كان لكم فتح من الله قالوا ألم نكن معكم وإن كان للكافرين نصيب قالوا ألم نستحوذ عليكم ونمنعكم من المؤمنين فالله يحكم بينكم يوم القيامة ولن يجعل الله للكافرين على المؤمنين سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 4, 'إن المنافقين يخادعون الله وهو خادعهم وإذا قاموا إلى الصلاة قاموا كسالى يراءون الناس ولا يذكرون الله إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 4, 'مذبذبين بين ذلك لا إلى هؤلاء ولا إلى هؤلاء ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 4, 'يا أيها الذين آمنوا لا تتخذوا الكافرين أولياء من دون المؤمنين أتريدون أن تجعلوا لله عليكم سلطانا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 4, 'إن المنافقين في الدرك الأسفل من النار ولن تجد لهم نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 4, 'إلا الذين تابوا وأصلحوا واعتصموا بالله وأخلصوا دينهم لله فأولئك مع المؤمنين وسوف يؤت الله المؤمنين أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 4, 'ما يفعل الله بعذابكم إن شكرتم وآمنتم وكان الله شاكرا عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 4, 'لا يحب الله الجهر بالسوء من القول إلا من ظلم وكان الله سميعا عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 4, 'إن تبدوا خيرا أو تخفوه أو تعفوا عن سوء فإن الله كان عفوا قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 4, 'إن الذين يكفرون بالله ورسله ويريدون أن يفرقوا بين الله ورسله ويقولون نؤمن ببعض ونكفر ببعض ويريدون أن يتخذوا بين ذلك سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 4, 'أولئك هم الكافرون حقا وأعتدنا للكافرين عذابا مهينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 4, 'والذين آمنوا بالله ورسله ولم يفرقوا بين أحد منهم أولئك سوف يؤتيهم أجورهم وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 4, 'يسألك أهل الكتاب أن تنزل عليهم كتابا من السماء فقد سألوا موسى أكبر من ذلك فقالوا أرنا الله جهرة فأخذتهم الصاعقة بظلمهم ثم اتخذوا العجل من بعد ما جاءتهم البينات فعفونا عن ذلك وآتينا موسى سلطانا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 4, 'ورفعنا فوقهم الطور بميثاقهم وقلنا لهم ادخلوا الباب سجدا وقلنا لهم لا تعدوا في السبت وأخذنا منهم ميثاقا غليظا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 4, 'فبما نقضهم ميثاقهم وكفرهم بآيات الله وقتلهم الأنبياء بغير حق وقولهم قلوبنا غلف بل طبع الله عليها بكفرهم فلا يؤمنون إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 4, 'وبكفرهم وقولهم على مريم بهتانا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 4, 'وقولهم إنا قتلنا المسيح عيسى ابن مريم رسول الله وما قتلوه وما صلبوه ولكن شبه لهم وإن الذين اختلفوا فيه لفي شك منه ما لهم به من علم إلا اتباع الظن وما قتلوه يقينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 4, 'بل رفعه الله إليه وكان الله عزيزا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 4, 'وإن من أهل الكتاب إلا ليؤمنن به قبل موته ويوم القيامة يكون عليهم شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 4, 'فبظلم من الذين هادوا حرمنا عليهم طيبات أحلت لهم وبصدهم عن سبيل الله كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 4, 'وأخذهم الربا وقد نهوا عنه وأكلهم أموال الناس بالباطل وأعتدنا للكافرين منهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 4, 'لكن الراسخون في العلم منهم والمؤمنون يؤمنون بما أنزل إليك وما أنزل من قبلك والمقيمين الصلاة والمؤتون الزكاة والمؤمنون بالله واليوم الآخر أولئك سنؤتيهم أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 4, 'إنا أوحينا إليك كما أوحينا إلى نوح والنبيين من بعده وأوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وعيسى وأيوب ويونس وهارون وسليمان وآتينا داوود زبورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 4, 'ورسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك وكلم الله موسى تكليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 4, 'رسلا مبشرين ومنذرين لئلا يكون للناس على الله حجة بعد الرسل وكان الله عزيزا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 4, 'لكن الله يشهد بما أنزل إليك أنزله بعلمه والملائكة يشهدون وكفى بالله شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 4, 'إن الذين كفروا وصدوا عن سبيل الله قد ضلوا ضلالا بعيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 4, 'إن الذين كفروا وظلموا لم يكن الله ليغفر لهم ولا ليهديهم طريقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 4, 'إلا طريق جهنم خالدين فيها أبدا وكان ذلك على الله يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 4, 'يا أيها الناس قد جاءكم الرسول بالحق من ربكم فآمنوا خيرا لكم وإن تكفروا فإن لله ما في السماوات والأرض وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 4, 'يا أهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله إلا الحق إنما المسيح عيسى ابن مريم رسول الله وكلمته ألقاها إلى مريم وروح منه فآمنوا بالله ورسله ولا تقولوا ثلاثة انتهوا خيرا لكم إنما الله إله واحد سبحانه أن يكون له ولد له ما في السماوات وما في الأرض وكفى بالله وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 4, 'لن يستنكف المسيح أن يكون عبدا لله ولا الملائكة المقربون ومن يستنكف عن عبادته ويستكبر فسيحشرهم إليه جميعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 4, 'فأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم ويزيدهم من فضله وأما الذين استنكفوا واستكبروا فيعذبهم عذابا أليما ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 4, 'يا أيها الناس قد جاءكم برهان من ربكم وأنزلنا إليكم نورا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 4, 'فأما الذين آمنوا بالله واعتصموا به فسيدخلهم في رحمة منه وفضل ويهديهم إليه صراطا مستقيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 4, 'يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد فإن كانتا اثنتين فلهما الثلثان مما ترك وإن كانوا إخوة رجالا ونساء فللذكر مثل حظ الأنثيين يبين الله لكم أن تضلوا والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 5, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا أوفوا بالعقود أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم غير محلي الصيد وأنتم حرم إن الله يحكم ما يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 5, 'يا أيها الذين آمنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا آمين البيت الحرام يبتغون فضلا من ربهم ورضوانا وإذا حللتم فاصطادوا ولا يجرمنكم شنآن قوم أن صدوكم عن المسجد الحرام أن تعتدوا وتعاونوا على البر والتقوى ولا تعاونوا على الإثم والعدوان واتقوا الله إن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 5, 'حرمت عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما أكل السبع إلا ما ذكيتم وما ذبح على النصب وأن تستقسموا بالأزلام ذلكم فسق اليوم يئس الذين كفروا من دينكم فلا تخشوهم واخشون اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا فمن اضطر في مخمصة غير متجانف لإثم فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 5, 'يسألونك ماذا أحل لهم قل أحل لكم الطيبات وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله فكلوا مما أمسكن عليكم واذكروا اسم الله عليه واتقوا الله إن الله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 5, 'اليوم أحل لكم الطيبات وطعام الذين أوتوا الكتاب حل لكم وطعامكم حل لهم والمحصنات من المؤمنات والمحصنات من الذين أوتوا الكتاب من قبلكم إذا آتيتموهن أجورهن محصنين غير مسافحين ولا متخذي أخدان ومن يكفر بالإيمان فقد حبط عمله وهو في الآخرة من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 5, 'يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين وإن كنتم جنبا فاطهروا وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم منه ما يريد الله ليجعل عليكم من حرج ولكن يريد ليطهركم وليتم نعمته عليكم لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 5, 'واذكروا نعمة الله عليكم وميثاقه الذي واثقكم به إذ قلتم سمعنا وأطعنا واتقوا الله إن الله عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 5, 'يا أيها الذين آمنوا كونوا قوامين لله شهداء بالقسط ولا يجرمنكم شنآن قوم على ألا تعدلوا اعدلوا هو أقرب للتقوى واتقوا الله إن الله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 5, 'وعد الله الذين آمنوا وعملوا الصالحات لهم مغفرة وأجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 5, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 5, 'يا أيها الذين آمنوا اذكروا نعمت الله عليكم إذ هم قوم أن يبسطوا إليكم أيديهم فكف أيديهم عنكم واتقوا الله وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 5, 'ولقد أخذ الله ميثاق بني إسرائيل وبعثنا منهم اثني عشر نقيبا وقال الله إني معكم لئن أقمتم الصلاة وآتيتم الزكاة وآمنتم برسلي وعزرتموهم وأقرضتم الله قرضا حسنا لأكفرن عنكم سيئاتكم ولأدخلنكم جنات تجري من تحتها الأنهار فمن كفر بعد ذلك منكم فقد ضل سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 5, 'فبما نقضهم ميثاقهم لعناهم وجعلنا قلوبهم قاسية يحرفون الكلم عن مواضعه ونسوا حظا مما ذكروا به ولا تزال تطلع على خائنة منهم إلا قليلا منهم فاعف عنهم واصفح إن الله يحب المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 5, 'ومن الذين قالوا إنا نصارى أخذنا ميثاقهم فنسوا حظا مما ذكروا به فأغرينا بينهم العداوة والبغضاء إلى يوم القيامة وسوف ينبئهم الله بما كانوا يصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 5, 'يا أهل الكتاب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتاب ويعفو عن كثير قد جاءكم من الله نور وكتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 5, 'يهدي به الله من اتبع رضوانه سبل السلام ويخرجهم من الظلمات إلى النور بإذنه ويهديهم إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 5, 'لقد كفر الذين قالوا إن الله هو المسيح ابن مريم قل فمن يملك من الله شيئا إن أراد أن يهلك المسيح ابن مريم وأمه ومن في الأرض جميعا ولله ملك السماوات والأرض وما بينهما يخلق ما يشاء والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 5, 'وقالت اليهود والنصارى نحن أبناء الله وأحباؤه قل فلم يعذبكم بذنوبكم بل أنتم بشر ممن خلق يغفر لمن يشاء ويعذب من يشاء ولله ملك السماوات والأرض وما بينهما وإليه المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 5, 'يا أهل الكتاب قد جاءكم رسولنا يبين لكم على فترة من الرسل أن تقولوا ما جاءنا من بشير ولا نذير فقد جاءكم بشير ونذير والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 5, 'وإذ قال موسى لقومه يا قوم اذكروا نعمة الله عليكم إذ جعل فيكم أنبياء وجعلكم ملوكا وآتاكم ما لم يؤت أحدا من العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 5, 'يا قوم ادخلوا الأرض المقدسة التي كتب الله لكم ولا ترتدوا على أدباركم فتنقلبوا خاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 5, 'قالوا يا موسى إن فيها قوما جبارين وإنا لن ندخلها حتى يخرجوا منها فإن يخرجوا منها فإنا داخلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 5, 'قال رجلان من الذين يخافون أنعم الله عليهما ادخلوا عليهم الباب فإذا دخلتموه فإنكم غالبون وعلى الله فتوكلوا إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 5, 'قالوا يا موسى إنا لن ندخلها أبدا ما داموا فيها فاذهب أنت وربك فقاتلا إنا هاهنا قاعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 5, 'قال رب إني لا أملك إلا نفسي وأخي فافرق بيننا وبين القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 5, 'قال فإنها محرمة عليهم أربعين سنة يتيهون في الأرض فلا تأس على القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 5, 'واتل عليهم نبأ ابني آدم بالحق إذ قربا قربانا فتقبل من أحدهما ولم يتقبل من الآخر قال لأقتلنك قال إنما يتقبل الله من المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 5, 'لئن بسطت إلي يدك لتقتلني ما أنا بباسط يدي إليك لأقتلك إني أخاف الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 5, 'إني أريد أن تبوء بإثمي وإثمك فتكون من أصحاب النار وذلك جزاء الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 5, 'فطوعت له نفسه قتل أخيه فقتله فأصبح من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 5, 'فبعث الله غرابا يبحث في الأرض ليريه كيف يواري سوءة أخيه قال يا ويلتا أعجزت أن أكون مثل هذا الغراب فأواري سوءة أخي فأصبح من النادمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 5, 'من أجل ذلك كتبنا على بني إسرائيل أنه من قتل نفسا بغير نفس أو فساد في الأرض فكأنما قتل الناس جميعا ومن أحياها فكأنما أحيا الناس جميعا ولقد جاءتهم رسلنا بالبينات ثم إن كثيرا منهم بعد ذلك في الأرض لمسرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 5, 'إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فسادا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض ذلك لهم خزي في الدنيا ولهم في الآخرة عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 5, 'إلا الذين تابوا من قبل أن تقدروا عليهم فاعلموا أن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 5, 'يا أيها الذين آمنوا اتقوا الله وابتغوا إليه الوسيلة وجاهدوا في سبيله لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 5, 'إن الذين كفروا لو أن لهم ما في الأرض جميعا ومثله معه ليفتدوا به من عذاب يوم القيامة ما تقبل منهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 5, 'يريدون أن يخرجوا من النار وما هم بخارجين منها ولهم عذاب مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 5, 'والسارق والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالا من الله والله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 5, 'فمن تاب من بعد ظلمه وأصلح فإن الله يتوب عليه إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 5, 'ألم تعلم أن الله له ملك السماوات والأرض يعذب من يشاء ويغفر لمن يشاء والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 5, 'يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم ومن الذين هادوا سماعون للكذب سماعون لقوم آخرين لم يأتوك يحرفون الكلم من بعد مواضعه يقولون إن أوتيتم هذا فخذوه وإن لم تؤتوه فاحذروا ومن يرد الله فتنته فلن تملك له من الله شيئا أولئك الذين لم يرد الله أن يطهر قلوبهم لهم في الدنيا خزي ولهم في الآخرة عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 5, 'سماعون للكذب أكالون للسحت فإن جاءوك فاحكم بينهم أو أعرض عنهم وإن تعرض عنهم فلن يضروك شيئا وإن حكمت فاحكم بينهم بالقسط إن الله يحب المقسطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 5, 'وكيف يحكمونك وعندهم التوراة فيها حكم الله ثم يتولون من بعد ذلك وما أولئك بالمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 5, 'إنا أنزلنا التوراة فيها هدى ونور يحكم بها النبيون الذين أسلموا للذين هادوا والربانيون والأحبار بما استحفظوا من كتاب الله وكانوا عليه شهداء فلا تخشوا الناس واخشون ولا تشتروا بآياتي ثمنا قليلا ومن لم يحكم بما أنزل الله فأولئك هم الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 5, 'وكتبنا عليهم فيها أن النفس بالنفس والعين بالعين والأنف بالأنف والأذن بالأذن والسن بالسن والجروح قصاص فمن تصدق به فهو كفارة له ومن لم يحكم بما أنزل الله فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 5, 'وقفينا على آثارهم بعيسى ابن مريم مصدقا لما بين يديه من التوراة وآتيناه الإنجيل فيه هدى ونور ومصدقا لما بين يديه من التوراة وهدى وموعظة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 5, 'وليحكم أهل الإنجيل بما أنزل الله فيه ومن لم يحكم بما أنزل الله فأولئك هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 5, 'وأنزلنا إليك الكتاب بالحق مصدقا لما بين يديه من الكتاب ومهيمنا عليه فاحكم بينهم بما أنزل الله ولا تتبع أهواءهم عما جاءك من الحق لكل جعلنا منكم شرعة ومنهاجا ولو شاء الله لجعلكم أمة واحدة ولكن ليبلوكم في ما آتاكم فاستبقوا الخيرات إلى الله مرجعكم جميعا فينبئكم بما كنتم فيه تختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 5, 'وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم واحذرهم أن يفتنوك عن بعض ما أنزل الله إليك فإن تولوا فاعلم أنما يريد الله أن يصيبهم ببعض ذنوبهم وإن كثيرا من الناس لفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 5, 'أفحكم الجاهلية يبغون ومن أحسن من الله حكما لقوم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 5, 'يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء بعضهم أولياء بعض ومن يتولهم منكم فإنه منهم إن الله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 5, 'فترى الذين في قلوبهم مرض يسارعون فيهم يقولون نخشى أن تصيبنا دائرة فعسى الله أن يأتي بالفتح أو أمر من عنده فيصبحوا على ما أسروا في أنفسهم نادمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 5, 'ويقول الذين آمنوا أهؤلاء الذين أقسموا بالله جهد أيمانهم إنهم لمعكم حبطت أعمالهم فأصبحوا خاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 5, 'يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقوم يحبهم ويحبونه أذلة على المؤمنين أعزة على الكافرين يجاهدون في سبيل الله ولا يخافون لومة لائم ذلك فضل الله يؤتيه من يشاء والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 5, 'إنما وليكم الله ورسوله والذين آمنوا الذين يقيمون الصلاة ويؤتون الزكاة وهم راكعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 5, 'ومن يتول الله ورسوله والذين آمنوا فإن حزب الله هم الغالبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 5, 'يا أيها الذين آمنوا لا تتخذوا الذين اتخذوا دينكم هزوا ولعبا من الذين أوتوا الكتاب من قبلكم والكفار أولياء واتقوا الله إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 5, 'وإذا ناديتم إلى الصلاة اتخذوها هزوا ولعبا ذلك بأنهم قوم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 5, 'قل يا أهل الكتاب هل تنقمون منا إلا أن آمنا بالله وما أنزل إلينا وما أنزل من قبل وأن أكثركم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 5, 'قل هل أنبئكم بشر من ذلك مثوبة عند الله من لعنه الله وغضب عليه وجعل منهم القردة والخنازير وعبد الطاغوت أولئك شر مكانا وأضل عن سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 5, 'وإذا جاءوكم قالوا آمنا وقد دخلوا بالكفر وهم قد خرجوا به والله أعلم بما كانوا يكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 5, 'وترى كثيرا منهم يسارعون في الإثم والعدوان وأكلهم السحت لبئس ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 5, 'لولا ينهاهم الربانيون والأحبار عن قولهم الإثم وأكلهم السحت لبئس ما كانوا يصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 5, 'وقالت اليهود يد الله مغلولة غلت أيديهم ولعنوا بما قالوا بل يداه مبسوطتان ينفق كيف يشاء وليزيدن كثيرا منهم ما أنزل إليك من ربك طغيانا وكفرا وألقينا بينهم العداوة والبغضاء إلى يوم القيامة كلما أوقدوا نارا للحرب أطفأها الله ويسعون في الأرض فسادا والله لا يحب المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 5, 'ولو أن أهل الكتاب آمنوا واتقوا لكفرنا عنهم سيئاتهم ولأدخلناهم جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 5, 'ولو أنهم أقاموا التوراة والإنجيل وما أنزل إليهم من ربهم لأكلوا من فوقهم ومن تحت أرجلهم منهم أمة مقتصدة وكثير منهم ساء ما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 5, 'يا أيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته والله يعصمك من الناس إن الله لا يهدي القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 5, 'قل يا أهل الكتاب لستم على شيء حتى تقيموا التوراة والإنجيل وما أنزل إليكم من ربكم وليزيدن كثيرا منهم ما أنزل إليك من ربك طغيانا وكفرا فلا تأس على القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 5, 'إن الذين آمنوا والذين هادوا والصابئون والنصارى من آمن بالله واليوم الآخر وعمل صالحا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 5, 'لقد أخذنا ميثاق بني إسرائيل وأرسلنا إليهم رسلا كلما جاءهم رسول بما لا تهوى أنفسهم فريقا كذبوا وفريقا يقتلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 5, 'وحسبوا ألا تكون فتنة فعموا وصموا ثم تاب الله عليهم ثم عموا وصموا كثير منهم والله بصير بما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 5, 'لقد كفر الذين قالوا إن الله هو المسيح ابن مريم وقال المسيح يا بني إسرائيل اعبدوا الله ربي وربكم إنه من يشرك بالله فقد حرم الله عليه الجنة ومأواه النار وما للظالمين من أنصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 5, 'لقد كفر الذين قالوا إن الله ثالث ثلاثة وما من إله إلا إله واحد وإن لم ينتهوا عما يقولون ليمسن الذين كفروا منهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 5, 'أفلا يتوبون إلى الله ويستغفرونه والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 5, 'ما المسيح ابن مريم إلا رسول قد خلت من قبله الرسل وأمه صديقة كانا يأكلان الطعام انظر كيف نبين لهم الآيات ثم انظر أنى يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 5, 'قل أتعبدون من دون الله ما لا يملك لكم ضرا ولا نفعا والله هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 5, 'قل يا أهل الكتاب لا تغلوا في دينكم غير الحق ولا تتبعوا أهواء قوم قد ضلوا من قبل وأضلوا كثيرا وضلوا عن سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 5, 'لعن الذين كفروا من بني إسرائيل على لسان داوود وعيسى ابن مريم ذلك بما عصوا وكانوا يعتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 5, 'كانوا لا يتناهون عن منكر فعلوه لبئس ما كانوا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 5, 'ترى كثيرا منهم يتولون الذين كفروا لبئس ما قدمت لهم أنفسهم أن سخط الله عليهم وفي العذاب هم خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 5, 'ولو كانوا يؤمنون بالله والنبي وما أنزل إليه ما اتخذوهم أولياء ولكن كثيرا منهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 5, 'لتجدن أشد الناس عداوة للذين آمنوا اليهود والذين أشركوا ولتجدن أقربهم مودة للذين آمنوا الذين قالوا إنا نصارى ذلك بأن منهم قسيسين ورهبانا وأنهم لا يستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 5, 'وإذا سمعوا ما أنزل إلى الرسول ترى أعينهم تفيض من الدمع مما عرفوا من الحق يقولون ربنا آمنا فاكتبنا مع الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 5, 'وما لنا لا نؤمن بالله وما جاءنا من الحق ونطمع أن يدخلنا ربنا مع القوم الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 5, 'فأثابهم الله بما قالوا جنات تجري من تحتها الأنهار خالدين فيها وذلك جزاء المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 5, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 5, 'يا أيها الذين آمنوا لا تحرموا طيبات ما أحل الله لكم ولا تعتدوا إن الله لا يحب المعتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 5, 'وكلوا مما رزقكم الله حلالا طيبا واتقوا الله الذي أنتم به مؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 5, 'لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما عقدتم الأيمان فكفارته إطعام عشرة مساكين من أوسط ما تطعمون أهليكم أو كسوتهم أو تحرير رقبة فمن لم يجد فصيام ثلاثة أيام ذلك كفارة أيمانكم إذا حلفتم واحفظوا أيمانكم كذلك يبين الله لكم آياته لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 5, 'يا أيها الذين آمنوا إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 5, 'إنما يريد الشيطان أن يوقع بينكم العداوة والبغضاء في الخمر والميسر ويصدكم عن ذكر الله وعن الصلاة فهل أنتم منتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 5, 'وأطيعوا الله وأطيعوا الرسول واحذروا فإن توليتم فاعلموا أنما على رسولنا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 5, 'ليس على الذين آمنوا وعملوا الصالحات جناح فيما طعموا إذا ما اتقوا وآمنوا وعملوا الصالحات ثم اتقوا وآمنوا ثم اتقوا وأحسنوا والله يحب المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 5, 'يا أيها الذين آمنوا ليبلونكم الله بشيء من الصيد تناله أيديكم ورماحكم ليعلم الله من يخافه بالغيب فمن اعتدى بعد ذلك فله عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 5, 'يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديا بالغ الكعبة أو كفارة طعام مساكين أو عدل ذلك صياما ليذوق وبال أمره عفا الله عما سلف ومن عاد فينتقم الله منه والله عزيز ذو انتقام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 5, 'أحل لكم صيد البحر وطعامه متاعا لكم وللسيارة وحرم عليكم صيد البر ما دمتم حرما واتقوا الله الذي إليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 5, 'جعل الله الكعبة البيت الحرام قياما للناس والشهر الحرام والهدي والقلائد ذلك لتعلموا أن الله يعلم ما في السماوات وما في الأرض وأن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 5, 'اعلموا أن الله شديد العقاب وأن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 5, 'ما على الرسول إلا البلاغ والله يعلم ما تبدون وما تكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 5, 'قل لا يستوي الخبيث والطيب ولو أعجبك كثرة الخبيث فاتقوا الله يا أولي الألباب لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 5, 'يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم وإن تسألوا عنها حين ينزل القرآن تبد لكم عفا الله عنها والله غفور حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 5, 'قد سألها قوم من قبلكم ثم أصبحوا بها كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 5, 'ما جعل الله من بحيرة ولا سائبة ولا وصيلة ولا حام ولكن الذين كفروا يفترون على الله الكذب وأكثرهم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 5, 'وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول قالوا حسبنا ما وجدنا عليه آباءنا أولو كان آباؤهم لا يعلمون شيئا ولا يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 5, 'يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم إلى الله مرجعكم جميعا فينبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 5, 'يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية اثنان ذوا عدل منكم أو آخران من غيركم إن أنتم ضربتم في الأرض فأصابتكم مصيبة الموت تحبسونهما من بعد الصلاة فيقسمان بالله إن ارتبتم لا نشتري به ثمنا ولو كان ذا قربى ولا نكتم شهادة الله إنا إذا لمن الآثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 5, 'فإن عثر على أنهما استحقا إثما فآخران يقومان مقامهما من الذين استحق عليهم الأوليان فيقسمان بالله لشهادتنا أحق من شهادتهما وما اعتدينا إنا إذا لمن الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 5, 'ذلك أدنى أن يأتوا بالشهادة على وجهها أو يخافوا أن ترد أيمان بعد أيمانهم واتقوا الله واسمعوا والله لا يهدي القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 5, 'يوم يجمع الله الرسل فيقول ماذا أجبتم قالوا لا علم لنا إنك أنت علام الغيوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 5, 'إذ قال الله يا عيسى ابن مريم اذكر نعمتي عليك وعلى والدتك إذ أيدتك بروح القدس تكلم الناس في المهد وكهلا وإذ علمتك الكتاب والحكمة والتوراة والإنجيل وإذ تخلق من الطين كهيئة الطير بإذني فتنفخ فيها فتكون طيرا بإذني وتبرئ الأكمه والأبرص بإذني وإذ تخرج الموتى بإذني وإذ كففت بني إسرائيل عنك إذ جئتهم بالبينات فقال الذين كفروا منهم إن هذا إلا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 5, 'وإذ أوحيت إلى الحواريين أن آمنوا بي وبرسولي قالوا آمنا واشهد بأننا مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 5, 'إذ قال الحواريون يا عيسى ابن مريم هل يستطيع ربك أن ينزل علينا مائدة من السماء قال اتقوا الله إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 5, 'قالوا نريد أن نأكل منها وتطمئن قلوبنا ونعلم أن قد صدقتنا ونكون عليها من الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 5, 'قال عيسى ابن مريم اللهم ربنا أنزل علينا مائدة من السماء تكون لنا عيدا لأولنا وآخرنا وآية منك وارزقنا وأنت خير الرازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 5, 'قال الله إني منزلها عليكم فمن يكفر بعد منكم فإني أعذبه عذابا لا أعذبه أحدا من العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 5, 'وإذ قال الله يا عيسى ابن مريم أأنت قلت للناس اتخذوني وأمي إلهين من دون الله قال سبحانك ما يكون لي أن أقول ما ليس لي بحق إن كنت قلته فقد علمته تعلم ما في نفسي ولا أعلم ما في نفسك إنك أنت علام الغيوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 5, 'ما قلت لهم إلا ما أمرتني به أن اعبدوا الله ربي وربكم وكنت عليهم شهيدا ما دمت فيهم فلما توفيتني كنت أنت الرقيب عليهم وأنت على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 5, 'إن تعذبهم فإنهم عبادك وإن تغفر لهم فإنك أنت العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 5, 'قال الله هذا يوم ينفع الصادقين صدقهم لهم جنات تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم ورضوا عنه ذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 5, 'لله ملك السماوات والأرض وما فيهن وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 6, 'بسم الله الرحمن الرحيم الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور ثم الذين كفروا بربهم يعدلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 6, 'هو الذي خلقكم من طين ثم قضى أجلا وأجل مسمى عنده ثم أنتم تمترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 6, 'وهو الله في السماوات وفي الأرض يعلم سركم وجهركم ويعلم ما تكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 6, 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 6, 'فقد كذبوا بالحق لما جاءهم فسوف يأتيهم أنباء ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 6, 'ألم يروا كم أهلكنا من قبلهم من قرن مكناهم في الأرض ما لم نمكن لكم وأرسلنا السماء عليهم مدرارا وجعلنا الأنهار تجري من تحتهم فأهلكناهم بذنوبهم وأنشأنا من بعدهم قرنا آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 6, 'ولو نزلنا عليك كتابا في قرطاس فلمسوه بأيديهم لقال الذين كفروا إن هذا إلا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 6, 'وقالوا لولا أنزل عليه ملك ولو أنزلنا ملكا لقضي الأمر ثم لا ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 6, 'ولو جعلناه ملكا لجعلناه رجلا وللبسنا عليهم ما يلبسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 6, 'ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 6, 'قل سيروا في الأرض ثم انظروا كيف كان عاقبة المكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 6, 'قل لمن ما في السماوات والأرض قل لله كتب على نفسه الرحمة ليجمعنكم إلى يوم القيامة لا ريب فيه الذين خسروا أنفسهم فهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 6, 'وله ما سكن في الليل والنهار وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 6, 'قل أغير الله أتخذ وليا فاطر السماوات والأرض وهو يطعم ولا يطعم قل إني أمرت أن أكون أول من أسلم ولا تكونن من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 6, 'قل إني أخاف إن عصيت ربي عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 6, 'من يصرف عنه يومئذ فقد رحمه وذلك الفوز المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 6, 'وإن يمسسك الله بضر فلا كاشف له إلا هو وإن يمسسك بخير فهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 6, 'وهو القاهر فوق عباده وهو الحكيم الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 6, 'قل أي شيء أكبر شهادة قل الله شهيد بيني وبينكم وأوحي إلي هذا القرآن لأنذركم به ومن بلغ أئنكم لتشهدون أن مع الله آلهة أخرى قل لا أشهد قل إنما هو إله واحد وإنني بريء مما تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 6, 'الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم الذين خسروا أنفسهم فهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 6, 'ومن أظلم ممن افترى على الله كذبا أو كذب بآياته إنه لا يفلح الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 6, 'ويوم نحشرهم جميعا ثم نقول للذين أشركوا أين شركاؤكم الذين كنتم تزعمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 6, 'ثم لم تكن فتنتهم إلا أن قالوا والله ربنا ما كنا مشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 6, 'انظر كيف كذبوا على أنفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 6, 'ومنهم من يستمع إليك وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن يروا كل آية لا يؤمنوا بها حتى إذا جاءوك يجادلونك يقول الذين كفروا إن هذا إلا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 6, 'وهم ينهون عنه وينأون عنه وإن يهلكون إلا أنفسهم وما يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 6, 'ولو ترى إذ وقفوا على النار فقالوا يا ليتنا نرد ولا نكذب بآيات ربنا ونكون من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 6, 'بل بدا لهم ما كانوا يخفون من قبل ولو ردوا لعادوا لما نهوا عنه وإنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 6, 'وقالوا إن هي إلا حياتنا الدنيا وما نحن بمبعوثين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 6, 'ولو ترى إذ وقفوا على ربهم قال أليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 6, 'قد خسر الذين كذبوا بلقاء الله حتى إذا جاءتهم الساعة بغتة قالوا يا حسرتنا على ما فرطنا فيها وهم يحملون أوزارهم على ظهورهم ألا ساء ما يزرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 6, 'وما الحياة الدنيا إلا لعب ولهو وللدار الآخرة خير للذين يتقون أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 6, 'قد نعلم إنه ليحزنك الذي يقولون فإنهم لا يكذبونك ولكن الظالمين بآيات الله يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 6, 'ولقد كذبت رسل من قبلك فصبروا على ما كذبوا وأوذوا حتى أتاهم نصرنا ولا مبدل لكلمات الله ولقد جاءك من نبإ المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 6, 'وإن كان كبر عليك إعراضهم فإن استطعت أن تبتغي نفقا في الأرض أو سلما في السماء فتأتيهم بآية ولو شاء الله لجمعهم على الهدى فلا تكونن من الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 6, 'إنما يستجيب الذين يسمعون والموتى يبعثهم الله ثم إليه يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 6, 'وقالوا لولا نزل عليه آية من ربه قل إن الله قادر على أن ينزل آية ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 6, 'وما من دابة في الأرض ولا طائر يطير بجناحيه إلا أمم أمثالكم ما فرطنا في الكتاب من شيء ثم إلى ربهم يحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 6, 'والذين كذبوا بآياتنا صم وبكم في الظلمات من يشإ الله يضلله ومن يشأ يجعله على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 6, 'قل أرأيتكم إن أتاكم عذاب الله أو أتتكم الساعة أغير الله تدعون إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 6, 'بل إياه تدعون فيكشف ما تدعون إليه إن شاء وتنسون ما تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 6, 'ولقد أرسلنا إلى أمم من قبلك فأخذناهم بالبأساء والضراء لعلهم يتضرعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 6, 'فلولا إذ جاءهم بأسنا تضرعوا ولكن قست قلوبهم وزين لهم الشيطان ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 6, 'فلما نسوا ما ذكروا به فتحنا عليهم أبواب كل شيء حتى إذا فرحوا بما أوتوا أخذناهم بغتة فإذا هم مبلسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 6, 'فقطع دابر القوم الذين ظلموا والحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 6, 'قل أرأيتم إن أخذ الله سمعكم وأبصاركم وختم على قلوبكم من إله غير الله يأتيكم به انظر كيف نصرف الآيات ثم هم يصدفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 6, 'قل أرأيتكم إن أتاكم عذاب الله بغتة أو جهرة هل يهلك إلا القوم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 6, 'وما نرسل المرسلين إلا مبشرين ومنذرين فمن آمن وأصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 6, 'والذين كذبوا بآياتنا يمسهم العذاب بما كانوا يفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 6, 'قل لا أقول لكم عندي خزائن الله ولا أعلم الغيب ولا أقول لكم إني ملك إن أتبع إلا ما يوحى إلي قل هل يستوي الأعمى والبصير أفلا تتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 6, 'وأنذر به الذين يخافون أن يحشروا إلى ربهم ليس لهم من دونه ولي ولا شفيع لعلهم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 6, 'ولا تطرد الذين يدعون ربهم بالغداة والعشي يريدون وجهه ما عليك من حسابهم من شيء وما من حسابك عليهم من شيء فتطردهم فتكون من الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 6, 'وكذلك فتنا بعضهم ببعض ليقولوا أهؤلاء من الله عليهم من بيننا أليس الله بأعلم بالشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 6, 'وإذا جاءك الذين يؤمنون بآياتنا فقل سلام عليكم كتب ربكم على نفسه الرحمة أنه من عمل منكم سوءا بجهالة ثم تاب من بعده وأصلح فأنه غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 6, 'وكذلك نفصل الآيات ولتستبين سبيل المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 6, 'قل إني نهيت أن أعبد الذين تدعون من دون الله قل لا أتبع أهواءكم قد ضللت إذا وما أنا من المهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 6, 'قل إني على بينة من ربي وكذبتم به ما عندي ما تستعجلون به إن الحكم إلا لله يقص الحق وهو خير الفاصلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 6, 'قل لو أن عندي ما تستعجلون به لقضي الأمر بيني وبينكم والله أعلم بالظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 6, 'وعنده مفاتح الغيب لا يعلمها إلا هو ويعلم ما في البر والبحر وما تسقط من ورقة إلا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس إلا في كتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 6, 'وهو الذي يتوفاكم بالليل ويعلم ما جرحتم بالنهار ثم يبعثكم فيه ليقضى أجل مسمى ثم إليه مرجعكم ثم ينبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 6, 'وهو القاهر فوق عباده ويرسل عليكم حفظة حتى إذا جاء أحدكم الموت توفته رسلنا وهم لا يفرطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 6, 'ثم ردوا إلى الله مولاهم الحق ألا له الحكم وهو أسرع الحاسبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 6, 'قل من ينجيكم من ظلمات البر والبحر تدعونه تضرعا وخفية لئن أنجانا من هذه لنكونن من الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 6, 'قل الله ينجيكم منها ومن كل كرب ثم أنتم تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 6, 'قل هو القادر على أن يبعث عليكم عذابا من فوقكم أو من تحت أرجلكم أو يلبسكم شيعا ويذيق بعضكم بأس بعض انظر كيف نصرف الآيات لعلهم يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 6, 'وكذب به قومك وهو الحق قل لست عليكم بوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 6, 'لكل نبإ مستقر وسوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 6, 'وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم حتى يخوضوا في حديث غيره وإما ينسينك الشيطان فلا تقعد بعد الذكرى مع القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 6, 'وما على الذين يتقون من حسابهم من شيء ولكن ذكرى لعلهم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 6, 'وذر الذين اتخذوا دينهم لعبا ولهوا وغرتهم الحياة الدنيا وذكر به أن تبسل نفس بما كسبت ليس لها من دون الله ولي ولا شفيع وإن تعدل كل عدل لا يؤخذ منها أولئك الذين أبسلوا بما كسبوا لهم شراب من حميم وعذاب أليم بما كانوا يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 6, 'قل أندعو من دون الله ما لا ينفعنا ولا يضرنا ونرد على أعقابنا بعد إذ هدانا الله كالذي استهوته الشياطين في الأرض حيران له أصحاب يدعونه إلى الهدى ائتنا قل إن هدى الله هو الهدى وأمرنا لنسلم لرب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 6, 'وأن أقيموا الصلاة واتقوه وهو الذي إليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 6, 'وهو الذي خلق السماوات والأرض بالحق ويوم يقول كن فيكون قوله الحق وله الملك يوم ينفخ في الصور عالم الغيب والشهادة وهو الحكيم الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 6, 'وإذ قال إبراهيم لأبيه آزر أتتخذ أصناما آلهة إني أراك وقومك في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 6, 'وكذلك نري إبراهيم ملكوت السماوات والأرض وليكون من الموقنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 6, 'فلما جن عليه الليل رأى كوكبا قال هذا ربي فلما أفل قال لا أحب الآفلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 6, 'فلما رأى القمر بازغا قال هذا ربي فلما أفل قال لئن لم يهدني ربي لأكونن من القوم الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 6, 'فلما رأى الشمس بازغة قال هذا ربي هذا أكبر فلما أفلت قال يا قوم إني بريء مما تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 6, 'إني وجهت وجهي للذي فطر السماوات والأرض حنيفا وما أنا من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 6, 'وحاجه قومه قال أتحاجوني في الله وقد هدان ولا أخاف ما تشركون به إلا أن يشاء ربي شيئا وسع ربي كل شيء علما أفلا تتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 6, 'وكيف أخاف ما أشركتم ولا تخافون أنكم أشركتم بالله ما لم ينزل به عليكم سلطانا فأي الفريقين أحق بالأمن إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 6, 'الذين آمنوا ولم يلبسوا إيمانهم بظلم أولئك لهم الأمن وهم مهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 6, 'وتلك حجتنا آتيناها إبراهيم على قومه نرفع درجات من نشاء إن ربك حكيم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 6, 'ووهبنا له إسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل ومن ذريته داوود وسليمان وأيوب ويوسف وموسى وهارون وكذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 6, 'وزكريا ويحيى وعيسى وإلياس كل من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 6, 'وإسماعيل واليسع ويونس ولوطا وكلا فضلنا على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 6, 'ومن آبائهم وذرياتهم وإخوانهم واجتبيناهم وهديناهم إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 6, 'ذلك هدى الله يهدي به من يشاء من عباده ولو أشركوا لحبط عنهم ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 6, 'أولئك الذين آتيناهم الكتاب والحكم والنبوة فإن يكفر بها هؤلاء فقد وكلنا بها قوما ليسوا بها بكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 6, 'أولئك الذين هدى الله فبهداهم اقتده قل لا أسألكم عليه أجرا إن هو إلا ذكرى للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 6, 'وما قدروا الله حق قدره إذ قالوا ما أنزل الله على بشر من شيء قل من أنزل الكتاب الذي جاء به موسى نورا وهدى للناس تجعلونه قراطيس تبدونها وتخفون كثيرا وعلمتم ما لم تعلموا أنتم ولا آباؤكم قل الله ثم ذرهم في خوضهم يلعبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 6, 'وهذا كتاب أنزلناه مبارك مصدق الذي بين يديه ولتنذر أم القرى ومن حولها والذين يؤمنون بالآخرة يؤمنون به وهم على صلاتهم يحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 6, 'ومن أظلم ممن افترى على الله كذبا أو قال أوحي إلي ولم يوح إليه شيء ومن قال سأنزل مثل ما أنزل الله ولو ترى إذ الظالمون في غمرات الموت والملائكة باسطو أيديهم أخرجوا أنفسكم اليوم تجزون عذاب الهون بما كنتم تقولون على الله غير الحق وكنتم عن آياته تستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 6, 'ولقد جئتمونا فرادى كما خلقناكم أول مرة وتركتم ما خولناكم وراء ظهوركم وما نرى معكم شفعاءكم الذين زعمتم أنهم فيكم شركاء لقد تقطع بينكم وضل عنكم ما كنتم تزعمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 6, 'إن الله فالق الحب والنوى يخرج الحي من الميت ومخرج الميت من الحي ذلكم الله فأنى تؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 6, 'فالق الإصباح وجعل الليل سكنا والشمس والقمر حسبانا ذلك تقدير العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 6, 'وهو الذي جعل لكم النجوم لتهتدوا بها في ظلمات البر والبحر قد فصلنا الآيات لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 6, 'وهو الذي أنشأكم من نفس واحدة فمستقر ومستودع قد فصلنا الآيات لقوم يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 6, 'وهو الذي أنزل من السماء ماء فأخرجنا به نبات كل شيء فأخرجنا منه خضرا نخرج منه حبا متراكبا ومن النخل من طلعها قنوان دانية وجنات من أعناب والزيتون والرمان مشتبها وغير متشابه انظروا إلى ثمره إذا أثمر وينعه إن في ذلكم لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 6, 'وجعلوا لله شركاء الجن وخلقهم وخرقوا له بنين وبنات بغير علم سبحانه وتعالى عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 6, 'بديع السماوات والأرض أنى يكون له ولد ولم تكن له صاحبة وخلق كل شيء وهو بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 6, 'ذلكم الله ربكم لا إله إلا هو خالق كل شيء فاعبدوه وهو على كل شيء وكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 6, 'لا تدركه الأبصار وهو يدرك الأبصار وهو اللطيف الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 6, 'قد جاءكم بصائر من ربكم فمن أبصر فلنفسه ومن عمي فعليها وما أنا عليكم بحفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 6, 'وكذلك نصرف الآيات وليقولوا درست ولنبينه لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 6, 'اتبع ما أوحي إليك من ربك لا إله إلا هو وأعرض عن المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 6, 'ولو شاء الله ما أشركوا وما جعلناك عليهم حفيظا وما أنت عليهم بوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 6, 'ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم كذلك زينا لكل أمة عملهم ثم إلى ربهم مرجعهم فينبئهم بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 6, 'وأقسموا بالله جهد أيمانهم لئن جاءتهم آية ليؤمنن بها قل إنما الآيات عند الله وما يشعركم أنها إذا جاءت لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 6, 'ونقلب أفئدتهم وأبصارهم كما لم يؤمنوا به أول مرة ونذرهم في طغيانهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 6, 'ولو أننا نزلنا إليهم الملائكة وكلمهم الموتى وحشرنا عليهم كل شيء قبلا ما كانوا ليؤمنوا إلا أن يشاء الله ولكن أكثرهم يجهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 6, 'وكذلك جعلنا لكل نبي عدوا شياطين الإنس والجن يوحي بعضهم إلى بعض زخرف القول غرورا ولو شاء ربك ما فعلوه فذرهم وما يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 6, 'ولتصغى إليه أفئدة الذين لا يؤمنون بالآخرة وليرضوه وليقترفوا ما هم مقترفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 6, 'أفغير الله أبتغي حكما وهو الذي أنزل إليكم الكتاب مفصلا والذين آتيناهم الكتاب يعلمون أنه منزل من ربك بالحق فلا تكونن من الممترين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 6, 'وتمت كلمت ربك صدقا وعدلا لا مبدل لكلماته وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 6, 'وإن تطع أكثر من في الأرض يضلوك عن سبيل الله إن يتبعون إلا الظن وإن هم إلا يخرصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 6, 'إن ربك هو أعلم من يضل عن سبيله وهو أعلم بالمهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 6, 'فكلوا مما ذكر اسم الله عليه إن كنتم بآياته مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 6, 'وما لكم ألا تأكلوا مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم إلا ما اضطررتم إليه وإن كثيرا ليضلون بأهوائهم بغير علم إن ربك هو أعلم بالمعتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 6, 'وذروا ظاهر الإثم وباطنه إن الذين يكسبون الإثم سيجزون بما كانوا يقترفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 6, 'ولا تأكلوا مما لم يذكر اسم الله عليه وإنه لفسق وإن الشياطين ليوحون إلى أوليائهم ليجادلوكم وإن أطعتموهم إنكم لمشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 6, 'أومن كان ميتا فأحييناه وجعلنا له نورا يمشي به في الناس كمن مثله في الظلمات ليس بخارج منها كذلك زين للكافرين ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 6, 'وكذلك جعلنا في كل قرية أكابر مجرميها ليمكروا فيها وما يمكرون إلا بأنفسهم وما يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 6, 'وإذا جاءتهم آية قالوا لن نؤمن حتى نؤتى مثل ما أوتي رسل الله الله أعلم حيث يجعل رسالته سيصيب الذين أجرموا صغار عند الله وعذاب شديد بما كانوا يمكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 6, 'فمن يرد الله أن يهديه يشرح صدره للإسلام ومن يرد أن يضله يجعل صدره ضيقا حرجا كأنما يصعد في السماء كذلك يجعل الله الرجس على الذين لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 6, 'وهذا صراط ربك مستقيما قد فصلنا الآيات لقوم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 6, 'لهم دار السلام عند ربهم وهو وليهم بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 6, 'ويوم يحشرهم جميعا يا معشر الجن قد استكثرتم من الإنس وقال أولياؤهم من الإنس ربنا استمتع بعضنا ببعض وبلغنا أجلنا الذي أجلت لنا قال النار مثواكم خالدين فيها إلا ما شاء الله إن ربك حكيم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 6, 'وكذلك نولي بعض الظالمين بعضا بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 6, 'يا معشر الجن والإنس ألم يأتكم رسل منكم يقصون عليكم آياتي وينذرونكم لقاء يومكم هذا قالوا شهدنا على أنفسنا وغرتهم الحياة الدنيا وشهدوا على أنفسهم أنهم كانوا كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 6, 'ذلك أن لم يكن ربك مهلك القرى بظلم وأهلها غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 6, 'ولكل درجات مما عملوا وما ربك بغافل عما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 6, 'وربك الغني ذو الرحمة إن يشأ يذهبكم ويستخلف من بعدكم ما يشاء كما أنشأكم من ذرية قوم آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 6, 'إن ما توعدون لآت وما أنتم بمعجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 6, 'قل يا قوم اعملوا على مكانتكم إني عامل فسوف تعلمون من تكون له عاقبة الدار إنه لا يفلح الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 6, 'وجعلوا لله مما ذرأ من الحرث والأنعام نصيبا فقالوا هذا لله بزعمهم وهذا لشركائنا فما كان لشركائهم فلا يصل إلى الله وما كان لله فهو يصل إلى شركائهم ساء ما يحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 6, 'وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 6, 'وقالوا هذه أنعام وحرث حجر لا يطعمها إلا من نشاء بزعمهم وأنعام حرمت ظهورها وأنعام لا يذكرون اسم الله عليها افتراء عليه سيجزيهم بما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 6, 'وقالوا ما في بطون هذه الأنعام خالصة لذكورنا ومحرم على أزواجنا وإن يكن ميتة فهم فيه شركاء سيجزيهم وصفهم إنه حكيم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 6, 'قد خسر الذين قتلوا أولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله قد ضلوا وما كانوا مهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 6, 'وهو الذي أنشأ جنات معروشات وغير معروشات والنخل والزرع مختلفا أكله والزيتون والرمان متشابها وغير متشابه كلوا من ثمره إذا أثمر وآتوا حقه يوم حصاده ولا تسرفوا إنه لا يحب المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 6, 'ومن الأنعام حمولة وفرشا كلوا مما رزقكم الله ولا تتبعوا خطوات الشيطان إنه لكم عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 6, 'ثمانية أزواج من الضأن اثنين ومن المعز اثنين قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين نبئوني بعلم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 6, 'ومن الإبل اثنين ومن البقر اثنين قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين أم كنتم شهداء إذ وصاكم الله بهذا فمن أظلم ممن افترى على الله كذبا ليضل الناس بغير علم إن الله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 6, 'قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير فإنه رجس أو فسقا أهل لغير الله به فمن اضطر غير باغ ولا عاد فإن ربك غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 6, 'وعلى الذين هادوا حرمنا كل ذي ظفر ومن البقر والغنم حرمنا عليهم شحومهما إلا ما حملت ظهورهما أو الحوايا أو ما اختلط بعظم ذلك جزيناهم ببغيهم وإنا لصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 6, 'فإن كذبوك فقل ربكم ذو رحمة واسعة ولا يرد بأسه عن القوم المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 6, 'سيقول الذين أشركوا لو شاء الله ما أشركنا ولا آباؤنا ولا حرمنا من شيء كذلك كذب الذين من قبلهم حتى ذاقوا بأسنا قل هل عندكم من علم فتخرجوه لنا إن تتبعون إلا الظن وإن أنتم إلا تخرصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 6, 'قل فلله الحجة البالغة فلو شاء لهداكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 6, 'قل هلم شهداءكم الذين يشهدون أن الله حرم هذا فإن شهدوا فلا تشهد معهم ولا تتبع أهواء الذين كذبوا بآياتنا والذين لا يؤمنون بالآخرة وهم بربهم يعدلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 6, 'قل تعالوا أتل ما حرم ربكم عليكم ألا تشركوا به شيئا وبالوالدين إحسانا ولا تقتلوا أولادكم من إملاق نحن نرزقكم وإياهم ولا تقربوا الفواحش ما ظهر منها وما بطن ولا تقتلوا النفس التي حرم الله إلا بالحق ذلكم وصاكم به لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 6, 'ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده وأوفوا الكيل والميزان بالقسط لا نكلف نفسا إلا وسعها وإذا قلتم فاعدلوا ولو كان ذا قربى وبعهد الله أوفوا ذلكم وصاكم به لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 6, 'وأن هذا صراطي مستقيما فاتبعوه ولا تتبعوا السبل فتفرق بكم عن سبيله ذلكم وصاكم به لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 6, 'ثم آتينا موسى الكتاب تماما على الذي أحسن وتفصيلا لكل شيء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 6, 'وهذا كتاب أنزلناه مبارك فاتبعوه واتقوا لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 6, 'أن تقولوا إنما أنزل الكتاب على طائفتين من قبلنا وإن كنا عن دراستهم لغافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 6, 'أو تقولوا لو أنا أنزل علينا الكتاب لكنا أهدى منهم فقد جاءكم بينة من ربكم وهدى ورحمة فمن أظلم ممن كذب بآيات الله وصدف عنها سنجزي الذين يصدفون عن آياتنا سوء العذاب بما كانوا يصدفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 6, 'هل ينظرون إلا أن تأتيهم الملائكة أو يأتي ربك أو يأتي بعض آيات ربك يوم يأتي بعض آيات ربك لا ينفع نفسا إيمانها لم تكن آمنت من قبل أو كسبت في إيمانها خيرا قل انتظروا إنا منتظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 6, 'إن الذين فرقوا دينهم وكانوا شيعا لست منهم في شيء إنما أمرهم إلى الله ثم ينبئهم بما كانوا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 6, 'من جاء بالحسنة فله عشر أمثالها ومن جاء بالسيئة فلا يجزى إلا مثلها وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 6, 'قل إنني هداني ربي إلى صراط مستقيم دينا قيما ملة إبراهيم حنيفا وما كان من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 6, 'قل إن صلاتي ونسكي ومحياي ومماتي لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 6, 'لا شريك له وبذلك أمرت وأنا أول المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 6, 'قل أغير الله أبغي ربا وهو رب كل شيء ولا تكسب كل نفس إلا عليها ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم فيه تختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 6, 'وهو الذي جعلكم خلائف الأرض ورفع بعضكم فوق بعض درجات ليبلوكم في ما آتاكم إن ربك سريع العقاب وإنه لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 7, 'بسم الله الرحمن الرحيم المص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 7, 'كتاب أنزل إليك فلا يكن في صدرك حرج منه لتنذر به وذكرى للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 7, 'اتبعوا ما أنزل إليكم من ربكم ولا تتبعوا من دونه أولياء قليلا ما تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 7, 'وكم من قرية أهلكناها فجاءها بأسنا بياتا أو هم قائلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 7, 'فما كان دعواهم إذ جاءهم بأسنا إلا أن قالوا إنا كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 7, 'فلنسألن الذين أرسل إليهم ولنسألن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 7, 'فلنقصن عليهم بعلم وما كنا غائبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 7, 'والوزن يومئذ الحق فمن ثقلت موازينه فأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 7, 'ومن خفت موازينه فأولئك الذين خسروا أنفسهم بما كانوا بآياتنا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 7, 'ولقد مكناكم في الأرض وجعلنا لكم فيها معايش قليلا ما تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 7, 'ولقد خلقناكم ثم صورناكم ثم قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس لم يكن من الساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 7, 'قال ما منعك ألا تسجد إذ أمرتك قال أنا خير منه خلقتني من نار وخلقته من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 7, 'قال فاهبط منها فما يكون لك أن تتكبر فيها فاخرج إنك من الصاغرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 7, 'قال أنظرني إلى يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 7, 'قال إنك من المنظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 7, 'قال فبما أغويتني لأقعدن لهم صراطك المستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 7, 'ثم لآتينهم من بين أيديهم ومن خلفهم وعن أيمانهم وعن شمائلهم ولا تجد أكثرهم شاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 7, 'قال اخرج منها مذءوما مدحورا لمن تبعك منهم لأملأن جهنم منكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 7, 'ويا آدم اسكن أنت وزوجك الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 7, 'فوسوس لهما الشيطان ليبدي لهما ما ووري عنهما من سوآتهما وقال ما نهاكما ربكما عن هذه الشجرة إلا أن تكونا ملكين أو تكونا من الخالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 7, 'وقاسمهما إني لكما لمن الناصحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 7, 'فدلاهما بغرور فلما ذاقا الشجرة بدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة وناداهما ربهما ألم أنهكما عن تلكما الشجرة وأقل لكما إن الشيطان لكما عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 7, 'قالا ربنا ظلمنا أنفسنا وإن لم تغفر لنا وترحمنا لنكونن من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 7, 'قال اهبطوا بعضكم لبعض عدو ولكم في الأرض مستقر ومتاع إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 7, 'قال فيها تحيون وفيها تموتون ومنها تخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 7, 'يا بني آدم قد أنزلنا عليكم لباسا يواري سوآتكم وريشا ولباس التقوى ذلك خير ذلك من آيات الله لعلهم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 7, 'يا بني آدم لا يفتننكم الشيطان كما أخرج أبويكم من الجنة ينزع عنهما لباسهما ليريهما سوآتهما إنه يراكم هو وقبيله من حيث لا ترونهم إنا جعلنا الشياطين أولياء للذين لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 7, 'وإذا فعلوا فاحشة قالوا وجدنا عليها آباءنا والله أمرنا بها قل إن الله لا يأمر بالفحشاء أتقولون على الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 7, 'قل أمر ربي بالقسط وأقيموا وجوهكم عند كل مسجد وادعوه مخلصين له الدين كما بدأكم تعودون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 7, 'فريقا هدى وفريقا حق عليهم الضلالة إنهم اتخذوا الشياطين أولياء من دون الله ويحسبون أنهم مهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 7, 'يا بني آدم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا إنه لا يحب المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 7, 'قل من حرم زينة الله التي أخرج لعباده والطيبات من الرزق قل هي للذين آمنوا في الحياة الدنيا خالصة يوم القيامة كذلك نفصل الآيات لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 7, 'قل إنما حرم ربي الفواحش ما ظهر منها وما بطن والإثم والبغي بغير الحق وأن تشركوا بالله ما لم ينزل به سلطانا وأن تقولوا على الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 7, 'ولكل أمة أجل فإذا جاء أجلهم لا يستأخرون ساعة ولا يستقدمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 7, 'يا بني آدم إما يأتينكم رسل منكم يقصون عليكم آياتي فمن اتقى وأصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 7, 'والذين كذبوا بآياتنا واستكبروا عنها أولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 7, 'فمن أظلم ممن افترى على الله كذبا أو كذب بآياته أولئك ينالهم نصيبهم من الكتاب حتى إذا جاءتهم رسلنا يتوفونهم قالوا أين ما كنتم تدعون من دون الله قالوا ضلوا عنا وشهدوا على أنفسهم أنهم كانوا كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 7, 'قال ادخلوا في أمم قد خلت من قبلكم من الجن والإنس في النار كلما دخلت أمة لعنت أختها حتى إذا اداركوا فيها جميعا قالت أخراهم لأولاهم ربنا هؤلاء أضلونا فآتهم عذابا ضعفا من النار قال لكل ضعف ولكن لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 7, 'وقالت أولاهم لأخراهم فما كان لكم علينا من فضل فذوقوا العذاب بما كنتم تكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 7, 'إن الذين كذبوا بآياتنا واستكبروا عنها لا تفتح لهم أبواب السماء ولا يدخلون الجنة حتى يلج الجمل في سم الخياط وكذلك نجزي المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 7, 'لهم من جهنم مهاد ومن فوقهم غواش وكذلك نجزي الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 7, 'والذين آمنوا وعملوا الصالحات لا نكلف نفسا إلا وسعها أولئك أصحاب الجنة هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 7, 'ونزعنا ما في صدورهم من غل تجري من تحتهم الأنهار وقالوا الحمد لله الذي هدانا لهذا وما كنا لنهتدي لولا أن هدانا الله لقد جاءت رسل ربنا بالحق ونودوا أن تلكم الجنة أورثتموها بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 7, 'ونادى أصحاب الجنة أصحاب النار أن قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا قالوا نعم فأذن مؤذن بينهم أن لعنة الله على الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 7, 'الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالآخرة كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 7, 'وبينهما حجاب وعلى الأعراف رجال يعرفون كلا بسيماهم ونادوا أصحاب الجنة أن سلام عليكم لم يدخلوها وهم يطمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 7, 'وإذا صرفت أبصارهم تلقاء أصحاب النار قالوا ربنا لا تجعلنا مع القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 7, 'ونادى أصحاب الأعراف رجالا يعرفونهم بسيماهم قالوا ما أغنى عنكم جمعكم وما كنتم تستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 7, 'أهؤلاء الذين أقسمتم لا ينالهم الله برحمة ادخلوا الجنة لا خوف عليكم ولا أنتم تحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 7, 'ونادى أصحاب النار أصحاب الجنة أن أفيضوا علينا من الماء أو مما رزقكم الله قالوا إن الله حرمهما على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 7, 'الذين اتخذوا دينهم لهوا ولعبا وغرتهم الحياة الدنيا فاليوم ننساهم كما نسوا لقاء يومهم هذا وما كانوا بآياتنا يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 7, 'ولقد جئناهم بكتاب فصلناه على علم هدى ورحمة لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 7, 'هل ينظرون إلا تأويله يوم يأتي تأويله يقول الذين نسوه من قبل قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا لنا أو نرد فنعمل غير الذي كنا نعمل قد خسروا أنفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 7, 'إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يغشي الليل النهار يطلبه حثيثا والشمس والقمر والنجوم مسخرات بأمره ألا له الخلق والأمر تبارك الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 7, 'ادعوا ربكم تضرعا وخفية إنه لا يحب المعتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 7, 'ولا تفسدوا في الأرض بعد إصلاحها وادعوه خوفا وطمعا إن رحمت الله قريب من المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 7, 'وهو الذي يرسل الرياح بشرا بين يدي رحمته حتى إذا أقلت سحابا ثقالا سقناه لبلد ميت فأنزلنا به الماء فأخرجنا به من كل الثمرات كذلك نخرج الموتى لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 7, 'والبلد الطيب يخرج نباته بإذن ربه والذي خبث لا يخرج إلا نكدا كذلك نصرف الآيات لقوم يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 7, 'لقد أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غيره إني أخاف عليكم عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 7, 'قال الملأ من قومه إنا لنراك في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 7, 'قال يا قوم ليس بي ضلالة ولكني رسول من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 7, 'أبلغكم رسالات ربي وأنصح لكم وأعلم من الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 7, 'أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم ولتتقوا ولعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 7, 'فكذبوه فأنجيناه والذين معه في الفلك وأغرقنا الذين كذبوا بآياتنا إنهم كانوا قوما عمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 7, 'وإلى عاد أخاهم هودا قال يا قوم اعبدوا الله ما لكم من إله غيره أفلا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 7, 'قال الملأ الذين كفروا من قومه إنا لنراك في سفاهة وإنا لنظنك من الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 7, 'قال يا قوم ليس بي سفاهة ولكني رسول من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 7, 'أبلغكم رسالات ربي وأنا لكم ناصح أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 7, 'أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم واذكروا إذ جعلكم خلفاء من بعد قوم نوح وزادكم في الخلق بسطة فاذكروا آلاء الله لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 7, 'قالوا أجئتنا لنعبد الله وحده ونذر ما كان يعبد آباؤنا فأتنا بما تعدنا إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 7, 'قال قد وقع عليكم من ربكم رجس وغضب أتجادلونني في أسماء سميتموها أنتم وآباؤكم ما نزل الله بها من سلطان فانتظروا إني معكم من المنتظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 7, 'فأنجيناه والذين معه برحمة منا وقطعنا دابر الذين كذبوا بآياتنا وما كانوا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 7, 'وإلى ثمود أخاهم صالحا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بينة من ربكم هذه ناقة الله لكم آية فذروها تأكل في أرض الله ولا تمسوها بسوء فيأخذكم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 7, 'واذكروا إذ جعلكم خلفاء من بعد عاد وبوأكم في الأرض تتخذون من سهولها قصورا وتنحتون الجبال بيوتا فاذكروا آلاء الله ولا تعثوا في الأرض مفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 7, 'قال الملأ الذين استكبروا من قومه للذين استضعفوا لمن آمن منهم أتعلمون أن صالحا مرسل من ربه قالوا إنا بما أرسل به مؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 7, 'قال الذين استكبروا إنا بالذي آمنتم به كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 7, 'فعقروا الناقة وعتوا عن أمر ربهم وقالوا يا صالح ائتنا بما تعدنا إن كنت من المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 7, 'فأخذتهم الرجفة فأصبحوا في دارهم جاثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 7, 'فتولى عنهم وقال يا قوم لقد أبلغتكم رسالة ربي ونصحت لكم ولكن لا تحبون الناصحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 7, 'ولوطا إذ قال لقومه أتأتون الفاحشة ما سبقكم بها من أحد من العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 7, 'إنكم لتأتون الرجال شهوة من دون النساء بل أنتم قوم مسرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 7, 'وما كان جواب قومه إلا أن قالوا أخرجوهم من قريتكم إنهم أناس يتطهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 7, 'فأنجيناه وأهله إلا امرأته كانت من الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 7, 'وأمطرنا عليهم مطرا فانظر كيف كان عاقبة المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 7, 'وإلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بينة من ربكم فأوفوا الكيل والميزان ولا تبخسوا الناس أشياءهم ولا تفسدوا في الأرض بعد إصلاحها ذلكم خير لكم إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 7, 'ولا تقعدوا بكل صراط توعدون وتصدون عن سبيل الله من آمن به وتبغونها عوجا واذكروا إذ كنتم قليلا فكثركم وانظروا كيف كان عاقبة المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 7, 'وإن كان طائفة منكم آمنوا بالذي أرسلت به وطائفة لم يؤمنوا فاصبروا حتى يحكم الله بيننا وهو خير الحاكمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 7, 'قال الملأ الذين استكبروا من قومه لنخرجنك يا شعيب والذين آمنوا معك من قريتنا أو لتعودن في ملتنا قال أولو كنا كارهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 7, 'قد افترينا على الله كذبا إن عدنا في ملتكم بعد إذ نجانا الله منها وما يكون لنا أن نعود فيها إلا أن يشاء الله ربنا وسع ربنا كل شيء علما على الله توكلنا ربنا افتح بيننا وبين قومنا بالحق وأنت خير الفاتحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 7, 'وقال الملأ الذين كفروا من قومه لئن اتبعتم شعيبا إنكم إذا لخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 7, 'فأخذتهم الرجفة فأصبحوا في دارهم جاثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 7, 'الذين كذبوا شعيبا كأن لم يغنوا فيها الذين كذبوا شعيبا كانوا هم الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 7, 'فتولى عنهم وقال يا قوم لقد أبلغتكم رسالات ربي ونصحت لكم فكيف آسى على قوم كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 7, 'وما أرسلنا في قرية من نبي إلا أخذنا أهلها بالبأساء والضراء لعلهم يضرعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 7, 'ثم بدلنا مكان السيئة الحسنة حتى عفوا وقالوا قد مس آباءنا الضراء والسراء فأخذناهم بغتة وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 7, 'ولو أن أهل القرى آمنوا واتقوا لفتحنا عليهم بركات من السماء والأرض ولكن كذبوا فأخذناهم بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 7, 'أفأمن أهل القرى أن يأتيهم بأسنا بياتا وهم نائمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 7, 'أوأمن أهل القرى أن يأتيهم بأسنا ضحى وهم يلعبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 7, 'أفأمنوا مكر الله فلا يأمن مكر الله إلا القوم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 7, 'أولم يهد للذين يرثون الأرض من بعد أهلها أن لو نشاء أصبناهم بذنوبهم ونطبع على قلوبهم فهم لا يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 7, 'تلك القرى نقص عليك من أنبائها ولقد جاءتهم رسلهم بالبينات فما كانوا ليؤمنوا بما كذبوا من قبل كذلك يطبع الله على قلوب الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 7, 'وما وجدنا لأكثرهم من عهد وإن وجدنا أكثرهم لفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 7, 'ثم بعثنا من بعدهم موسى بآياتنا إلى فرعون وملئه فظلموا بها فانظر كيف كان عاقبة المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 7, 'وقال موسى يا فرعون إني رسول من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 7, 'حقيق على أن لا أقول على الله إلا الحق قد جئتكم ببينة من ربكم فأرسل معي بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 7, 'قال إن كنت جئت بآية فأت بها إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 7, 'فألقى عصاه فإذا هي ثعبان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 7, 'ونزع يده فإذا هي بيضاء للناظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 7, 'قال الملأ من قوم فرعون إن هذا لساحر عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 7, 'يريد أن يخرجكم من أرضكم فماذا تأمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 7, 'قالوا أرجه وأخاه وأرسل في المدائن حاشرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 7, 'يأتوك بكل ساحر عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 7, 'وجاء السحرة فرعون قالوا إن لنا لأجرا إن كنا نحن الغالبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 7, 'قال نعم وإنكم لمن المقربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 7, 'قالوا يا موسى إما أن تلقي وإما أن نكون نحن الملقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 7, 'قال ألقوا فلما ألقوا سحروا أعين الناس واسترهبوهم وجاءوا بسحر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 7, 'وأوحينا إلى موسى أن ألق عصاك فإذا هي تلقف ما يأفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 7, 'فوقع الحق وبطل ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 7, 'فغلبوا هنالك وانقلبوا صاغرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 7, 'وألقي السحرة ساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 7, 'قالوا آمنا برب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 7, 'رب موسى وهارون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 7, 'قال فرعون آمنتم به قبل أن آذن لكم إن هذا لمكر مكرتموه في المدينة لتخرجوا منها أهلها فسوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 7, 'لأقطعن أيديكم وأرجلكم من خلاف ثم لأصلبنكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 7, 'قالوا إنا إلى ربنا منقلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 7, 'وما تنقم منا إلا أن آمنا بآيات ربنا لما جاءتنا ربنا أفرغ علينا صبرا وتوفنا مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 7, 'وقال الملأ من قوم فرعون أتذر موسى وقومه ليفسدوا في الأرض ويذرك وآلهتك قال سنقتل أبناءهم ونستحيي نساءهم وإنا فوقهم قاهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 7, 'قال موسى لقومه استعينوا بالله واصبروا إن الأرض لله يورثها من يشاء من عباده والعاقبة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 7, 'قالوا أوذينا من قبل أن تأتينا ومن بعد ما جئتنا قال عسى ربكم أن يهلك عدوكم ويستخلفكم في الأرض فينظر كيف تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 7, 'ولقد أخذنا آل فرعون بالسنين ونقص من الثمرات لعلهم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 7, 'فإذا جاءتهم الحسنة قالوا لنا هذه وإن تصبهم سيئة يطيروا بموسى ومن معه ألا إنما طائرهم عند الله ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 7, 'وقالوا مهما تأتنا به من آية لتسحرنا بها فما نحن لك بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 7, 'فأرسلنا عليهم الطوفان والجراد والقمل والضفادع والدم آيات مفصلات فاستكبروا وكانوا قوما مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 7, 'ولما وقع عليهم الرجز قالوا يا موسى ادع لنا ربك بما عهد عندك لئن كشفت عنا الرجز لنؤمنن لك ولنرسلن معك بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 7, 'فلما كشفنا عنهم الرجز إلى أجل هم بالغوه إذا هم ينكثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 7, 'فانتقمنا منهم فأغرقناهم في اليم بأنهم كذبوا بآياتنا وكانوا عنها غافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 7, 'وأورثنا القوم الذين كانوا يستضعفون مشارق الأرض ومغاربها التي باركنا فيها وتمت كلمت ربك الحسنى على بني إسرائيل بما صبروا ودمرنا ما كان يصنع فرعون وقومه وما كانوا يعرشون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 7, 'وجاوزنا ببني إسرائيل البحر فأتوا على قوم يعكفون على أصنام لهم قالوا يا موسى اجعل لنا إلها كما لهم آلهة قال إنكم قوم تجهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 7, 'إن هؤلاء متبر ما هم فيه وباطل ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 7, 'قال أغير الله أبغيكم إلها وهو فضلكم على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 7, 'وإذ أنجيناكم من آل فرعون يسومونكم سوء العذاب يقتلون أبناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 7, 'وواعدنا موسى ثلاثين ليلة وأتممناها بعشر فتم ميقات ربه أربعين ليلة وقال موسى لأخيه هارون اخلفني في قومي وأصلح ولا تتبع سبيل المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 7, 'ولما جاء موسى لميقاتنا وكلمه ربه قال رب أرني أنظر إليك قال لن تراني ولكن انظر إلى الجبل فإن استقر مكانه فسوف تراني فلما تجلى ربه للجبل جعله دكا وخر موسى صعقا فلما أفاق قال سبحانك تبت إليك وأنا أول المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 7, 'قال يا موسى إني اصطفيتك على الناس برسالاتي وبكلامي فخذ ما آتيتك وكن من الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 7, 'وكتبنا له في الألواح من كل شيء موعظة وتفصيلا لكل شيء فخذها بقوة وأمر قومك يأخذوا بأحسنها سأريكم دار الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 7, 'سأصرف عن آياتي الذين يتكبرون في الأرض بغير الحق وإن يروا كل آية لا يؤمنوا بها وإن يروا سبيل الرشد لا يتخذوه سبيلا وإن يروا سبيل الغي يتخذوه سبيلا ذلك بأنهم كذبوا بآياتنا وكانوا عنها غافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 7, 'والذين كذبوا بآياتنا ولقاء الآخرة حبطت أعمالهم هل يجزون إلا ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 7, 'واتخذ قوم موسى من بعده من حليهم عجلا جسدا له خوار ألم يروا أنه لا يكلمهم ولا يهديهم سبيلا اتخذوه وكانوا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 7, 'ولما سقط في أيديهم ورأوا أنهم قد ضلوا قالوا لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 7, 'ولما رجع موسى إلى قومه غضبان أسفا قال بئسما خلفتموني من بعدي أعجلتم أمر ربكم وألقى الألواح وأخذ برأس أخيه يجره إليه قال ابن أم إن القوم استضعفوني وكادوا يقتلونني فلا تشمت بي الأعداء ولا تجعلني مع القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 7, 'قال رب اغفر لي ولأخي وأدخلنا في رحمتك وأنت أرحم الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 7, 'إن الذين اتخذوا العجل سينالهم غضب من ربهم وذلة في الحياة الدنيا وكذلك نجزي المفترين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 7, 'والذين عملوا السيئات ثم تابوا من بعدها وآمنوا إن ربك من بعدها لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 7, 'ولما سكت عن موسى الغضب أخذ الألواح وفي نسختها هدى ورحمة للذين هم لربهم يرهبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 7, 'واختار موسى قومه سبعين رجلا لميقاتنا فلما أخذتهم الرجفة قال رب لو شئت أهلكتهم من قبل وإياي أتهلكنا بما فعل السفهاء منا إن هي إلا فتنتك تضل بها من تشاء وتهدي من تشاء أنت ولينا فاغفر لنا وارحمنا وأنت خير الغافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 7, 'واكتب لنا في هذه الدنيا حسنة وفي الآخرة إنا هدنا إليك قال عذابي أصيب به من أشاء ورحمتي وسعت كل شيء فسأكتبها للذين يتقون ويؤتون الزكاة والذين هم بآياتنا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 7, 'الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات ويحرم عليهم الخبائث ويضع عنهم إصرهم والأغلال التي كانت عليهم فالذين آمنوا به وعزروه ونصروه واتبعوا النور الذي أنزل معه أولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 7, 'قل يا أيها الناس إني رسول الله إليكم جميعا الذي له ملك السماوات والأرض لا إله إلا هو يحيي ويميت فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته واتبعوه لعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 7, 'ومن قوم موسى أمة يهدون بالحق وبه يعدلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 7, 'وقطعناهم اثنتي عشرة أسباطا أمما وأوحينا إلى موسى إذ استسقاه قومه أن اضرب بعصاك الحجر فانبجست منه اثنتا عشرة عينا قد علم كل أناس مشربهم وظللنا عليهم الغمام وأنزلنا عليهم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 7, 'وإذ قيل لهم اسكنوا هذه القرية وكلوا منها حيث شئتم وقولوا حطة وادخلوا الباب سجدا نغفر لكم خطيئاتكم سنزيد المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 7, 'فبدل الذين ظلموا منهم قولا غير الذي قيل لهم فأرسلنا عليهم رجزا من السماء بما كانوا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 7, 'واسألهم عن القرية التي كانت حاضرة البحر إذ يعدون في السبت إذ تأتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تأتيهم كذلك نبلوهم بما كانوا يفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 7, 'وإذ قالت أمة منهم لم تعظون قوما الله مهلكهم أو معذبهم عذابا شديدا قالوا معذرة إلى ربكم ولعلهم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 7, 'فلما نسوا ما ذكروا به أنجينا الذين ينهون عن السوء وأخذنا الذين ظلموا بعذاب بئيس بما كانوا يفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 7, 'فلما عتوا عن ما نهوا عنه قلنا لهم كونوا قردة خاسئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 7, 'وإذ تأذن ربك ليبعثن عليهم إلى يوم القيامة من يسومهم سوء العذاب إن ربك لسريع العقاب وإنه لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 7, 'وقطعناهم في الأرض أمما منهم الصالحون ومنهم دون ذلك وبلوناهم بالحسنات والسيئات لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 7, 'فخلف من بعدهم خلف ورثوا الكتاب يأخذون عرض هذا الأدنى ويقولون سيغفر لنا وإن يأتهم عرض مثله يأخذوه ألم يؤخذ عليهم ميثاق الكتاب أن لا يقولوا على الله إلا الحق ودرسوا ما فيه والدار الآخرة خير للذين يتقون أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 7, 'والذين يمسكون بالكتاب وأقاموا الصلاة إنا لا نضيع أجر المصلحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 7, 'وإذ نتقنا الجبل فوقهم كأنه ظلة وظنوا أنه واقع بهم خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 7, 'وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم وأشهدهم على أنفسهم ألست بربكم قالوا بلى شهدنا أن تقولوا يوم القيامة إنا كنا عن هذا غافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 7, 'أو تقولوا إنما أشرك آباؤنا من قبل وكنا ذرية من بعدهم أفتهلكنا بما فعل المبطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 7, 'وكذلك نفصل الآيات ولعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 7, 'واتل عليهم نبأ الذي آتيناه آياتنا فانسلخ منها فأتبعه الشيطان فكان من الغاوين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 7, 'ولو شئنا لرفعناه بها ولكنه أخلد إلى الأرض واتبع هواه فمثله كمثل الكلب إن تحمل عليه يلهث أو تتركه يلهث ذلك مثل القوم الذين كذبوا بآياتنا فاقصص القصص لعلهم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(177, 1, 7, 'ساء مثلا القوم الذين كذبوا بآياتنا وأنفسهم كانوا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(178, 1, 7, 'من يهد الله فهو المهتدي ومن يضلل فأولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(179, 1, 7, 'ولقد ذرأنا لجهنم كثيرا من الجن والإنس لهم قلوب لا يفقهون بها ولهم أعين لا يبصرون بها ولهم آذان لا يسمعون بها أولئك كالأنعام بل هم أضل أولئك هم الغافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(180, 1, 7, 'ولله الأسماء الحسنى فادعوه بها وذروا الذين يلحدون في أسمائه سيجزون ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(181, 1, 7, 'وممن خلقنا أمة يهدون بالحق وبه يعدلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(182, 1, 7, 'والذين كذبوا بآياتنا سنستدرجهم من حيث لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(183, 1, 7, 'وأملي لهم إن كيدي متين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(184, 1, 7, 'أولم يتفكروا ما بصاحبهم من جنة إن هو إلا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(185, 1, 7, 'أولم ينظروا في ملكوت السماوات والأرض وما خلق الله من شيء وأن عسى أن يكون قد اقترب أجلهم فبأي حديث بعده يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(186, 1, 7, 'من يضلل الله فلا هادي له ويذرهم في طغيانهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(187, 1, 7, 'يسألونك عن الساعة أيان مرساها قل إنما علمها عند ربي لا يجليها لوقتها إلا هو ثقلت في السماوات والأرض لا تأتيكم إلا بغتة يسألونك كأنك حفي عنها قل إنما علمها عند الله ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(188, 1, 7, 'قل لا أملك لنفسي نفعا ولا ضرا إلا ما شاء الله ولو كنت أعلم الغيب لاستكثرت من الخير وما مسني السوء إن أنا إلا نذير وبشير لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(189, 1, 7, 'هو الذي خلقكم من نفس واحدة وجعل منها زوجها ليسكن إليها فلما تغشاها حملت حملا خفيفا فمرت به فلما أثقلت دعوا الله ربهما لئن آتيتنا صالحا لنكونن من الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(190, 1, 7, 'فلما آتاهما صالحا جعلا له شركاء فيما آتاهما فتعالى الله عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(191, 1, 7, 'أيشركون ما لا يخلق شيئا وهم يخلقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(192, 1, 7, 'ولا يستطيعون لهم نصرا ولا أنفسهم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(193, 1, 7, 'وإن تدعوهم إلى الهدى لا يتبعوكم سواء عليكم أدعوتموهم أم أنتم صامتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(194, 1, 7, 'إن الذين تدعون من دون الله عباد أمثالكم فادعوهم فليستجيبوا لكم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(195, 1, 7, 'ألهم أرجل يمشون بها أم لهم أيد يبطشون بها أم لهم أعين يبصرون بها أم لهم آذان يسمعون بها قل ادعوا شركاءكم ثم كيدون فلا تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(196, 1, 7, 'إن وليي الله الذي نزل الكتاب وهو يتولى الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(197, 1, 7, 'والذين تدعون من دونه لا يستطيعون نصركم ولا أنفسهم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(198, 1, 7, 'وإن تدعوهم إلى الهدى لا يسمعوا وتراهم ينظرون إليك وهم لا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(199, 1, 7, 'خذ العفو وأمر بالعرف وأعرض عن الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(200, 1, 7, 'وإما ينزغنك من الشيطان نزغ فاستعذ بالله إنه سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(201, 1, 7, 'إن الذين اتقوا إذا مسهم طائف من الشيطان تذكروا فإذا هم مبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(202, 1, 7, 'وإخوانهم يمدونهم في الغي ثم لا يقصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(203, 1, 7, 'وإذا لم تأتهم بآية قالوا لولا اجتبيتها قل إنما أتبع ما يوحى إلي من ربي هذا بصائر من ربكم وهدى ورحمة لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(204, 1, 7, 'وإذا قرئ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(205, 1, 7, 'واذكر ربك في نفسك تضرعا وخيفة ودون الجهر من القول بالغدو والآصال ولا تكن من الغافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(206, 1, 7, 'إن الذين عند ربك لا يستكبرون عن عبادته ويسبحونه وله يسجدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 8, 'بسم الله الرحمن الرحيم يسألونك عن الأنفال قل الأنفال لله والرسول فاتقوا الله وأصلحوا ذات بينكم وأطيعوا الله ورسوله إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 8, 'إنما المؤمنون الذين إذا ذكر الله وجلت قلوبهم وإذا تليت عليهم آياته زادتهم إيمانا وعلى ربهم يتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 8, 'الذين يقيمون الصلاة ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 8, 'أولئك هم المؤمنون حقا لهم درجات عند ربهم ومغفرة ورزق كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 8, 'كما أخرجك ربك من بيتك بالحق وإن فريقا من المؤمنين لكارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 8, 'يجادلونك في الحق بعدما تبين كأنما يساقون إلى الموت وهم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 8, 'وإذ يعدكم الله إحدى الطائفتين أنها لكم وتودون أن غير ذات الشوكة تكون لكم ويريد الله أن يحق الحق بكلماته ويقطع دابر الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 8, 'ليحق الحق ويبطل الباطل ولو كره المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 8, 'إذ تستغيثون ربكم فاستجاب لكم أني ممدكم بألف من الملائكة مردفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 8, 'وما جعله الله إلا بشرى ولتطمئن به قلوبكم وما النصر إلا من عند الله إن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 8, 'إذ يغشيكم النعاس أمنة منه وينزل عليكم من السماء ماء ليطهركم به ويذهب عنكم رجز الشيطان وليربط على قلوبكم ويثبت به الأقدام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 8, 'إذ يوحي ربك إلى الملائكة أني معكم فثبتوا الذين آمنوا سألقي في قلوب الذين كفروا الرعب فاضربوا فوق الأعناق واضربوا منهم كل بنان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 8, 'ذلك بأنهم شاقوا الله ورسوله ومن يشاقق الله ورسوله فإن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 8, 'ذلكم فذوقوه وأن للكافرين عذاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 8, 'يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا فلا تولوهم الأدبار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 8, 'ومن يولهم يومئذ دبره إلا متحرفا لقتال أو متحيزا إلى فئة فقد باء بغضب من الله ومأواه جهنم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 8, 'فلم تقتلوهم ولكن الله قتلهم وما رميت إذ رميت ولكن الله رمى وليبلي المؤمنين منه بلاء حسنا إن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 8, 'ذلكم وأن الله موهن كيد الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 8, 'إن تستفتحوا فقد جاءكم الفتح وإن تنتهوا فهو خير لكم وإن تعودوا نعد ولن تغني عنكم فئتكم شيئا ولو كثرت وأن الله مع المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 8, 'يا أيها الذين آمنوا أطيعوا الله ورسوله ولا تولوا عنه وأنتم تسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 8, 'ولا تكونوا كالذين قالوا سمعنا وهم لا يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 8, 'إن شر الدواب عند الله الصم البكم الذين لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 8, 'ولو علم الله فيهم خيرا لأسمعهم ولو أسمعهم لتولوا وهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 8, 'يا أيها الذين آمنوا استجيبوا لله وللرسول إذا دعاكم لما يحييكم واعلموا أن الله يحول بين المرء وقلبه وأنه إليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 8, 'واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة واعلموا أن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 8, 'واذكروا إذ أنتم قليل مستضعفون في الأرض تخافون أن يتخطفكم الناس فآواكم وأيدكم بنصره ورزقكم من الطيبات لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 8, 'يا أيها الذين آمنوا لا تخونوا الله والرسول وتخونوا أماناتكم وأنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 8, 'واعلموا أنما أموالكم وأولادكم فتنة وأن الله عنده أجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 8, 'يا أيها الذين آمنوا إن تتقوا الله يجعل لكم فرقانا ويكفر عنكم سيئاتكم ويغفر لكم والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 8, 'وإذ يمكر بك الذين كفروا ليثبتوك أو يقتلوك أو يخرجوك ويمكرون ويمكر الله والله خير الماكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 8, 'وإذا تتلى عليهم آياتنا قالوا قد سمعنا لو نشاء لقلنا مثل هذا إن هذا إلا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 8, 'وإذ قالوا اللهم إن كان هذا هو الحق من عندك فأمطر علينا حجارة من السماء أو ائتنا بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 8, 'وما كان الله ليعذبهم وأنت فيهم وما كان الله معذبهم وهم يستغفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 8, 'وما لهم ألا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا أولياءه إن أولياؤه إلا المتقون ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 8, 'وما كان صلاتهم عند البيت إلا مكاء وتصدية فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 8, 'إن الذين كفروا ينفقون أموالهم ليصدوا عن سبيل الله فسينفقونها ثم تكون عليهم حسرة ثم يغلبون والذين كفروا إلى جهنم يحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 8, 'ليميز الله الخبيث من الطيب ويجعل الخبيث بعضه على بعض فيركمه جميعا فيجعله في جهنم أولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 8, 'قل للذين كفروا إن ينتهوا يغفر لهم ما قد سلف وإن يعودوا فقد مضت سنت الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 8, 'وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله فإن انتهوا فإن الله بما يعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 8, 'وإن تولوا فاعلموا أن الله مولاكم نعم المولى ونعم النصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 8, 'واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل إن كنتم آمنتم بالله وما أنزلنا على عبدنا يوم الفرقان يوم التقى الجمعان والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 8, 'إذ أنتم بالعدوة الدنيا وهم بالعدوة القصوى والركب أسفل منكم ولو تواعدتم لاختلفتم في الميعاد ولكن ليقضي الله أمرا كان مفعولا ليهلك من هلك عن بينة ويحيى من حي عن بينة وإن الله لسميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 8, 'إذ يريكهم الله في منامك قليلا ولو أراكهم كثيرا لفشلتم ولتنازعتم في الأمر ولكن الله سلم إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 8, 'وإذ يريكموهم إذ التقيتم في أعينكم قليلا ويقللكم في أعينهم ليقضي الله أمرا كان مفعولا وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 8, 'يا أيها الذين آمنوا إذا لقيتم فئة فاثبتوا واذكروا الله كثيرا لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 8, 'وأطيعوا الله ورسوله ولا تنازعوا فتفشلوا وتذهب ريحكم واصبروا إن الله مع الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 8, 'ولا تكونوا كالذين خرجوا من ديارهم بطرا ورئاء الناس ويصدون عن سبيل الله والله بما يعملون محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 8, 'وإذ زين لهم الشيطان أعمالهم وقال لا غالب لكم اليوم من الناس وإني جار لكم فلما تراءت الفئتان نكص على عقبيه وقال إني بريء منكم إني أرى ما لا ترون إني أخاف الله والله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 8, 'إذ يقول المنافقون والذين في قلوبهم مرض غر هؤلاء دينهم ومن يتوكل على الله فإن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 8, 'ولو ترى إذ يتوفى الذين كفروا الملائكة يضربون وجوههم وأدبارهم وذوقوا عذاب الحريق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 8, 'ذلك بما قدمت أيديكم وأن الله ليس بظلام للعبيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 8, 'كدأب آل فرعون والذين من قبلهم كفروا بآيات الله فأخذهم الله بذنوبهم إن الله قوي شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 8, 'ذلك بأن الله لم يك مغيرا نعمة أنعمها على قوم حتى يغيروا ما بأنفسهم وأن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 8, 'كدأب آل فرعون والذين من قبلهم كذبوا بآيات ربهم فأهلكناهم بذنوبهم وأغرقنا آل فرعون وكل كانوا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 8, 'إن شر الدواب عند الله الذين كفروا فهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 8, 'الذين عاهدت منهم ثم ينقضون عهدهم في كل مرة وهم لا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 8, 'فإما تثقفنهم في الحرب فشرد بهم من خلفهم لعلهم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 8, 'وإما تخافن من قوم خيانة فانبذ إليهم على سواء إن الله لا يحب الخائنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 8, 'ولا يحسبن الذين كفروا سبقوا إنهم لا يعجزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 8, 'وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم وآخرين من دونهم لا تعلمونهم الله يعلمهم وما تنفقوا من شيء في سبيل الله يوف إليكم وأنتم لا تظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 8, 'وإن جنحوا للسلم فاجنح لها وتوكل على الله إنه هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 8, 'وإن يريدوا أن يخدعوك فإن حسبك الله هو الذي أيدك بنصره وبالمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 8, 'وألف بين قلوبهم لو أنفقت ما في الأرض جميعا ما ألفت بين قلوبهم ولكن الله ألف بينهم إنه عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 8, 'يا أيها النبي حسبك الله ومن اتبعك من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 8, 'يا أيها النبي حرض المؤمنين على القتال إن يكن منكم عشرون صابرون يغلبوا مائتين وإن يكن منكم مائة يغلبوا ألفا من الذين كفروا بأنهم قوم لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 8, 'الآن خفف الله عنكم وعلم أن فيكم ضعفا فإن يكن منكم مائة صابرة يغلبوا مائتين وإن يكن منكم ألف يغلبوا ألفين بإذن الله والله مع الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 8, 'ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض تريدون عرض الدنيا والله يريد الآخرة والله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 8, 'لولا كتاب من الله سبق لمسكم فيما أخذتم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 8, 'فكلوا مما غنمتم حلالا طيبا واتقوا الله إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 8, 'يا أيها النبي قل لمن في أيديكم من الأسرى إن يعلم الله في قلوبكم خيرا يؤتكم خيرا مما أخذ منكم ويغفر لكم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 8, 'وإن يريدوا خيانتك فقد خانوا الله من قبل فأمكن منهم والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 8, 'إن الذين آمنوا وهاجروا وجاهدوا بأموالهم وأنفسهم في سبيل الله والذين آووا ونصروا أولئك بعضهم أولياء بعض والذين آمنوا ولم يهاجروا ما لكم من ولايتهم من شيء حتى يهاجروا وإن استنصروكم في الدين فعليكم النصر إلا على قوم بينكم وبينهم ميثاق والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 8, 'والذين كفروا بعضهم أولياء بعض إلا تفعلوه تكن فتنة في الأرض وفساد كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 8, 'والذين آمنوا وهاجروا وجاهدوا في سبيل الله والذين آووا ونصروا أولئك هم المؤمنون حقا لهم مغفرة ورزق كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 8, 'والذين آمنوا من بعد وهاجروا وجاهدوا معكم فأولئك منكم وأولو الأرحام بعضهم أولى ببعض في كتاب الله إن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 9, 'براءة من الله ورسوله إلى الذين عاهدتم من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 9, 'فسيحوا في الأرض أربعة أشهر واعلموا أنكم غير معجزي الله وأن الله مخزي الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 9, 'وأذان من الله ورسوله إلى الناس يوم الحج الأكبر أن الله بريء من المشركين ورسوله فإن تبتم فهو خير لكم وإن توليتم فاعلموا أنكم غير معجزي الله وبشر الذين كفروا بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 9, 'إلا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا ولم يظاهروا عليكم أحدا فأتموا إليهم عهدهم إلى مدتهم إن الله يحب المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 9, 'فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد فإن تابوا وأقاموا الصلاة وآتوا الزكاة فخلوا سبيلهم إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 9, 'وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه ذلك بأنهم قوم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 9, 'كيف يكون للمشركين عهد عند الله وعند رسوله إلا الذين عاهدتم عند المسجد الحرام فما استقاموا لكم فاستقيموا لهم إن الله يحب المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 9, 'كيف وإن يظهروا عليكم لا يرقبوا فيكم إلا ولا ذمة يرضونكم بأفواههم وتأبى قلوبهم وأكثرهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 9, 'اشتروا بآيات الله ثمنا قليلا فصدوا عن سبيله إنهم ساء ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 9, 'لا يرقبون في مؤمن إلا ولا ذمة وأولئك هم المعتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 9, 'فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم في الدين ونفصل الآيات لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 9, 'وإن نكثوا أيمانهم من بعد عهدهم وطعنوا في دينكم فقاتلوا أئمة الكفر إنهم لا أيمان لهم لعلهم ينتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 9, 'ألا تقاتلون قوما نكثوا أيمانهم وهموا بإخراج الرسول وهم بدءوكم أول مرة أتخشونهم فالله أحق أن تخشوه إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 9, 'قاتلوهم يعذبهم الله بأيديكم ويخزهم وينصركم عليهم ويشف صدور قوم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 9, 'ويذهب غيظ قلوبهم ويتوب الله على من يشاء والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 9, 'أم حسبتم أن تتركوا ولما يعلم الله الذين جاهدوا منكم ولم يتخذوا من دون الله ولا رسوله ولا المؤمنين وليجة والله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 9, 'ما كان للمشركين أن يعمروا مساجد الله شاهدين على أنفسهم بالكفر أولئك حبطت أعمالهم وفي النار هم خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 9, 'إنما يعمر مساجد الله من آمن بالله واليوم الآخر وأقام الصلاة وآتى الزكاة ولم يخش إلا الله فعسى أولئك أن يكونوا من المهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 9, 'أجعلتم سقاية الحاج وعمارة المسجد الحرام كمن آمن بالله واليوم الآخر وجاهد في سبيل الله لا يستوون عند الله والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 9, 'الذين آمنوا وهاجروا وجاهدوا في سبيل الله بأموالهم وأنفسهم أعظم درجة عند الله وأولئك هم الفائزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 9, 'يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها نعيم مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 9, 'خالدين فيها أبدا إن الله عنده أجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 9, 'يا أيها الذين آمنوا لا تتخذوا آباءكم وإخوانكم أولياء إن استحبوا الكفر على الإيمان ومن يتولهم منكم فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 9, 'قل إن كان آباؤكم وأبناؤكم وإخوانكم وأزواجكم وعشيرتكم وأموال اقترفتموها وتجارة تخشون كسادها ومساكن ترضونها أحب إليكم من الله ورسوله وجهاد في سبيله فتربصوا حتى يأتي الله بأمره والله لا يهدي القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 9, 'لقد نصركم الله في مواطن كثيرة ويوم حنين إذ أعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت ثم وليتم مدبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 9, 'ثم أنزل الله سكينته على رسوله وعلى المؤمنين وأنزل جنودا لم تروها وعذب الذين كفروا وذلك جزاء الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 9, 'ثم يتوب الله من بعد ذلك على من يشاء والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 9, 'يا أيها الذين آمنوا إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا وإن خفتم عيلة فسوف يغنيكم الله من فضله إن شاء إن الله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 9, 'قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب حتى يعطوا الجزية عن يد وهم صاغرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 9, 'وقالت اليهود عزير ابن الله وقالت النصارى المسيح ابن الله ذلك قولهم بأفواههم يضاهئون قول الذين كفروا من قبل قاتلهم الله أنى يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 9, 'اتخذوا أحبارهم ورهبانهم أربابا من دون الله والمسيح ابن مريم وما أمروا إلا ليعبدوا إلها واحدا لا إله إلا هو سبحانه عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 9, 'يريدون أن يطفئوا نور الله بأفواههم ويأبى الله إلا أن يتم نوره ولو كره الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 9, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 9, 'يا أيها الذين آمنوا إن كثيرا من الأحبار والرهبان ليأكلون أموال الناس بالباطل ويصدون عن سبيل الله والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 9, 'يوم يحمى عليها في نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم هذا ما كنزتم لأنفسكم فذوقوا ما كنتم تكنزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 9, 'إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ذلك الدين القيم فلا تظلموا فيهن أنفسكم وقاتلوا المشركين كافة كما يقاتلونكم كافة واعلموا أن الله مع المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 9, 'إنما النسيء زيادة في الكفر يضل به الذين كفروا يحلونه عاما ويحرمونه عاما ليواطئوا عدة ما حرم الله فيحلوا ما حرم الله زين لهم سوء أعمالهم والله لا يهدي القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 9, 'يا أيها الذين آمنوا ما لكم إذا قيل لكم انفروا في سبيل الله اثاقلتم إلى الأرض أرضيتم بالحياة الدنيا من الآخرة فما متاع الحياة الدنيا في الآخرة إلا قليل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 9, 'إلا تنفروا يعذبكم عذابا أليما ويستبدل قوما غيركم ولا تضروه شيئا والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 9, 'إلا تنصروه فقد نصره الله إذ أخرجه الذين كفروا ثاني اثنين إذ هما في الغار إذ يقول لصاحبه لا تحزن إن الله معنا فأنزل الله سكينته عليه وأيده بجنود لم تروها وجعل كلمة الذين كفروا السفلى وكلمة الله هي العليا والله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 9, 'انفروا خفافا وثقالا وجاهدوا بأموالكم وأنفسكم في سبيل الله ذلكم خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 9, 'لو كان عرضا قريبا وسفرا قاصدا لاتبعوك ولكن بعدت عليهم الشقة وسيحلفون بالله لو استطعنا لخرجنا معكم يهلكون أنفسهم والله يعلم إنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 9, 'عفا الله عنك لم أذنت لهم حتى يتبين لك الذين صدقوا وتعلم الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 9, 'لا يستأذنك الذين يؤمنون بالله واليوم الآخر أن يجاهدوا بأموالهم وأنفسهم والله عليم بالمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 9, 'إنما يستأذنك الذين لا يؤمنون بالله واليوم الآخر وارتابت قلوبهم فهم في ريبهم يترددون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 9, 'ولو أرادوا الخروج لأعدوا له عدة ولكن كره الله انبعاثهم فثبطهم وقيل اقعدوا مع القاعدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 9, 'لو خرجوا فيكم ما زادوكم إلا خبالا ولأوضعوا خلالكم يبغونكم الفتنة وفيكم سماعون لهم والله عليم بالظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 9, 'لقد ابتغوا الفتنة من قبل وقلبوا لك الأمور حتى جاء الحق وظهر أمر الله وهم كارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 9, 'ومنهم من يقول ائذن لي ولا تفتني ألا في الفتنة سقطوا وإن جهنم لمحيطة بالكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 9, 'إن تصبك حسنة تسؤهم وإن تصبك مصيبة يقولوا قد أخذنا أمرنا من قبل ويتولوا وهم فرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 9, 'قل لن يصيبنا إلا ما كتب الله لنا هو مولانا وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 9, 'قل هل تربصون بنا إلا إحدى الحسنيين ونحن نتربص بكم أن يصيبكم الله بعذاب من عنده أو بأيدينا فتربصوا إنا معكم متربصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 9, 'قل أنفقوا طوعا أو كرها لن يتقبل منكم إنكم كنتم قوما فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 9, 'وما منعهم أن تقبل منهم نفقاتهم إلا أنهم كفروا بالله وبرسوله ولا يأتون الصلاة إلا وهم كسالى ولا ينفقون إلا وهم كارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 9, 'فلا تعجبك أموالهم ولا أولادهم إنما يريد الله ليعذبهم بها في الحياة الدنيا وتزهق أنفسهم وهم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 9, 'ويحلفون بالله إنهم لمنكم وما هم منكم ولكنهم قوم يفرقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 9, 'لو يجدون ملجأ أو مغارات أو مدخلا لولوا إليه وهم يجمحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 9, 'ومنهم من يلمزك في الصدقات فإن أعطوا منها رضوا وإن لم يعطوا منها إذا هم يسخطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 9, 'ولو أنهم رضوا ما آتاهم الله ورسوله وقالوا حسبنا الله سيؤتينا الله من فضله ورسوله إنا إلى الله راغبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 9, 'إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل فريضة من الله والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 9, 'ومنهم الذين يؤذون النبي ويقولون هو أذن قل أذن خير لكم يؤمن بالله ويؤمن للمؤمنين ورحمة للذين آمنوا منكم والذين يؤذون رسول الله لهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 9, 'يحلفون بالله لكم ليرضوكم والله ورسوله أحق أن يرضوه إن كانوا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 9, 'ألم يعلموا أنه من يحادد الله ورسوله فأن له نار جهنم خالدا فيها ذلك الخزي العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 9, 'يحذر المنافقون أن تنزل عليهم سورة تنبئهم بما في قلوبهم قل استهزئوا إن الله مخرج ما تحذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 9, 'ولئن سألتهم ليقولن إنما كنا نخوض ونلعب قل أبالله وآياته ورسوله كنتم تستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 9, 'لا تعتذروا قد كفرتم بعد إيمانكم إن نعف عن طائفة منكم نعذب طائفة بأنهم كانوا مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 9, 'المنافقون والمنافقات بعضهم من بعض يأمرون بالمنكر وينهون عن المعروف ويقبضون أيديهم نسوا الله فنسيهم إن المنافقين هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 9, 'وعد الله المنافقين والمنافقات والكفار نار جهنم خالدين فيها هي حسبهم ولعنهم الله ولهم عذاب مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 9, 'كالذين من قبلكم كانوا أشد منكم قوة وأكثر أموالا وأولادا فاستمتعوا بخلاقهم فاستمتعتم بخلاقكم كما استمتع الذين من قبلكم بخلاقهم وخضتم كالذي خاضوا أولئك حبطت أعمالهم في الدنيا والآخرة وأولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 9, 'ألم يأتهم نبأ الذين من قبلهم قوم نوح وعاد وثمود وقوم إبراهيم وأصحاب مدين والمؤتفكات أتتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 9, 'والمؤمنون والمؤمنات بعضهم أولياء بعض يأمرون بالمعروف وينهون عن المنكر ويقيمون الصلاة ويؤتون الزكاة ويطيعون الله ورسوله أولئك سيرحمهم الله إن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 9, 'وعد الله المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ومساكن طيبة في جنات عدن ورضوان من الله أكبر ذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 9, 'يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ومأواهم جهنم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 9, 'يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم وهموا بما لم ينالوا وما نقموا إلا أن أغناهم الله ورسوله من فضله فإن يتوبوا يك خيرا لهم وإن يتولوا يعذبهم الله عذابا أليما في الدنيا والآخرة وما لهم في الأرض من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 9, 'ومنهم من عاهد الله لئن آتانا من فضله لنصدقن ولنكونن من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 9, 'فلما آتاهم من فضله بخلوا به وتولوا وهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 9, 'فأعقبهم نفاقا في قلوبهم إلى يوم يلقونه بما أخلفوا الله ما وعدوه وبما كانوا يكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 9, 'ألم يعلموا أن الله يعلم سرهم ونجواهم وأن الله علام الغيوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 9, 'الذين يلمزون المطوعين من المؤمنين في الصدقات والذين لا يجدون إلا جهدهم فيسخرون منهم سخر الله منهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 9, 'استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم ذلك بأنهم كفروا بالله ورسوله والله لا يهدي القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 9, 'فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله وقالوا لا تنفروا في الحر قل نار جهنم أشد حرا لو كانوا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 9, 'فليضحكوا قليلا وليبكوا كثيرا جزاء بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 9, 'فإن رجعك الله إلى طائفة منهم فاستأذنوك للخروج فقل لن تخرجوا معي أبدا ولن تقاتلوا معي عدوا إنكم رضيتم بالقعود أول مرة فاقعدوا مع الخالفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 9, 'ولا تصل على أحد منهم مات أبدا ولا تقم على قبره إنهم كفروا بالله ورسوله وماتوا وهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 9, 'ولا تعجبك أموالهم وأولادهم إنما يريد الله أن يعذبهم بها في الدنيا وتزهق أنفسهم وهم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 9, 'وإذا أنزلت سورة أن آمنوا بالله وجاهدوا مع رسوله استأذنك أولو الطول منهم وقالوا ذرنا نكن مع القاعدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 9, 'رضوا بأن يكونوا مع الخوالف وطبع على قلوبهم فهم لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 9, 'لكن الرسول والذين آمنوا معه جاهدوا بأموالهم وأنفسهم وأولئك لهم الخيرات وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 9, 'أعد الله لهم جنات تجري من تحتها الأنهار خالدين فيها ذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 9, 'وجاء المعذرون من الأعراب ليؤذن لهم وقعد الذين كذبوا الله ورسوله سيصيب الذين كفروا منهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 9, 'ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج إذا نصحوا لله ورسوله ما على المحسنين من سبيل والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 9, 'ولا على الذين إذا ما أتوك لتحملهم قلت لا أجد ما أحملكم عليه تولوا وأعينهم تفيض من الدمع حزنا ألا يجدوا ما ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 9, 'إنما السبيل على الذين يستأذنونك وهم أغنياء رضوا بأن يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 9, 'يعتذرون إليكم إذا رجعتم إليهم قل لا تعتذروا لن نؤمن لكم قد نبأنا الله من أخباركم وسيرى الله عملكم ورسوله ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 9, 'سيحلفون بالله لكم إذا انقلبتم إليهم لتعرضوا عنهم فأعرضوا عنهم إنهم رجس ومأواهم جهنم جزاء بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 9, 'يحلفون لكم لترضوا عنهم فإن ترضوا عنهم فإن الله لا يرضى عن القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 9, 'الأعراب أشد كفرا ونفاقا وأجدر ألا يعلموا حدود ما أنزل الله على رسوله والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 9, 'ومن الأعراب من يتخذ ما ينفق مغرما ويتربص بكم الدوائر عليهم دائرة السوء والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 9, 'ومن الأعراب من يؤمن بالله واليوم الآخر ويتخذ ما ينفق قربات عند الله وصلوات الرسول ألا إنها قربة لهم سيدخلهم الله في رحمته إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 9, 'والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وأعد لهم جنات تجري تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 9, 'وممن حولكم من الأعراب منافقون ومن أهل المدينة مردوا على النفاق لا تعلمهم نحن نعلمهم سنعذبهم مرتين ثم يردون إلى عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 9, 'وآخرون اعترفوا بذنوبهم خلطوا عملا صالحا وآخر سيئا عسى الله أن يتوب عليهم إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 9, 'خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم إن صلاتك سكن لهم والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 9, 'ألم يعلموا أن الله هو يقبل التوبة عن عباده ويأخذ الصدقات وأن الله هو التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 9, 'وقل اعملوا فسيرى الله عملكم ورسوله والمؤمنون وستردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 9, 'وآخرون مرجون لأمر الله إما يعذبهم وإما يتوب عليهم والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 9, 'والذين اتخذوا مسجدا ضرارا وكفرا وتفريقا بين المؤمنين وإرصادا لمن حارب الله ورسوله من قبل وليحلفن إن أردنا إلا الحسنى والله يشهد إنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 9, 'لا تقم فيه أبدا لمسجد أسس على التقوى من أول يوم أحق أن تقوم فيه فيه رجال يحبون أن يتطهروا والله يحب المطهرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 9, 'أفمن أسس بنيانه على تقوى من الله ورضوان خير أم من أسس بنيانه على شفا جرف هار فانهار به في نار جهنم والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 9, 'لا يزال بنيانهم الذي بنوا ريبة في قلوبهم إلا أن تقطع قلوبهم والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 9, 'إن الله اشترى من المؤمنين أنفسهم وأموالهم بأن لهم الجنة يقاتلون في سبيل الله فيقتلون ويقتلون وعدا عليه حقا في التوراة والإنجيل والقرآن ومن أوفى بعهده من الله فاستبشروا ببيعكم الذي بايعتم به وذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 9, 'التائبون العابدون الحامدون السائحون الراكعون الساجدون الآمرون بالمعروف والناهون عن المنكر والحافظون لحدود الله وبشر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 9, 'ما كان للنبي والذين آمنوا أن يستغفروا للمشركين ولو كانوا أولي قربى من بعد ما تبين لهم أنهم أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 9, 'وما كان استغفار إبراهيم لأبيه إلا عن موعدة وعدها إياه فلما تبين له أنه عدو لله تبرأ منه إن إبراهيم لأواه حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 9, 'وما كان الله ليضل قوما بعد إذ هداهم حتى يبين لهم ما يتقون إن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 9, 'إن الله له ملك السماوات والأرض يحيي ويميت وما لكم من دون الله من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 9, 'لقد تاب الله على النبي والمهاجرين والأنصار الذين اتبعوه في ساعة العسرة من بعد ما كاد يزيغ قلوب فريق منهم ثم تاب عليهم إنه بهم رءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 9, 'وعلى الثلاثة الذين خلفوا حتى إذا ضاقت عليهم الأرض بما رحبت وضاقت عليهم أنفسهم وظنوا أن لا ملجأ من الله إلا إليه ثم تاب عليهم ليتوبوا إن الله هو التواب الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 9, 'يا أيها الذين آمنوا اتقوا الله وكونوا مع الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 9, 'ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله ولا يرغبوا بأنفسهم عن نفسه ذلك بأنهم لا يصيبهم ظمأ ولا نصب ولا مخمصة في سبيل الله ولا يطئون موطئا يغيظ الكفار ولا ينالون من عدو نيلا إلا كتب لهم به عمل صالح إن الله لا يضيع أجر المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 9, 'ولا ينفقون نفقة صغيرة ولا كبيرة ولا يقطعون واديا إلا كتب لهم ليجزيهم الله أحسن ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 9, 'وما كان المؤمنون لينفروا كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 9, 'يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار وليجدوا فيكم غلظة واعلموا أن الله مع المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 9, 'وإذا ما أنزلت سورة فمنهم من يقول أيكم زادته هذه إيمانا فأما الذين آمنوا فزادتهم إيمانا وهم يستبشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 9, 'وأما الذين في قلوبهم مرض فزادتهم رجسا إلى رجسهم وماتوا وهم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 9, 'أولا يرون أنهم يفتنون في كل عام مرة أو مرتين ثم لا يتوبون ولا هم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 9, 'وإذا ما أنزلت سورة نظر بعضهم إلى بعض هل يراكم من أحد ثم انصرفوا صرف الله قلوبهم بأنهم قوم لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 9, 'لقد جاءكم رسول من أنفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 9, 'فإن تولوا فقل حسبي الله لا إله إلا هو عليه توكلت وهو رب العرش العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 10, 'بسم الله الرحمن الرحيم الر تلك آيات الكتاب الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 10, 'أكان للناس عجبا أن أوحينا إلى رجل منهم أن أنذر الناس وبشر الذين آمنوا أن لهم قدم صدق عند ربهم قال الكافرون إن هذا لساحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 10, 'إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يدبر الأمر ما من شفيع إلا من بعد إذنه ذلكم الله ربكم فاعبدوه أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 10, 'إليه مرجعكم جميعا وعد الله حقا إنه يبدأ الخلق ثم يعيده ليجزي الذين آمنوا وعملوا الصالحات بالقسط والذين كفروا لهم شراب من حميم وعذاب أليم بما كانوا يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 10, 'هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب ما خلق الله ذلك إلا بالحق يفصل الآيات لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 10, 'إن في اختلاف الليل والنهار وما خلق الله في السماوات والأرض لآيات لقوم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 10, 'إن الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمأنوا بها والذين هم عن آياتنا غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 10, 'أولئك مأواهم النار بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 10, 'إن الذين آمنوا وعملوا الصالحات يهديهم ربهم بإيمانهم تجري من تحتهم الأنهار في جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 10, 'دعواهم فيها سبحانك اللهم وتحيتهم فيها سلام وآخر دعواهم أن الحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 10, 'ولو يعجل الله للناس الشر استعجالهم بالخير لقضي إليهم أجلهم فنذر الذين لا يرجون لقاءنا في طغيانهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 10, 'وإذا مس الإنسان الضر دعانا لجنبه أو قاعدا أو قائما فلما كشفنا عنه ضره مر كأن لم يدعنا إلى ضر مسه كذلك زين للمسرفين ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 10, 'ولقد أهلكنا القرون من قبلكم لما ظلموا وجاءتهم رسلهم بالبينات وما كانوا ليؤمنوا كذلك نجزي القوم المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 10, 'ثم جعلناكم خلائف في الأرض من بعدهم لننظر كيف تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 10, 'وإذا تتلى عليهم آياتنا بينات قال الذين لا يرجون لقاءنا ائت بقرآن غير هذا أو بدله قل ما يكون لي أن أبدله من تلقاء نفسي إن أتبع إلا ما يوحى إلي إني أخاف إن عصيت ربي عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 10, 'قل لو شاء الله ما تلوته عليكم ولا أدراكم به فقد لبثت فيكم عمرا من قبله أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 10, 'فمن أظلم ممن افترى على الله كذبا أو كذب بآياته إنه لا يفلح المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 10, 'ويعبدون من دون الله ما لا يضرهم ولا ينفعهم ويقولون هؤلاء شفعاؤنا عند الله قل أتنبئون الله بما لا يعلم في السماوات ولا في الأرض سبحانه وتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 10, 'وما كان الناس إلا أمة واحدة فاختلفوا ولولا كلمة سبقت من ربك لقضي بينهم فيما فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 10, 'ويقولون لولا أنزل عليه آية من ربه فقل إنما الغيب لله فانتظروا إني معكم من المنتظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 10, 'وإذا أذقنا الناس رحمة من بعد ضراء مستهم إذا لهم مكر في آياتنا قل الله أسرع مكرا إن رسلنا يكتبون ما تمكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 10, 'هو الذي يسيركم في البر والبحر حتى إذا كنتم في الفلك وجرين بهم بريح طيبة وفرحوا بها جاءتها ريح عاصف وجاءهم الموج من كل مكان وظنوا أنهم أحيط بهم دعوا الله مخلصين له الدين لئن أنجيتنا من هذه لنكونن من الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 10, 'فلما أنجاهم إذا هم يبغون في الأرض بغير الحق يا أيها الناس إنما بغيكم على أنفسكم متاع الحياة الدنيا ثم إلينا مرجعكم فننبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 10, 'إنما مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض مما يأكل الناس والأنعام حتى إذا أخذت الأرض زخرفها وازينت وظن أهلها أنهم قادرون عليها أتاها أمرنا ليلا أو نهارا فجعلناها حصيدا كأن لم تغن بالأمس كذلك نفصل الآيات لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 10, 'والله يدعو إلى دار السلام ويهدي من يشاء إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 10, 'للذين أحسنوا الحسنى وزيادة ولا يرهق وجوههم قتر ولا ذلة أولئك أصحاب الجنة هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 10, 'والذين كسبوا السيئات جزاء سيئة بمثلها وترهقهم ذلة ما لهم من الله من عاصم كأنما أغشيت وجوههم قطعا من الليل مظلما أولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 10, 'ويوم نحشرهم جميعا ثم نقول للذين أشركوا مكانكم أنتم وشركاؤكم فزيلنا بينهم وقال شركاؤهم ما كنتم إيانا تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 10, 'فكفى بالله شهيدا بيننا وبينكم إن كنا عن عبادتكم لغافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 10, 'هنالك تبلو كل نفس ما أسلفت وردوا إلى الله مولاهم الحق وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 10, 'قل من يرزقكم من السماء والأرض أمن يملك السمع والأبصار ومن يخرج الحي من الميت ويخرج الميت من الحي ومن يدبر الأمر فسيقولون الله فقل أفلا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 10, 'فذلكم الله ربكم الحق فماذا بعد الحق إلا الضلال فأنى تصرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 10, 'كذلك حقت كلمت ربك على الذين فسقوا أنهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 10, 'قل هل من شركائكم من يبدأ الخلق ثم يعيده قل الله يبدأ الخلق ثم يعيده فأنى تؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 10, 'قل هل من شركائكم من يهدي إلى الحق قل الله يهدي للحق أفمن يهدي إلى الحق أحق أن يتبع أمن لا يهدي إلا أن يهدى فما لكم كيف تحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 10, 'وما يتبع أكثرهم إلا ظنا إن الظن لا يغني من الحق شيئا إن الله عليم بما يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 10, 'وما كان هذا القرآن أن يفترى من دون الله ولكن تصديق الذي بين يديه وتفصيل الكتاب لا ريب فيه من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 10, 'أم يقولون افتراه قل فأتوا بسورة مثله وادعوا من استطعتم من دون الله إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 10, 'بل كذبوا بما لم يحيطوا بعلمه ولما يأتهم تأويله كذلك كذب الذين من قبلهم فانظر كيف كان عاقبة الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 10, 'ومنهم من يؤمن به ومنهم من لا يؤمن به وربك أعلم بالمفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 10, 'وإن كذبوك فقل لي عملي ولكم عملكم أنتم بريئون مما أعمل وأنا بريء مما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 10, 'ومنهم من يستمعون إليك أفأنت تسمع الصم ولو كانوا لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 10, 'ومنهم من ينظر إليك أفأنت تهدي العمي ولو كانوا لا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 10, 'إن الله لا يظلم الناس شيئا ولكن الناس أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 10, 'ويوم يحشرهم كأن لم يلبثوا إلا ساعة من النهار يتعارفون بينهم قد خسر الذين كذبوا بلقاء الله وما كانوا مهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 10, 'وإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا مرجعهم ثم الله شهيد على ما يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 10, 'ولكل أمة رسول فإذا جاء رسولهم قضي بينهم بالقسط وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 10, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 10, 'قل لا أملك لنفسي ضرا ولا نفعا إلا ما شاء الله لكل أمة أجل إذا جاء أجلهم فلا يستأخرون ساعة ولا يستقدمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 10, 'قل أرأيتم إن أتاكم عذابه بياتا أو نهارا ماذا يستعجل منه المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 10, 'أثم إذا ما وقع آمنتم به آلآن وقد كنتم به تستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 10, 'ثم قيل للذين ظلموا ذوقوا عذاب الخلد هل تجزون إلا بما كنتم تكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 10, 'ويستنبئونك أحق هو قل إي وربي إنه لحق وما أنتم بمعجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 10, 'ولو أن لكل نفس ظلمت ما في الأرض لافتدت به وأسروا الندامة لما رأوا العذاب وقضي بينهم بالقسط وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 10, 'ألا إن لله ما في السماوات والأرض ألا إن وعد الله حق ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 10, 'هو يحيي ويميت وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 10, 'يا أيها الناس قد جاءتكم موعظة من ربكم وشفاء لما في الصدور وهدى ورحمة للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 10, 'قل بفضل الله وبرحمته فبذلك فليفرحوا هو خير مما يجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 10, 'قل أرأيتم ما أنزل الله لكم من رزق فجعلتم منه حراما وحلالا قل آلله أذن لكم أم على الله تفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 10, 'وما ظن الذين يفترون على الله الكذب يوم القيامة إن الله لذو فضل على الناس ولكن أكثرهم لا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 10, 'وما تكون في شأن وما تتلو منه من قرآن ولا تعملون من عمل إلا كنا عليكم شهودا إذ تفيضون فيه وما يعزب عن ربك من مثقال ذرة في الأرض ولا في السماء ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 10, 'ألا إن أولياء الله لا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 10, 'الذين آمنوا وكانوا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 10, 'لهم البشرى في الحياة الدنيا وفي الآخرة لا تبديل لكلمات الله ذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 10, 'ولا يحزنك قولهم إن العزة لله جميعا هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 10, 'ألا إن لله من في السماوات ومن في الأرض وما يتبع الذين يدعون من دون الله شركاء إن يتبعون إلا الظن وإن هم إلا يخرصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 10, 'هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا إن في ذلك لآيات لقوم يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 10, 'قالوا اتخذ الله ولدا سبحانه هو الغني له ما في السماوات وما في الأرض إن عندكم من سلطان بهذا أتقولون على الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 10, 'قل إن الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 10, 'متاع في الدنيا ثم إلينا مرجعهم ثم نذيقهم العذاب الشديد بما كانوا يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 10, 'واتل عليهم نبأ نوح إذ قال لقومه يا قوم إن كان كبر عليكم مقامي وتذكيري بآيات الله فعلى الله توكلت فأجمعوا أمركم وشركاءكم ثم لا يكن أمركم عليكم غمة ثم اقضوا إلي ولا تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 10, 'فإن توليتم فما سألتكم من أجر إن أجري إلا على الله وأمرت أن أكون من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 10, 'فكذبوه فنجيناه ومن معه في الفلك وجعلناهم خلائف وأغرقنا الذين كذبوا بآياتنا فانظر كيف كان عاقبة المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 10, 'ثم بعثنا من بعده رسلا إلى قومهم فجاءوهم بالبينات فما كانوا ليؤمنوا بما كذبوا به من قبل كذلك نطبع على قلوب المعتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 10, 'ثم بعثنا من بعدهم موسى وهارون إلى فرعون وملئه بآياتنا فاستكبروا وكانوا قوما مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 10, 'فلما جاءهم الحق من عندنا قالوا إن هذا لسحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 10, 'قال موسى أتقولون للحق لما جاءكم أسحر هذا ولا يفلح الساحرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 10, 'قالوا أجئتنا لتلفتنا عما وجدنا عليه آباءنا وتكون لكما الكبرياء في الأرض وما نحن لكما بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 10, 'وقال فرعون ائتوني بكل ساحر عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 10, 'فلما جاء السحرة قال لهم موسى ألقوا ما أنتم ملقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 10, 'فلما ألقوا قال موسى ما جئتم به السحر إن الله سيبطله إن الله لا يصلح عمل المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 10, 'ويحق الله الحق بكلماته ولو كره المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 10, 'فما آمن لموسى إلا ذرية من قومه على خوف من فرعون وملئهم أن يفتنهم وإن فرعون لعال في الأرض وإنه لمن المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 10, 'وقال موسى يا قوم إن كنتم آمنتم بالله فعليه توكلوا إن كنتم مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 10, 'فقالوا على الله توكلنا ربنا لا تجعلنا فتنة للقوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 10, 'ونجنا برحمتك من القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 10, 'وأوحينا إلى موسى وأخيه أن تبوآ لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة وأقيموا الصلاة وبشر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 10, 'وقال موسى ربنا إنك آتيت فرعون وملأه زينة وأموالا في الحياة الدنيا ربنا ليضلوا عن سبيلك ربنا اطمس على أموالهم واشدد على قلوبهم فلا يؤمنوا حتى يروا العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 10, 'قال قد أجيبت دعوتكما فاستقيما ولا تتبعان سبيل الذين لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 10, 'وجاوزنا ببني إسرائيل البحر فأتبعهم فرعون وجنوده بغيا وعدوا حتى إذا أدركه الغرق قال آمنت أنه لا إله إلا الذي آمنت به بنو إسرائيل وأنا من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 10, 'آلآن وقد عصيت قبل وكنت من المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 10, 'فاليوم ننجيك ببدنك لتكون لمن خلفك آية وإن كثيرا من الناس عن آياتنا لغافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 10, 'ولقد بوأنا بني إسرائيل مبوأ صدق ورزقناهم من الطيبات فما اختلفوا حتى جاءهم العلم إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 10, 'فإن كنت في شك مما أنزلنا إليك فاسأل الذين يقرءون الكتاب من قبلك لقد جاءك الحق من ربك فلا تكونن من الممترين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 10, 'ولا تكونن من الذين كذبوا بآيات الله فتكون من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 10, 'إن الذين حقت عليهم كلمت ربك لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 10, 'ولو جاءتهم كل آية حتى يروا العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 10, 'فلولا كانت قرية آمنت فنفعها إيمانها إلا قوم يونس لما آمنوا كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 10, 'ولو شاء ربك لآمن من في الأرض كلهم جميعا أفأنت تكره الناس حتى يكونوا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 10, 'وما كان لنفس أن تؤمن إلا بإذن الله ويجعل الرجس على الذين لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 10, 'قل انظروا ماذا في السماوات والأرض وما تغني الآيات والنذر عن قوم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 10, 'فهل ينتظرون إلا مثل أيام الذين خلوا من قبلهم قل فانتظروا إني معكم من المنتظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 10, 'ثم ننجي رسلنا والذين آمنوا كذلك حقا علينا ننج المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 10, 'قل يا أيها الناس إن كنتم في شك من ديني فلا أعبد الذين تعبدون من دون الله ولكن أعبد الله الذي يتوفاكم وأمرت أن أكون من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 10, 'وأن أقم وجهك للدين حنيفا ولا تكونن من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 10, 'ولا تدع من دون الله ما لا ينفعك ولا يضرك فإن فعلت فإنك إذا من الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 10, 'وإن يمسسك الله بضر فلا كاشف له إلا هو وإن يردك بخير فلا راد لفضله يصيب به من يشاء من عباده وهو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 10, 'قل يا أيها الناس قد جاءكم الحق من ربكم فمن اهتدى فإنما يهتدي لنفسه ومن ضل فإنما يضل عليها وما أنا عليكم بوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 10, 'واتبع ما يوحى إليك واصبر حتى يحكم الله وهو خير الحاكمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 11, 'بسم الله الرحمن الرحيم الر كتاب أحكمت آياته ثم فصلت من لدن حكيم خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 11, 'ألا تعبدوا إلا الله إنني لكم منه نذير وبشير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 11, 'وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى ويؤت كل ذي فضل فضله وإن تولوا فإني أخاف عليكم عذاب يوم كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 11, 'إلى الله مرجعكم وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 11, 'ألا إنهم يثنون صدورهم ليستخفوا منه ألا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 11, 'وما من دابة في الأرض إلا على الله رزقها ويعلم مستقرها ومستودعها كل في كتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 11, 'وهو الذي خلق السماوات والأرض في ستة أيام وكان عرشه على الماء ليبلوكم أيكم أحسن عملا ولئن قلت إنكم مبعوثون من بعد الموت ليقولن الذين كفروا إن هذا إلا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 11, 'ولئن أخرنا عنهم العذاب إلى أمة معدودة ليقولن ما يحبسه ألا يوم يأتيهم ليس مصروفا عنهم وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 11, 'ولئن أذقنا الإنسان منا رحمة ثم نزعناها منه إنه ليئوس كفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 11, 'ولئن أذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني إنه لفرح فخور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 11, 'إلا الذين صبروا وعملوا الصالحات أولئك لهم مغفرة وأجر كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 11, 'فلعلك تارك بعض ما يوحى إليك وضائق به صدرك أن يقولوا لولا أنزل عليه كنز أو جاء معه ملك إنما أنت نذير والله على كل شيء وكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 11, 'أم يقولون افتراه قل فأتوا بعشر سور مثله مفتريات وادعوا من استطعتم من دون الله إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 11, 'فإلم يستجيبوا لكم فاعلموا أنما أنزل بعلم الله وأن لا إله إلا هو فهل أنتم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 11, 'من كان يريد الحياة الدنيا وزينتها نوف إليهم أعمالهم فيها وهم فيها لا يبخسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 11, 'أولئك الذين ليس لهم في الآخرة إلا النار وحبط ما صنعوا فيها وباطل ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 11, 'أفمن كان على بينة من ربه ويتلوه شاهد منه ومن قبله كتاب موسى إماما ورحمة أولئك يؤمنون به ومن يكفر به من الأحزاب فالنار موعده فلا تك في مرية منه إنه الحق من ربك ولكن أكثر الناس لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 11, 'ومن أظلم ممن افترى على الله كذبا أولئك يعرضون على ربهم ويقول الأشهاد هؤلاء الذين كذبوا على ربهم ألا لعنة الله على الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 11, 'الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالآخرة هم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 11, 'أولئك لم يكونوا معجزين في الأرض وما كان لهم من دون الله من أولياء يضاعف لهم العذاب ما كانوا يستطيعون السمع وما كانوا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 11, 'أولئك الذين خسروا أنفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 11, 'لا جرم أنهم في الآخرة هم الأخسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 11, 'إن الذين آمنوا وعملوا الصالحات وأخبتوا إلى ربهم أولئك أصحاب الجنة هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 11, 'مثل الفريقين كالأعمى والأصم والبصير والسميع هل يستويان مثلا أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 11, 'ولقد أرسلنا نوحا إلى قومه إني لكم نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 11, 'أن لا تعبدوا إلا الله إني أخاف عليكم عذاب يوم أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 11, 'فقال الملأ الذين كفروا من قومه ما نراك إلا بشرا مثلنا وما نراك اتبعك إلا الذين هم أراذلنا بادي الرأي وما نرى لكم علينا من فضل بل نظنكم كاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 11, 'قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني رحمة من عنده فعميت عليكم أنلزمكموها وأنتم لها كارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 11, 'ويا قوم لا أسألكم عليه مالا إن أجري إلا على الله وما أنا بطارد الذين آمنوا إنهم ملاقو ربهم ولكني أراكم قوما تجهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 11, 'ويا قوم من ينصرني من الله إن طردتهم أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 11, 'ولا أقول لكم عندي خزائن الله ولا أعلم الغيب ولا أقول إني ملك ولا أقول للذين تزدري أعينكم لن يؤتيهم الله خيرا الله أعلم بما في أنفسهم إني إذا لمن الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 11, 'قالوا يا نوح قد جادلتنا فأكثرت جدالنا فأتنا بما تعدنا إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 11, 'قال إنما يأتيكم به الله إن شاء وما أنتم بمعجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 11, 'ولا ينفعكم نصحي إن أردت أن أنصح لكم إن كان الله يريد أن يغويكم هو ربكم وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 11, 'أم يقولون افتراه قل إن افتريته فعلي إجرامي وأنا بريء مما تجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 11, 'وأوحي إلى نوح أنه لن يؤمن من قومك إلا من قد آمن فلا تبتئس بما كانوا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 11, 'واصنع الفلك بأعيننا ووحينا ولا تخاطبني في الذين ظلموا إنهم مغرقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 11, 'ويصنع الفلك وكلما مر عليه ملأ من قومه سخروا منه قال إن تسخروا منا فإنا نسخر منكم كما تسخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 11, 'فسوف تعلمون من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 11, 'حتى إذا جاء أمرنا وفار التنور قلنا احمل فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول ومن آمن وما آمن معه إلا قليل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 11, 'وقال اركبوا فيها بسم الله مجراها ومرساها إن ربي لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 11, 'وهي تجري بهم في موج كالجبال ونادى نوح ابنه وكان في معزل يا بني اركب معنا ولا تكن مع الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 11, 'قال سآوي إلى جبل يعصمني من الماء قال لا عاصم اليوم من أمر الله إلا من رحم وحال بينهما الموج فكان من المغرقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 11, 'وقيل يا أرض ابلعي ماءك ويا سماء أقلعي وغيض الماء وقضي الأمر واستوت على الجودي وقيل بعدا للقوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 11, 'ونادى نوح ربه فقال رب إن ابني من أهلي وإن وعدك الحق وأنت أحكم الحاكمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 11, 'قال يا نوح إنه ليس من أهلك إنه عمل غير صالح فلا تسألن ما ليس لك به علم إني أعظك أن تكون من الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 11, 'قال رب إني أعوذ بك أن أسألك ما ليس لي به علم وإلا تغفر لي وترحمني أكن من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 11, 'قيل يا نوح اهبط بسلام منا وبركات عليك وعلى أمم ممن معك وأمم سنمتعهم ثم يمسهم منا عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 11, 'تلك من أنباء الغيب نوحيها إليك ما كنت تعلمها أنت ولا قومك من قبل هذا فاصبر إن العاقبة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 11, 'وإلى عاد أخاهم هودا قال يا قوم اعبدوا الله ما لكم من إله غيره إن أنتم إلا مفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 11, 'يا قوم لا أسألكم عليه أجرا إن أجري إلا على الذي فطرني أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 11, 'ويا قوم استغفروا ربكم ثم توبوا إليه يرسل السماء عليكم مدرارا ويزدكم قوة إلى قوتكم ولا تتولوا مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 11, 'قالوا يا هود ما جئتنا ببينة وما نحن بتاركي آلهتنا عن قولك وما نحن لك بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 11, 'إن نقول إلا اعتراك بعض آلهتنا بسوء قال إني أشهد الله واشهدوا أني بريء مما تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 11, 'من دونه فكيدوني جميعا ثم لا تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 11, 'إني توكلت على الله ربي وربكم ما من دابة إلا هو آخذ بناصيتها إن ربي على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 11, 'فإن تولوا فقد أبلغتكم ما أرسلت به إليكم ويستخلف ربي قوما غيركم ولا تضرونه شيئا إن ربي على كل شيء حفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 11, 'ولما جاء أمرنا نجينا هودا والذين آمنوا معه برحمة منا ونجيناهم من عذاب غليظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 11, 'وتلك عاد جحدوا بآيات ربهم وعصوا رسله واتبعوا أمر كل جبار عنيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 11, 'وأتبعوا في هذه الدنيا لعنة ويوم القيامة ألا إن عادا كفروا ربهم ألا بعدا لعاد قوم هود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 11, 'وإلى ثمود أخاهم صالحا قال يا قوم اعبدوا الله ما لكم من إله غيره هو أنشأكم من الأرض واستعمركم فيها فاستغفروه ثم توبوا إليه إن ربي قريب مجيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 11, 'قالوا يا صالح قد كنت فينا مرجوا قبل هذا أتنهانا أن نعبد ما يعبد آباؤنا وإننا لفي شك مما تدعونا إليه مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 11, 'قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني منه رحمة فمن ينصرني من الله إن عصيته فما تزيدونني غير تخسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 11, 'ويا قوم هذه ناقة الله لكم آية فذروها تأكل في أرض الله ولا تمسوها بسوء فيأخذكم عذاب قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 11, 'فعقروها فقال تمتعوا في داركم ثلاثة أيام ذلك وعد غير مكذوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 11, 'فلما جاء أمرنا نجينا صالحا والذين آمنوا معه برحمة منا ومن خزي يومئذ إن ربك هو القوي العزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 11, 'وأخذ الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 11, 'كأن لم يغنوا فيها ألا إن ثمود كفروا ربهم ألا بعدا لثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 11, 'ولقد جاءت رسلنا إبراهيم بالبشرى قالوا سلاما قال سلام فما لبث أن جاء بعجل حنيذ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 11, 'فلما رأى أيديهم لا تصل إليه نكرهم وأوجس منهم خيفة قالوا لا تخف إنا أرسلنا إلى قوم لوط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 11, 'وامرأته قائمة فضحكت فبشرناها بإسحاق ومن وراء إسحاق يعقوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 11, 'قالت يا ويلتى أألد وأنا عجوز وهذا بعلي شيخا إن هذا لشيء عجيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 11, 'قالوا أتعجبين من أمر الله رحمت الله وبركاته عليكم أهل البيت إنه حميد مجيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 11, 'فلما ذهب عن إبراهيم الروع وجاءته البشرى يجادلنا في قوم لوط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 11, 'إن إبراهيم لحليم أواه منيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 11, 'يا إبراهيم أعرض عن هذا إنه قد جاء أمر ربك وإنهم آتيهم عذاب غير مردود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 11, 'ولما جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقال هذا يوم عصيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 11, 'وجاءه قومه يهرعون إليه ومن قبل كانوا يعملون السيئات قال يا قوم هؤلاء بناتي هن أطهر لكم فاتقوا الله ولا تخزون في ضيفي أليس منكم رجل رشيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 11, 'قالوا لقد علمت ما لنا في بناتك من حق وإنك لتعلم ما نريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 11, 'قال لو أن لي بكم قوة أو آوي إلى ركن شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 11, 'قالوا يا لوط إنا رسل ربك لن يصلوا إليك فأسر بأهلك بقطع من الليل ولا يلتفت منكم أحد إلا امرأتك إنه مصيبها ما أصابهم إن موعدهم الصبح أليس الصبح بقريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 11, 'فلما جاء أمرنا جعلنا عاليها سافلها وأمطرنا عليها حجارة من سجيل منضود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 11, 'مسومة عند ربك وما هي من الظالمين ببعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 11, 'وإلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من إله غيره ولا تنقصوا المكيال والميزان إني أراكم بخير وإني أخاف عليكم عذاب يوم محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 11, 'ويا قوم أوفوا المكيال والميزان بالقسط ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 11, 'بقيت الله خير لكم إن كنتم مؤمنين وما أنا عليكم بحفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 11, 'قالوا يا شعيب أصلاتك تأمرك أن نترك ما يعبد آباؤنا أو أن نفعل في أموالنا ما نشاء إنك لأنت الحليم الرشيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 11, 'قال يا قوم أرأيتم إن كنت على بينة من ربي ورزقني منه رزقا حسنا وما أريد أن أخالفكم إلى ما أنهاكم عنه إن أريد إلا الإصلاح ما استطعت وما توفيقي إلا بالله عليه توكلت وإليه أنيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 11, 'ويا قوم لا يجرمنكم شقاقي أن يصيبكم مثل ما أصاب قوم نوح أو قوم هود أو قوم صالح وما قوم لوط منكم ببعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 11, 'واستغفروا ربكم ثم توبوا إليه إن ربي رحيم ودود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 11, 'قالوا يا شعيب ما نفقه كثيرا مما تقول وإنا لنراك فينا ضعيفا ولولا رهطك لرجمناك وما أنت علينا بعزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 11, 'قال يا قوم أرهطي أعز عليكم من الله واتخذتموه وراءكم ظهريا إن ربي بما تعملون محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 11, 'ويا قوم اعملوا على مكانتكم إني عامل سوف تعلمون من يأتيه عذاب يخزيه ومن هو كاذب وارتقبوا إني معكم رقيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 11, 'ولما جاء أمرنا نجينا شعيبا والذين آمنوا معه برحمة منا وأخذت الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 11, 'كأن لم يغنوا فيها ألا بعدا لمدين كما بعدت ثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 11, 'ولقد أرسلنا موسى بآياتنا وسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 11, 'إلى فرعون وملئه فاتبعوا أمر فرعون وما أمر فرعون برشيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 11, 'يقدم قومه يوم القيامة فأوردهم النار وبئس الورد المورود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 11, 'وأتبعوا في هذه لعنة ويوم القيامة بئس الرفد المرفود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 11, 'ذلك من أنباء القرى نقصه عليك منها قائم وحصيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 11, 'وما ظلمناهم ولكن ظلموا أنفسهم فما أغنت عنهم آلهتهم التي يدعون من دون الله من شيء لما جاء أمر ربك وما زادوهم غير تتبيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 11, 'وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة إن أخذه أليم شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 11, 'إن في ذلك لآية لمن خاف عذاب الآخرة ذلك يوم مجموع له الناس وذلك يوم مشهود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 11, 'وما نؤخره إلا لأجل معدود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 11, 'يوم يأت لا تكلم نفس إلا بإذنه فمنهم شقي وسعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 11, 'فأما الذين شقوا ففي النار لهم فيها زفير وشهيق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 11, 'خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك إن ربك فعال لما يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 11, 'وأما الذين سعدوا ففي الجنة خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك عطاء غير مجذوذ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 11, 'فلا تك في مرية مما يعبد هؤلاء ما يعبدون إلا كما يعبد آباؤهم من قبل وإنا لموفوهم نصيبهم غير منقوص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 11, 'ولقد آتينا موسى الكتاب فاختلف فيه ولولا كلمة سبقت من ربك لقضي بينهم وإنهم لفي شك منه مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 11, 'وإن كلا لما ليوفينهم ربك أعمالهم إنه بما يعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 11, 'فاستقم كما أمرت ومن تاب معك ولا تطغوا إنه بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 11, 'ولا تركنوا إلى الذين ظلموا فتمسكم النار وما لكم من دون الله من أولياء ثم لا تنصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 11, 'وأقم الصلاة طرفي النهار وزلفا من الليل إن الحسنات يذهبن السيئات ذلك ذكرى للذاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 11, 'واصبر فإن الله لا يضيع أجر المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 11, 'فلولا كان من القرون من قبلكم أولو بقية ينهون عن الفساد في الأرض إلا قليلا ممن أنجينا منهم واتبع الذين ظلموا ما أترفوا فيه وكانوا مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 11, 'وما كان ربك ليهلك القرى بظلم وأهلها مصلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 11, 'ولو شاء ربك لجعل الناس أمة واحدة ولا يزالون مختلفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 11, 'إلا من رحم ربك ولذلك خلقهم وتمت كلمة ربك لأملأن جهنم من الجنة والناس أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 11, 'وكلا نقص عليك من أنباء الرسل ما نثبت به فؤادك وجاءك في هذه الحق وموعظة وذكرى للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 11, 'وقل للذين لا يؤمنون اعملوا على مكانتكم إنا عاملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 11, 'وانتظروا إنا منتظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 11, 'ولله غيب السماوات والأرض وإليه يرجع الأمر كله فاعبده وتوكل عليه وما ربك بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 12, 'بسم الله الرحمن الرحيم الر تلك آيات الكتاب المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 12, 'إنا أنزلناه قرآنا عربيا لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 12, 'نحن نقص عليك أحسن القصص بما أوحينا إليك هذا القرآن وإن كنت من قبله لمن الغافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 12, 'إذ قال يوسف لأبيه يا أبت إني رأيت أحد عشر كوكبا والشمس والقمر رأيتهم لي ساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 12, 'قال يا بني لا تقصص رؤياك على إخوتك فيكيدوا لك كيدا إن الشيطان للإنسان عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 12, 'وكذلك يجتبيك ربك ويعلمك من تأويل الأحاديث ويتم نعمته عليك وعلى آل يعقوب كما أتمها على أبويك من قبل إبراهيم وإسحاق إن ربك عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 12, 'لقد كان في يوسف وإخوته آيات للسائلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 12, 'إذ قالوا ليوسف وأخوه أحب إلى أبينا منا ونحن عصبة إن أبانا لفي ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 12, 'اقتلوا يوسف أو اطرحوه أرضا يخل لكم وجه أبيكم وتكونوا من بعده قوما صالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 12, 'قال قائل منهم لا تقتلوا يوسف وألقوه في غيابت الجب يلتقطه بعض السيارة إن كنتم فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 12, 'قالوا يا أبانا ما لك لا تأمنا على يوسف وإنا له لناصحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 12, 'أرسله معنا غدا يرتع ويلعب وإنا له لحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 12, 'قال إني ليحزنني أن تذهبوا به وأخاف أن يأكله الذئب وأنتم عنه غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 12, 'قالوا لئن أكله الذئب ونحن عصبة إنا إذا لخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 12, 'فلما ذهبوا به وأجمعوا أن يجعلوه في غيابت الجب وأوحينا إليه لتنبئنهم بأمرهم هذا وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 12, 'وجاءوا أباهم عشاء يبكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 12, 'قالوا يا أبانا إنا ذهبنا نستبق وتركنا يوسف عند متاعنا فأكله الذئب وما أنت بمؤمن لنا ولو كنا صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 12, 'وجاءوا على قميصه بدم كذب قال بل سولت لكم أنفسكم أمرا فصبر جميل والله المستعان على ما تصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 12, 'وجاءت سيارة فأرسلوا واردهم فأدلى دلوه قال يا بشرى هذا غلام وأسروه بضاعة والله عليم بما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 12, 'وشروه بثمن بخس دراهم معدودة وكانوا فيه من الزاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 12, 'وقال الذي اشتراه من مصر لامرأته أكرمي مثواه عسى أن ينفعنا أو نتخذه ولدا وكذلك مكنا ليوسف في الأرض ولنعلمه من تأويل الأحاديث والله غالب على أمره ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 12, 'ولما بلغ أشده آتيناه حكما وعلما وكذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 12, 'وراودته التي هو في بيتها عن نفسه وغلقت الأبواب وقالت هيت لك قال معاذ الله إنه ربي أحسن مثواي إنه لا يفلح الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 12, 'ولقد همت به وهم بها لولا أن رأى برهان ربه كذلك لنصرف عنه السوء والفحشاء إنه من عبادنا المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 12, 'واستبقا الباب وقدت قميصه من دبر وألفيا سيدها لدى الباب قالت ما جزاء من أراد بأهلك سوءا إلا أن يسجن أو عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 12, 'قال هي راودتني عن نفسي وشهد شاهد من أهلها إن كان قميصه قد من قبل فصدقت وهو من الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 12, 'وإن كان قميصه قد من دبر فكذبت وهو من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 12, 'فلما رأى قميصه قد من دبر قال إنه من كيدكن إن كيدكن عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 12, 'يوسف أعرض عن هذا واستغفري لذنبك إنك كنت من الخاطئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 12, 'وقال نسوة في المدينة امرأت العزيز تراود فتاها عن نفسه قد شغفها حبا إنا لنراها في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 12, 'فلما سمعت بمكرهن أرسلت إليهن وأعتدت لهن متكأ وآتت كل واحدة منهن سكينا وقالت اخرج عليهن فلما رأينه أكبرنه وقطعن أيديهن وقلن حاش لله ما هذا بشرا إن هذا إلا ملك كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 12, 'قالت فذلكن الذي لمتنني فيه ولقد راودته عن نفسه فاستعصم ولئن لم يفعل ما آمره ليسجنن وليكونا من الصاغرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 12, 'قال رب السجن أحب إلي مما يدعونني إليه وإلا تصرف عني كيدهن أصب إليهن وأكن من الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 12, 'فاستجاب له ربه فصرف عنه كيدهن إنه هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 12, 'ثم بدا لهم من بعد ما رأوا الآيات ليسجننه حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 12, 'ودخل معه السجن فتيان قال أحدهما إني أراني أعصر خمرا وقال الآخر إني أراني أحمل فوق رأسي خبزا تأكل الطير منه نبئنا بتأويله إنا نراك من المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 12, 'قال لا يأتيكما طعام ترزقانه إلا نبأتكما بتأويله قبل أن يأتيكما ذلكما مما علمني ربي إني تركت ملة قوم لا يؤمنون بالله وهم بالآخرة هم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 12, 'واتبعت ملة آبائي إبراهيم وإسحاق ويعقوب ما كان لنا أن نشرك بالله من شيء ذلك من فضل الله علينا وعلى الناس ولكن أكثر الناس لا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 12, 'يا صاحبي السجن أأرباب متفرقون خير أم الله الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 12, 'ما تعبدون من دونه إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان إن الحكم إلا لله أمر ألا تعبدوا إلا إياه ذلك الدين القيم ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 12, 'يا صاحبي السجن أما أحدكما فيسقي ربه خمرا وأما الآخر فيصلب فتأكل الطير من رأسه قضي الأمر الذي فيه تستفتيان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 12, 'وقال للذي ظن أنه ناج منهما اذكرني عند ربك فأنساه الشيطان ذكر ربه فلبث في السجن بضع سنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 12, 'وقال الملك إني أرى سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات يا أيها الملأ أفتوني في رؤياي إن كنتم للرؤيا تعبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 12, 'قالوا أضغاث أحلام وما نحن بتأويل الأحلام بعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 12, 'وقال الذي نجا منهما وادكر بعد أمة أنا أنبئكم بتأويله فأرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 12, 'يوسف أيها الصديق أفتنا في سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات لعلي أرجع إلى الناس لعلهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 12, 'قال تزرعون سبع سنين دأبا فما حصدتم فذروه في سنبله إلا قليلا مما تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 12, 'ثم يأتي من بعد ذلك سبع شداد يأكلن ما قدمتم لهن إلا قليلا مما تحصنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 12, 'ثم يأتي من بعد ذلك عام فيه يغاث الناس وفيه يعصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 12, 'وقال الملك ائتوني به فلما جاءه الرسول قال ارجع إلى ربك فاسأله ما بال النسوة اللاتي قطعن أيديهن إن ربي بكيدهن عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 12, 'قال ما خطبكن إذ راودتن يوسف عن نفسه قلن حاش لله ما علمنا عليه من سوء قالت امرأت العزيز الآن حصحص الحق أنا راودته عن نفسه وإنه لمن الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 12, 'ذلك ليعلم أني لم أخنه بالغيب وأن الله لا يهدي كيد الخائنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 12, 'وما أبرئ نفسي إن النفس لأمارة بالسوء إلا ما رحم ربي إن ربي غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 12, 'وقال الملك ائتوني به أستخلصه لنفسي فلما كلمه قال إنك اليوم لدينا مكين أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 12, 'قال اجعلني على خزائن الأرض إني حفيظ عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 12, 'وكذلك مكنا ليوسف في الأرض يتبوأ منها حيث يشاء نصيب برحمتنا من نشاء ولا نضيع أجر المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 12, 'ولأجر الآخرة خير للذين آمنوا وكانوا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 12, 'وجاء إخوة يوسف فدخلوا عليه فعرفهم وهم له منكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 12, 'ولما جهزهم بجهازهم قال ائتوني بأخ لكم من أبيكم ألا ترون أني أوفي الكيل وأنا خير المنزلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 12, 'فإن لم تأتوني به فلا كيل لكم عندي ولا تقربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 12, 'قالوا سنراود عنه أباه وإنا لفاعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 12, 'وقال لفتيانه اجعلوا بضاعتهم في رحالهم لعلهم يعرفونها إذا انقلبوا إلى أهلهم لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 12, 'فلما رجعوا إلى أبيهم قالوا يا أبانا منع منا الكيل فأرسل معنا أخانا نكتل وإنا له لحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 12, 'قال هل آمنكم عليه إلا كما أمنتكم على أخيه من قبل فالله خير حافظا وهو أرحم الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 12, 'ولما فتحوا متاعهم وجدوا بضاعتهم ردت إليهم قالوا يا أبانا ما نبغي هذه بضاعتنا ردت إلينا ونمير أهلنا ونحفظ أخانا ونزداد كيل بعير ذلك كيل يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 12, 'قال لن أرسله معكم حتى تؤتون موثقا من الله لتأتنني به إلا أن يحاط بكم فلما آتوه موثقهم قال الله على ما نقول وكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 12, 'وقال يا بني لا تدخلوا من باب واحد وادخلوا من أبواب متفرقة وما أغني عنكم من الله من شيء إن الحكم إلا لله عليه توكلت وعليه فليتوكل المتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 12, 'ولما دخلوا من حيث أمرهم أبوهم ما كان يغني عنهم من الله من شيء إلا حاجة في نفس يعقوب قضاها وإنه لذو علم لما علمناه ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 12, 'ولما دخلوا على يوسف آوى إليه أخاه قال إني أنا أخوك فلا تبتئس بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 12, 'فلما جهزهم بجهازهم جعل السقاية في رحل أخيه ثم أذن مؤذن أيتها العير إنكم لسارقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 12, 'قالوا وأقبلوا عليهم ماذا تفقدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 12, 'قالوا نفقد صواع الملك ولمن جاء به حمل بعير وأنا به زعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 12, 'قالوا تالله لقد علمتم ما جئنا لنفسد في الأرض وما كنا سارقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 12, 'قالوا فما جزاؤه إن كنتم كاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 12, 'قالوا جزاؤه من وجد في رحله فهو جزاؤه كذلك نجزي الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 12, 'فبدأ بأوعيتهم قبل وعاء أخيه ثم استخرجها من وعاء أخيه كذلك كدنا ليوسف ما كان ليأخذ أخاه في دين الملك إلا أن يشاء الله نرفع درجات من نشاء وفوق كل ذي علم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 12, 'قالوا إن يسرق فقد سرق أخ له من قبل فأسرها يوسف في نفسه ولم يبدها لهم قال أنتم شر مكانا والله أعلم بما تصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 12, 'قالوا يا أيها العزيز إن له أبا شيخا كبيرا فخذ أحدنا مكانه إنا نراك من المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 12, 'قال معاذ الله أن نأخذ إلا من وجدنا متاعنا عنده إنا إذا لظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 12, 'فلما استيأسوا منه خلصوا نجيا قال كبيرهم ألم تعلموا أن أباكم قد أخذ عليكم موثقا من الله ومن قبل ما فرطتم في يوسف فلن أبرح الأرض حتى يأذن لي أبي أو يحكم الله لي وهو خير الحاكمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 12, 'ارجعوا إلى أبيكم فقولوا يا أبانا إن ابنك سرق وما شهدنا إلا بما علمنا وما كنا للغيب حافظين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 12, 'واسأل القرية التي كنا فيها والعير التي أقبلنا فيها وإنا لصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 12, 'قال بل سولت لكم أنفسكم أمرا فصبر جميل عسى الله أن يأتيني بهم جميعا إنه هو العليم الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 12, 'وتولى عنهم وقال يا أسفى على يوسف وابيضت عيناه من الحزن فهو كظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 12, 'قالوا تالله تفتأ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 12, 'قال إنما أشكو بثي وحزني إلى الله وأعلم من الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 12, 'يا بني اذهبوا فتحسسوا من يوسف وأخيه ولا تيأسوا من روح الله إنه لا ييأس من روح الله إلا القوم الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 12, 'فلما دخلوا عليه قالوا يا أيها العزيز مسنا وأهلنا الضر وجئنا ببضاعة مزجاة فأوف لنا الكيل وتصدق علينا إن الله يجزي المتصدقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 12, 'قال هل علمتم ما فعلتم بيوسف وأخيه إذ أنتم جاهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 12, 'قالوا أإنك لأنت يوسف قال أنا يوسف وهذا أخي قد من الله علينا إنه من يتق ويصبر فإن الله لا يضيع أجر المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 12, 'قالوا تالله لقد آثرك الله علينا وإن كنا لخاطئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 12, 'قال لا تثريب عليكم اليوم يغفر الله لكم وهو أرحم الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 12, 'اذهبوا بقميصي هذا فألقوه على وجه أبي يأت بصيرا وأتوني بأهلكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 12, 'ولما فصلت العير قال أبوهم إني لأجد ريح يوسف لولا أن تفندون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 12, 'قالوا تالله إنك لفي ضلالك القديم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 12, 'فلما أن جاء البشير ألقاه على وجهه فارتد بصيرا قال ألم أقل لكم إني أعلم من الله ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 12, 'قالوا يا أبانا استغفر لنا ذنوبنا إنا كنا خاطئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 12, 'قال سوف أستغفر لكم ربي إنه هو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 12, 'فلما دخلوا على يوسف آوى إليه أبويه وقال ادخلوا مصر إن شاء الله آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 12, 'ورفع أبويه على العرش وخروا له سجدا وقال يا أبت هذا تأويل رؤياي من قبل قد جعلها ربي حقا وقد أحسن بي إذ أخرجني من السجن وجاء بكم من البدو من بعد أن نزغ الشيطان بيني وبين إخوتي إن ربي لطيف لما يشاء إنه هو العليم الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 12, 'رب قد آتيتني من الملك وعلمتني من تأويل الأحاديث فاطر السماوات والأرض أنت وليي في الدنيا والآخرة توفني مسلما وألحقني بالصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 12, 'ذلك من أنباء الغيب نوحيه إليك وما كنت لديهم إذ أجمعوا أمرهم وهم يمكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 12, 'وما أكثر الناس ولو حرصت بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 12, 'وما تسألهم عليه من أجر إن هو إلا ذكر للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 12, 'وكأين من آية في السماوات والأرض يمرون عليها وهم عنها معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 12, 'وما يؤمن أكثرهم بالله إلا وهم مشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 12, 'أفأمنوا أن تأتيهم غاشية من عذاب الله أو تأتيهم الساعة بغتة وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 12, 'قل هذه سبيلي أدعو إلى الله على بصيرة أنا ومن اتبعني وسبحان الله وما أنا من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 12, 'وما أرسلنا من قبلك إلا رجالا نوحي إليهم من أهل القرى أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ولدار الآخرة خير للذين اتقوا أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 12, 'حتى إذا استيأس الرسل وظنوا أنهم قد كذبوا جاءهم نصرنا فنجي من نشاء ولا يرد بأسنا عن القوم المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 12, 'لقد كان في قصصهم عبرة لأولي الألباب ما كان حديثا يفترى ولكن تصديق الذي بين يديه وتفصيل كل شيء وهدى ورحمة لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 13, 'بسم الله الرحمن الرحيم المر تلك آيات الكتاب والذي أنزل إليك من ربك الحق ولكن أكثر الناس لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 13, 'الله الذي رفع السماوات بغير عمد ترونها ثم استوى على العرش وسخر الشمس والقمر كل يجري لأجل مسمى يدبر الأمر يفصل الآيات لعلكم بلقاء ربكم توقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 13, 'وهو الذي مد الأرض وجعل فيها رواسي وأنهارا ومن كل الثمرات جعل فيها زوجين اثنين يغشي الليل النهار إن في ذلك لآيات لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 13, 'وفي الأرض قطع متجاورات وجنات من أعناب وزرع ونخيل صنوان وغير صنوان يسقى بماء واحد ونفضل بعضها على بعض في الأكل إن في ذلك لآيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 13, 'وإن تعجب فعجب قولهم أإذا كنا ترابا أإنا لفي خلق جديد أولئك الذين كفروا بربهم وأولئك الأغلال في أعناقهم وأولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 13, 'ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلات وإن ربك لذو مغفرة للناس على ظلمهم وإن ربك لشديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 13, 'ويقول الذين كفروا لولا أنزل عليه آية من ربه إنما أنت منذر ولكل قوم هاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 13, 'الله يعلم ما تحمل كل أنثى وما تغيض الأرحام وما تزداد وكل شيء عنده بمقدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 13, 'عالم الغيب والشهادة الكبير المتعال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 13, 'سواء منكم من أسر القول ومن جهر به ومن هو مستخف بالليل وسارب بالنهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 13, 'له معقبات من بين يديه ومن خلفه يحفظونه من أمر الله إن الله لا يغير ما بقوم حتى يغيروا ما بأنفسهم وإذا أراد الله بقوم سوءا فلا مرد له وما لهم من دونه من وال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 13, 'هو الذي يريكم البرق خوفا وطمعا وينشئ السحاب الثقال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 13, 'ويسبح الرعد بحمده والملائكة من خيفته ويرسل الصواعق فيصيب بها من يشاء وهم يجادلون في الله وهو شديد المحال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 13, 'له دعوة الحق والذين يدعون من دونه لا يستجيبون لهم بشيء إلا كباسط كفيه إلى الماء ليبلغ فاه وما هو ببالغه وما دعاء الكافرين إلا في ضلال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 13, 'ولله يسجد من في السماوات والأرض طوعا وكرها وظلالهم بالغدو والآصال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 13, 'قل من رب السماوات والأرض قل الله قل أفاتخذتم من دونه أولياء لا يملكون لأنفسهم نفعا ولا ضرا قل هل يستوي الأعمى والبصير أم هل تستوي الظلمات والنور أم جعلوا لله شركاء خلقوا كخلقه فتشابه الخلق عليهم قل الله خالق كل شيء وهو الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 13, 'أنزل من السماء ماء فسالت أودية بقدرها فاحتمل السيل زبدا رابيا ومما يوقدون عليه في النار ابتغاء حلية أو متاع زبد مثله كذلك يضرب الله الحق والباطل فأما الزبد فيذهب جفاء وأما ما ينفع الناس فيمكث في الأرض كذلك يضرب الله الأمثال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 13, 'للذين استجابوا لربهم الحسنى والذين لم يستجيبوا له لو أن لهم ما في الأرض جميعا ومثله معه لافتدوا به أولئك لهم سوء الحساب ومأواهم جهنم وبئس المهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 13, 'أفمن يعلم أنما أنزل إليك من ربك الحق كمن هو أعمى إنما يتذكر أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 13, 'الذين يوفون بعهد الله ولا ينقضون الميثاق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 13, 'والذين يصلون ما أمر الله به أن يوصل ويخشون ربهم ويخافون سوء الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 13, 'والذين صبروا ابتغاء وجه ربهم وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية ويدرءون بالحسنة السيئة أولئك لهم عقبى الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 13, 'جنات عدن يدخلونها ومن صلح من آبائهم وأزواجهم وذرياتهم والملائكة يدخلون عليهم من كل باب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 13, 'سلام عليكم بما صبرتم فنعم عقبى الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 13, 'والذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض أولئك لهم اللعنة ولهم سوء الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 13, 'الله يبسط الرزق لمن يشاء ويقدر وفرحوا بالحياة الدنيا وما الحياة الدنيا في الآخرة إلا متاع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 13, 'ويقول الذين كفروا لولا أنزل عليه آية من ربه قل إن الله يضل من يشاء ويهدي إليه من أناب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 13, 'الذين آمنوا وتطمئن قلوبهم بذكر الله ألا بذكر الله تطمئن القلوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 13, 'الذين آمنوا وعملوا الصالحات طوبى لهم وحسن مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 13, 'كذلك أرسلناك في أمة قد خلت من قبلها أمم لتتلو عليهم الذي أوحينا إليك وهم يكفرون بالرحمن قل هو ربي لا إله إلا هو عليه توكلت وإليه متاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 13, 'ولو أن قرآنا سيرت به الجبال أو قطعت به الأرض أو كلم به الموتى بل لله الأمر جميعا أفلم ييأس الذين آمنوا أن لو يشاء الله لهدى الناس جميعا ولا يزال الذين كفروا تصيبهم بما صنعوا قارعة أو تحل قريبا من دارهم حتى يأتي وعد الله إن الله لا يخلف الميعاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 13, 'ولقد استهزئ برسل من قبلك فأمليت للذين كفروا ثم أخذتهم فكيف كان عقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 13, 'أفمن هو قائم على كل نفس بما كسبت وجعلوا لله شركاء قل سموهم أم تنبئونه بما لا يعلم في الأرض أم بظاهر من القول بل زين للذين كفروا مكرهم وصدوا عن السبيل ومن يضلل الله فما له من هاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 13, 'لهم عذاب في الحياة الدنيا ولعذاب الآخرة أشق وما لهم من الله من واق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 13, 'مثل الجنة التي وعد المتقون تجري من تحتها الأنهار أكلها دائم وظلها تلك عقبى الذين اتقوا وعقبى الكافرين النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 13, 'والذين آتيناهم الكتاب يفرحون بما أنزل إليك ومن الأحزاب من ينكر بعضه قل إنما أمرت أن أعبد الله ولا أشرك به إليه أدعو وإليه مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 13, 'وكذلك أنزلناه حكما عربيا ولئن اتبعت أهواءهم بعدما جاءك من العلم ما لك من الله من ولي ولا واق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 13, 'ولقد أرسلنا رسلا من قبلك وجعلنا لهم أزواجا وذرية وما كان لرسول أن يأتي بآية إلا بإذن الله لكل أجل كتاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 13, 'يمحو الله ما يشاء ويثبت وعنده أم الكتاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 13, 'وإن ما نرينك بعض الذي نعدهم أو نتوفينك فإنما عليك البلاغ وعلينا الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 13, 'أولم يروا أنا نأتي الأرض ننقصها من أطرافها والله يحكم لا معقب لحكمه وهو سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 13, 'وقد مكر الذين من قبلهم فلله المكر جميعا يعلم ما تكسب كل نفس وسيعلم الكفار لمن عقبى الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 13, 'ويقول الذين كفروا لست مرسلا قل كفى بالله شهيدا بيني وبينكم ومن عنده علم الكتاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 14, 'بسم الله الرحمن الرحيم الر كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور بإذن ربهم إلى صراط العزيز الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 14, 'الله الذي له ما في السماوات وما في الأرض وويل للكافرين من عذاب شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 14, 'الذين يستحبون الحياة الدنيا على الآخرة ويصدون عن سبيل الله ويبغونها عوجا أولئك في ضلال بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 14, 'وما أرسلنا من رسول إلا بلسان قومه ليبين لهم فيضل الله من يشاء ويهدي من يشاء وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 14, 'ولقد أرسلنا موسى بآياتنا أن أخرج قومك من الظلمات إلى النور وذكرهم بأيام الله إن في ذلك لآيات لكل صبار شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 14, 'وإذ قال موسى لقومه اذكروا نعمة الله عليكم إذ أنجاكم من آل فرعون يسومونكم سوء العذاب ويذبحون أبناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 14, 'وإذ تأذن ربكم لئن شكرتم لأزيدنكم ولئن كفرتم إن عذابي لشديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 14, 'وقال موسى إن تكفروا أنتم ومن في الأرض جميعا فإن الله لغني حميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 14, 'ألم يأتكم نبأ الذين من قبلكم قوم نوح وعاد وثمود والذين من بعدهم لا يعلمهم إلا الله جاءتهم رسلهم بالبينات فردوا أيديهم في أفواههم وقالوا إنا كفرنا بما أرسلتم به وإنا لفي شك مما تدعوننا إليه مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 14, 'قالت رسلهم أفي الله شك فاطر السماوات والأرض يدعوكم ليغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى قالوا إن أنتم إلا بشر مثلنا تريدون أن تصدونا عما كان يعبد آباؤنا فأتونا بسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 14, 'قالت لهم رسلهم إن نحن إلا بشر مثلكم ولكن الله يمن على من يشاء من عباده وما كان لنا أن نأتيكم بسلطان إلا بإذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 14, 'وما لنا ألا نتوكل على الله وقد هدانا سبلنا ولنصبرن على ما آذيتمونا وعلى الله فليتوكل المتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 14, 'وقال الذين كفروا لرسلهم لنخرجنكم من أرضنا أو لتعودن في ملتنا فأوحى إليهم ربهم لنهلكن الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 14, 'ولنسكننكم الأرض من بعدهم ذلك لمن خاف مقامي وخاف وعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 14, 'واستفتحوا وخاب كل جبار عنيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 14, 'من ورائه جهنم ويسقى من ماء صديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 14, 'يتجرعه ولا يكاد يسيغه ويأتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 14, 'مثل الذين كفروا بربهم أعمالهم كرماد اشتدت به الريح في يوم عاصف لا يقدرون مما كسبوا على شيء ذلك هو الضلال البعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 14, 'ألم تر أن الله خلق السماوات والأرض بالحق إن يشأ يذهبكم ويأت بخلق جديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 14, 'وما ذلك على الله بعزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 14, 'وبرزوا لله جميعا فقال الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا من عذاب الله من شيء قالوا لو هدانا الله لهديناكم سواء علينا أجزعنا أم صبرنا ما لنا من محيص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 14, 'وقال الشيطان لما قضي الأمر إن الله وعدكم وعد الحق ووعدتكم فأخلفتكم وما كان لي عليكم من سلطان إلا أن دعوتكم فاستجبتم لي فلا تلوموني ولوموا أنفسكم ما أنا بمصرخكم وما أنتم بمصرخي إني كفرت بما أشركتمون من قبل إن الظالمين لهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 14, 'وأدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار خالدين فيها بإذن ربهم تحيتهم فيها سلام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 14, 'ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة أصلها ثابت وفرعها في السماء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 14, 'تؤتي أكلها كل حين بإذن ربها ويضرب الله الأمثال للناس لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 14, 'ومثل كلمة خبيثة كشجرة خبيثة اجتثت من فوق الأرض ما لها من قرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 14, 'يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة ويضل الله الظالمين ويفعل الله ما يشاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 14, 'ألم تر إلى الذين بدلوا نعمت الله كفرا وأحلوا قومهم دار البوار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 14, 'جهنم يصلونها وبئس القرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 14, 'وجعلوا لله أندادا ليضلوا عن سبيله قل تمتعوا فإن مصيركم إلى النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 14, 'قل لعبادي الذين آمنوا يقيموا الصلاة وينفقوا مما رزقناهم سرا وعلانية من قبل أن يأتي يوم لا بيع فيه ولا خلال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 14, 'الله الذي خلق السماوات والأرض وأنزل من السماء ماء فأخرج به من الثمرات رزقا لكم وسخر لكم الفلك لتجري في البحر بأمره وسخر لكم الأنهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 14, 'وسخر لكم الشمس والقمر دائبين وسخر لكم الليل والنهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 14, 'وآتاكم من كل ما سألتموه وإن تعدوا نعمت الله لا تحصوها إن الإنسان لظلوم كفار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 14, 'وإذ قال إبراهيم رب اجعل هذا البلد آمنا واجنبني وبني أن نعبد الأصنام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 14, 'رب إنهن أضللن كثيرا من الناس فمن تبعني فإنه مني ومن عصاني فإنك غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 14, 'ربنا إني أسكنت من ذريتي بواد غير ذي زرع عند بيتك المحرم ربنا ليقيموا الصلاة فاجعل أفئدة من الناس تهوي إليهم وارزقهم من الثمرات لعلهم يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 14, 'ربنا إنك تعلم ما نخفي وما نعلن وما يخفى على الله من شيء في الأرض ولا في السماء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 14, 'الحمد لله الذي وهب لي على الكبر إسماعيل وإسحاق إن ربي لسميع الدعاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 14, 'رب اجعلني مقيم الصلاة ومن ذريتي ربنا وتقبل دعاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 14, 'ربنا اغفر لي ولوالدي وللمؤمنين يوم يقوم الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 14, 'ولا تحسبن الله غافلا عما يعمل الظالمون إنما يؤخرهم ليوم تشخص فيه الأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 14, 'مهطعين مقنعي رءوسهم لا يرتد إليهم طرفهم وأفئدتهم هواء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 14, 'وأنذر الناس يوم يأتيهم العذاب فيقول الذين ظلموا ربنا أخرنا إلى أجل قريب نجب دعوتك ونتبع الرسل أولم تكونوا أقسمتم من قبل ما لكم من زوال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 14, 'وسكنتم في مساكن الذين ظلموا أنفسهم وتبين لكم كيف فعلنا بهم وضربنا لكم الأمثال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 14, 'وقد مكروا مكرهم وعند الله مكرهم وإن كان مكرهم لتزول منه الجبال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 14, 'فلا تحسبن الله مخلف وعده رسله إن الله عزيز ذو انتقام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 14, 'يوم تبدل الأرض غير الأرض والسماوات وبرزوا لله الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 14, 'وترى المجرمين يومئذ مقرنين في الأصفاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 14, 'سرابيلهم من قطران وتغشى وجوههم النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 14, 'ليجزي الله كل نفس ما كسبت إن الله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 14, 'هذا بلاغ للناس ولينذروا به وليعلموا أنما هو إله واحد وليذكر أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 15, 'بسم الله الرحمن الرحيم الر تلك آيات الكتاب وقرآن مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 15, 'ربما يود الذين كفروا لو كانوا مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 15, 'ذرهم يأكلوا ويتمتعوا ويلههم الأمل فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 15, 'وما أهلكنا من قرية إلا ولها كتاب معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 15, 'ما تسبق من أمة أجلها وما يستأخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 15, 'وقالوا يا أيها الذي نزل عليه الذكر إنك لمجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 15, 'لو ما تأتينا بالملائكة إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 15, 'ما ننزل الملائكة إلا بالحق وما كانوا إذا منظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 15, 'إنا نحن نزلنا الذكر وإنا له لحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 15, 'ولقد أرسلنا من قبلك في شيع الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 15, 'وما يأتيهم من رسول إلا كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 15, 'كذلك نسلكه في قلوب المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 15, 'لا يؤمنون به وقد خلت سنة الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 15, 'ولو فتحنا عليهم بابا من السماء فظلوا فيه يعرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 15, 'لقالوا إنما سكرت أبصارنا بل نحن قوم مسحورون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 15, 'ولقد جعلنا في السماء بروجا وزيناها للناظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 15, 'وحفظناها من كل شيطان رجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 15, 'إلا من استرق السمع فأتبعه شهاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 15, 'والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل شيء موزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 15, 'وجعلنا لكم فيها معايش ومن لستم له برازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 15, 'وإن من شيء إلا عندنا خزائنه وما ننزله إلا بقدر معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 15, 'وأرسلنا الرياح لواقح فأنزلنا من السماء ماء فأسقيناكموه وما أنتم له بخازنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 15, 'وإنا لنحن نحيي ونميت ونحن الوارثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 15, 'ولقد علمنا المستقدمين منكم ولقد علمنا المستأخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 15, 'وإن ربك هو يحشرهم إنه حكيم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 15, 'ولقد خلقنا الإنسان من صلصال من حمإ مسنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 15, 'والجان خلقناه من قبل من نار السموم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 15, 'وإذ قال ربك للملائكة إني خالق بشرا من صلصال من حمإ مسنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 15, 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 15, 'فسجد الملائكة كلهم أجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 15, 'إلا إبليس أبى أن يكون مع الساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 15, 'قال يا إبليس ما لك ألا تكون مع الساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 15, 'قال لم أكن لأسجد لبشر خلقته من صلصال من حمإ مسنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 15, 'قال فاخرج منها فإنك رجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 15, 'وإن عليك اللعنة إلى يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 15, 'قال رب فأنظرني إلى يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 15, 'قال فإنك من المنظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 15, 'إلى يوم الوقت المعلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 15, 'قال رب بما أغويتني لأزينن لهم في الأرض ولأغوينهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 15, 'إلا عبادك منهم المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 15, 'قال هذا صراط علي مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 15, 'إن عبادي ليس لك عليهم سلطان إلا من اتبعك من الغاوين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 15, 'وإن جهنم لموعدهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 15, 'لها سبعة أبواب لكل باب منهم جزء مقسوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 15, 'إن المتقين في جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 15, 'ادخلوها بسلام آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 15, 'ونزعنا ما في صدورهم من غل إخوانا على سرر متقابلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 15, 'لا يمسهم فيها نصب وما هم منها بمخرجين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 15, 'نبئ عبادي أني أنا الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 15, 'وأن عذابي هو العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 15, 'ونبئهم عن ضيف إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 15, 'إذ دخلوا عليه فقالوا سلاما قال إنا منكم وجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 15, 'قالوا لا توجل إنا نبشرك بغلام عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 15, 'قال أبشرتموني على أن مسني الكبر فبم تبشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 15, 'قالوا بشرناك بالحق فلا تكن من القانطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 15, 'قال ومن يقنط من رحمة ربه إلا الضالون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 15, 'قال فما خطبكم أيها المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 15, 'قالوا إنا أرسلنا إلى قوم مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 15, 'إلا آل لوط إنا لمنجوهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 15, 'إلا امرأته قدرنا إنها لمن الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 15, 'فلما جاء آل لوط المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 15, 'قال إنكم قوم منكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 15, 'قالوا بل جئناك بما كانوا فيه يمترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 15, 'وأتيناك بالحق وإنا لصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 15, 'فأسر بأهلك بقطع من الليل واتبع أدبارهم ولا يلتفت منكم أحد وامضوا حيث تؤمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 15, 'وقضينا إليه ذلك الأمر أن دابر هؤلاء مقطوع مصبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 15, 'وجاء أهل المدينة يستبشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 15, 'قال إن هؤلاء ضيفي فلا تفضحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 15, 'واتقوا الله ولا تخزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 15, 'قالوا أولم ننهك عن العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 15, 'قال هؤلاء بناتي إن كنتم فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 15, 'لعمرك إنهم لفي سكرتهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 15, 'فأخذتهم الصيحة مشرقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 15, 'فجعلنا عاليها سافلها وأمطرنا عليهم حجارة من سجيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 15, 'إن في ذلك لآيات للمتوسمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 15, 'وإنها لبسبيل مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 15, 'إن في ذلك لآية للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 15, 'وإن كان أصحاب الأيكة لظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 15, 'فانتقمنا منهم وإنهما لبإمام مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 15, 'ولقد كذب أصحاب الحجر المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 15, 'وآتيناهم آياتنا فكانوا عنها معرضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 15, 'وكانوا ينحتون من الجبال بيوتا آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 15, 'فأخذتهم الصيحة مصبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 15, 'فما أغنى عنهم ما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 15, 'وما خلقنا السماوات والأرض وما بينهما إلا بالحق وإن الساعة لآتية فاصفح الصفح الجميل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 15, 'إن ربك هو الخلاق العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 15, 'ولقد آتيناك سبعا من المثاني والقرآن العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 15, 'لا تمدن عينيك إلى ما متعنا به أزواجا منهم ولا تحزن عليهم واخفض جناحك للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 15, 'وقل إني أنا النذير المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 15, 'كما أنزلنا على المقتسمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 15, 'الذين جعلوا القرآن عضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 15, 'فوربك لنسألنهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 15, 'عما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 15, 'فاصدع بما تؤمر وأعرض عن المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 15, 'إنا كفيناك المستهزئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 15, 'الذين يجعلون مع الله إلها آخر فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 15, 'ولقد نعلم أنك يضيق صدرك بما يقولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 15, 'فسبح بحمد ربك وكن من الساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 15, 'واعبد ربك حتى يأتيك اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 16, 'بسم الله الرحمن الرحيم أتى أمر الله فلا تستعجلوه سبحانه وتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 16, 'ينزل الملائكة بالروح من أمره على من يشاء من عباده أن أنذروا أنه لا إله إلا أنا فاتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 16, 'خلق السماوات والأرض بالحق تعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 16, 'خلق الإنسان من نطفة فإذا هو خصيم مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 16, 'والأنعام خلقها لكم فيها دفء ومنافع ومنها تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 16, 'ولكم فيها جمال حين تريحون وحين تسرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 16, 'وتحمل أثقالكم إلى بلد لم تكونوا بالغيه إلا بشق الأنفس إن ربكم لرءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 16, 'والخيل والبغال والحمير لتركبوها وزينة ويخلق ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 16, 'وعلى الله قصد السبيل ومنها جائر ولو شاء لهداكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 16, 'هو الذي أنزل من السماء ماء لكم منه شراب ومنه شجر فيه تسيمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 16, 'ينبت لكم به الزرع والزيتون والنخيل والأعناب ومن كل الثمرات إن في ذلك لآية لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 16, 'وسخر لكم الليل والنهار والشمس والقمر والنجوم مسخرات بأمره إن في ذلك لآيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 16, 'وما ذرأ لكم في الأرض مختلفا ألوانه إن في ذلك لآية لقوم يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 16, 'وهو الذي سخر البحر لتأكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 16, 'وألقى في الأرض رواسي أن تميد بكم وأنهارا وسبلا لعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 16, 'وعلامات وبالنجم هم يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 16, 'أفمن يخلق كمن لا يخلق أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 16, 'وإن تعدوا نعمة الله لا تحصوها إن الله لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 16, 'والله يعلم ما تسرون وما تعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 16, 'والذين يدعون من دون الله لا يخلقون شيئا وهم يخلقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 16, 'أموات غير أحياء وما يشعرون أيان يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 16, 'إلهكم إله واحد فالذين لا يؤمنون بالآخرة قلوبهم منكرة وهم مستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 16, 'لا جرم أن الله يعلم ما يسرون وما يعلنون إنه لا يحب المستكبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 16, 'وإذا قيل لهم ماذا أنزل ربكم قالوا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 16, 'ليحملوا أوزارهم كاملة يوم القيامة ومن أوزار الذين يضلونهم بغير علم ألا ساء ما يزرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 16, 'قد مكر الذين من قبلهم فأتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم وأتاهم العذاب من حيث لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 16, 'ثم يوم القيامة يخزيهم ويقول أين شركائي الذين كنتم تشاقون فيهم قال الذين أوتوا العلم إن الخزي اليوم والسوء على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 16, 'الذين تتوفاهم الملائكة ظالمي أنفسهم فألقوا السلم ما كنا نعمل من سوء بلى إن الله عليم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 16, 'فادخلوا أبواب جهنم خالدين فيها فلبئس مثوى المتكبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 16, 'وقيل للذين اتقوا ماذا أنزل ربكم قالوا خيرا للذين أحسنوا في هذه الدنيا حسنة ولدار الآخرة خير ولنعم دار المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 16, 'جنات عدن يدخلونها تجري من تحتها الأنهار لهم فيها ما يشاءون كذلك يجزي الله المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 16, 'الذين تتوفاهم الملائكة طيبين يقولون سلام عليكم ادخلوا الجنة بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 16, 'هل ينظرون إلا أن تأتيهم الملائكة أو يأتي أمر ربك كذلك فعل الذين من قبلهم وما ظلمهم الله ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 16, 'فأصابهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 16, 'وقال الذين أشركوا لو شاء الله ما عبدنا من دونه من شيء نحن ولا آباؤنا ولا حرمنا من دونه من شيء كذلك فعل الذين من قبلهم فهل على الرسل إلا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 16, 'ولقد بعثنا في كل أمة رسولا أن اعبدوا الله واجتنبوا الطاغوت فمنهم من هدى الله ومنهم من حقت عليه الضلالة فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 16, 'إن تحرص على هداهم فإن الله لا يهدي من يضل وما لهم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 16, 'وأقسموا بالله جهد أيمانهم لا يبعث الله من يموت بلى وعدا عليه حقا ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 16, 'ليبين لهم الذي يختلفون فيه وليعلم الذين كفروا أنهم كانوا كاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 16, 'إنما قولنا لشيء إذا أردناه أن نقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 16, 'والذين هاجروا في الله من بعد ما ظلموا لنبوئنهم في الدنيا حسنة ولأجر الآخرة أكبر لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 16, 'الذين صبروا وعلى ربهم يتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 16, 'وما أرسلنا من قبلك إلا رجالا نوحي إليهم فاسألوا أهل الذكر إن كنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 16, 'بالبينات والزبر وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم ولعلهم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 16, 'أفأمن الذين مكروا السيئات أن يخسف الله بهم الأرض أو يأتيهم العذاب من حيث لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 16, 'أو يأخذهم في تقلبهم فما هم بمعجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 16, 'أو يأخذهم على تخوف فإن ربكم لرءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 16, 'أولم يروا إلى ما خلق الله من شيء يتفيأ ظلاله عن اليمين والشمائل سجدا لله وهم داخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 16, 'ولله يسجد ما في السماوات وما في الأرض من دابة والملائكة وهم لا يستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 16, 'يخافون ربهم من فوقهم ويفعلون ما يؤمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 16, 'وقال الله لا تتخذوا إلهين اثنين إنما هو إله واحد فإياي فارهبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 16, 'وله ما في السماوات والأرض وله الدين واصبا أفغير الله تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 16, 'وما بكم من نعمة فمن الله ثم إذا مسكم الضر فإليه تجأرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 16, 'ثم إذا كشف الضر عنكم إذا فريق منكم بربهم يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 16, 'ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 16, 'ويجعلون لما لا يعلمون نصيبا مما رزقناهم تالله لتسألن عما كنتم تفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 16, 'ويجعلون لله البنات سبحانه ولهم ما يشتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 16, 'وإذا بشر أحدهم بالأنثى ظل وجهه مسودا وهو كظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 16, 'يتوارى من القوم من سوء ما بشر به أيمسكه على هون أم يدسه في التراب ألا ساء ما يحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 16, 'للذين لا يؤمنون بالآخرة مثل السوء ولله المثل الأعلى وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 16, 'ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة ولكن يؤخرهم إلى أجل مسمى فإذا جاء أجلهم لا يستأخرون ساعة ولا يستقدمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 16, 'ويجعلون لله ما يكرهون وتصف ألسنتهم الكذب أن لهم الحسنى لا جرم أن لهم النار وأنهم مفرطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 16, 'تالله لقد أرسلنا إلى أمم من قبلك فزين لهم الشيطان أعمالهم فهو وليهم اليوم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 16, 'وما أنزلنا عليك الكتاب إلا لتبين لهم الذي اختلفوا فيه وهدى ورحمة لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 16, 'والله أنزل من السماء ماء فأحيا به الأرض بعد موتها إن في ذلك لآية لقوم يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 16, 'وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 16, 'ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا إن في ذلك لآية لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 16, 'وأوحى ربك إلى النحل أن اتخذي من الجبال بيوتا ومن الشجر ومما يعرشون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 16, 'ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا يخرج من بطونها شراب مختلف ألوانه فيه شفاء للناس إن في ذلك لآية لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 16, 'والله خلقكم ثم يتوفاكم ومنكم من يرد إلى أرذل العمر لكي لا يعلم بعد علم شيئا إن الله عليم قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 16, 'والله فضل بعضكم على بعض في الرزق فما الذين فضلوا برادي رزقهم على ما ملكت أيمانهم فهم فيه سواء أفبنعمة الله يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 16, 'والله جعل لكم من أنفسكم أزواجا وجعل لكم من أزواجكم بنين وحفدة ورزقكم من الطيبات أفبالباطل يؤمنون وبنعمت الله هم يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 16, 'ويعبدون من دون الله ما لا يملك لهم رزقا من السماوات والأرض شيئا ولا يستطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 16, 'فلا تضربوا لله الأمثال إن الله يعلم وأنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 16, 'ضرب الله مثلا عبدا مملوكا لا يقدر على شيء ومن رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا هل يستوون الحمد لله بل أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 16, 'وضرب الله مثلا رجلين أحدهما أبكم لا يقدر على شيء وهو كل على مولاه أينما يوجهه لا يأت بخير هل يستوي هو ومن يأمر بالعدل وهو على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 16, 'ولله غيب السماوات والأرض وما أمر الساعة إلا كلمح البصر أو هو أقرب إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 16, 'والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا وجعل لكم السمع والأبصار والأفئدة لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 16, 'ألم يروا إلى الطير مسخرات في جو السماء ما يمسكهن إلا الله إن في ذلك لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 16, 'والله جعل لكم من بيوتكم سكنا وجعل لكم من جلود الأنعام بيوتا تستخفونها يوم ظعنكم ويوم إقامتكم ومن أصوافها وأوبارها وأشعارها أثاثا ومتاعا إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 16, 'والله جعل لكم مما خلق ظلالا وجعل لكم من الجبال أكنانا وجعل لكم سرابيل تقيكم الحر وسرابيل تقيكم بأسكم كذلك يتم نعمته عليكم لعلكم تسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 16, 'فإن تولوا فإنما عليك البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 16, 'يعرفون نعمت الله ثم ينكرونها وأكثرهم الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 16, 'ويوم نبعث من كل أمة شهيدا ثم لا يؤذن للذين كفروا ولا هم يستعتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 16, 'وإذا رأى الذين ظلموا العذاب فلا يخفف عنهم ولا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 16, 'وإذا رأى الذين أشركوا شركاءهم قالوا ربنا هؤلاء شركاؤنا الذين كنا ندعو من دونك فألقوا إليهم القول إنكم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 16, 'وألقوا إلى الله يومئذ السلم وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 16, 'الذين كفروا وصدوا عن سبيل الله زدناهم عذابا فوق العذاب بما كانوا يفسدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 16, 'ويوم نبعث في كل أمة شهيدا عليهم من أنفسهم وجئنا بك شهيدا على هؤلاء ونزلنا عليك الكتاب تبيانا لكل شيء وهدى ورحمة وبشرى للمسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 16, 'إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 16, 'وأوفوا بعهد الله إذا عاهدتم ولا تنقضوا الأيمان بعد توكيدها وقد جعلتم الله عليكم كفيلا إن الله يعلم ما تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 16, 'ولا تكونوا كالتي نقضت غزلها من بعد قوة أنكاثا تتخذون أيمانكم دخلا بينكم أن تكون أمة هي أربى من أمة إنما يبلوكم الله به وليبينن لكم يوم القيامة ما كنتم فيه تختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 16, 'ولو شاء الله لجعلكم أمة واحدة ولكن يضل من يشاء ويهدي من يشاء ولتسألن عما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 16, 'ولا تتخذوا أيمانكم دخلا بينكم فتزل قدم بعد ثبوتها وتذوقوا السوء بما صددتم عن سبيل الله ولكم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 16, 'ولا تشتروا بعهد الله ثمنا قليلا إنما عند الله هو خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 16, 'ما عندكم ينفد وما عند الله باق ولنجزين الذين صبروا أجرهم بأحسن ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 16, 'من عمل صالحا من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة ولنجزينهم أجرهم بأحسن ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 16, 'فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 16, 'إنه ليس له سلطان على الذين آمنوا وعلى ربهم يتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 16, 'إنما سلطانه على الذين يتولونه والذين هم به مشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 16, 'وإذا بدلنا آية مكان آية والله أعلم بما ينزل قالوا إنما أنت مفتر بل أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 16, 'قل نزله روح القدس من ربك بالحق ليثبت الذين آمنوا وهدى وبشرى للمسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 16, 'ولقد نعلم أنهم يقولون إنما يعلمه بشر لسان الذي يلحدون إليه أعجمي وهذا لسان عربي مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 16, 'إن الذين لا يؤمنون بآيات الله لا يهديهم الله ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 16, 'إنما يفتري الكذب الذين لا يؤمنون بآيات الله وأولئك هم الكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 16, 'من كفر بالله من بعد إيمانه إلا من أكره وقلبه مطمئن بالإيمان ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 16, 'ذلك بأنهم استحبوا الحياة الدنيا على الآخرة وأن الله لا يهدي القوم الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 16, 'أولئك الذين طبع الله على قلوبهم وسمعهم وأبصارهم وأولئك هم الغافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 16, 'لا جرم أنهم في الآخرة هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 16, 'ثم إن ربك للذين هاجروا من بعد ما فتنوا ثم جاهدوا وصبروا إن ربك من بعدها لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 16, 'يوم تأتي كل نفس تجادل عن نفسها وتوفى كل نفس ما عملت وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 16, 'وضرب الله مثلا قرية كانت آمنة مطمئنة يأتيها رزقها رغدا من كل مكان فكفرت بأنعم الله فأذاقها الله لباس الجوع والخوف بما كانوا يصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 16, 'ولقد جاءهم رسول منهم فكذبوه فأخذهم العذاب وهم ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 16, 'فكلوا مما رزقكم الله حلالا طيبا واشكروا نعمت الله إن كنتم إياه تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 16, 'إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به فمن اضطر غير باغ ولا عاد فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 16, 'ولا تقولوا لما تصف ألسنتكم الكذب هذا حلال وهذا حرام لتفتروا على الله الكذب إن الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 16, 'متاع قليل ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 16, 'وعلى الذين هادوا حرمنا ما قصصنا عليك من قبل وما ظلمناهم ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 16, 'ثم إن ربك للذين عملوا السوء بجهالة ثم تابوا من بعد ذلك وأصلحوا إن ربك من بعدها لغفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 16, 'إن إبراهيم كان أمة قانتا لله حنيفا ولم يك من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 16, 'شاكرا لأنعمه اجتباه وهداه إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 16, 'وآتيناه في الدنيا حسنة وإنه في الآخرة لمن الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 16, 'ثم أوحينا إليك أن اتبع ملة إبراهيم حنيفا وما كان من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 16, 'إنما جعل السبت على الذين اختلفوا فيه وإن ربك ليحكم بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 16, 'ادع إلى سبيل ربك بالحكمة والموعظة الحسنة وجادلهم بالتي هي أحسن إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 16, 'وإن عاقبتم فعاقبوا بمثل ما عوقبتم به ولئن صبرتم لهو خير للصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 16, 'واصبر وما صبرك إلا بالله ولا تحزن عليهم ولا تك في ضيق مما يمكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 16, 'إن الله مع الذين اتقوا والذين هم محسنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 17, 'بسم الله الرحمن الرحيم سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصى الذي باركنا حوله لنريه من آياتنا إنه هو السميع البصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 17, 'وآتينا موسى الكتاب وجعلناه هدى لبني إسرائيل ألا تتخذوا من دوني وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 17, 'ذرية من حملنا مع نوح إنه كان عبدا شكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 17, 'وقضينا إلى بني إسرائيل في الكتاب لتفسدن في الأرض مرتين ولتعلن علوا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 17, 'فإذا جاء وعد أولاهما بعثنا عليكم عبادا لنا أولي بأس شديد فجاسوا خلال الديار وكان وعدا مفعولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 17, 'ثم رددنا لكم الكرة عليهم وأمددناكم بأموال وبنين وجعلناكم أكثر نفيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 17, 'إن أحسنتم أحسنتم لأنفسكم وإن أسأتم فلها فإذا جاء وعد الآخرة ليسوءوا وجوهكم وليدخلوا المسجد كما دخلوه أول مرة وليتبروا ما علوا تتبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 17, 'عسى ربكم أن يرحمكم وإن عدتم عدنا وجعلنا جهنم للكافرين حصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 17, 'إن هذا القرآن يهدي للتي هي أقوم ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 17, 'وأن الذين لا يؤمنون بالآخرة أعتدنا لهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 17, 'ويدع الإنسان بالشر دعاءه بالخير وكان الإنسان عجولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 17, 'وجعلنا الليل والنهار آيتين فمحونا آية الليل وجعلنا آية النهار مبصرة لتبتغوا فضلا من ربكم ولتعلموا عدد السنين والحساب وكل شيء فصلناه تفصيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 17, 'وكل إنسان ألزمناه طائره في عنقه ونخرج له يوم القيامة كتابا يلقاه منشورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 17, 'اقرأ كتابك كفى بنفسك اليوم عليك حسيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 17, 'من اهتدى فإنما يهتدي لنفسه ومن ضل فإنما يضل عليها ولا تزر وازرة وزر أخرى وما كنا معذبين حتى نبعث رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 17, 'وإذا أردنا أن نهلك قرية أمرنا مترفيها ففسقوا فيها فحق عليها القول فدمرناها تدميرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 17, 'وكم أهلكنا من القرون من بعد نوح وكفى بربك بذنوب عباده خبيرا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 17, 'من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا له جهنم يصلاها مذموما مدحورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 17, 'ومن أراد الآخرة وسعى لها سعيها وهو مؤمن فأولئك كان سعيهم مشكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 17, 'كلا نمد هؤلاء وهؤلاء من عطاء ربك وما كان عطاء ربك محظورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 17, 'انظر كيف فضلنا بعضهم على بعض وللآخرة أكبر درجات وأكبر تفضيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 17, 'لا تجعل مع الله إلها آخر فتقعد مذموما مخذولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 17, 'وقضى ربك ألا تعبدوا إلا إياه وبالوالدين إحسانا إما يبلغن عندك الكبر أحدهما أو كلاهما فلا تقل لهما أف ولا تنهرهما وقل لهما قولا كريما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 17, 'واخفض لهما جناح الذل من الرحمة وقل رب ارحمهما كما ربياني صغيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 17, 'ربكم أعلم بما في نفوسكم إن تكونوا صالحين فإنه كان للأوابين غفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 17, 'وآت ذا القربى حقه والمسكين وابن السبيل ولا تبذر تبذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 17, 'إن المبذرين كانوا إخوان الشياطين وكان الشيطان لربه كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 17, 'وإما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 17, 'ولا تجعل يدك مغلولة إلى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 17, 'إن ربك يبسط الرزق لمن يشاء ويقدر إنه كان بعباده خبيرا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 17, 'ولا تقتلوا أولادكم خشية إملاق نحن نرزقهم وإياكم إن قتلهم كان خطئا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 17, 'ولا تقربوا الزنا إنه كان فاحشة وساء سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 17, 'ولا تقتلوا النفس التي حرم الله إلا بالحق ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل إنه كان منصورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 17, 'ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده وأوفوا بالعهد إن العهد كان مسئولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 17, 'وأوفوا الكيل إذا كلتم وزنوا بالقسطاس المستقيم ذلك خير وأحسن تأويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 17, 'ولا تقف ما ليس لك به علم إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 17, 'ولا تمش في الأرض مرحا إنك لن تخرق الأرض ولن تبلغ الجبال طولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 17, 'كل ذلك كان سيئه عند ربك مكروها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 17, 'ذلك مما أوحى إليك ربك من الحكمة ولا تجعل مع الله إلها آخر فتلقى في جهنم ملوما مدحورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 17, 'أفأصفاكم ربكم بالبنين واتخذ من الملائكة إناثا إنكم لتقولون قولا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 17, 'ولقد صرفنا في هذا القرآن ليذكروا وما يزيدهم إلا نفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 17, 'قل لو كان معه آلهة كما يقولون إذا لابتغوا إلى ذي العرش سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 17, 'سبحانه وتعالى عما يقولون علوا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 17, 'تسبح له السماوات السبع والأرض ومن فيهن وإن من شيء إلا يسبح بحمده ولكن لا تفقهون تسبيحهم إنه كان حليما غفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 17, 'وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 17, 'وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإذا ذكرت ربك في القرآن وحده ولوا على أدبارهم نفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 17, 'نحن أعلم بما يستمعون به إذ يستمعون إليك وإذ هم نجوى إذ يقول الظالمون إن تتبعون إلا رجلا مسحورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 17, 'انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 17, 'وقالوا أإذا كنا عظاما ورفاتا أإنا لمبعوثون خلقا جديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 17, 'قل كونوا حجارة أو حديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 17, 'أو خلقا مما يكبر في صدوركم فسيقولون من يعيدنا قل الذي فطركم أول مرة فسينغضون إليك رءوسهم ويقولون متى هو قل عسى أن يكون قريبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 17, 'يوم يدعوكم فتستجيبون بحمده وتظنون إن لبثتم إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 17, 'وقل لعبادي يقولوا التي هي أحسن إن الشيطان ينزغ بينهم إن الشيطان كان للإنسان عدوا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 17, 'ربكم أعلم بكم إن يشأ يرحمكم أو إن يشأ يعذبكم وما أرسلناك عليهم وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 17, 'وربك أعلم بمن في السماوات والأرض ولقد فضلنا بعض النبيين على بعض وآتينا داوود زبورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 17, 'قل ادعوا الذين زعمتم من دونه فلا يملكون كشف الضر عنكم ولا تحويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 17, 'أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب ويرجون رحمته ويخافون عذابه إن عذاب ربك كان محذورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 17, 'وإن من قرية إلا نحن مهلكوها قبل يوم القيامة أو معذبوها عذابا شديدا كان ذلك في الكتاب مسطورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 17, 'وما منعنا أن نرسل بالآيات إلا أن كذب بها الأولون وآتينا ثمود الناقة مبصرة فظلموا بها وما نرسل بالآيات إلا تخويفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 17, 'وإذ قلنا لك إن ربك أحاط بالناس وما جعلنا الرؤيا التي أريناك إلا فتنة للناس والشجرة الملعونة في القرآن ونخوفهم فما يزيدهم إلا طغيانا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 17, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس قال أأسجد لمن خلقت طينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 17, 'قال أرأيتك هذا الذي كرمت علي لئن أخرتن إلى يوم القيامة لأحتنكن ذريته إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 17, 'قال اذهب فمن تبعك منهم فإن جهنم جزاؤكم جزاء موفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 17, 'واستفزز من استطعت منهم بصوتك وأجلب عليهم بخيلك ورجلك وشاركهم في الأموال والأولاد وعدهم وما يعدهم الشيطان إلا غرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 17, 'إن عبادي ليس لك عليهم سلطان وكفى بربك وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 17, 'ربكم الذي يزجي لكم الفلك في البحر لتبتغوا من فضله إنه كان بكم رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 17, 'وإذا مسكم الضر في البحر ضل من تدعون إلا إياه فلما نجاكم إلى البر أعرضتم وكان الإنسان كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 17, 'أفأمنتم أن يخسف بكم جانب البر أو يرسل عليكم حاصبا ثم لا تجدوا لكم وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 17, 'أم أمنتم أن يعيدكم فيه تارة أخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ثم لا تجدوا لكم علينا به تبيعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 17, 'ولقد كرمنا بني آدم وحملناهم في البر والبحر ورزقناهم من الطيبات وفضلناهم على كثير ممن خلقنا تفضيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 17, 'يوم ندعو كل أناس بإمامهم فمن أوتي كتابه بيمينه فأولئك يقرءون كتابهم ولا يظلمون فتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 17, 'ومن كان في هذه أعمى فهو في الآخرة أعمى وأضل سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 17, 'وإن كادوا ليفتنونك عن الذي أوحينا إليك لتفتري علينا غيره وإذا لاتخذوك خليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 17, 'ولولا أن ثبتناك لقد كدت تركن إليهم شيئا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 17, 'إذا لأذقناك ضعف الحياة وضعف الممات ثم لا تجد لك علينا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 17, 'وإن كادوا ليستفزونك من الأرض ليخرجوك منها وإذا لا يلبثون خلافك إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 17, 'سنة من قد أرسلنا قبلك من رسلنا ولا تجد لسنتنا تحويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 17, 'أقم الصلاة لدلوك الشمس إلى غسق الليل وقرآن الفجر إن قرآن الفجر كان مشهودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 17, 'ومن الليل فتهجد به نافلة لك عسى أن يبعثك ربك مقاما محمودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 17, 'وقل رب أدخلني مدخل صدق وأخرجني مخرج صدق واجعل لي من لدنك سلطانا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 17, 'وقل جاء الحق وزهق الباطل إن الباطل كان زهوقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 17, 'وننزل من القرآن ما هو شفاء ورحمة للمؤمنين ولا يزيد الظالمين إلا خسارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 17, 'وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر كان يئوسا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 17, 'قل كل يعمل على شاكلته فربكم أعلم بمن هو أهدى سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 17, 'ويسألونك عن الروح قل الروح من أمر ربي وما أوتيتم من العلم إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 17, 'ولئن شئنا لنذهبن بالذي أوحينا إليك ثم لا تجد لك به علينا وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 17, 'إلا رحمة من ربك إن فضله كان عليك كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 17, 'قل لئن اجتمعت الإنس والجن على أن يأتوا بمثل هذا القرآن لا يأتون بمثله ولو كان بعضهم لبعض ظهيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 17, 'ولقد صرفنا للناس في هذا القرآن من كل مثل فأبى أكثر الناس إلا كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 17, 'وقالوا لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 17, 'أو تكون لك جنة من نخيل وعنب فتفجر الأنهار خلالها تفجيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 17, 'أو تسقط السماء كما زعمت علينا كسفا أو تأتي بالله والملائكة قبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 17, 'أو يكون لك بيت من زخرف أو ترقى في السماء ولن نؤمن لرقيك حتى تنزل علينا كتابا نقرؤه قل سبحان ربي هل كنت إلا بشرا رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 17, 'وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا أبعث الله بشرا رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 17, 'قل لو كان في الأرض ملائكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 17, 'قل كفى بالله شهيدا بيني وبينكم إنه كان بعباده خبيرا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 17, 'ومن يهد الله فهو المهتد ومن يضلل فلن تجد لهم أولياء من دونه ونحشرهم يوم القيامة على وجوههم عميا وبكما وصما مأواهم جهنم كلما خبت زدناهم سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 17, 'ذلك جزاؤهم بأنهم كفروا بآياتنا وقالوا أإذا كنا عظاما ورفاتا أإنا لمبعوثون خلقا جديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 17, 'أولم يروا أن الله الذي خلق السماوات والأرض قادر على أن يخلق مثلهم وجعل لهم أجلا لا ريب فيه فأبى الظالمون إلا كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 17, 'قل لو أنتم تملكون خزائن رحمة ربي إذا لأمسكتم خشية الإنفاق وكان الإنسان قتورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 17, 'ولقد آتينا موسى تسع آيات بينات فاسأل بني إسرائيل إذ جاءهم فقال له فرعون إني لأظنك يا موسى مسحورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 17, 'قال لقد علمت ما أنزل هؤلاء إلا رب السماوات والأرض بصائر وإني لأظنك يا فرعون مثبورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 17, 'فأراد أن يستفزهم من الأرض فأغرقناه ومن معه جميعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 17, 'وقلنا من بعده لبني إسرائيل اسكنوا الأرض فإذا جاء وعد الآخرة جئنا بكم لفيفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 17, 'وبالحق أنزلناه وبالحق نزل وما أرسلناك إلا مبشرا ونذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 17, 'وقرآنا فرقناه لتقرأه على الناس على مكث ونزلناه تنزيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 17, 'قل آمنوا به أو لا تؤمنوا إن الذين أوتوا العلم من قبله إذا يتلى عليهم يخرون للأذقان سجدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 17, 'ويقولون سبحان ربنا إن كان وعد ربنا لمفعولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 17, 'ويخرون للأذقان يبكون ويزيدهم خشوعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 17, 'قل ادعوا الله أو ادعوا الرحمن أيا ما تدعوا فله الأسماء الحسنى ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 17, 'وقل الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك ولم يكن له ولي من الذل وكبره تكبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 18, 'بسم الله الرحمن الرحيم الحمد لله الذي أنزل على عبده الكتاب ولم يجعل له عوجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 18, 'قيما لينذر بأسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا حسنا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 18, 'ماكثين فيه أبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 18, 'وينذر الذين قالوا اتخذ الله ولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 18, 'ما لهم به من علم ولا لآبائهم كبرت كلمة تخرج من أفواههم إن يقولون إلا كذبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 18, 'فلعلك باخع نفسك على آثارهم إن لم يؤمنوا بهذا الحديث أسفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 18, 'إنا جعلنا ما على الأرض زينة لها لنبلوهم أيهم أحسن عملا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 18, 'وإنا لجاعلون ما عليها صعيدا جرزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 18, 'أم حسبت أن أصحاب الكهف والرقيم كانوا من آياتنا عجبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 18, 'إذ أوى الفتية إلى الكهف فقالوا ربنا آتنا من لدنك رحمة وهيئ لنا من أمرنا رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 18, 'فضربنا على آذانهم في الكهف سنين عددا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 18, 'ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 18, 'نحن نقص عليك نبأهم بالحق إنهم فتية آمنوا بربهم وزدناهم هدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 18, 'وربطنا على قلوبهم إذ قاموا فقالوا ربنا رب السماوات والأرض لن ندعو من دونه إلها لقد قلنا إذا شططا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 18, 'هؤلاء قومنا اتخذوا من دونه آلهة لولا يأتون عليهم بسلطان بين فمن أظلم ممن افترى على الله كذبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 18, 'وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 18, 'وترى الشمس إذا طلعت تزاور عن كهفهم ذات اليمين وإذا غربت تقرضهم ذات الشمال وهم في فجوة منه ذلك من آيات الله من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 18, 'وتحسبهم أيقاظا وهم رقود ونقلبهم ذات اليمين وذات الشمال وكلبهم باسط ذراعيه بالوصيد لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 18, 'وكذلك بعثناهم ليتساءلوا بينهم قال قائل منهم كم لبثتم قالوا لبثنا يوما أو بعض يوم قالوا ربكم أعلم بما لبثتم فابعثوا أحدكم بورقكم هذه إلى المدينة فلينظر أيها أزكى طعاما فليأتكم برزق منه وليتلطف ولا يشعرن بكم أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 18, 'إنهم إن يظهروا عليكم يرجموكم أو يعيدوكم في ملتهم ولن تفلحوا إذا أبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 18, 'وكذلك أعثرنا عليهم ليعلموا أن وعد الله حق وأن الساعة لا ريب فيها إذ يتنازعون بينهم أمرهم فقالوا ابنوا عليهم بنيانا ربهم أعلم بهم قال الذين غلبوا على أمرهم لنتخذن عليهم مسجدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 18, 'سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ويقولون سبعة وثامنهم كلبهم قل ربي أعلم بعدتهم ما يعلمهم إلا قليل فلا تمار فيهم إلا مراء ظاهرا ولا تستفت فيهم منهم أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 18, 'ولا تقولن لشيء إني فاعل ذلك غدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 18, 'إلا أن يشاء الله واذكر ربك إذا نسيت وقل عسى أن يهدين ربي لأقرب من هذا رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 18, 'ولبثوا في كهفهم ثلاث مائة سنين وازدادوا تسعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 18, 'قل الله أعلم بما لبثوا له غيب السماوات والأرض أبصر به وأسمع ما لهم من دونه من ولي ولا يشرك في حكمه أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 18, 'واتل ما أوحي إليك من كتاب ربك لا مبدل لكلماته ولن تجد من دونه ملتحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 18, 'واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه ولا تعد عيناك عنهم تريد زينة الحياة الدنيا ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه وكان أمره فرطا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 18, 'وقل الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر إنا أعتدنا للظالمين نارا أحاط بهم سرادقها وإن يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 18, 'إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 18, 'أولئك لهم جنات عدن تجري من تحتهم الأنهار يحلون فيها من أساور من ذهب ويلبسون ثيابا خضرا من سندس وإستبرق متكئين فيها على الأرائك نعم الثواب وحسنت مرتفقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 18, 'واضرب لهم مثلا رجلين جعلنا لأحدهما جنتين من أعناب وحففناهما بنخل وجعلنا بينهما زرعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 18, 'كلتا الجنتين آتت أكلها ولم تظلم منه شيئا وفجرنا خلالهما نهرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 18, 'وكان له ثمر فقال لصاحبه وهو يحاوره أنا أكثر منك مالا وأعز نفرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 18, 'ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 18, 'وما أظن الساعة قائمة ولئن رددت إلى ربي لأجدن خيرا منها منقلبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 18, 'قال له صاحبه وهو يحاوره أكفرت بالذي خلقك من تراب ثم من نطفة ثم سواك رجلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 18, 'لكنا هو الله ربي ولا أشرك بربي أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 18, 'ولولا إذ دخلت جنتك قلت ما شاء الله لا قوة إلا بالله إن ترن أنا أقل منك مالا وولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 18, 'فعسى ربي أن يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 18, 'أو يصبح ماؤها غورا فلن تستطيع له طلبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 18, 'وأحيط بثمره فأصبح يقلب كفيه على ما أنفق فيها وهي خاوية على عروشها ويقول يا ليتني لم أشرك بربي أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 18, 'ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 18, 'هنالك الولاية لله الحق هو خير ثوابا وخير عقبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 18, 'واضرب لهم مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض فأصبح هشيما تذروه الرياح وكان الله على كل شيء مقتدرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 18, 'المال والبنون زينة الحياة الدنيا والباقيات الصالحات خير عند ربك ثوابا وخير أملا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 18, 'ويوم نسير الجبال وترى الأرض بارزة وحشرناهم فلم نغادر منهم أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 18, 'وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم أول مرة بل زعمتم ألن نجعل لكم موعدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 18, 'ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها ووجدوا ما عملوا حاضرا ولا يظلم ربك أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 18, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس كان من الجن ففسق عن أمر ربه أفتتخذونه وذريته أولياء من دوني وهم لكم عدو بئس للظالمين بدلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 18, 'ما أشهدتهم خلق السماوات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 18, 'ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 18, 'ورأى المجرمون النار فظنوا أنهم مواقعوها ولم يجدوا عنها مصرفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 18, 'ولقد صرفنا في هذا القرآن للناس من كل مثل وكان الإنسان أكثر شيء جدلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 18, 'وما منع الناس أن يؤمنوا إذ جاءهم الهدى ويستغفروا ربهم إلا أن تأتيهم سنة الأولين أو يأتيهم العذاب قبلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 18, 'وما نرسل المرسلين إلا مبشرين ومنذرين ويجادل الذين كفروا بالباطل ليدحضوا به الحق واتخذوا آياتي وما أنذروا هزوا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 18, 'ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه إنا جعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن تدعهم إلى الهدى فلن يهتدوا إذا أبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 18, 'وربك الغفور ذو الرحمة لو يؤاخذهم بما كسبوا لعجل لهم العذاب بل لهم موعد لن يجدوا من دونه موئلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 18, 'وتلك القرى أهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 18, 'وإذ قال موسى لفتاه لا أبرح حتى أبلغ مجمع البحرين أو أمضي حقبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 18, 'فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله في البحر سربا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 18, 'فلما جاوزا قال لفتاه آتنا غداءنا لقد لقينا من سفرنا هذا نصبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 18, 'قال أرأيت إذ أوينا إلى الصخرة فإني نسيت الحوت وما أنسانيه إلا الشيطان أن أذكره واتخذ سبيله في البحر عجبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 18, 'قال ذلك ما كنا نبغ فارتدا على آثارهما قصصا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 18, 'فوجدا عبدا من عبادنا آتيناه رحمة من عندنا وعلمناه من لدنا علما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 18, 'قال له موسى هل أتبعك على أن تعلمن مما علمت رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 18, 'قال إنك لن تستطيع معي صبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 18, 'وكيف تصبر على ما لم تحط به خبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 18, 'قال ستجدني إن شاء الله صابرا ولا أعصي لك أمرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 18, 'قال فإن اتبعتني فلا تسألني عن شيء حتى أحدث لك منه ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 18, 'فانطلقا حتى إذا ركبا في السفينة خرقها قال أخرقتها لتغرق أهلها لقد جئت شيئا إمرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 18, 'قال ألم أقل إنك لن تستطيع معي صبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 18, 'قال لا تؤاخذني بما نسيت ولا ترهقني من أمري عسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 18, 'فانطلقا حتى إذا لقيا غلاما فقتله قال أقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 18, 'قال ألم أقل لك إنك لن تستطيع معي صبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 18, 'قال إن سألتك عن شيء بعدها فلا تصاحبني قد بلغت من لدني عذرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 18, 'فانطلقا حتى إذا أتيا أهل قرية استطعما أهلها فأبوا أن يضيفوهما فوجدا فيها جدارا يريد أن ينقض فأقامه قال لو شئت لاتخذت عليه أجرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 18, 'قال هذا فراق بيني وبينك سأنبئك بتأويل ما لم تستطع عليه صبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 18, 'أما السفينة فكانت لمساكين يعملون في البحر فأردت أن أعيبها وكان وراءهم ملك يأخذ كل سفينة غصبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 18, 'وأما الغلام فكان أبواه مؤمنين فخشينا أن يرهقهما طغيانا وكفرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 18, 'فأردنا أن يبدلهما ربهما خيرا منه زكاة وأقرب رحما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 18, 'وأما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز لهما وكان أبوهما صالحا فأراد ربك أن يبلغا أشدهما ويستخرجا كنزهما رحمة من ربك وما فعلته عن أمري ذلك تأويل ما لم تسطع عليه صبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 18, 'ويسألونك عن ذي القرنين قل سأتلو عليكم منه ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 18, 'إنا مكنا له في الأرض وآتيناه من كل شيء سببا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 18, 'فأتبع سببا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 18, 'حتى إذا بلغ مغرب الشمس وجدها تغرب في عين حمئة ووجد عندها قوما قلنا يا ذا القرنين إما أن تعذب وإما أن تتخذ فيهم حسنا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 18, 'قال أما من ظلم فسوف نعذبه ثم يرد إلى ربه فيعذبه عذابا نكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 18, 'وأما من آمن وعمل صالحا فله جزاء الحسنى وسنقول له من أمرنا يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 18, 'ثم أتبع سببا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 18, 'حتى إذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 18, 'كذلك وقد أحطنا بما لديه خبرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 18, 'ثم أتبع سببا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 18, 'حتى إذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 18, 'قالوا يا ذا القرنين إن يأجوج ومأجوج مفسدون في الأرض فهل نجعل لك خرجا على أن تجعل بيننا وبينهم سدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 18, 'قال ما مكني فيه ربي خير فأعينوني بقوة أجعل بينكم وبينهم ردما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 18, 'آتوني زبر الحديد حتى إذا ساوى بين الصدفين قال انفخوا حتى إذا جعله نارا قال آتوني أفرغ عليه قطرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 18, 'فما اسطاعوا أن يظهروه وما استطاعوا له نقبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 18, 'قال هذا رحمة من ربي فإذا جاء وعد ربي جعله دكاء وكان وعد ربي حقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 18, 'وتركنا بعضهم يومئذ يموج في بعض ونفخ في الصور فجمعناهم جمعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 18, 'وعرضنا جهنم يومئذ للكافرين عرضا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 18, 'الذين كانت أعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 18, 'أفحسب الذين كفروا أن يتخذوا عبادي من دوني أولياء إنا أعتدنا جهنم للكافرين نزلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 18, 'قل هل ننبئكم بالأخسرين أعمالا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 18, 'الذين ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 18, 'أولئك الذين كفروا بآيات ربهم ولقائه فحبطت أعمالهم فلا نقيم لهم يوم القيامة وزنا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 18, 'ذلك جزاؤهم جهنم بما كفروا واتخذوا آياتي ورسلي هزوا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 18, 'إن الذين آمنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 18, 'خالدين فيها لا يبغون عنها حولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 18, 'قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله مددا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 18, 'قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 19, 'بسم الله الرحمن الرحيم كهيعص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 19, 'ذكر رحمت ربك عبده زكريا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 19, 'إذ نادى ربه نداء خفيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 19, 'قال رب إني وهن العظم مني واشتعل الرأس شيبا ولم أكن بدعائك رب شقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 19, 'وإني خفت الموالي من ورائي وكانت امرأتي عاقرا فهب لي من لدنك وليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 19, 'يرثني ويرث من آل يعقوب واجعله رب رضيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 19, 'يا زكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 19, 'قال رب أنى يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 19, 'قال كذلك قال ربك هو علي هين وقد خلقتك من قبل ولم تك شيئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 19, 'قال رب اجعل لي آية قال آيتك ألا تكلم الناس ثلاث ليال سويا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 19, 'فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 19, 'يا يحيى خذ الكتاب بقوة وآتيناه الحكم صبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 19, 'وحنانا من لدنا وزكاة وكان تقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 19, 'وبرا بوالديه ولم يكن جبارا عصيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 19, 'وسلام عليه يوم ولد ويوم يموت ويوم يبعث حيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 19, 'واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 19, 'فاتخذت من دونهم حجابا فأرسلنا إليها روحنا فتمثل لها بشرا سويا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 19, 'قالت إني أعوذ بالرحمن منك إن كنت تقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 19, 'قال إنما أنا رسول ربك لأهب لك غلاما زكيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 19, 'قالت أنى يكون لي غلام ولم يمسسني بشر ولم أك بغيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 19, 'قال كذلك قال ربك هو علي هين ولنجعله آية للناس ورحمة منا وكان أمرا مقضيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 19, 'فحملته فانتبذت به مكانا قصيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 19, 'فأجاءها المخاض إلى جذع النخلة قالت يا ليتني مت قبل هذا وكنت نسيا منسيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 19, 'فناداها من تحتها ألا تحزني قد جعل ربك تحتك سريا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 19, 'وهزي إليك بجذع النخلة تساقط عليك رطبا جنيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 19, 'فكلي واشربي وقري عينا فإما ترين من البشر أحدا فقولي إني نذرت للرحمن صوما فلن أكلم اليوم إنسيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 19, 'فأتت به قومها تحمله قالوا يا مريم لقد جئت شيئا فريا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 19, 'يا أخت هارون ما كان أبوك امرأ سوء وما كانت أمك بغيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 19, 'فأشارت إليه قالوا كيف نكلم من كان في المهد صبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 19, 'قال إني عبد الله آتاني الكتاب وجعلني نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 19, 'وجعلني مباركا أين ما كنت وأوصاني بالصلاة والزكاة ما دمت حيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 19, 'وبرا بوالدتي ولم يجعلني جبارا شقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 19, 'والسلام علي يوم ولدت ويوم أموت ويوم أبعث حيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 19, 'ذلك عيسى ابن مريم قول الحق الذي فيه يمترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 19, 'ما كان لله أن يتخذ من ولد سبحانه إذا قضى أمرا فإنما يقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 19, 'وإن الله ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 19, 'فاختلف الأحزاب من بينهم فويل للذين كفروا من مشهد يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 19, 'أسمع بهم وأبصر يوم يأتوننا لكن الظالمون اليوم في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 19, 'وأنذرهم يوم الحسرة إذ قضي الأمر وهم في غفلة وهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 19, 'إنا نحن نرث الأرض ومن عليها وإلينا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 19, 'واذكر في الكتاب إبراهيم إنه كان صديقا نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 19, 'إذ قال لأبيه يا أبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 19, 'يا أبت إني قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا سويا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 19, 'يا أبت لا تعبد الشيطان إن الشيطان كان للرحمن عصيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 19, 'يا أبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 19, 'قال أراغب أنت عن آلهتي يا إبراهيم لئن لم تنته لأرجمنك واهجرني مليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 19, 'قال سلام عليك سأستغفر لك ربي إنه كان بي حفيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 19, 'وأعتزلكم وما تدعون من دون الله وأدعو ربي عسى ألا أكون بدعاء ربي شقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 19, 'فلما اعتزلهم وما يعبدون من دون الله وهبنا له إسحاق ويعقوب وكلا جعلنا نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 19, 'ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 19, 'واذكر في الكتاب موسى إنه كان مخلصا وكان رسولا نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 19, 'وناديناه من جانب الطور الأيمن وقربناه نجيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 19, 'ووهبنا له من رحمتنا أخاه هارون نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 19, 'واذكر في الكتاب إسماعيل إنه كان صادق الوعد وكان رسولا نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 19, 'وكان يأمر أهله بالصلاة والزكاة وكان عند ربه مرضيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 19, 'واذكر في الكتاب إدريس إنه كان صديقا نبيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 19, 'ورفعناه مكانا عليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 19, 'أولئك الذين أنعم الله عليهم من النبيين من ذرية آدم وممن حملنا مع نوح ومن ذرية إبراهيم وإسرائيل وممن هدينا واجتبينا إذا تتلى عليهم آيات الرحمن خروا سجدا وبكيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 19, 'فخلف من بعدهم خلف أضاعوا الصلاة واتبعوا الشهوات فسوف يلقون غيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 19, 'إلا من تاب وآمن وعمل صالحا فأولئك يدخلون الجنة ولا يظلمون شيئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 19, 'جنات عدن التي وعد الرحمن عباده بالغيب إنه كان وعده مأتيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 19, 'لا يسمعون فيها لغوا إلا سلاما ولهم رزقهم فيها بكرة وعشيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 19, 'تلك الجنة التي نورث من عبادنا من كان تقيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 19, 'وما نتنزل إلا بأمر ربك له ما بين أيدينا وما خلفنا وما بين ذلك وما كان ربك نسيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 19, 'رب السماوات والأرض وما بينهما فاعبده واصطبر لعبادته هل تعلم له سميا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 19, 'ويقول الإنسان أإذا ما مت لسوف أخرج حيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 19, 'أولا يذكر الإنسان أنا خلقناه من قبل ولم يك شيئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 19, 'فوربك لنحشرنهم والشياطين ثم لنحضرنهم حول جهنم جثيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 19, 'ثم لننزعن من كل شيعة أيهم أشد على الرحمن عتيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 19, 'ثم لنحن أعلم بالذين هم أولى بها صليا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 19, 'وإن منكم إلا واردها كان على ربك حتما مقضيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 19, 'ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 19, 'وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للذين آمنوا أي الفريقين خير مقاما وأحسن نديا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 19, 'وكم أهلكنا قبلهم من قرن هم أحسن أثاثا ورئيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 19, 'قل من كان في الضلالة فليمدد له الرحمن مدا حتى إذا رأوا ما يوعدون إما العذاب وإما الساعة فسيعلمون من هو شر مكانا وأضعف جندا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 19, 'ويزيد الله الذين اهتدوا هدى والباقيات الصالحات خير عند ربك ثوابا وخير مردا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 19, 'أفرأيت الذي كفر بآياتنا وقال لأوتين مالا وولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 19, 'أطلع الغيب أم اتخذ عند الرحمن عهدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 19, 'كلا سنكتب ما يقول ونمد له من العذاب مدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 19, 'ونرثه ما يقول ويأتينا فردا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 19, 'واتخذوا من دون الله آلهة ليكونوا لهم عزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 19, 'كلا سيكفرون بعبادتهم ويكونون عليهم ضدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 19, 'ألم تر أنا أرسلنا الشياطين على الكافرين تؤزهم أزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 19, 'فلا تعجل عليهم إنما نعد لهم عدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 19, 'يوم نحشر المتقين إلى الرحمن وفدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 19, 'ونسوق المجرمين إلى جهنم وردا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 19, 'لا يملكون الشفاعة إلا من اتخذ عند الرحمن عهدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 19, 'وقالوا اتخذ الرحمن ولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 19, 'لقد جئتم شيئا إدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 19, 'تكاد السماوات يتفطرن منه وتنشق الأرض وتخر الجبال هدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 19, 'أن دعوا للرحمن ولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 19, 'وما ينبغي للرحمن أن يتخذ ولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 19, 'إن كل من في السماوات والأرض إلا آتي الرحمن عبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 19, 'لقد أحصاهم وعدهم عدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 19, 'وكلهم آتيه يوم القيامة فردا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 19, 'إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن ودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 19, 'فإنما يسرناه بلسانك لتبشر به المتقين وتنذر به قوما لدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 19, 'وكم أهلكنا قبلهم من قرن هل تحس منهم من أحد أو تسمع لهم ركزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 20, 'بسم الله الرحمن الرحيم طه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 20, 'ما أنزلنا عليك القرآن لتشقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 20, 'إلا تذكرة لمن يخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 20, 'تنزيلا ممن خلق الأرض والسماوات العلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 20, 'الرحمن على العرش استوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 20, 'له ما في السماوات وما في الأرض وما بينهما وما تحت الثرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 20, 'وإن تجهر بالقول فإنه يعلم السر وأخفى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 20, 'الله لا إله إلا هو له الأسماء الحسنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 20, 'وهل أتاك حديث موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 20, 'إذ رأى نارا فقال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بقبس أو أجد على النار هدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 20, 'فلما أتاها نودي يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 20, 'إني أنا ربك فاخلع نعليك إنك بالواد المقدس طوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 20, 'وأنا اخترتك فاستمع لما يوحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 20, 'إنني أنا الله لا إله إلا أنا فاعبدني وأقم الصلاة لذكري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 20, 'إن الساعة آتية أكاد أخفيها لتجزى كل نفس بما تسعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 20, 'فلا يصدنك عنها من لا يؤمن بها واتبع هواه فتردى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 20, 'وما تلك بيمينك يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 20, 'قال هي عصاي أتوكأ عليها وأهش بها على غنمي ولي فيها مآرب أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 20, 'قال ألقها يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 20, 'فألقاها فإذا هي حية تسعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 20, 'قال خذها ولا تخف سنعيدها سيرتها الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 20, 'واضمم يدك إلى جناحك تخرج بيضاء من غير سوء آية أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 20, 'لنريك من آياتنا الكبرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 20, 'اذهب إلى فرعون إنه طغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 20, 'قال رب اشرح لي صدري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 20, 'ويسر لي أمري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 20, 'واحلل عقدة من لساني');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 20, 'يفقهوا قولي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 20, 'واجعل لي وزيرا من أهلي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 20, 'هارون أخي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 20, 'اشدد به أزري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 20, 'وأشركه في أمري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 20, 'كي نسبحك كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 20, 'ونذكرك كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 20, 'إنك كنت بنا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 20, 'قال قد أوتيت سؤلك يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 20, 'ولقد مننا عليك مرة أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 20, 'إذ أوحينا إلى أمك ما يوحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 20, 'أن اقذفيه في التابوت فاقذفيه في اليم فليلقه اليم بالساحل يأخذه عدو لي وعدو له وألقيت عليك محبة مني ولتصنع على عيني');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 20, 'إذ تمشي أختك فتقول هل أدلكم على من يكفله فرجعناك إلى أمك كي تقر عينها ولا تحزن وقتلت نفسا فنجيناك من الغم وفتناك فتونا فلبثت سنين في أهل مدين ثم جئت على قدر يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 20, 'واصطنعتك لنفسي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 20, 'اذهب أنت وأخوك بآياتي ولا تنيا في ذكري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 20, 'اذهبا إلى فرعون إنه طغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 20, 'فقولا له قولا لينا لعله يتذكر أو يخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 20, 'قالا ربنا إننا نخاف أن يفرط علينا أو أن يطغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 20, 'قال لا تخافا إنني معكما أسمع وأرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 20, 'فأتياه فقولا إنا رسولا ربك فأرسل معنا بني إسرائيل ولا تعذبهم قد جئناك بآية من ربك والسلام على من اتبع الهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 20, 'إنا قد أوحي إلينا أن العذاب على من كذب وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 20, 'قال فمن ربكما يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 20, 'قال ربنا الذي أعطى كل شيء خلقه ثم هدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 20, 'قال فما بال القرون الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 20, 'قال علمها عند ربي في كتاب لا يضل ربي ولا ينسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 20, 'الذي جعل لكم الأرض مهدا وسلك لكم فيها سبلا وأنزل من السماء ماء فأخرجنا به أزواجا من نبات شتى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 20, 'كلوا وارعوا أنعامكم إن في ذلك لآيات لأولي النهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 20, 'منها خلقناكم وفيها نعيدكم ومنها نخرجكم تارة أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 20, 'ولقد أريناه آياتنا كلها فكذب وأبى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 20, 'قال أجئتنا لتخرجنا من أرضنا بسحرك يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 20, 'فلنأتينك بسحر مثله فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا أنت مكانا سوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 20, 'قال موعدكم يوم الزينة وأن يحشر الناس ضحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 20, 'فتولى فرعون فجمع كيده ثم أتى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 20, 'قال لهم موسى ويلكم لا تفتروا على الله كذبا فيسحتكم بعذاب وقد خاب من افترى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 20, 'فتنازعوا أمرهم بينهم وأسروا النجوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 20, 'قالوا إن هذان لساحران يريدان أن يخرجاكم من أرضكم بسحرهما ويذهبا بطريقتكم المثلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 20, 'فأجمعوا كيدكم ثم ائتوا صفا وقد أفلح اليوم من استعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 20, 'قالوا يا موسى إما أن تلقي وإما أن نكون أول من ألقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 20, 'قال بل ألقوا فإذا حبالهم وعصيهم يخيل إليه من سحرهم أنها تسعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 20, 'فأوجس في نفسه خيفة موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 20, 'قلنا لا تخف إنك أنت الأعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 20, 'وألق ما في يمينك تلقف ما صنعوا إنما صنعوا كيد ساحر ولا يفلح الساحر حيث أتى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 20, 'فألقي السحرة سجدا قالوا آمنا برب هارون وموسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 20, 'قال آمنتم له قبل أن آذن لكم إنه لكبيركم الذي علمكم السحر فلأقطعن أيديكم وأرجلكم من خلاف ولأصلبنكم في جذوع النخل ولتعلمن أينا أشد عذابا وأبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 20, 'قالوا لن نؤثرك على ما جاءنا من البينات والذي فطرنا فاقض ما أنت قاض إنما تقضي هذه الحياة الدنيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 20, 'إنا آمنا بربنا ليغفر لنا خطايانا وما أكرهتنا عليه من السحر والله خير وأبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 20, 'إنه من يأت ربه مجرما فإن له جهنم لا يموت فيها ولا يحيى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 20, 'ومن يأته مؤمنا قد عمل الصالحات فأولئك لهم الدرجات العلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 20, 'جنات عدن تجري من تحتها الأنهار خالدين فيها وذلك جزاء من تزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 20, 'ولقد أوحينا إلى موسى أن أسر بعبادي فاضرب لهم طريقا في البحر يبسا لا تخاف دركا ولا تخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 20, 'فأتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 20, 'وأضل فرعون قومه وما هدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 20, 'يا بني إسرائيل قد أنجيناكم من عدوكم وواعدناكم جانب الطور الأيمن ونزلنا عليكم المن والسلوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 20, 'كلوا من طيبات ما رزقناكم ولا تطغوا فيه فيحل عليكم غضبي ومن يحلل عليه غضبي فقد هوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 20, 'وإني لغفار لمن تاب وآمن وعمل صالحا ثم اهتدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 20, 'وما أعجلك عن قومك يا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 20, 'قال هم أولاء على أثري وعجلت إليك رب لترضى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 20, 'قال فإنا قد فتنا قومك من بعدك وأضلهم السامري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 20, 'فرجع موسى إلى قومه غضبان أسفا قال يا قوم ألم يعدكم ربكم وعدا حسنا أفطال عليكم العهد أم أردتم أن يحل عليكم غضب من ربكم فأخلفتم موعدي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 20, 'قالوا ما أخلفنا موعدك بملكنا ولكنا حملنا أوزارا من زينة القوم فقذفناها فكذلك ألقى السامري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 20, 'فأخرج لهم عجلا جسدا له خوار فقالوا هذا إلهكم وإله موسى فنسي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 20, 'أفلا يرون ألا يرجع إليهم قولا ولا يملك لهم ضرا ولا نفعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 20, 'ولقد قال لهم هارون من قبل يا قوم إنما فتنتم به وإن ربكم الرحمن فاتبعوني وأطيعوا أمري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 20, 'قالوا لن نبرح عليه عاكفين حتى يرجع إلينا موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 20, 'قال يا هارون ما منعك إذ رأيتهم ضلوا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 20, 'ألا تتبعن أفعصيت أمري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 20, 'قال يبنؤم لا تأخذ بلحيتي ولا برأسي إني خشيت أن تقول فرقت بين بني إسرائيل ولم ترقب قولي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 20, 'قال فما خطبك يا سامري');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 20, 'قال بصرت بما لم يبصروا به فقبضت قبضة من أثر الرسول فنبذتها وكذلك سولت لي نفسي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 20, 'قال فاذهب فإن لك في الحياة أن تقول لا مساس وإن لك موعدا لن تخلفه وانظر إلى إلهك الذي ظلت عليه عاكفا لنحرقنه ثم لننسفنه في اليم نسفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 20, 'إنما إلهكم الله الذي لا إله إلا هو وسع كل شيء علما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 20, 'كذلك نقص عليك من أنباء ما قد سبق وقد آتيناك من لدنا ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 20, 'من أعرض عنه فإنه يحمل يوم القيامة وزرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 20, 'خالدين فيه وساء لهم يوم القيامة حملا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 20, 'يوم ينفخ في الصور ونحشر المجرمين يومئذ زرقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 20, 'يتخافتون بينهم إن لبثتم إلا عشرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 20, 'نحن أعلم بما يقولون إذ يقول أمثلهم طريقة إن لبثتم إلا يوما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 20, 'ويسألونك عن الجبال فقل ينسفها ربي نسفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 20, 'فيذرها قاعا صفصفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 20, 'لا ترى فيها عوجا ولا أمتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 20, 'يومئذ يتبعون الداعي لا عوج له وخشعت الأصوات للرحمن فلا تسمع إلا همسا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 20, 'يومئذ لا تنفع الشفاعة إلا من أذن له الرحمن ورضي له قولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 20, 'يعلم ما بين أيديهم وما خلفهم ولا يحيطون به علما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 20, 'وعنت الوجوه للحي القيوم وقد خاب من حمل ظلما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 20, 'ومن يعمل من الصالحات وهو مؤمن فلا يخاف ظلما ولا هضما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 20, 'وكذلك أنزلناه قرآنا عربيا وصرفنا فيه من الوعيد لعلهم يتقون أو يحدث لهم ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 20, 'فتعالى الله الملك الحق ولا تعجل بالقرآن من قبل أن يقضى إليك وحيه وقل رب زدني علما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 20, 'ولقد عهدنا إلى آدم من قبل فنسي ولم نجد له عزما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 20, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 20, 'فقلنا يا آدم إن هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 20, 'إن لك ألا تجوع فيها ولا تعرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 20, 'وأنك لا تظمأ فيها ولا تضحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 20, 'فوسوس إليه الشيطان قال يا آدم هل أدلك على شجرة الخلد وملك لا يبلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 20, 'فأكلا منها فبدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة وعصى آدم ربه فغوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 20, 'ثم اجتباه ربه فتاب عليه وهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 20, 'قال اهبطا منها جميعا بعضكم لبعض عدو فإما يأتينكم مني هدى فمن اتبع هداي فلا يضل ولا يشقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 20, 'ومن أعرض عن ذكري فإن له معيشة ضنكا ونحشره يوم القيامة أعمى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 20, 'قال رب لم حشرتني أعمى وقد كنت بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 20, 'قال كذلك أتتك آياتنا فنسيتها وكذلك اليوم تنسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 20, 'وكذلك نجزي من أسرف ولم يؤمن بآيات ربه ولعذاب الآخرة أشد وأبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 20, 'أفلم يهد لهم كم أهلكنا قبلهم من القرون يمشون في مساكنهم إن في ذلك لآيات لأولي النهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 20, 'ولولا كلمة سبقت من ربك لكان لزاما وأجل مسمى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 20, 'فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها ومن آناء الليل فسبح وأطراف النهار لعلك ترضى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 20, 'ولا تمدن عينيك إلى ما متعنا به أزواجا منهم زهرة الحياة الدنيا لنفتنهم فيه ورزق ربك خير وأبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 20, 'وأمر أهلك بالصلاة واصطبر عليها لا نسألك رزقا نحن نرزقك والعاقبة للتقوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 20, 'وقالوا لولا يأتينا بآية من ربه أولم تأتهم بينة ما في الصحف الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 20, 'ولو أنا أهلكناهم بعذاب من قبله لقالوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك من قبل أن نذل ونخزى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 20, 'قل كل متربص فتربصوا فستعلمون من أصحاب الصراط السوي ومن اهتدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 21, 'بسم الله الرحمن الرحيم اقترب للناس حسابهم وهم في غفلة معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 21, 'ما يأتيهم من ذكر من ربهم محدث إلا استمعوه وهم يلعبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 21, 'لاهية قلوبهم وأسروا النجوى الذين ظلموا هل هذا إلا بشر مثلكم أفتأتون السحر وأنتم تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 21, 'قال ربي يعلم القول في السماء والأرض وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 21, 'بل قالوا أضغاث أحلام بل افتراه بل هو شاعر فليأتنا بآية كما أرسل الأولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 21, 'ما آمنت قبلهم من قرية أهلكناها أفهم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 21, 'وما أرسلنا قبلك إلا رجالا نوحي إليهم فاسألوا أهل الذكر إن كنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 21, 'وما جعلناهم جسدا لا يأكلون الطعام وما كانوا خالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 21, 'ثم صدقناهم الوعد فأنجيناهم ومن نشاء وأهلكنا المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 21, 'لقد أنزلنا إليكم كتابا فيه ذكركم أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 21, 'وكم قصمنا من قرية كانت ظالمة وأنشأنا بعدها قوما آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 21, 'فلما أحسوا بأسنا إذا هم منها يركضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 21, 'لا تركضوا وارجعوا إلى ما أترفتم فيه ومساكنكم لعلكم تسألون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 21, 'قالوا يا ويلنا إنا كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 21, 'فما زالت تلك دعواهم حتى جعلناهم حصيدا خامدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 21, 'وما خلقنا السماء والأرض وما بينهما لاعبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 21, 'لو أردنا أن نتخذ لهوا لاتخذناه من لدنا إن كنا فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 21, 'بل نقذف بالحق على الباطل فيدمغه فإذا هو زاهق ولكم الويل مما تصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 21, 'وله من في السماوات والأرض ومن عنده لا يستكبرون عن عبادته ولا يستحسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 21, 'يسبحون الليل والنهار لا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 21, 'أم اتخذوا آلهة من الأرض هم ينشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 21, 'لو كان فيهما آلهة إلا الله لفسدتا فسبحان الله رب العرش عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 21, 'لا يسأل عما يفعل وهم يسألون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 21, 'أم اتخذوا من دونه آلهة قل هاتوا برهانكم هذا ذكر من معي وذكر من قبلي بل أكثرهم لا يعلمون الحق فهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 21, 'وما أرسلنا من قبلك من رسول إلا نوحي إليه أنه لا إله إلا أنا فاعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 21, 'وقالوا اتخذ الرحمن ولدا سبحانه بل عباد مكرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 21, 'لا يسبقونه بالقول وهم بأمره يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 21, 'يعلم ما بين أيديهم وما خلفهم ولا يشفعون إلا لمن ارتضى وهم من خشيته مشفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 21, 'ومن يقل منهم إني إله من دونه فذلك نجزيه جهنم كذلك نجزي الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 21, 'أولم ير الذين كفروا أن السماوات والأرض كانتا رتقا ففتقناهما وجعلنا من الماء كل شيء حي أفلا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 21, 'وجعلنا في الأرض رواسي أن تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 21, 'وجعلنا السماء سقفا محفوظا وهم عن آياتها معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 21, 'وهو الذي خلق الليل والنهار والشمس والقمر كل في فلك يسبحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 21, 'وما جعلنا لبشر من قبلك الخلد أفإن مت فهم الخالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 21, 'كل نفس ذائقة الموت ونبلوكم بالشر والخير فتنة وإلينا ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 21, 'وإذا رآك الذين كفروا إن يتخذونك إلا هزوا أهذا الذي يذكر آلهتكم وهم بذكر الرحمن هم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 21, 'خلق الإنسان من عجل سأريكم آياتي فلا تستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 21, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 21, 'لو يعلم الذين كفروا حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 21, 'بل تأتيهم بغتة فتبهتهم فلا يستطيعون ردها ولا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 21, 'ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 21, 'قل من يكلؤكم بالليل والنهار من الرحمن بل هم عن ذكر ربهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 21, 'أم لهم آلهة تمنعهم من دوننا لا يستطيعون نصر أنفسهم ولا هم منا يصحبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 21, 'بل متعنا هؤلاء وآباءهم حتى طال عليهم العمر أفلا يرون أنا نأتي الأرض ننقصها من أطرافها أفهم الغالبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 21, 'قل إنما أنذركم بالوحي ولا يسمع الصم الدعاء إذا ما ينذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 21, 'ولئن مستهم نفحة من عذاب ربك ليقولن يا ويلنا إنا كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 21, 'ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا وإن كان مثقال حبة من خردل أتينا بها وكفى بنا حاسبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 21, 'ولقد آتينا موسى وهارون الفرقان وضياء وذكرا للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 21, 'الذين يخشون ربهم بالغيب وهم من الساعة مشفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 21, 'وهذا ذكر مبارك أنزلناه أفأنتم له منكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 21, 'ولقد آتينا إبراهيم رشده من قبل وكنا به عالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 21, 'إذ قال لأبيه وقومه ما هذه التماثيل التي أنتم لها عاكفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 21, 'قالوا وجدنا آباءنا لها عابدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 21, 'قال لقد كنتم أنتم وآباؤكم في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 21, 'قالوا أجئتنا بالحق أم أنت من اللاعبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 21, 'قال بل ربكم رب السماوات والأرض الذي فطرهن وأنا على ذلكم من الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 21, 'وتالله لأكيدن أصنامكم بعد أن تولوا مدبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 21, 'فجعلهم جذاذا إلا كبيرا لهم لعلهم إليه يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 21, 'قالوا من فعل هذا بآلهتنا إنه لمن الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 21, 'قالوا سمعنا فتى يذكرهم يقال له إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 21, 'قالوا فأتوا به على أعين الناس لعلهم يشهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 21, 'قالوا أأنت فعلت هذا بآلهتنا يا إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 21, 'قال بل فعله كبيرهم هذا فاسألوهم إن كانوا ينطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 21, 'فرجعوا إلى أنفسهم فقالوا إنكم أنتم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 21, 'ثم نكسوا على رءوسهم لقد علمت ما هؤلاء ينطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 21, 'قال أفتعبدون من دون الله ما لا ينفعكم شيئا ولا يضركم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 21, 'أف لكم ولما تعبدون من دون الله أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 21, 'قالوا حرقوه وانصروا آلهتكم إن كنتم فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 21, 'قلنا يا نار كوني بردا وسلاما على إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 21, 'وأرادوا به كيدا فجعلناهم الأخسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 21, 'ونجيناه ولوطا إلى الأرض التي باركنا فيها للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 21, 'ووهبنا له إسحاق ويعقوب نافلة وكلا جعلنا صالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 21, 'وجعلناهم أئمة يهدون بأمرنا وأوحينا إليهم فعل الخيرات وإقام الصلاة وإيتاء الزكاة وكانوا لنا عابدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 21, 'ولوطا آتيناه حكما وعلما ونجيناه من القرية التي كانت تعمل الخبائث إنهم كانوا قوم سوء فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 21, 'وأدخلناه في رحمتنا إنه من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 21, 'ونوحا إذ نادى من قبل فاستجبنا له فنجيناه وأهله من الكرب العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 21, 'ونصرناه من القوم الذين كذبوا بآياتنا إنهم كانوا قوم سوء فأغرقناهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 21, 'وداوود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 21, 'ففهمناها سليمان وكلا آتينا حكما وعلما وسخرنا مع داوود الجبال يسبحن والطير وكنا فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 21, 'وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم فهل أنتم شاكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 21, 'ولسليمان الريح عاصفة تجري بأمره إلى الأرض التي باركنا فيها وكنا بكل شيء عالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 21, 'ومن الشياطين من يغوصون له ويعملون عملا دون ذلك وكنا لهم حافظين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 21, 'وأيوب إذ نادى ربه أني مسني الضر وأنت أرحم الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 21, 'فاستجبنا له فكشفنا ما به من ضر وآتيناه أهله ومثلهم معهم رحمة من عندنا وذكرى للعابدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 21, 'وإسماعيل وإدريس وذا الكفل كل من الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 21, 'وأدخلناهم في رحمتنا إنهم من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 21, 'وذا النون إذ ذهب مغاضبا فظن أن لن نقدر عليه فنادى في الظلمات أن لا إله إلا أنت سبحانك إني كنت من الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 21, 'فاستجبنا له ونجيناه من الغم وكذلك ننجي المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 21, 'وزكريا إذ نادى ربه رب لا تذرني فردا وأنت خير الوارثين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 21, 'فاستجبنا له ووهبنا له يحيى وأصلحنا له زوجه إنهم كانوا يسارعون في الخيرات ويدعوننا رغبا ورهبا وكانوا لنا خاشعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 21, 'والتي أحصنت فرجها فنفخنا فيها من روحنا وجعلناها وابنها آية للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 21, 'إن هذه أمتكم أمة واحدة وأنا ربكم فاعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 21, 'وتقطعوا أمرهم بينهم كل إلينا راجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 21, 'فمن يعمل من الصالحات وهو مؤمن فلا كفران لسعيه وإنا له كاتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 21, 'وحرام على قرية أهلكناها أنهم لا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 21, 'حتى إذا فتحت يأجوج ومأجوج وهم من كل حدب ينسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 21, 'واقترب الوعد الحق فإذا هي شاخصة أبصار الذين كفروا يا ويلنا قد كنا في غفلة من هذا بل كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 21, 'إنكم وما تعبدون من دون الله حصب جهنم أنتم لها واردون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 21, 'لو كان هؤلاء آلهة ما وردوها وكل فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 21, 'لهم فيها زفير وهم فيها لا يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 21, 'إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 21, 'لا يسمعون حسيسها وهم في ما اشتهت أنفسهم خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 21, 'لا يحزنهم الفزع الأكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 21, 'يوم نطوي السماء كطي السجل للكتب كما بدأنا أول خلق نعيده وعدا علينا إنا كنا فاعلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 21, 'ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 21, 'إن في هذا لبلاغا لقوم عابدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 21, 'وما أرسلناك إلا رحمة للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 21, 'قل إنما يوحى إلي أنما إلهكم إله واحد فهل أنتم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 21, 'فإن تولوا فقل آذنتكم على سواء وإن أدري أقريب أم بعيد ما توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 21, 'إنه يعلم الجهر من القول ويعلم ما تكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 21, 'وإن أدري لعله فتنة لكم ومتاع إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 21, 'قال رب احكم بالحق وربنا الرحمن المستعان على ما تصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 22, 'بسم الله الرحمن الرحيم يا أيها الناس اتقوا ربكم إن زلزلة الساعة شيء عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 22, 'يوم ترونها تذهل كل مرضعة عما أرضعت وتضع كل ذات حمل حملها وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 22, 'ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 22, 'كتب عليه أنه من تولاه فأنه يضله ويهديه إلى عذاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 22, 'يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ونقر في الأرحام ما نشاء إلى أجل مسمى ثم نخرجكم طفلا ثم لتبلغوا أشدكم ومنكم من يتوفى ومنكم من يرد إلى أرذل العمر لكيلا يعلم من بعد علم شيئا وترى الأرض هامدة فإذا أنزلنا عليها الماء اهتزت وربت وأنبتت من كل زوج بهيج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 22, 'ذلك بأن الله هو الحق وأنه يحيي الموتى وأنه على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 22, 'وأن الساعة آتية لا ريب فيها وأن الله يبعث من في القبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 22, 'ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 22, 'ثاني عطفه ليضل عن سبيل الله له في الدنيا خزي ونذيقه يوم القيامة عذاب الحريق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 22, 'ذلك بما قدمت يداك وأن الله ليس بظلام للعبيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 22, 'ومن الناس من يعبد الله على حرف فإن أصابه خير اطمأن به وإن أصابته فتنة انقلب على وجهه خسر الدنيا والآخرة ذلك هو الخسران المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 22, 'يدعو من دون الله ما لا يضره وما لا ينفعه ذلك هو الضلال البعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 22, 'يدعو لمن ضره أقرب من نفعه لبئس المولى ولبئس العشير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 22, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار إن الله يفعل ما يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 22, 'من كان يظن أن لن ينصره الله في الدنيا والآخرة فليمدد بسبب إلى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 22, 'وكذلك أنزلناه آيات بينات وأن الله يهدي من يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 22, 'إن الذين آمنوا والذين هادوا والصابئين والنصارى والمجوس والذين أشركوا إن الله يفصل بينهم يوم القيامة إن الله على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 22, 'ألم تر أن الله يسجد له من في السماوات ومن في الأرض والشمس والقمر والنجوم والجبال والشجر والدواب وكثير من الناس وكثير حق عليه العذاب ومن يهن الله فما له من مكرم إن الله يفعل ما يشاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 22, 'هذان خصمان اختصموا في ربهم فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 22, 'يصهر به ما في بطونهم والجلود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 22, 'ولهم مقامع من حديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 22, 'كلما أرادوا أن يخرجوا منها من غم أعيدوا فيها وذوقوا عذاب الحريق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 22, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 22, 'وهدوا إلى الطيب من القول وهدوا إلى صراط الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 22, 'إن الذين كفروا ويصدون عن سبيل الله والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد ومن يرد فيه بإلحاد بظلم نذقه من عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 22, 'وإذ بوأنا لإبراهيم مكان البيت أن لا تشرك بي شيئا وطهر بيتي للطائفين والقائمين والركع السجود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 22, 'وأذن في الناس بالحج يأتوك رجالا وعلى كل ضامر يأتين من كل فج عميق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 22, 'ليشهدوا منافع لهم ويذكروا اسم الله في أيام معلومات على ما رزقهم من بهيمة الأنعام فكلوا منها وأطعموا البائس الفقير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 22, 'ثم ليقضوا تفثهم وليوفوا نذورهم وليطوفوا بالبيت العتيق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 22, 'ذلك ومن يعظم حرمات الله فهو خير له عند ربه وأحلت لكم الأنعام إلا ما يتلى عليكم فاجتنبوا الرجس من الأوثان واجتنبوا قول الزور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 22, 'حنفاء لله غير مشركين به ومن يشرك بالله فكأنما خر من السماء فتخطفه الطير أو تهوي به الريح في مكان سحيق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 22, 'ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 22, 'لكم فيها منافع إلى أجل مسمى ثم محلها إلى البيت العتيق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 22, 'ولكل أمة جعلنا منسكا ليذكروا اسم الله على ما رزقهم من بهيمة الأنعام فإلهكم إله واحد فله أسلموا وبشر المخبتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 22, 'الذين إذا ذكر الله وجلت قلوبهم والصابرين على ما أصابهم والمقيمي الصلاة ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 22, 'والبدن جعلناها لكم من شعائر الله لكم فيها خير فاذكروا اسم الله عليها صواف فإذا وجبت جنوبها فكلوا منها وأطعموا القانع والمعتر كذلك سخرناها لكم لعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 22, 'لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم كذلك سخرها لكم لتكبروا الله على ما هداكم وبشر المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 22, 'إن الله يدافع عن الذين آمنوا إن الله لا يحب كل خوان كفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 22, 'أذن للذين يقاتلون بأنهم ظلموا وإن الله على نصرهم لقدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 22, 'الذين أخرجوا من ديارهم بغير حق إلا أن يقولوا ربنا الله ولولا دفع الله الناس بعضهم ببعض لهدمت صوامع وبيع وصلوات ومساجد يذكر فيها اسم الله كثيرا ولينصرن الله من ينصره إن الله لقوي عزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 22, 'الذين إن مكناهم في الأرض أقاموا الصلاة وآتوا الزكاة وأمروا بالمعروف ونهوا عن المنكر ولله عاقبة الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 22, 'وإن يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 22, 'وقوم إبراهيم وقوم لوط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 22, 'وأصحاب مدين وكذب موسى فأمليت للكافرين ثم أخذتهم فكيف كان نكير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 22, 'فكأين من قرية أهلكناها وهي ظالمة فهي خاوية على عروشها وبئر معطلة وقصر مشيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 22, 'أفلم يسيروا في الأرض فتكون لهم قلوب يعقلون بها أو آذان يسمعون بها فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 22, 'ويستعجلونك بالعذاب ولن يخلف الله وعده وإن يوما عند ربك كألف سنة مما تعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 22, 'وكأين من قرية أمليت لها وهي ظالمة ثم أخذتها وإلي المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 22, 'قل يا أيها الناس إنما أنا لكم نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 22, 'فالذين آمنوا وعملوا الصالحات لهم مغفرة ورزق كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 22, 'والذين سعوا في آياتنا معاجزين أولئك أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 22, 'وما أرسلنا من قبلك من رسول ولا نبي إلا إذا تمنى ألقى الشيطان في أمنيته فينسخ الله ما يلقي الشيطان ثم يحكم الله آياته والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 22, 'ليجعل ما يلقي الشيطان فتنة للذين في قلوبهم مرض والقاسية قلوبهم وإن الظالمين لفي شقاق بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 22, 'وليعلم الذين أوتوا العلم أنه الحق من ربك فيؤمنوا به فتخبت له قلوبهم وإن الله لهاد الذين آمنوا إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 22, 'ولا يزال الذين كفروا في مرية منه حتى تأتيهم الساعة بغتة أو يأتيهم عذاب يوم عقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 22, 'الملك يومئذ لله يحكم بينهم فالذين آمنوا وعملوا الصالحات في جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 22, 'والذين كفروا وكذبوا بآياتنا فأولئك لهم عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 22, 'والذين هاجروا في سبيل الله ثم قتلوا أو ماتوا ليرزقنهم الله رزقا حسنا وإن الله لهو خير الرازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 22, 'ليدخلنهم مدخلا يرضونه وإن الله لعليم حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 22, 'ذلك ومن عاقب بمثل ما عوقب به ثم بغي عليه لينصرنه الله إن الله لعفو غفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 22, 'ذلك بأن الله يولج الليل في النهار ويولج النهار في الليل وأن الله سميع بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 22, 'ذلك بأن الله هو الحق وأن ما يدعون من دونه هو الباطل وأن الله هو العلي الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 22, 'ألم تر أن الله أنزل من السماء ماء فتصبح الأرض مخضرة إن الله لطيف خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 22, 'له ما في السماوات وما في الأرض وإن الله لهو الغني الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 22, 'ألم تر أن الله سخر لكم ما في الأرض والفلك تجري في البحر بأمره ويمسك السماء أن تقع على الأرض إلا بإذنه إن الله بالناس لرءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 22, 'وهو الذي أحياكم ثم يميتكم ثم يحييكم إن الإنسان لكفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 22, 'لكل أمة جعلنا منسكا هم ناسكوه فلا ينازعنك في الأمر وادع إلى ربك إنك لعلى هدى مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 22, 'وإن جادلوك فقل الله أعلم بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 22, 'الله يحكم بينكم يوم القيامة فيما كنتم فيه تختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 22, 'ألم تعلم أن الله يعلم ما في السماء والأرض إن ذلك في كتاب إن ذلك على الله يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 22, 'ويعبدون من دون الله ما لم ينزل به سلطانا وما ليس لهم به علم وما للظالمين من نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 22, 'وإذا تتلى عليهم آياتنا بينات تعرف في وجوه الذين كفروا المنكر يكادون يسطون بالذين يتلون عليهم آياتنا قل أفأنبئكم بشر من ذلكم النار وعدها الله الذين كفروا وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 22, 'يا أيها الناس ضرب مثل فاستمعوا له إن الذين تدعون من دون الله لن يخلقوا ذبابا ولو اجتمعوا له وإن يسلبهم الذباب شيئا لا يستنقذوه منه ضعف الطالب والمطلوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 22, 'ما قدروا الله حق قدره إن الله لقوي عزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 22, 'الله يصطفي من الملائكة رسلا ومن الناس إن الله سميع بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 22, 'يعلم ما بين أيديهم وما خلفهم وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 22, 'يا أيها الذين آمنوا اركعوا واسجدوا واعبدوا ربكم وافعلوا الخير لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 22, 'وجاهدوا في الله حق جهاده هو اجتباكم وما جعل عليكم في الدين من حرج ملة أبيكم إبراهيم هو سماكم المسلمين من قبل وفي هذا ليكون الرسول شهيدا عليكم وتكونوا شهداء على الناس فأقيموا الصلاة وآتوا الزكاة واعتصموا بالله هو مولاكم فنعم المولى ونعم النصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 23, 'بسم الله الرحمن الرحيم قد أفلح المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 23, 'الذين هم في صلاتهم خاشعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 23, 'والذين هم عن اللغو معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 23, 'والذين هم للزكاة فاعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 23, 'والذين هم لفروجهم حافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 23, 'إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 23, 'فمن ابتغى وراء ذلك فأولئك هم العادون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 23, 'والذين هم لأماناتهم وعهدهم راعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 23, 'والذين هم على صلواتهم يحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 23, 'أولئك هم الوارثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 23, 'الذين يرثون الفردوس هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 23, 'ولقد خلقنا الإنسان من سلالة من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 23, 'ثم جعلناه نطفة في قرار مكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 23, 'ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم أنشأناه خلقا آخر فتبارك الله أحسن الخالقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 23, 'ثم إنكم بعد ذلك لميتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 23, 'ثم إنكم يوم القيامة تبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 23, 'ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 23, 'وأنزلنا من السماء ماء بقدر فأسكناه في الأرض وإنا على ذهاب به لقادرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 23, 'فأنشأنا لكم به جنات من نخيل وأعناب لكم فيها فواكه كثيرة ومنها تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 23, 'وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للآكلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 23, 'وإن لكم في الأنعام لعبرة نسقيكم مما في بطونها ولكم فيها منافع كثيرة ومنها تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 23, 'وعليها وعلى الفلك تحملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 23, 'ولقد أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غيره أفلا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 23, 'فقال الملأ الذين كفروا من قومه ما هذا إلا بشر مثلكم يريد أن يتفضل عليكم ولو شاء الله لأنزل ملائكة ما سمعنا بهذا في آبائنا الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 23, 'إن هو إلا رجل به جنة فتربصوا به حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 23, 'قال رب انصرني بما كذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 23, 'فأوحينا إليه أن اصنع الفلك بأعيننا ووحينا فإذا جاء أمرنا وفار التنور فاسلك فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول منهم ولا تخاطبني في الذين ظلموا إنهم مغرقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 23, 'فإذا استويت أنت ومن معك على الفلك فقل الحمد لله الذي نجانا من القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 23, 'وقل رب أنزلني منزلا مباركا وأنت خير المنزلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 23, 'إن في ذلك لآيات وإن كنا لمبتلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 23, 'ثم أنشأنا من بعدهم قرنا آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 23, 'فأرسلنا فيهم رسولا منهم أن اعبدوا الله ما لكم من إله غيره أفلا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 23, 'وقال الملأ من قومه الذين كفروا وكذبوا بلقاء الآخرة وأترفناهم في الحياة الدنيا ما هذا إلا بشر مثلكم يأكل مما تأكلون منه ويشرب مما تشربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 23, 'ولئن أطعتم بشرا مثلكم إنكم إذا لخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 23, 'أيعدكم أنكم إذا متم وكنتم ترابا وعظاما أنكم مخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 23, 'هيهات هيهات لما توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 23, 'إن هي إلا حياتنا الدنيا نموت ونحيا وما نحن بمبعوثين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 23, 'إن هو إلا رجل افترى على الله كذبا وما نحن له بمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 23, 'قال رب انصرني بما كذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 23, 'قال عما قليل ليصبحن نادمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 23, 'فأخذتهم الصيحة بالحق فجعلناهم غثاء فبعدا للقوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 23, 'ثم أنشأنا من بعدهم قرونا آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 23, 'ما تسبق من أمة أجلها وما يستأخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 23, 'ثم أرسلنا رسلنا تترى كل ما جاء أمة رسولها كذبوه فأتبعنا بعضهم بعضا وجعلناهم أحاديث فبعدا لقوم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 23, 'ثم أرسلنا موسى وأخاه هارون بآياتنا وسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 23, 'إلى فرعون وملئه فاستكبروا وكانوا قوما عالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 23, 'فقالوا أنؤمن لبشرين مثلنا وقومهما لنا عابدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 23, 'فكذبوهما فكانوا من المهلكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 23, 'ولقد آتينا موسى الكتاب لعلهم يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 23, 'وجعلنا ابن مريم وأمه آية وآويناهما إلى ربوة ذات قرار ومعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 23, 'يا أيها الرسل كلوا من الطيبات واعملوا صالحا إني بما تعملون عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 23, 'وإن هذه أمتكم أمة واحدة وأنا ربكم فاتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 23, 'فتقطعوا أمرهم بينهم زبرا كل حزب بما لديهم فرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 23, 'فذرهم في غمرتهم حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 23, 'أيحسبون أنما نمدهم به من مال وبنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 23, 'نسارع لهم في الخيرات بل لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 23, 'إن الذين هم من خشية ربهم مشفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 23, 'والذين هم بآيات ربهم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 23, 'والذين هم بربهم لا يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 23, 'والذين يؤتون ما آتوا وقلوبهم وجلة أنهم إلى ربهم راجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 23, 'أولئك يسارعون في الخيرات وهم لها سابقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 23, 'ولا نكلف نفسا إلا وسعها ولدينا كتاب ينطق بالحق وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 23, 'بل قلوبهم في غمرة من هذا ولهم أعمال من دون ذلك هم لها عاملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 23, 'حتى إذا أخذنا مترفيهم بالعذاب إذا هم يجأرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 23, 'لا تجأروا اليوم إنكم منا لا تنصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 23, 'قد كانت آياتي تتلى عليكم فكنتم على أعقابكم تنكصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 23, 'مستكبرين به سامرا تهجرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 23, 'أفلم يدبروا القول أم جاءهم ما لم يأت آباءهم الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 23, 'أم لم يعرفوا رسولهم فهم له منكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 23, 'أم يقولون به جنة بل جاءهم بالحق وأكثرهم للحق كارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 23, 'ولو اتبع الحق أهواءهم لفسدت السماوات والأرض ومن فيهن بل أتيناهم بذكرهم فهم عن ذكرهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 23, 'أم تسألهم خرجا فخراج ربك خير وهو خير الرازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 23, 'وإنك لتدعوهم إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 23, 'وإن الذين لا يؤمنون بالآخرة عن الصراط لناكبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 23, 'ولو رحمناهم وكشفنا ما بهم من ضر للجوا في طغيانهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 23, 'ولقد أخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 23, 'حتى إذا فتحنا عليهم بابا ذا عذاب شديد إذا هم فيه مبلسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 23, 'وهو الذي أنشأ لكم السمع والأبصار والأفئدة قليلا ما تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 23, 'وهو الذي ذرأكم في الأرض وإليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 23, 'وهو الذي يحيي ويميت وله اختلاف الليل والنهار أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 23, 'بل قالوا مثل ما قال الأولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 23, 'قالوا أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 23, 'لقد وعدنا نحن وآباؤنا هذا من قبل إن هذا إلا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 23, 'قل لمن الأرض ومن فيها إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 23, 'سيقولون لله قل أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 23, 'قل من رب السماوات السبع ورب العرش العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 23, 'سيقولون لله قل أفلا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 23, 'قل من بيده ملكوت كل شيء وهو يجير ولا يجار عليه إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 23, 'سيقولون لله قل فأنى تسحرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 23, 'بل أتيناهم بالحق وإنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 23, 'ما اتخذ الله من ولد وما كان معه من إله إذا لذهب كل إله بما خلق ولعلا بعضهم على بعض سبحان الله عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 23, 'عالم الغيب والشهادة فتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 23, 'قل رب إما تريني ما يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 23, 'رب فلا تجعلني في القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 23, 'وإنا على أن نريك ما نعدهم لقادرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 23, 'ادفع بالتي هي أحسن السيئة نحن أعلم بما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 23, 'وقل رب أعوذ بك من همزات الشياطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 23, 'وأعوذ بك رب أن يحضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 23, 'حتى إذا جاء أحدهم الموت قال رب ارجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 23, 'لعلي أعمل صالحا فيما تركت كلا إنها كلمة هو قائلها ومن ورائهم برزخ إلى يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 23, 'فإذا نفخ في الصور فلا أنساب بينهم يومئذ ولا يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 23, 'فمن ثقلت موازينه فأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 23, 'ومن خفت موازينه فأولئك الذين خسروا أنفسهم في جهنم خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 23, 'تلفح وجوههم النار وهم فيها كالحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 23, 'ألم تكن آياتي تتلى عليكم فكنتم بها تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 23, 'قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 23, 'ربنا أخرجنا منها فإن عدنا فإنا ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 23, 'قال اخسئوا فيها ولا تكلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 23, 'إنه كان فريق من عبادي يقولون ربنا آمنا فاغفر لنا وارحمنا وأنت خير الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 23, 'فاتخذتموهم سخريا حتى أنسوكم ذكري وكنتم منهم تضحكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 23, 'إني جزيتهم اليوم بما صبروا أنهم هم الفائزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 23, 'قال كم لبثتم في الأرض عدد سنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 23, 'قالوا لبثنا يوما أو بعض يوم فاسأل العادين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 23, 'قال إن لبثتم إلا قليلا لو أنكم كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 23, 'أفحسبتم أنما خلقناكم عبثا وأنكم إلينا لا ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 23, 'فتعالى الله الملك الحق لا إله إلا هو رب العرش الكريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 23, 'ومن يدع مع الله إلها آخر لا برهان له به فإنما حسابه عند ربه إنه لا يفلح الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 23, 'وقل رب اغفر وارحم وأنت خير الراحمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 24, 'بسم الله الرحمن الرحيم سورة أنزلناها وفرضناها وأنزلنا فيها آيات بينات لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 24, 'الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ولا تأخذكم بهما رأفة في دين الله إن كنتم تؤمنون بالله واليوم الآخر وليشهد عذابهما طائفة من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 24, 'الزاني لا ينكح إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك وحرم ذلك على المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 24, 'والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدا وأولئك هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 24, 'إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 24, 'والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله إنه لمن الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 24, 'والخامسة أن لعنت الله عليه إن كان من الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 24, 'ويدرأ عنها العذاب أن تشهد أربع شهادات بالله إنه لمن الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 24, 'والخامسة أن غضب الله عليها إن كان من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 24, 'ولولا فضل الله عليكم ورحمته وأن الله تواب حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 24, 'إن الذين جاءوا بالإفك عصبة منكم لا تحسبوه شرا لكم بل هو خير لكم لكل امرئ منهم ما اكتسب من الإثم والذي تولى كبره منهم له عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 24, 'لولا إذ سمعتموه ظن المؤمنون والمؤمنات بأنفسهم خيرا وقالوا هذا إفك مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 24, 'لولا جاءوا عليه بأربعة شهداء فإذ لم يأتوا بالشهداء فأولئك عند الله هم الكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 24, 'ولولا فضل الله عليكم ورحمته في الدنيا والآخرة لمسكم في ما أفضتم فيه عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 24, 'إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 24, 'ولولا إذ سمعتموه قلتم ما يكون لنا أن نتكلم بهذا سبحانك هذا بهتان عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 24, 'يعظكم الله أن تعودوا لمثله أبدا إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 24, 'ويبين الله لكم الآيات والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 24, 'إن الذين يحبون أن تشيع الفاحشة في الذين آمنوا لهم عذاب أليم في الدنيا والآخرة والله يعلم وأنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 24, 'ولولا فضل الله عليكم ورحمته وأن الله رءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 24, 'يا أيها الذين آمنوا لا تتبعوا خطوات الشيطان ومن يتبع خطوات الشيطان فإنه يأمر بالفحشاء والمنكر ولولا فضل الله عليكم ورحمته ما زكى منكم من أحد أبدا ولكن الله يزكي من يشاء والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 24, 'ولا يأتل أولو الفضل منكم والسعة أن يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله وليعفوا وليصفحوا ألا تحبون أن يغفر الله لكم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 24, 'إن الذين يرمون المحصنات الغافلات المؤمنات لعنوا في الدنيا والآخرة ولهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 24, 'يوم تشهد عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 24, 'يومئذ يوفيهم الله دينهم الحق ويعلمون أن الله هو الحق المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 24, 'الخبيثات للخبيثين والخبيثون للخبيثات والطيبات للطيبين والطيبون للطيبات أولئك مبرءون مما يقولون لهم مغفرة ورزق كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 24, 'يا أيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها ذلكم خير لكم لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 24, 'فإن لم تجدوا فيها أحدا فلا تدخلوها حتى يؤذن لكم وإن قيل لكم ارجعوا فارجعوا هو أزكى لكم والله بما تعملون عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 24, 'ليس عليكم جناح أن تدخلوا بيوتا غير مسكونة فيها متاع لكم والله يعلم ما تبدون وما تكتمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 24, 'قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم ذلك أزكى لهم إن الله خبير بما يصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 24, 'وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن ولا يبدين زينتهن إلا ما ظهر منها وليضربن بخمرهن على جيوبهن ولا يبدين زينتهن إلا لبعولتهن أو آبائهن أو آباء بعولتهن أو أبنائهن أو أبناء بعولتهن أو إخوانهن أو بني إخوانهن أو بني أخواتهن أو نسائهن أو ما ملكت أيمانهن أو التابعين غير أولي الإربة من الرجال أو الطفل الذين لم يظهروا على عورات النساء ولا يضربن بأرجلهن ليعلم ما يخفين من زينتهن وتوبوا إلى الله جميعا أيه المؤمنون لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 24, 'وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم إن يكونوا فقراء يغنهم الله من فضله والله واسع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 24, 'وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا وآتوهم من مال الله الذي آتاكم ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا لتبتغوا عرض الحياة الدنيا ومن يكرههن فإن الله من بعد إكراههن غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 24, 'ولقد أنزلنا إليكم آيات مبينات ومثلا من الذين خلوا من قبلكم وموعظة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 24, 'الله نور السماوات والأرض مثل نوره كمشكاة فيها مصباح المصباح في زجاجة الزجاجة كأنها كوكب دري يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية يكاد زيتها يضيء ولو لم تمسسه نار نور على نور يهدي الله لنوره من يشاء ويضرب الله الأمثال للناس والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 24, 'في بيوت أذن الله أن ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والآصال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 24, 'رجال لا تلهيهم تجارة ولا بيع عن ذكر الله وإقام الصلاة وإيتاء الزكاة يخافون يوما تتقلب فيه القلوب والأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 24, 'ليجزيهم الله أحسن ما عملوا ويزيدهم من فضله والله يرزق من يشاء بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 24, 'والذين كفروا أعمالهم كسراب بقيعة يحسبه الظمآن ماء حتى إذا جاءه لم يجده شيئا ووجد الله عنده فوفاه حسابه والله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 24, 'أو كظلمات في بحر لجي يغشاه موج من فوقه موج من فوقه سحاب ظلمات بعضها فوق بعض إذا أخرج يده لم يكد يراها ومن لم يجعل الله له نورا فما له من نور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 24, 'ألم تر أن الله يسبح له من في السماوات والأرض والطير صافات كل قد علم صلاته وتسبيحه والله عليم بما يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 24, 'ولله ملك السماوات والأرض وإلى الله المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 24, 'ألم تر أن الله يزجي سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلاله وينزل من السماء من جبال فيها من برد فيصيب به من يشاء ويصرفه عن من يشاء يكاد سنا برقه يذهب بالأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 24, 'يقلب الله الليل والنهار إن في ذلك لعبرة لأولي الأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 24, 'والله خلق كل دابة من ماء فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على أربع يخلق الله ما يشاء إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 24, 'لقد أنزلنا آيات مبينات والله يهدي من يشاء إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 24, 'ويقولون آمنا بالله وبالرسول وأطعنا ثم يتولى فريق منهم من بعد ذلك وما أولئك بالمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 24, 'وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 24, 'وإن يكن لهم الحق يأتوا إليه مذعنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 24, 'أفي قلوبهم مرض أم ارتابوا أم يخافون أن يحيف الله عليهم ورسوله بل أولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 24, 'إنما كان قول المؤمنين إذا دعوا إلى الله ورسوله ليحكم بينهم أن يقولوا سمعنا وأطعنا وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 24, 'ومن يطع الله ورسوله ويخش الله ويتقه فأولئك هم الفائزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 24, 'وأقسموا بالله جهد أيمانهم لئن أمرتهم ليخرجن قل لا تقسموا طاعة معروفة إن الله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 24, 'قل أطيعوا الله وأطيعوا الرسول فإن تولوا فإنما عليه ما حمل وعليكم ما حملتم وإن تطيعوه تهتدوا وما على الرسول إلا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 24, 'وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم أمنا يعبدونني لا يشركون بي شيئا ومن كفر بعد ذلك فأولئك هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 24, 'وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 24, 'لا تحسبن الذين كفروا معجزين في الأرض ومأواهم النار ولبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 24, 'يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ثلاث عورات لكم ليس عليكم ولا عليهم جناح بعدهن طوافون عليكم بعضكم على بعض كذلك يبين الله لكم الآيات والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 24, 'وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم كذلك يبين الله لكم آياته والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 24, 'والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة وأن يستعففن خير لهن والله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 24, 'ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ولا على أنفسكم أن تأكلوا من بيوتكم أو بيوت آبائكم أو بيوت أمهاتكم أو بيوت إخوانكم أو بيوت أخواتكم أو بيوت أعمامكم أو بيوت عماتكم أو بيوت أخوالكم أو بيوت خالاتكم أو ما ملكتم مفاتحه أو صديقكم ليس عليكم جناح أن تأكلوا جميعا أو أشتاتا فإذا دخلتم بيوتا فسلموا على أنفسكم تحية من عند الله مباركة طيبة كذلك يبين الله لكم الآيات لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 24, 'إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه إن الذين يستأذنونك أولئك الذين يؤمنون بالله ورسوله فإذا استأذنوك لبعض شأنهم فأذن لمن شئت منهم واستغفر لهم الله إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 24, 'لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا قد يعلم الله الذين يتسللون منكم لواذا فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 24, 'ألا إن لله ما في السماوات والأرض قد يعلم ما أنتم عليه ويوم يرجعون إليه فينبئهم بما عملوا والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 25, 'بسم الله الرحمن الرحيم تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 25, 'الذي له ملك السماوات والأرض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 25, 'واتخذوا من دونه آلهة لا يخلقون شيئا وهم يخلقون ولا يملكون لأنفسهم ضرا ولا نفعا ولا يملكون موتا ولا حياة ولا نشورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 25, 'وقال الذين كفروا إن هذا إلا إفك افتراه وأعانه عليه قوم آخرون فقد جاءوا ظلما وزورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 25, 'وقالوا أساطير الأولين اكتتبها فهي تملى عليه بكرة وأصيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 25, 'قل أنزله الذي يعلم السر في السماوات والأرض إنه كان غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 25, 'وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق لولا أنزل إليه ملك فيكون معه نذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 25, 'أو يلقى إليه كنز أو تكون له جنة يأكل منها وقال الظالمون إن تتبعون إلا رجلا مسحورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 25, 'انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 25, 'تبارك الذي إن شاء جعل لك خيرا من ذلك جنات تجري من تحتها الأنهار ويجعل لك قصورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 25, 'بل كذبوا بالساعة وأعتدنا لمن كذب بالساعة سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 25, 'إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 25, 'وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 25, 'لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 25, 'قل أذلك خير أم جنة الخلد التي وعد المتقون كانت لهم جزاء ومصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 25, 'لهم فيها ما يشاءون خالدين كان على ربك وعدا مسئولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 25, 'ويوم يحشرهم وما يعبدون من دون الله فيقول أأنتم أضللتم عبادي هؤلاء أم هم ضلوا السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 25, 'قالوا سبحانك ما كان ينبغي لنا أن نتخذ من دونك من أولياء ولكن متعتهم وآباءهم حتى نسوا الذكر وكانوا قوما بورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 25, 'فقد كذبوكم بما تقولون فما تستطيعون صرفا ولا نصرا ومن يظلم منكم نذقه عذابا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 25, 'وما أرسلنا قبلك من المرسلين إلا إنهم ليأكلون الطعام ويمشون في الأسواق وجعلنا بعضكم لبعض فتنة أتصبرون وكان ربك بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 25, 'وقال الذين لا يرجون لقاءنا لولا أنزل علينا الملائكة أو نرى ربنا لقد استكبروا في أنفسهم وعتوا عتوا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 25, 'يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 25, 'وقدمنا إلى ما عملوا من عمل فجعلناه هباء منثورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 25, 'أصحاب الجنة يومئذ خير مستقرا وأحسن مقيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 25, 'ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 25, 'الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 25, 'ويوم يعض الظالم على يديه يقول يا ليتني اتخذت مع الرسول سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 25, 'يا ويلتى ليتني لم أتخذ فلانا خليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 25, 'لقد أضلني عن الذكر بعد إذ جاءني وكان الشيطان للإنسان خذولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 25, 'وقال الرسول يا رب إن قومي اتخذوا هذا القرآن مهجورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 25, 'وكذلك جعلنا لكل نبي عدوا من المجرمين وكفى بربك هاديا ونصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 25, 'وقال الذين كفروا لولا نزل عليه القرآن جملة واحدة كذلك لنثبت به فؤادك ورتلناه ترتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 25, 'ولا يأتونك بمثل إلا جئناك بالحق وأحسن تفسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 25, 'الذين يحشرون على وجوههم إلى جهنم أولئك شر مكانا وأضل سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 25, 'ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 25, 'فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 25, 'وقوم نوح لما كذبوا الرسل أغرقناهم وجعلناهم للناس آية وأعتدنا للظالمين عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 25, 'وعادا وثمود وأصحاب الرس وقرونا بين ذلك كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 25, 'وكلا ضربنا له الأمثال وكلا تبرنا تتبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 25, 'ولقد أتوا على القرية التي أمطرت مطر السوء أفلم يكونوا يرونها بل كانوا لا يرجون نشورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 25, 'وإذا رأوك إن يتخذونك إلا هزوا أهذا الذي بعث الله رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 25, 'إن كاد ليضلنا عن آلهتنا لولا أن صبرنا عليها وسوف يعلمون حين يرون العذاب من أضل سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 25, 'أرأيت من اتخذ إلهه هواه أفأنت تكون عليه وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 25, 'أم تحسب أن أكثرهم يسمعون أو يعقلون إن هم إلا كالأنعام بل هم أضل سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 25, 'ألم تر إلى ربك كيف مد الظل ولو شاء لجعله ساكنا ثم جعلنا الشمس عليه دليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 25, 'ثم قبضناه إلينا قبضا يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 25, 'وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 25, 'وهو الذي أرسل الرياح بشرا بين يدي رحمته وأنزلنا من السماء ماء طهورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 25, 'لنحيي به بلدة ميتا ونسقيه مما خلقنا أنعاما وأناسي كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 25, 'ولقد صرفناه بينهم ليذكروا فأبى أكثر الناس إلا كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 25, 'ولو شئنا لبعثنا في كل قرية نذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 25, 'فلا تطع الكافرين وجاهدهم به جهادا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 25, 'وهو الذي مرج البحرين هذا عذب فرات وهذا ملح أجاج وجعل بينهما برزخا وحجرا محجورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 25, 'وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 25, 'ويعبدون من دون الله ما لا ينفعهم ولا يضرهم وكان الكافر على ربه ظهيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 25, 'وما أرسلناك إلا مبشرا ونذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 25, 'قل ما أسألكم عليه من أجر إلا من شاء أن يتخذ إلى ربه سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 25, 'وتوكل على الحي الذي لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 25, 'الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش الرحمن فاسأل به خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 25, 'وإذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا وزادهم نفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 25, 'تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 25, 'وهو الذي جعل الليل والنهار خلفة لمن أراد أن يذكر أو أراد شكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 25, 'وعباد الرحمن الذين يمشون على الأرض هونا وإذا خاطبهم الجاهلون قالوا سلاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 25, 'والذين يبيتون لربهم سجدا وقياما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 25, 'والذين يقولون ربنا اصرف عنا عذاب جهنم إن عذابها كان غراما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 25, 'إنها ساءت مستقرا ومقاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 25, 'والذين إذا أنفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 25, 'والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله إلا بالحق ولا يزنون ومن يفعل ذلك يلق أثاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 25, 'يضاعف له العذاب يوم القيامة ويخلد فيه مهانا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 25, 'إلا من تاب وآمن وعمل عملا صالحا فأولئك يبدل الله سيئاتهم حسنات وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 25, 'ومن تاب وعمل صالحا فإنه يتوب إلى الله متابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 25, 'والذين لا يشهدون الزور وإذا مروا باللغو مروا كراما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 25, 'والذين إذا ذكروا بآيات ربهم لم يخروا عليها صما وعميانا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 25, 'والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا قرة أعين واجعلنا للمتقين إماما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 25, 'أولئك يجزون الغرفة بما صبروا ويلقون فيها تحية وسلاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 25, 'خالدين فيها حسنت مستقرا ومقاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 25, 'قل ما يعبأ بكم ربي لولا دعاؤكم فقد كذبتم فسوف يكون لزاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 26, 'بسم الله الرحمن الرحيم طسم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 26, 'تلك آيات الكتاب المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 26, 'لعلك باخع نفسك ألا يكونوا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 26, 'إن نشأ ننزل عليهم من السماء آية فظلت أعناقهم لها خاضعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 26, 'وما يأتيهم من ذكر من الرحمن محدث إلا كانوا عنه معرضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 26, 'فقد كذبوا فسيأتيهم أنباء ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 26, 'أولم يروا إلى الأرض كم أنبتنا فيها من كل زوج كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 26, 'وإذ نادى ربك موسى أن ائت القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 26, 'قوم فرعون ألا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 26, 'قال رب إني أخاف أن يكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 26, 'ويضيق صدري ولا ينطلق لساني فأرسل إلى هارون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 26, 'ولهم علي ذنب فأخاف أن يقتلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 26, 'قال كلا فاذهبا بآياتنا إنا معكم مستمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 26, 'فأتيا فرعون فقولا إنا رسول رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 26, 'أن أرسل معنا بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 26, 'قال ألم نربك فينا وليدا ولبثت فينا من عمرك سنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 26, 'وفعلت فعلتك التي فعلت وأنت من الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 26, 'قال فعلتها إذا وأنا من الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 26, 'ففررت منكم لما خفتكم فوهب لي ربي حكما وجعلني من المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 26, 'وتلك نعمة تمنها علي أن عبدت بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 26, 'قال فرعون وما رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 26, 'قال رب السماوات والأرض وما بينهما إن كنتم موقنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 26, 'قال لمن حوله ألا تستمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 26, 'قال ربكم ورب آبائكم الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 26, 'قال إن رسولكم الذي أرسل إليكم لمجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 26, 'قال رب المشرق والمغرب وما بينهما إن كنتم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 26, 'قال لئن اتخذت إلها غيري لأجعلنك من المسجونين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 26, 'قال أولو جئتك بشيء مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 26, 'قال فأت به إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 26, 'فألقى عصاه فإذا هي ثعبان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 26, 'ونزع يده فإذا هي بيضاء للناظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 26, 'قال للملإ حوله إن هذا لساحر عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 26, 'يريد أن يخرجكم من أرضكم بسحره فماذا تأمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 26, 'قالوا أرجه وأخاه وابعث في المدائن حاشرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 26, 'يأتوك بكل سحار عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 26, 'فجمع السحرة لميقات يوم معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 26, 'وقيل للناس هل أنتم مجتمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 26, 'لعلنا نتبع السحرة إن كانوا هم الغالبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 26, 'فلما جاء السحرة قالوا لفرعون أئن لنا لأجرا إن كنا نحن الغالبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 26, 'قال نعم وإنكم إذا لمن المقربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 26, 'قال لهم موسى ألقوا ما أنتم ملقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 26, 'فألقوا حبالهم وعصيهم وقالوا بعزة فرعون إنا لنحن الغالبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 26, 'فألقى موسى عصاه فإذا هي تلقف ما يأفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 26, 'فألقي السحرة ساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 26, 'قالوا آمنا برب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 26, 'رب موسى وهارون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 26, 'قال آمنتم له قبل أن آذن لكم إنه لكبيركم الذي علمكم السحر فلسوف تعلمون لأقطعن أيديكم وأرجلكم من خلاف ولأصلبنكم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 26, 'قالوا لا ضير إنا إلى ربنا منقلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 26, 'إنا نطمع أن يغفر لنا ربنا خطايانا أن كنا أول المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 26, 'وأوحينا إلى موسى أن أسر بعبادي إنكم متبعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 26, 'فأرسل فرعون في المدائن حاشرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 26, 'إن هؤلاء لشرذمة قليلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 26, 'وإنهم لنا لغائظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 26, 'وإنا لجميع حاذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 26, 'فأخرجناهم من جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 26, 'وكنوز ومقام كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 26, 'كذلك وأورثناها بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 26, 'فأتبعوهم مشرقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 26, 'فلما تراءى الجمعان قال أصحاب موسى إنا لمدركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 26, 'قال كلا إن معي ربي سيهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 26, 'فأوحينا إلى موسى أن اضرب بعصاك البحر فانفلق فكان كل فرق كالطود العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 26, 'وأزلفنا ثم الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 26, 'وأنجينا موسى ومن معه أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 26, 'ثم أغرقنا الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 26, 'واتل عليهم نبأ إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 26, 'إذ قال لأبيه وقومه ما تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 26, 'قالوا نعبد أصناما فنظل لها عاكفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 26, 'قال هل يسمعونكم إذ تدعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 26, 'أو ينفعونكم أو يضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 26, 'قالوا بل وجدنا آباءنا كذلك يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 26, 'قال أفرأيتم ما كنتم تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 26, 'أنتم وآباؤكم الأقدمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 26, 'فإنهم عدو لي إلا رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 26, 'الذي خلقني فهو يهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 26, 'والذي هو يطعمني ويسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 26, 'وإذا مرضت فهو يشفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 26, 'والذي يميتني ثم يحيين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 26, 'والذي أطمع أن يغفر لي خطيئتي يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 26, 'رب هب لي حكما وألحقني بالصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 26, 'واجعل لي لسان صدق في الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 26, 'واجعلني من ورثة جنة النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 26, 'واغفر لأبي إنه كان من الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 26, 'ولا تخزني يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 26, 'يوم لا ينفع مال ولا بنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 26, 'إلا من أتى الله بقلب سليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 26, 'وأزلفت الجنة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 26, 'وبرزت الجحيم للغاوين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 26, 'وقيل لهم أين ما كنتم تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 26, 'من دون الله هل ينصرونكم أو ينتصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 26, 'فكبكبوا فيها هم والغاوون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 26, 'وجنود إبليس أجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 26, 'قالوا وهم فيها يختصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 26, 'تالله إن كنا لفي ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 26, 'إذ نسويكم برب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 26, 'وما أضلنا إلا المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 26, 'فما لنا من شافعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 26, 'ولا صديق حميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 26, 'فلو أن لنا كرة فنكون من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 26, 'كذبت قوم نوح المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 26, 'إذ قال لهم أخوهم نوح ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 26, 'إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 26, 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 26, 'قالوا أنؤمن لك واتبعك الأرذلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 26, 'قال وما علمي بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 26, 'إن حسابهم إلا على ربي لو تشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 26, 'وما أنا بطارد المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 26, 'إن أنا إلا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 26, 'قالوا لئن لم تنته يا نوح لتكونن من المرجومين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 26, 'قال رب إن قومي كذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 26, 'فافتح بيني وبينهم فتحا ونجني ومن معي من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 26, 'فأنجيناه ومن معه في الفلك المشحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 26, 'ثم أغرقنا بعد الباقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 26, 'كذبت عاد المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 26, 'إذ قال لهم أخوهم هود ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 26, 'إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 26, 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 26, 'أتبنون بكل ريع آية تعبثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 26, 'وتتخذون مصانع لعلكم تخلدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 26, 'وإذا بطشتم بطشتم جبارين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 26, 'واتقوا الذي أمدكم بما تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 26, 'أمدكم بأنعام وبنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 26, 'وجنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 26, 'إني أخاف عليكم عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 26, 'قالوا سواء علينا أوعظت أم لم تكن من الواعظين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 26, 'إن هذا إلا خلق الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 26, 'وما نحن بمعذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 26, 'فكذبوه فأهلكناهم إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 26, 'كذبت ثمود المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 26, 'إذ قال لهم أخوهم صالح ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 26, 'إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 26, 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 26, 'أتتركون في ما هاهنا آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 26, 'في جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 26, 'وزروع ونخل طلعها هضيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 26, 'وتنحتون من الجبال بيوتا فارهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 26, 'ولا تطيعوا أمر المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 26, 'الذين يفسدون في الأرض ولا يصلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 26, 'قالوا إنما أنت من المسحرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 26, 'ما أنت إلا بشر مثلنا فأت بآية إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 26, 'قال هذه ناقة لها شرب ولكم شرب يوم معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 26, 'ولا تمسوها بسوء فيأخذكم عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 26, 'فعقروها فأصبحوا نادمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 26, 'فأخذهم العذاب إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 26, 'كذبت قوم لوط المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 26, 'إذ قال لهم أخوهم لوط ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 26, 'إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 26, 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 26, 'أتأتون الذكران من العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 26, 'وتذرون ما خلق لكم ربكم من أزواجكم بل أنتم قوم عادون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 26, 'قالوا لئن لم تنته يا لوط لتكونن من المخرجين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 26, 'قال إني لعملكم من القالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 26, 'رب نجني وأهلي مما يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 26, 'فنجيناه وأهله أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 26, 'إلا عجوزا في الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 26, 'ثم دمرنا الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 26, 'وأمطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 26, 'كذب أصحاب الأيكة المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(177, 1, 26, 'إذ قال لهم شعيب ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(178, 1, 26, 'إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(179, 1, 26, 'فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(180, 1, 26, 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(181, 1, 26, 'أوفوا الكيل ولا تكونوا من المخسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(182, 1, 26, 'وزنوا بالقسطاس المستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(183, 1, 26, 'ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(184, 1, 26, 'واتقوا الذي خلقكم والجبلة الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(185, 1, 26, 'قالوا إنما أنت من المسحرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(186, 1, 26, 'وما أنت إلا بشر مثلنا وإن نظنك لمن الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(187, 1, 26, 'فأسقط علينا كسفا من السماء إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(188, 1, 26, 'قال ربي أعلم بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(189, 1, 26, 'فكذبوه فأخذهم عذاب يوم الظلة إنه كان عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(190, 1, 26, 'إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(191, 1, 26, 'وإن ربك لهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(192, 1, 26, 'وإنه لتنزيل رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(193, 1, 26, 'نزل به الروح الأمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(194, 1, 26, 'على قلبك لتكون من المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(195, 1, 26, 'بلسان عربي مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(196, 1, 26, 'وإنه لفي زبر الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(197, 1, 26, 'أولم يكن لهم آية أن يعلمه علماء بني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(198, 1, 26, 'ولو نزلناه على بعض الأعجمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(199, 1, 26, 'فقرأه عليهم ما كانوا به مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(200, 1, 26, 'كذلك سلكناه في قلوب المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(201, 1, 26, 'لا يؤمنون به حتى يروا العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(202, 1, 26, 'فيأتيهم بغتة وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(203, 1, 26, 'فيقولوا هل نحن منظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(204, 1, 26, 'أفبعذابنا يستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(205, 1, 26, 'أفرأيت إن متعناهم سنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(206, 1, 26, 'ثم جاءهم ما كانوا يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(207, 1, 26, 'ما أغنى عنهم ما كانوا يمتعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(208, 1, 26, 'وما أهلكنا من قرية إلا لها منذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(209, 1, 26, 'ذكرى وما كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(210, 1, 26, 'وما تنزلت به الشياطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(211, 1, 26, 'وما ينبغي لهم وما يستطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(212, 1, 26, 'إنهم عن السمع لمعزولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(213, 1, 26, 'فلا تدع مع الله إلها آخر فتكون من المعذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(214, 1, 26, 'وأنذر عشيرتك الأقربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(215, 1, 26, 'واخفض جناحك لمن اتبعك من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(216, 1, 26, 'فإن عصوك فقل إني بريء مما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(217, 1, 26, 'وتوكل على العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(218, 1, 26, 'الذي يراك حين تقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(219, 1, 26, 'وتقلبك في الساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(220, 1, 26, 'إنه هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(221, 1, 26, 'هل أنبئكم على من تنزل الشياطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(222, 1, 26, 'تنزل على كل أفاك أثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(223, 1, 26, 'يلقون السمع وأكثرهم كاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(224, 1, 26, 'والشعراء يتبعهم الغاوون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(225, 1, 26, 'ألم تر أنهم في كل واد يهيمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(226, 1, 26, 'وأنهم يقولون ما لا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(227, 1, 26, 'إلا الذين آمنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا وسيعلم الذين ظلموا أي منقلب ينقلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 27, 'بسم الله الرحمن الرحيم طس تلك آيات القرآن وكتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 27, 'هدى وبشرى للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 27, 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 27, 'إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم فهم يعمهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 27, 'أولئك الذين لهم سوء العذاب وهم في الآخرة هم الأخسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 27, 'وإنك لتلقى القرآن من لدن حكيم عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 27, 'إذ قال موسى لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 27, 'فلما جاءها نودي أن بورك من في النار ومن حولها وسبحان الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 27, 'يا موسى إنه أنا الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 27, 'وألق عصاك فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب يا موسى لا تخف إني لا يخاف لدي المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 27, 'إلا من ظلم ثم بدل حسنا بعد سوء فإني غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 27, 'وأدخل يدك في جيبك تخرج بيضاء من غير سوء في تسع آيات إلى فرعون وقومه إنهم كانوا قوما فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 27, 'فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 27, 'وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا فانظر كيف كان عاقبة المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 27, 'ولقد آتينا داوود وسليمان علما وقالا الحمد لله الذي فضلنا على كثير من عباده المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 27, 'وورث سليمان داوود وقال يا أيها الناس علمنا منطق الطير وأوتينا من كل شيء إن هذا لهو الفضل المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 27, 'وحشر لسليمان جنوده من الجن والإنس والطير فهم يوزعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 27, 'حتى إذا أتوا على واد النمل قالت نملة يا أيها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 27, 'فتبسم ضاحكا من قولها وقال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأدخلني برحمتك في عبادك الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 27, 'وتفقد الطير فقال ما لي لا أرى الهدهد أم كان من الغائبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 27, 'لأعذبنه عذابا شديدا أو لأذبحنه أو ليأتيني بسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 27, 'فمكث غير بعيد فقال أحطت بما لم تحط به وجئتك من سبإ بنبإ يقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 27, 'إني وجدت امرأة تملكهم وأوتيت من كل شيء ولها عرش عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 27, 'وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطان أعمالهم فصدهم عن السبيل فهم لا يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 27, 'ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 27, 'الله لا إله إلا هو رب العرش العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 27, 'قال سننظر أصدقت أم كنت من الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 27, 'اذهب بكتابي هذا فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 27, 'قالت يا أيها الملأ إني ألقي إلي كتاب كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 27, 'إنه من سليمان وإنه بسم الله الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 27, 'ألا تعلوا علي وأتوني مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 27, 'قالت يا أيها الملأ أفتوني في أمري ما كنت قاطعة أمرا حتى تشهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 27, 'قالوا نحن أولو قوة وأولو بأس شديد والأمر إليك فانظري ماذا تأمرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 27, 'قالت إن الملوك إذا دخلوا قرية أفسدوها وجعلوا أعزة أهلها أذلة وكذلك يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 27, 'وإني مرسلة إليهم بهدية فناظرة بم يرجع المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 27, 'فلما جاء سليمان قال أتمدونن بمال فما آتاني الله خير مما آتاكم بل أنتم بهديتكم تفرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 27, 'ارجع إليهم فلنأتينهم بجنود لا قبل لهم بها ولنخرجنهم منها أذلة وهم صاغرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 27, 'قال يا أيها الملأ أيكم يأتيني بعرشها قبل أن يأتوني مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 27, 'قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك وإني عليه لقوي أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 27, 'قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك فلما رآه مستقرا عنده قال هذا من فضل ربي ليبلوني أأشكر أم أكفر ومن شكر فإنما يشكر لنفسه ومن كفر فإن ربي غني كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 27, 'قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 27, 'فلما جاءت قيل أهكذا عرشك قالت كأنه هو وأوتينا العلم من قبلها وكنا مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 27, 'وصدها ما كانت تعبد من دون الله إنها كانت من قوم كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 27, 'قيل لها ادخلي الصرح فلما رأته حسبته لجة وكشفت عن ساقيها قال إنه صرح ممرد من قوارير قالت رب إني ظلمت نفسي وأسلمت مع سليمان لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 27, 'ولقد أرسلنا إلى ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 27, 'قال يا قوم لم تستعجلون بالسيئة قبل الحسنة لولا تستغفرون الله لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 27, 'قالوا اطيرنا بك وبمن معك قال طائركم عند الله بل أنتم قوم تفتنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 27, 'وكان في المدينة تسعة رهط يفسدون في الأرض ولا يصلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 27, 'قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 27, 'ومكروا مكرا ومكرنا مكرا وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 27, 'فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 27, 'فتلك بيوتهم خاوية بما ظلموا إن في ذلك لآية لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 27, 'وأنجينا الذين آمنوا وكانوا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 27, 'ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 27, 'أئنكم لتأتون الرجال شهوة من دون النساء بل أنتم قوم تجهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 27, 'فما كان جواب قومه إلا أن قالوا أخرجوا آل لوط من قريتكم إنهم أناس يتطهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 27, 'فأنجيناه وأهله إلا امرأته قدرناها من الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 27, 'وأمطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 27, 'قل الحمد لله وسلام على عباده الذين اصطفى آلله خير أما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 27, 'أمن خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة ما كان لكم أن تنبتوا شجرها أإله مع الله بل هم قوم يعدلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 27, 'أمن جعل الأرض قرارا وجعل خلالها أنهارا وجعل لها رواسي وجعل بين البحرين حاجزا أإله مع الله بل أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 27, 'أمن يجيب المضطر إذا دعاه ويكشف السوء ويجعلكم خلفاء الأرض أإله مع الله قليلا ما تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 27, 'أمن يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته أإله مع الله تعالى الله عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 27, 'أمن يبدأ الخلق ثم يعيده ومن يرزقكم من السماء والأرض أإله مع الله قل هاتوا برهانكم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 27, 'قل لا يعلم من في السماوات والأرض الغيب إلا الله وما يشعرون أيان يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 27, 'بل ادارك علمهم في الآخرة بل هم في شك منها بل هم منها عمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 27, 'وقال الذين كفروا أإذا كنا ترابا وآباؤنا أئنا لمخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 27, 'لقد وعدنا هذا نحن وآباؤنا من قبل إن هذا إلا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 27, 'قل سيروا في الأرض فانظروا كيف كان عاقبة المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 27, 'ولا تحزن عليهم ولا تكن في ضيق مما يمكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 27, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 27, 'قل عسى أن يكون ردف لكم بعض الذي تستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 27, 'وإن ربك لذو فضل على الناس ولكن أكثرهم لا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 27, 'وإن ربك ليعلم ما تكن صدورهم وما يعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 27, 'وما من غائبة في السماء والأرض إلا في كتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 27, 'إن هذا القرآن يقص على بني إسرائيل أكثر الذي هم فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 27, 'وإنه لهدى ورحمة للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 27, 'إن ربك يقضي بينهم بحكمه وهو العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 27, 'فتوكل على الله إنك على الحق المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 27, 'إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 27, 'وما أنت بهادي العمي عن ضلالتهم إن تسمع إلا من يؤمن بآياتنا فهم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 27, 'وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم أن الناس كانوا بآياتنا لا يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 27, 'ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 27, 'حتى إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أماذا كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 27, 'ووقع القول عليهم بما ظلموا فهم لا ينطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 27, 'ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا إن في ذلك لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 27, 'ويوم ينفخ في الصور ففزع من في السماوات ومن في الأرض إلا من شاء الله وكل أتوه داخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 27, 'وترى الجبال تحسبها جامدة وهي تمر مر السحاب صنع الله الذي أتقن كل شيء إنه خبير بما تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 27, 'من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 27, 'ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 27, 'إنما أمرت أن أعبد رب هذه البلدة الذي حرمها وله كل شيء وأمرت أن أكون من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 27, 'وأن أتلو القرآن فمن اهتدى فإنما يهتدي لنفسه ومن ضل فقل إنما أنا من المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 27, 'وقل الحمد لله سيريكم آياته فتعرفونها وما ربك بغافل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 28, 'بسم الله الرحمن الرحيم طسم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 28, 'تلك آيات الكتاب المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 28, 'نتلو عليك من نبإ موسى وفرعون بالحق لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 28, 'إن فرعون علا في الأرض وجعل أهلها شيعا يستضعف طائفة منهم يذبح أبناءهم ويستحيي نساءهم إنه كان من المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 28, 'ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة ونجعلهم الوارثين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 28, 'ونمكن لهم في الأرض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 28, 'وأوحينا إلى أم موسى أن أرضعيه فإذا خفت عليه فألقيه في اليم ولا تخافي ولا تحزني إنا رادوه إليك وجاعلوه من المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 28, 'فالتقطه آل فرعون ليكون لهم عدوا وحزنا إن فرعون وهامان وجنودهما كانوا خاطئين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 28, 'وقالت امرأت فرعون قرت عين لي ولك لا تقتلوه عسى أن ينفعنا أو نتخذه ولدا وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 28, 'وأصبح فؤاد أم موسى فارغا إن كادت لتبدي به لولا أن ربطنا على قلبها لتكون من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 28, 'وقالت لأخته قصيه فبصرت به عن جنب وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 28, 'وحرمنا عليه المراضع من قبل فقالت هل أدلكم على أهل بيت يكفلونه لكم وهم له ناصحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 28, 'فرددناه إلى أمه كي تقر عينها ولا تحزن ولتعلم أن وعد الله حق ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 28, 'ولما بلغ أشده واستوى آتيناه حكما وعلما وكذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 28, 'ودخل المدينة على حين غفلة من أهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسى فقضى عليه قال هذا من عمل الشيطان إنه عدو مضل مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 28, 'قال رب إني ظلمت نفسي فاغفر لي فغفر له إنه هو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 28, 'قال رب بما أنعمت علي فلن أكون ظهيرا للمجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 28, 'فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه قال له موسى إنك لغوي مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 28, 'فلما أن أراد أن يبطش بالذي هو عدو لهما قال يا موسى أتريد أن تقتلني كما قتلت نفسا بالأمس إن تريد إلا أن تكون جبارا في الأرض وما تريد أن تكون من المصلحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 28, 'وجاء رجل من أقصى المدينة يسعى قال يا موسى إن الملأ يأتمرون بك ليقتلوك فاخرج إني لك من الناصحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 28, 'فخرج منها خائفا يترقب قال رب نجني من القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 28, 'ولما توجه تلقاء مدين قال عسى ربي أن يهديني سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 28, 'ولما ورد ماء مدين وجد عليه أمة من الناس يسقون ووجد من دونهم امرأتين تذودان قال ما خطبكما قالتا لا نسقي حتى يصدر الرعاء وأبونا شيخ كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 28, 'فسقى لهما ثم تولى إلى الظل فقال رب إني لما أنزلت إلي من خير فقير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 28, 'فجاءته إحداهما تمشي على استحياء قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا فلما جاءه وقص عليه القصص قال لا تخف نجوت من القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 28, 'قالت إحداهما يا أبت استأجره إن خير من استأجرت القوي الأمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 28, 'قال إني أريد أن أنكحك إحدى ابنتي هاتين على أن تأجرني ثماني حجج فإن أتممت عشرا فمن عندك وما أريد أن أشق عليك ستجدني إن شاء الله من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 28, 'قال ذلك بيني وبينك أيما الأجلين قضيت فلا عدوان علي والله على ما نقول وكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 28, 'فلما قضى موسى الأجل وسار بأهله آنس من جانب الطور نارا قال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بخبر أو جذوة من النار لعلكم تصطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 28, 'فلما أتاها نودي من شاطئ الواد الأيمن في البقعة المباركة من الشجرة أن يا موسى إني أنا الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 28, 'وأن ألق عصاك فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب يا موسى أقبل ولا تخف إنك من الآمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 28, 'اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم إليك جناحك من الرهب فذانك برهانان من ربك إلى فرعون وملئه إنهم كانوا قوما فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 28, 'قال رب إني قتلت منهم نفسا فأخاف أن يقتلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 28, 'وأخي هارون هو أفصح مني لسانا فأرسله معي ردءا يصدقني إني أخاف أن يكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 28, 'قال سنشد عضدك بأخيك ونجعل لكما سلطانا فلا يصلون إليكما بآياتنا أنتما ومن اتبعكما الغالبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 28, 'فلما جاءهم موسى بآياتنا بينات قالوا ما هذا إلا سحر مفترى وما سمعنا بهذا في آبائنا الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 28, 'وقال موسى ربي أعلم بمن جاء بالهدى من عنده ومن تكون له عاقبة الدار إنه لا يفلح الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 28, 'وقال فرعون يا أيها الملأ ما علمت لكم من إله غيري فأوقد لي يا هامان على الطين فاجعل لي صرحا لعلي أطلع إلى إله موسى وإني لأظنه من الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 28, 'واستكبر هو وجنوده في الأرض بغير الحق وظنوا أنهم إلينا لا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 28, 'فأخذناه وجنوده فنبذناهم في اليم فانظر كيف كان عاقبة الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 28, 'وجعلناهم أئمة يدعون إلى النار ويوم القيامة لا ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 28, 'وأتبعناهم في هذه الدنيا لعنة ويوم القيامة هم من المقبوحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 28, 'ولقد آتينا موسى الكتاب من بعد ما أهلكنا القرون الأولى بصائر للناس وهدى ورحمة لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 28, 'وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر وما كنت من الشاهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 28, 'ولكنا أنشأنا قرونا فتطاول عليهم العمر وما كنت ثاويا في أهل مدين تتلو عليهم آياتنا ولكنا كنا مرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 28, 'وما كنت بجانب الطور إذ نادينا ولكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 28, 'ولولا أن تصيبهم مصيبة بما قدمت أيديهم فيقولوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك ونكون من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 28, 'فلما جاءهم الحق من عندنا قالوا لولا أوتي مثل ما أوتي موسى أولم يكفروا بما أوتي موسى من قبل قالوا سحران تظاهرا وقالوا إنا بكل كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 28, 'قل فأتوا بكتاب من عند الله هو أهدى منهما أتبعه إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 28, 'فإن لم يستجيبوا لك فاعلم أنما يتبعون أهواءهم ومن أضل ممن اتبع هواه بغير هدى من الله إن الله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 28, 'ولقد وصلنا لهم القول لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 28, 'الذين آتيناهم الكتاب من قبله هم به يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 28, 'وإذا يتلى عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 28, 'أولئك يؤتون أجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 28, 'وإذا سمعوا اللغو أعرضوا عنه وقالوا لنا أعمالنا ولكم أعمالكم سلام عليكم لا نبتغي الجاهلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 28, 'إنك لا تهدي من أحببت ولكن الله يهدي من يشاء وهو أعلم بالمهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 28, 'وقالوا إن نتبع الهدى معك نتخطف من أرضنا أولم نمكن لهم حرما آمنا يجبى إليه ثمرات كل شيء رزقا من لدنا ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 28, 'وكم أهلكنا من قرية بطرت معيشتها فتلك مساكنهم لم تسكن من بعدهم إلا قليلا وكنا نحن الوارثين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 28, 'وما كان ربك مهلك القرى حتى يبعث في أمها رسولا يتلو عليهم آياتنا وما كنا مهلكي القرى إلا وأهلها ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 28, 'وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها وما عند الله خير وأبقى أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 28, 'أفمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 28, 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 28, 'قال الذين حق عليهم القول ربنا هؤلاء الذين أغوينا أغويناهم كما غوينا تبرأنا إليك ما كانوا إيانا يعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 28, 'وقيل ادعوا شركاءكم فدعوهم فلم يستجيبوا لهم ورأوا العذاب لو أنهم كانوا يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 28, 'ويوم يناديهم فيقول ماذا أجبتم المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 28, 'فعميت عليهم الأنباء يومئذ فهم لا يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 28, 'فأما من تاب وآمن وعمل صالحا فعسى أن يكون من المفلحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 28, 'وربك يخلق ما يشاء ويختار ما كان لهم الخيرة سبحان الله وتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 28, 'وربك يعلم ما تكن صدورهم وما يعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 28, 'وهو الله لا إله إلا هو له الحمد في الأولى والآخرة وله الحكم وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 28, 'قل أرأيتم إن جعل الله عليكم الليل سرمدا إلى يوم القيامة من إله غير الله يأتيكم بضياء أفلا تسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 28, 'قل أرأيتم إن جعل الله عليكم النهار سرمدا إلى يوم القيامة من إله غير الله يأتيكم بليل تسكنون فيه أفلا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 28, 'ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 28, 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 28, 'ونزعنا من كل أمة شهيدا فقلنا هاتوا برهانكم فعلموا أن الحق لله وضل عنهم ما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 28, 'إن قارون كان من قوم موسى فبغى عليهم وآتيناه من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة إذ قال له قومه لا تفرح إن الله لا يحب الفرحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 28, 'وابتغ فيما آتاك الله الدار الآخرة ولا تنس نصيبك من الدنيا وأحسن كما أحسن الله إليك ولا تبغ الفساد في الأرض إن الله لا يحب المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 28, 'قال إنما أوتيته على علم عندي أولم يعلم أن الله قد أهلك من قبله من القرون من هو أشد منه قوة وأكثر جمعا ولا يسأل عن ذنوبهم المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 28, 'فخرج على قومه في زينته قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 28, 'وقال الذين أوتوا العلم ويلكم ثواب الله خير لمن آمن وعمل صالحا ولا يلقاها إلا الصابرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 28, 'فخسفنا به وبداره الأرض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 28, 'وأصبح الذين تمنوا مكانه بالأمس يقولون ويكأن الله يبسط الرزق لمن يشاء من عباده ويقدر لولا أن من الله علينا لخسف بنا ويكأنه لا يفلح الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 28, 'تلك الدار الآخرة نجعلها للذين لا يريدون علوا في الأرض ولا فسادا والعاقبة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 28, 'من جاء بالحسنة فله خير منها ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات إلا ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 28, 'إن الذي فرض عليك القرآن لرادك إلى معاد قل ربي أعلم من جاء بالهدى ومن هو في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 28, 'وما كنت ترجو أن يلقى إليك الكتاب إلا رحمة من ربك فلا تكونن ظهيرا للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 28, 'ولا يصدنك عن آيات الله بعد إذ أنزلت إليك وادع إلى ربك ولا تكونن من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 28, 'ولا تدع مع الله إلها آخر لا إله إلا هو كل شيء هالك إلا وجهه له الحكم وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 29, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 29, 'أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 29, 'ولقد فتنا الذين من قبلهم فليعلمن الله الذين صدقوا وليعلمن الكاذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 29, 'أم حسب الذين يعملون السيئات أن يسبقونا ساء ما يحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 29, 'من كان يرجو لقاء الله فإن أجل الله لآت وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 29, 'ومن جاهد فإنما يجاهد لنفسه إن الله لغني عن العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 29, 'والذين آمنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم ولنجزينهم أحسن الذي كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 29, 'ووصينا الإنسان بوالديه حسنا وإن جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما إلي مرجعكم فأنبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 29, 'والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 29, 'ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن إنا كنا معكم أوليس الله بأعلم بما في صدور العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 29, 'وليعلمن الله الذين آمنوا وليعلمن المنافقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 29, 'وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء إنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 29, 'وليحملن أثقالهم وأثقالا مع أثقالهم وليسألن يوم القيامة عما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 29, 'ولقد أرسلنا نوحا إلى قومه فلبث فيهم ألف سنة إلا خمسين عاما فأخذهم الطوفان وهم ظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 29, 'فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 29, 'وإبراهيم إذ قال لقومه اعبدوا الله واتقوه ذلكم خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 29, 'إنما تعبدون من دون الله أوثانا وتخلقون إفكا إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه واشكروا له إليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 29, 'وإن تكذبوا فقد كذب أمم من قبلكم وما على الرسول إلا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 29, 'أولم يروا كيف يبدئ الله الخلق ثم يعيده إن ذلك على الله يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 29, 'قل سيروا في الأرض فانظروا كيف بدأ الخلق ثم الله ينشئ النشأة الآخرة إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 29, 'يعذب من يشاء ويرحم من يشاء وإليه تقلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 29, 'وما أنتم بمعجزين في الأرض ولا في السماء وما لكم من دون الله من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 29, 'والذين كفروا بآيات الله ولقائه أولئك يئسوا من رحمتي وأولئك لهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 29, 'فما كان جواب قومه إلا أن قالوا اقتلوه أو حرقوه فأنجاه الله من النار إن في ذلك لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 29, 'وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا ومأواكم النار وما لكم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 29, 'فآمن له لوط وقال إني مهاجر إلى ربي إنه هو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 29, 'ووهبنا له إسحاق ويعقوب وجعلنا في ذريته النبوة والكتاب وآتيناه أجره في الدنيا وإنه في الآخرة لمن الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 29, 'ولوطا إذ قال لقومه إنكم لتأتون الفاحشة ما سبقكم بها من أحد من العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 29, 'أئنكم لتأتون الرجال وتقطعون السبيل وتأتون في ناديكم المنكر فما كان جواب قومه إلا أن قالوا ائتنا بعذاب الله إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 29, 'قال رب انصرني على القوم المفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 29, 'ولما جاءت رسلنا إبراهيم بالبشرى قالوا إنا مهلكو أهل هذه القرية إن أهلها كانوا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 29, 'قال إن فيها لوطا قالوا نحن أعلم بمن فيها لننجينه وأهله إلا امرأته كانت من الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 29, 'ولما أن جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن إنا منجوك وأهلك إلا امرأتك كانت من الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 29, 'إنا منزلون على أهل هذه القرية رجزا من السماء بما كانوا يفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 29, 'ولقد تركنا منها آية بينة لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 29, 'وإلى مدين أخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الآخر ولا تعثوا في الأرض مفسدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 29, 'فكذبوه فأخذتهم الرجفة فأصبحوا في دارهم جاثمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 29, 'وعادا وثمود وقد تبين لكم من مساكنهم وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 29, 'وقارون وفرعون وهامان ولقد جاءهم موسى بالبينات فاستكبروا في الأرض وما كانوا سابقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 29, 'فكلا أخذنا بذنبه فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا وما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 29, 'مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا وإن أوهن البيوت لبيت العنكبوت لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 29, 'إن الله يعلم ما يدعون من دونه من شيء وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 29, 'وتلك الأمثال نضربها للناس وما يعقلها إلا العالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 29, 'خلق الله السماوات والأرض بالحق إن في ذلك لآية للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 29, 'اتل ما أوحي إليك من الكتاب وأقم الصلاة إن الصلاة تنهى عن الفحشاء والمنكر ولذكر الله أكبر والله يعلم ما تصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 29, 'ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم وقولوا آمنا بالذي أنزل إلينا وأنزل إليكم وإلهنا وإلهكم واحد ونحن له مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 29, 'وكذلك أنزلنا إليك الكتاب فالذين آتيناهم الكتاب يؤمنون به ومن هؤلاء من يؤمن به وما يجحد بآياتنا إلا الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 29, 'وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك إذا لارتاب المبطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 29, 'بل هو آيات بينات في صدور الذين أوتوا العلم وما يجحد بآياتنا إلا الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 29, 'وقالوا لولا أنزل عليه آيات من ربه قل إنما الآيات عند الله وإنما أنا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 29, 'أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم إن في ذلك لرحمة وذكرى لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 29, 'قل كفى بالله بيني وبينكم شهيدا يعلم ما في السماوات والأرض والذين آمنوا بالباطل وكفروا بالله أولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 29, 'ويستعجلونك بالعذاب ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 29, 'يستعجلونك بالعذاب وإن جهنم لمحيطة بالكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 29, 'يوم يغشاهم العذاب من فوقهم ومن تحت أرجلهم ويقول ذوقوا ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 29, 'يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 29, 'كل نفس ذائقة الموت ثم إلينا ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 29, 'والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الأنهار خالدين فيها نعم أجر العاملين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 29, 'الذين صبروا وعلى ربهم يتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 29, 'وكأين من دابة لا تحمل رزقها الله يرزقها وإياكم وهو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 29, 'ولئن سألتهم من خلق السماوات والأرض وسخر الشمس والقمر ليقولن الله فأنى يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 29, 'الله يبسط الرزق لمن يشاء من عباده ويقدر له إن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 29, 'ولئن سألتهم من نزل من السماء ماء فأحيا به الأرض من بعد موتها ليقولن الله قل الحمد لله بل أكثرهم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 29, 'وما هذه الحياة الدنيا إلا لهو ولعب وإن الدار الآخرة لهي الحيوان لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 29, 'فإذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم إلى البر إذا هم يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 29, 'ليكفروا بما آتيناهم وليتمتعوا فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 29, 'أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم أفبالباطل يؤمنون وبنعمة الله يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 29, 'ومن أظلم ممن افترى على الله كذبا أو كذب بالحق لما جاءه أليس في جهنم مثوى للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 29, 'والذين جاهدوا فينا لنهدينهم سبلنا وإن الله لمع المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 30, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 30, 'غلبت الروم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 30, 'في أدنى الأرض وهم من بعد غلبهم سيغلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 30, 'في بضع سنين لله الأمر من قبل ومن بعد ويومئذ يفرح المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 30, 'بنصر الله ينصر من يشاء وهو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 30, 'وعد الله لا يخلف الله وعده ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 30, 'يعلمون ظاهرا من الحياة الدنيا وهم عن الآخرة هم غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 30, 'أولم يتفكروا في أنفسهم ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى وإن كثيرا من الناس بلقاء ربهم لكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 30, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أشد منهم قوة وأثاروا الأرض وعمروها أكثر مما عمروها وجاءتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 30, 'ثم كان عاقبة الذين أساءوا السوأى أن كذبوا بآيات الله وكانوا بها يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 30, 'الله يبدأ الخلق ثم يعيده ثم إليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 30, 'ويوم تقوم الساعة يبلس المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 30, 'ولم يكن لهم من شركائهم شفعاء وكانوا بشركائهم كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 30, 'ويوم تقوم الساعة يومئذ يتفرقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 30, 'فأما الذين آمنوا وعملوا الصالحات فهم في روضة يحبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 30, 'وأما الذين كفروا وكذبوا بآياتنا ولقاء الآخرة فأولئك في العذاب محضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 30, 'فسبحان الله حين تمسون وحين تصبحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 30, 'وله الحمد في السماوات والأرض وعشيا وحين تظهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 30, 'يخرج الحي من الميت ويخرج الميت من الحي ويحيي الأرض بعد موتها وكذلك تخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 30, 'ومن آياته أن خلقكم من تراب ثم إذا أنتم بشر تنتشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 30, 'ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها وجعل بينكم مودة ورحمة إن في ذلك لآيات لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 30, 'ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم إن في ذلك لآيات للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 30, 'ومن آياته منامكم بالليل والنهار وابتغاؤكم من فضله إن في ذلك لآيات لقوم يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 30, 'ومن آياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الأرض بعد موتها إن في ذلك لآيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 30, 'ومن آياته أن تقوم السماء والأرض بأمره ثم إذا دعاكم دعوة من الأرض إذا أنتم تخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 30, 'وله من في السماوات والأرض كل له قانتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 30, 'وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه وله المثل الأعلى في السماوات والأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 30, 'ضرب لكم مثلا من أنفسكم هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم فأنتم فيه سواء تخافونهم كخيفتكم أنفسكم كذلك نفصل الآيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 30, 'بل اتبع الذين ظلموا أهواءهم بغير علم فمن يهدي من أضل الله وما لهم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 30, 'فأقم وجهك للدين حنيفا فطرت الله التي فطر الناس عليها لا تبديل لخلق الله ذلك الدين القيم ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 30, 'منيبين إليه واتقوه وأقيموا الصلاة ولا تكونوا من المشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 30, 'من الذين فرقوا دينهم وكانوا شيعا كل حزب بما لديهم فرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 30, 'وإذا مس الناس ضر دعوا ربهم منيبين إليه ثم إذا أذاقهم منه رحمة إذا فريق منهم بربهم يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 30, 'ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 30, 'أم أنزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 30, 'وإذا أذقنا الناس رحمة فرحوا بها وإن تصبهم سيئة بما قدمت أيديهم إذا هم يقنطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 30, 'أولم يروا أن الله يبسط الرزق لمن يشاء ويقدر إن في ذلك لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 30, 'فآت ذا القربى حقه والمسكين وابن السبيل ذلك خير للذين يريدون وجه الله وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 30, 'وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله وما آتيتم من زكاة تريدون وجه الله فأولئك هم المضعفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 30, 'الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم هل من شركائكم من يفعل من ذلكم من شيء سبحانه وتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 30, 'ظهر الفساد في البر والبحر بما كسبت أيدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 30, 'قل سيروا في الأرض فانظروا كيف كان عاقبة الذين من قبل كان أكثرهم مشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 30, 'فأقم وجهك للدين القيم من قبل أن يأتي يوم لا مرد له من الله يومئذ يصدعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 30, 'من كفر فعليه كفره ومن عمل صالحا فلأنفسهم يمهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 30, 'ليجزي الذين آمنوا وعملوا الصالحات من فضله إنه لا يحب الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 30, 'ومن آياته أن يرسل الرياح مبشرات وليذيقكم من رحمته ولتجري الفلك بأمره ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 30, 'ولقد أرسلنا من قبلك رسلا إلى قومهم فجاءوهم بالبينات فانتقمنا من الذين أجرموا وكان حقا علينا نصر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 30, 'الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله فإذا أصاب به من يشاء من عباده إذا هم يستبشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 30, 'وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 30, 'فانظر إلى آثار رحمت الله كيف يحيي الأرض بعد موتها إن ذلك لمحيي الموتى وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 30, 'ولئن أرسلنا ريحا فرأوه مصفرا لظلوا من بعده يكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 30, 'فإنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 30, 'وما أنت بهاد العمي عن ضلالتهم إن تسمع إلا من يؤمن بآياتنا فهم مسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 30, 'الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة يخلق ما يشاء وهو العليم القدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 30, 'ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة كذلك كانوا يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 30, 'وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 30, 'فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 30, 'ولقد ضربنا للناس في هذا القرآن من كل مثل ولئن جئتهم بآية ليقولن الذين كفروا إن أنتم إلا مبطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 30, 'كذلك يطبع الله على قلوب الذين لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 30, 'فاصبر إن وعد الله حق ولا يستخفنك الذين لا يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 31, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 31, 'تلك آيات الكتاب الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 31, 'هدى ورحمة للمحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 31, 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 31, 'أولئك على هدى من ربهم وأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 31, 'ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا أولئك لهم عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 31, 'وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا فبشره بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 31, 'إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 31, 'خالدين فيها وعد الله حقا وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 31, 'خلق السماوات بغير عمد ترونها وألقى في الأرض رواسي أن تميد بكم وبث فيها من كل دابة وأنزلنا من السماء ماء فأنبتنا فيها من كل زوج كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 31, 'هذا خلق الله فأروني ماذا خلق الذين من دونه بل الظالمون في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 31, 'ولقد آتينا لقمان الحكمة أن اشكر لله ومن يشكر فإنما يشكر لنفسه ومن كفر فإن الله غني حميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 31, 'وإذ قال لقمان لابنه وهو يعظه يا بني لا تشرك بالله إن الشرك لظلم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 31, 'ووصينا الإنسان بوالديه حملته أمه وهنا على وهن وفصاله في عامين أن اشكر لي ولوالديك إلي المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 31, 'وإن جاهداك على أن تشرك بي ما ليس لك به علم فلا تطعهما وصاحبهما في الدنيا معروفا واتبع سبيل من أناب إلي ثم إلي مرجعكم فأنبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 31, 'يا بني إنها إن تك مثقال حبة من خردل فتكن في صخرة أو في السماوات أو في الأرض يأت بها الله إن الله لطيف خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 31, 'يا بني أقم الصلاة وأمر بالمعروف وانه عن المنكر واصبر على ما أصابك إن ذلك من عزم الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 31, 'ولا تصعر خدك للناس ولا تمش في الأرض مرحا إن الله لا يحب كل مختال فخور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 31, 'واقصد في مشيك واغضض من صوتك إن أنكر الأصوات لصوت الحمير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 31, 'ألم تروا أن الله سخر لكم ما في السماوات وما في الأرض وأسبغ عليكم نعمه ظاهرة وباطنة ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 31, 'وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا أولو كان الشيطان يدعوهم إلى عذاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 31, 'ومن يسلم وجهه إلى الله وهو محسن فقد استمسك بالعروة الوثقى وإلى الله عاقبة الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 31, 'ومن كفر فلا يحزنك كفره إلينا مرجعهم فننبئهم بما عملوا إن الله عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 31, 'نمتعهم قليلا ثم نضطرهم إلى عذاب غليظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 31, 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل الحمد لله بل أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 31, 'لله ما في السماوات والأرض إن الله هو الغني الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 31, 'ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله إن الله عزيز حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 31, 'ما خلقكم ولا بعثكم إلا كنفس واحدة إن الله سميع بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 31, 'ألم تر أن الله يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري إلى أجل مسمى وأن الله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 31, 'ذلك بأن الله هو الحق وأن ما يدعون من دونه الباطل وأن الله هو العلي الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 31, 'ألم تر أن الفلك تجري في البحر بنعمت الله ليريكم من آياته إن في ذلك لآيات لكل صبار شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 31, 'وإذا غشيهم موج كالظلل دعوا الله مخلصين له الدين فلما نجاهم إلى البر فمنهم مقتصد وما يجحد بآياتنا إلا كل ختار كفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 31, 'يا أيها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا إن وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 31, 'إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام وما تدري نفس ماذا تكسب غدا وما تدري نفس بأي أرض تموت إن الله عليم خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 32, 'بسم الله الرحمن الرحيم الم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 32, 'تنزيل الكتاب لا ريب فيه من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 32, 'أم يقولون افتراه بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 32, 'الله الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش ما لكم من دونه من ولي ولا شفيع أفلا تتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 32, 'يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 32, 'ذلك عالم الغيب والشهادة العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 32, 'الذي أحسن كل شيء خلقه وبدأ خلق الإنسان من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 32, 'ثم جعل نسله من سلالة من ماء مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 32, 'ثم سواه ونفخ فيه من روحه وجعل لكم السمع والأبصار والأفئدة قليلا ما تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 32, 'وقالوا أإذا ضللنا في الأرض أإنا لفي خلق جديد بل هم بلقاء ربهم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 32, 'قل يتوفاكم ملك الموت الذي وكل بكم ثم إلى ربكم ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 32, 'ولو ترى إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 32, 'ولو شئنا لآتينا كل نفس هداها ولكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 32, 'فذوقوا بما نسيتم لقاء يومكم هذا إنا نسيناكم وذوقوا عذاب الخلد بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 32, 'إنما يؤمن بآياتنا الذين إذا ذكروا بها خروا سجدا وسبحوا بحمد ربهم وهم لا يستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 32, 'تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 32, 'فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 32, 'أفمن كان مؤمنا كمن كان فاسقا لا يستوون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 32, 'أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوى نزلا بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 32, 'وأما الذين فسقوا فمأواهم النار كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 32, 'ولنذيقنهم من العذاب الأدنى دون العذاب الأكبر لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 32, 'ومن أظلم ممن ذكر بآيات ربه ثم أعرض عنها إنا من المجرمين منتقمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 32, 'ولقد آتينا موسى الكتاب فلا تكن في مرية من لقائه وجعلناه هدى لبني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 32, 'وجعلنا منهم أئمة يهدون بأمرنا لما صبروا وكانوا بآياتنا يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 32, 'إن ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 32, 'أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم إن في ذلك لآيات أفلا يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 32, 'أولم يروا أنا نسوق الماء إلى الأرض الجرز فنخرج به زرعا تأكل منه أنعامهم وأنفسهم أفلا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 32, 'ويقولون متى هذا الفتح إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 32, 'قل يوم الفتح لا ينفع الذين كفروا إيمانهم ولا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 32, 'فأعرض عنهم وانتظر إنهم منتظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 33, 'بسم الله الرحمن الرحيم يا أيها النبي اتق الله ولا تطع الكافرين والمنافقين إن الله كان عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 33, 'واتبع ما يوحى إليك من ربك إن الله كان بما تعملون خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 33, 'وتوكل على الله وكفى بالله وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 33, 'ما جعل الله لرجل من قلبين في جوفه وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم وما جعل أدعياءكم أبناءكم ذلكم قولكم بأفواهكم والله يقول الحق وهو يهدي السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 33, 'ادعوهم لآبائهم هو أقسط عند الله فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم وليس عليكم جناح فيما أخطأتم به ولكن ما تعمدت قلوبكم وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 33, 'النبي أولى بالمؤمنين من أنفسهم وأزواجه أمهاتهم وأولو الأرحام بعضهم أولى ببعض في كتاب الله من المؤمنين والمهاجرين إلا أن تفعلوا إلى أوليائكم معروفا كان ذلك في الكتاب مسطورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 33, 'وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم وأخذنا منهم ميثاقا غليظا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 33, 'ليسأل الصادقين عن صدقهم وأعد للكافرين عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 33, 'يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها وكان الله بما تعملون بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 33, 'إذ جاءوكم من فوقكم ومن أسفل منكم وإذ زاغت الأبصار وبلغت القلوب الحناجر وتظنون بالله الظنونا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 33, 'هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 33, 'وإذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله إلا غرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 33, 'وإذ قالت طائفة منهم يا أهل يثرب لا مقام لكم فارجعوا ويستأذن فريق منهم النبي يقولون إن بيوتنا عورة وما هي بعورة إن يريدون إلا فرارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 33, 'ولو دخلت عليهم من أقطارها ثم سئلوا الفتنة لآتوها وما تلبثوا بها إلا يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 33, 'ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار وكان عهد الله مسئولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 33, 'قل لن ينفعكم الفرار إن فررتم من الموت أو القتل وإذا لا تمتعون إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 33, 'قل من ذا الذي يعصمكم من الله إن أراد بكم سوءا أو أراد بكم رحمة ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 33, 'قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا ولا يأتون البأس إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 33, 'أشحة عليكم فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي يغشى عليه من الموت فإذا ذهب الخوف سلقوكم بألسنة حداد أشحة على الخير أولئك لم يؤمنوا فأحبط الله أعمالهم وكان ذلك على الله يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 33, 'يحسبون الأحزاب لم يذهبوا وإن يأت الأحزاب يودوا لو أنهم بادون في الأعراب يسألون عن أنبائكم ولو كانوا فيكم ما قاتلوا إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 33, 'لقد كان لكم في رسول الله أسوة حسنة لمن كان يرجو الله واليوم الآخر وذكر الله كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 33, 'ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم إلا إيمانا وتسليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 33, 'من المؤمنين رجال صدقوا ما عاهدوا الله عليه فمنهم من قضى نحبه ومنهم من ينتظر وما بدلوا تبديلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 33, 'ليجزي الله الصادقين بصدقهم ويعذب المنافقين إن شاء أو يتوب عليهم إن الله كان غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 33, 'ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 33, 'وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتأسرون فريقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 33, 'وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطئوها وكان الله على كل شيء قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 33, 'يا أيها النبي قل لأزواجك إن كنتن تردن الحياة الدنيا وزينتها فتعالين أمتعكن وأسرحكن سراحا جميلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 33, 'وإن كنتن تردن الله ورسوله والدار الآخرة فإن الله أعد للمحسنات منكن أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 33, 'يا نساء النبي من يأت منكن بفاحشة مبينة يضاعف لها العذاب ضعفين وكان ذلك على الله يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 33, 'ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين وأعتدنا لها رزقا كريما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 33, 'يا نساء النبي لستن كأحد من النساء إن اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 33, 'وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الأولى وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 33, 'واذكرن ما يتلى في بيوتكن من آيات الله والحكمة إن الله كان لطيفا خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 33, 'إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة وأجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 33, 'وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم ومن يعص الله ورسوله فقد ضل ضلالا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 33, 'وإذ تقول للذي أنعم الله عليه وأنعمت عليه أمسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله أحق أن تخشاه فلما قضى زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا منهن وطرا وكان أمر الله مفعولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 33, 'ما كان على النبي من حرج فيما فرض الله له سنة الله في الذين خلوا من قبل وكان أمر الله قدرا مقدورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 33, 'الذين يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله وكفى بالله حسيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 33, 'ما كان محمد أبا أحد من رجالكم ولكن رسول الله وخاتم النبيين وكان الله بكل شيء عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 33, 'يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 33, 'وسبحوه بكرة وأصيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 33, 'هو الذي يصلي عليكم وملائكته ليخرجكم من الظلمات إلى النور وكان بالمؤمنين رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 33, 'تحيتهم يوم يلقونه سلام وأعد لهم أجرا كريما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 33, 'يا أيها النبي إنا أرسلناك شاهدا ومبشرا ونذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 33, 'وداعيا إلى الله بإذنه وسراجا منيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 33, 'وبشر المؤمنين بأن لهم من الله فضلا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 33, 'ولا تطع الكافرين والمنافقين ودع أذاهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 33, 'يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها فمتعوهن وسرحوهن سراحا جميلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 33, 'يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها خالصة لك من دون المؤمنين قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 33, 'ترجي من تشاء منهن وتؤوي إليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك ذلك أدنى أن تقر أعينهن ولا يحزن ويرضين بما آتيتهن كلهن والله يعلم ما في قلوبكم وكان الله عليما حليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 33, 'لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك وكان الله على كل شيء رقيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 33, 'يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم إلى طعام غير ناظرين إناه ولكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا مستأنسين لحديث إن ذلكم كان يؤذي النبي فيستحيي منكم والله لا يستحيي من الحق وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ذلكم أطهر لقلوبكم وقلوبهن وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا إن ذلكم كان عند الله عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 33, 'إن تبدوا شيئا أو تخفوه فإن الله كان بكل شيء عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 33, 'لا جناح عليهن في آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا نسائهن ولا ما ملكت أيمانهن واتقين الله إن الله كان على كل شيء شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 33, 'إن الله وملائكته يصلون على النبي يا أيها الذين آمنوا صلوا عليه وسلموا تسليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 33, 'إن الذين يؤذون الله ورسوله لعنهم الله في الدنيا والآخرة وأعد لهم عذابا مهينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 33, 'والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتانا وإثما مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 33, 'يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ذلك أدنى أن يعرفن فلا يؤذين وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 33, 'لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 33, 'ملعونين أينما ثقفوا أخذوا وقتلوا تقتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 33, 'سنة الله في الذين خلوا من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 33, 'يسألك الناس عن الساعة قل إنما علمها عند الله وما يدريك لعل الساعة تكون قريبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 33, 'إن الله لعن الكافرين وأعد لهم سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 33, 'خالدين فيها أبدا لا يجدون وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 33, 'يوم تقلب وجوههم في النار يقولون يا ليتنا أطعنا الله وأطعنا الرسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 33, 'وقالوا ربنا إنا أطعنا سادتنا وكبراءنا فأضلونا السبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 33, 'ربنا آتهم ضعفين من العذاب والعنهم لعنا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 33, 'يا أيها الذين آمنوا لا تكونوا كالذين آذوا موسى فبرأه الله مما قالوا وكان عند الله وجيها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 33, 'يا أيها الذين آمنوا اتقوا الله وقولوا قولا سديدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 33, 'يصلح لكم أعمالكم ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 33, 'إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان إنه كان ظلوما جهولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 33, 'ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 34, 'بسم الله الرحمن الرحيم الحمد لله الذي له ما في السماوات وما في الأرض وله الحمد في الآخرة وهو الحكيم الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 34, 'يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 34, 'وقال الذين كفروا لا تأتينا الساعة قل بلى وربي لتأتينكم عالم الغيب لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 34, 'ليجزي الذين آمنوا وعملوا الصالحات أولئك لهم مغفرة ورزق كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 34, 'والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 34, 'ويرى الذين أوتوا العلم الذي أنزل إليك من ربك هو الحق ويهدي إلى صراط العزيز الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 34, 'وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 34, 'أفترى على الله كذبا أم به جنة بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 34, 'أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء إن في ذلك لآية لكل عبد منيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 34, 'ولقد آتينا داوود منا فضلا يا جبال أوبي معه والطير وألنا له الحديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 34, 'أن اعمل سابغات وقدر في السرد واعملوا صالحا إني بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 34, 'ولسليمان الريح غدوها شهر ورواحها شهر وأسلنا له عين القطر ومن الجن من يعمل بين يديه بإذن ربه ومن يزغ منهم عن أمرنا نذقه من عذاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 34, 'يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات اعملوا آل داوود شكرا وقليل من عبادي الشكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 34, 'فلما قضينا عليه الموت ما دلهم على موته إلا دابة الأرض تأكل منسأته فلما خر تبينت الجن أن لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 34, 'لقد كان لسبإ في مسكنهم آية جنتان عن يمين وشمال كلوا من رزق ربكم واشكروا له بلدة طيبة ورب غفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 34, 'فأعرضوا فأرسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي أكل خمط وأثل وشيء من سدر قليل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 34, 'ذلك جزيناهم بما كفروا وهل نجازي إلا الكفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 34, 'وجعلنا بينهم وبين القرى التي باركنا فيها قرى ظاهرة وقدرنا فيها السير سيروا فيها ليالي وأياما آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 34, 'فقالوا ربنا باعد بين أسفارنا وظلموا أنفسهم فجعلناهم أحاديث ومزقناهم كل ممزق إن في ذلك لآيات لكل صبار شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 34, 'ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 34, 'وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة ممن هو منها في شك وربك على كل شيء حفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 34, 'قل ادعوا الذين زعمتم من دون الله لا يملكون مثقال ذرة في السماوات ولا في الأرض وما لهم فيهما من شرك وما له منهم من ظهير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 34, 'ولا تنفع الشفاعة عنده إلا لمن أذن له حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم قالوا الحق وهو العلي الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 34, 'قل من يرزقكم من السماوات والأرض قل الله وإنا أو إياكم لعلى هدى أو في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 34, 'قل لا تسألون عما أجرمنا ولا نسأل عما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 34, 'قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 34, 'قل أروني الذين ألحقتم به شركاء كلا بل هو الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 34, 'وما أرسلناك إلا كافة للناس بشيرا ونذيرا ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 34, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 34, 'قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 34, 'وقال الذين كفروا لن نؤمن بهذا القرآن ولا بالذي بين يديه ولو ترى إذ الظالمون موقوفون عند ربهم يرجع بعضهم إلى بعض القول يقول الذين استضعفوا للذين استكبروا لولا أنتم لكنا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 34, 'قال الذين استكبروا للذين استضعفوا أنحن صددناكم عن الهدى بعد إذ جاءكم بل كنتم مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 34, 'وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار إذ تأمروننا أن نكفر بالله ونجعل له أندادا وأسروا الندامة لما رأوا العذاب وجعلنا الأغلال في أعناق الذين كفروا هل يجزون إلا ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 34, 'وما أرسلنا في قرية من نذير إلا قال مترفوها إنا بما أرسلتم به كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 34, 'وقالوا نحن أكثر أموالا وأولادا وما نحن بمعذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 34, 'قل إن ربي يبسط الرزق لمن يشاء ويقدر ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 34, 'وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفى إلا من آمن وعمل صالحا فأولئك لهم جزاء الضعف بما عملوا وهم في الغرفات آمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 34, 'والذين يسعون في آياتنا معاجزين أولئك في العذاب محضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 34, 'قل إن ربي يبسط الرزق لمن يشاء من عباده ويقدر له وما أنفقتم من شيء فهو يخلفه وهو خير الرازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 34, 'ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 34, 'قالوا سبحانك أنت ولينا من دونهم بل كانوا يعبدون الجن أكثرهم بهم مؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 34, 'فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا ذوقوا عذاب النار التي كنتم بها تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 34, 'وإذا تتلى عليهم آياتنا بينات قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم وقالوا ما هذا إلا إفك مفترى وقال الذين كفروا للحق لما جاءهم إن هذا إلا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 34, 'وما آتيناهم من كتب يدرسونها وما أرسلنا إليهم قبلك من نذير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 34, 'وكذب الذين من قبلهم وما بلغوا معشار ما آتيناهم فكذبوا رسلي فكيف كان نكير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 34, 'قل إنما أعظكم بواحدة أن تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة إن هو إلا نذير لكم بين يدي عذاب شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 34, 'قل ما سألتكم من أجر فهو لكم إن أجري إلا على الله وهو على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 34, 'قل إن ربي يقذف بالحق علام الغيوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 34, 'قل جاء الحق وما يبدئ الباطل وما يعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 34, 'قل إن ضللت فإنما أضل على نفسي وإن اهتديت فبما يوحي إلي ربي إنه سميع قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 34, 'ولو ترى إذ فزعوا فلا فوت وأخذوا من مكان قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 34, 'وقالوا آمنا به وأنى لهم التناوش من مكان بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 34, 'وقد كفروا به من قبل ويقذفون بالغيب من مكان بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 34, 'وحيل بينهم وبين ما يشتهون كما فعل بأشياعهم من قبل إنهم كانوا في شك مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 35, 'بسم الله الرحمن الرحيم الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنى وثلاث ورباع يزيد في الخلق ما يشاء إن الله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 35, 'ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له من بعده وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 35, 'يا أيها الناس اذكروا نعمت الله عليكم هل من خالق غير الله يرزقكم من السماء والأرض لا إله إلا هو فأنى تؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 35, 'وإن يكذبوك فقد كذبت رسل من قبلك وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 35, 'يا أيها الناس إن وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 35, 'إن الشيطان لكم عدو فاتخذوه عدوا إنما يدعو حزبه ليكونوا من أصحاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 35, 'الذين كفروا لهم عذاب شديد والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 35, 'أفمن زين له سوء عمله فرآه حسنا فإن الله يضل من يشاء ويهدي من يشاء فلا تذهب نفسك عليهم حسرات إن الله عليم بما يصنعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 35, 'والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها كذلك النشور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 35, 'من كان يريد العزة فلله العزة جميعا إليه يصعد الكلم الطيب والعمل الصالح يرفعه والذين يمكرون السيئات لهم عذاب شديد ومكر أولئك هو يبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 35, 'والله خلقكم من تراب ثم من نطفة ثم جعلكم أزواجا وما تحمل من أنثى ولا تضع إلا بعلمه وما يعمر من معمر ولا ينقص من عمره إلا في كتاب إن ذلك على الله يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 35, 'وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح أجاج ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 35, 'يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري لأجل مسمى ذلكم الله ربكم له الملك والذين تدعون من دونه ما يملكون من قطمير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 35, 'إن تدعوهم لا يسمعوا دعاءكم ولو سمعوا ما استجابوا لكم ويوم القيامة يكفرون بشرككم ولا ينبئك مثل خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 35, 'يا أيها الناس أنتم الفقراء إلى الله والله هو الغني الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 35, 'إن يشأ يذهبكم ويأت بخلق جديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 35, 'وما ذلك على الله بعزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 35, 'ولا تزر وازرة وزر أخرى وإن تدع مثقلة إلى حملها لا يحمل منه شيء ولو كان ذا قربى إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة ومن تزكى فإنما يتزكى لنفسه وإلى الله المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 35, 'وما يستوي الأعمى والبصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 35, 'ولا الظلمات ولا النور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 35, 'ولا الظل ولا الحرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 35, 'وما يستوي الأحياء ولا الأموات إن الله يسمع من يشاء وما أنت بمسمع من في القبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 35, 'إن أنت إلا نذير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 35, 'إنا أرسلناك بالحق بشيرا ونذيرا وإن من أمة إلا خلا فيها نذير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 35, 'وإن يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 35, 'ثم أخذت الذين كفروا فكيف كان نكير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 35, 'ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 35, 'ومن الناس والدواب والأنعام مختلف ألوانه كذلك إنما يخشى الله من عباده العلماء إن الله عزيز غفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 35, 'إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 35, 'ليوفيهم أجورهم ويزيدهم من فضله إنه غفور شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 35, 'والذي أوحينا إليك من الكتاب هو الحق مصدقا لما بين يديه إن الله بعباده لخبير بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 35, 'ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله ذلك هو الفضل الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 35, 'جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 35, 'وقالوا الحمد لله الذي أذهب عنا الحزن إن ربنا لغفور شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 35, 'الذي أحلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 35, 'والذين كفروا لهم نار جهنم لا يقضى عليهم فيموتوا ولا يخفف عنهم من عذابها كذلك نجزي كل كفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 35, 'وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل أولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير فذوقوا فما للظالمين من نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 35, 'إن الله عالم غيب السماوات والأرض إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 35, 'هو الذي جعلكم خلائف في الأرض فمن كفر فعليه كفره ولا يزيد الكافرين كفرهم عند ربهم إلا مقتا ولا يزيد الكافرين كفرهم إلا خسارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 35, 'قل أرأيتم شركاءكم الذين تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات أم آتيناهم كتابا فهم على بينت منه بل إن يعد الظالمون بعضهم بعضا إلا غرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 35, 'إن الله يمسك السماوات والأرض أن تزولا ولئن زالتا إن أمسكهما من أحد من بعده إنه كان حليما غفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 35, 'وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدى من إحدى الأمم فلما جاءهم نذير ما زادهم إلا نفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 35, 'استكبارا في الأرض ومكر السيئ ولا يحيق المكر السيئ إلا بأهله فهل ينظرون إلا سنت الأولين فلن تجد لسنت الله تبديلا ولن تجد لسنت الله تحويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 35, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا أشد منهم قوة وما كان الله ليعجزه من شيء في السماوات ولا في الأرض إنه كان عليما قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 35, 'ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة ولكن يؤخرهم إلى أجل مسمى فإذا جاء أجلهم فإن الله كان بعباده بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 36, 'بسم الله الرحمن الرحيم يس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 36, 'والقرآن الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 36, 'إنك لمن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 36, 'على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 36, 'تنزيل العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 36, 'لتنذر قوما ما أنذر آباؤهم فهم غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 36, 'لقد حق القول على أكثرهم فهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 36, 'إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 36, 'وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 36, 'وسواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 36, 'إنما تنذر من اتبع الذكر وخشي الرحمن بالغيب فبشره بمغفرة وأجر كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 36, 'إنا نحن نحيي الموتى ونكتب ما قدموا وآثارهم وكل شيء أحصيناه في إمام مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 36, 'واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 36, 'إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 36, 'قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن أنتم إلا تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 36, 'قالوا ربنا يعلم إنا إليكم لمرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 36, 'وما علينا إلا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 36, 'قالوا إنا تطيرنا بكم لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 36, 'قالوا طائركم معكم أئن ذكرتم بل أنتم قوم مسرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 36, 'وجاء من أقصى المدينة رجل يسعى قال يا قوم اتبعوا المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 36, 'اتبعوا من لا يسألكم أجرا وهم مهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 36, 'وما لي لا أعبد الذي فطرني وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 36, 'أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 36, 'إني إذا لفي ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 36, 'إني آمنت بربكم فاسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 36, 'قيل ادخل الجنة قال يا ليت قومي يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 36, 'بما غفر لي ربي وجعلني من المكرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 36, 'وما أنزلنا على قومه من بعده من جند من السماء وما كنا منزلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 36, 'إن كانت إلا صيحة واحدة فإذا هم خامدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 36, 'يا حسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 36, 'ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 36, 'وإن كل لما جميع لدينا محضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 36, 'وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 36, 'وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 36, 'ليأكلوا من ثمره وما عملته أيديهم أفلا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 36, 'سبحان الذي خلق الأزواج كلها مما تنبت الأرض ومن أنفسهم ومما لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 36, 'وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 36, 'والشمس تجري لمستقر لها ذلك تقدير العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 36, 'والقمر قدرناه منازل حتى عاد كالعرجون القديم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 36, 'لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار وكل في فلك يسبحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 36, 'وآية لهم أنا حملنا ذريتهم في الفلك المشحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 36, 'وخلقنا لهم من مثله ما يركبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 36, 'وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 36, 'إلا رحمة منا ومتاعا إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 36, 'وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 36, 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 36, 'وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 36, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 36, 'ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 36, 'فلا يستطيعون توصية ولا إلى أهلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 36, 'ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 36, 'قالوا يا ويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 36, 'إن كانت إلا صيحة واحدة فإذا هم جميع لدينا محضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 36, 'فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 36, 'إن أصحاب الجنة اليوم في شغل فاكهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 36, 'هم وأزواجهم في ظلال على الأرائك متكئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 36, 'لهم فيها فاكهة ولهم ما يدعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 36, 'سلام قولا من رب رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 36, 'وامتازوا اليوم أيها المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 36, 'ألم أعهد إليكم يا بني آدم أن لا تعبدوا الشيطان إنه لكم عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 36, 'وأن اعبدوني هذا صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 36, 'ولقد أضل منكم جبلا كثيرا أفلم تكونوا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 36, 'هذه جهنم التي كنتم توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 36, 'اصلوها اليوم بما كنتم تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 36, 'اليوم نختم على أفواههم وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 36, 'ولو نشاء لطمسنا على أعينهم فاستبقوا الصراط فأنى يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 36, 'ولو نشاء لمسخناهم على مكانتهم فما استطاعوا مضيا ولا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 36, 'ومن نعمره ننكسه في الخلق أفلا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 36, 'وما علمناه الشعر وما ينبغي له إن هو إلا ذكر وقرآن مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 36, 'لينذر من كان حيا ويحق القول على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 36, 'أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 36, 'وذللناها لهم فمنها ركوبهم ومنها يأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 36, 'ولهم فيها منافع ومشارب أفلا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 36, 'واتخذوا من دون الله آلهة لعلهم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 36, 'لا يستطيعون نصرهم وهم لهم جند محضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 36, 'فلا يحزنك قولهم إنا نعلم ما يسرون وما يعلنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 36, 'أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 36, 'وضرب لنا مثلا ونسي خلقه قال من يحيي العظام وهي رميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 36, 'قل يحييها الذي أنشأها أول مرة وهو بكل خلق عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 36, 'الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 36, 'أوليس الذي خلق السماوات والأرض بقادر على أن يخلق مثلهم بلى وهو الخلاق العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 36, 'إنما أمره إذا أراد شيئا أن يقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 36, 'فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 37, 'بسم الله الرحمن الرحيم والصافات صفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 37, 'فالزاجرات زجرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 37, 'فالتاليات ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 37, 'إن إلهكم لواحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 37, 'رب السماوات والأرض وما بينهما ورب المشارق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 37, 'إنا زينا السماء الدنيا بزينة الكواكب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 37, 'وحفظا من كل شيطان مارد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 37, 'لا يسمعون إلى الملإ الأعلى ويقذفون من كل جانب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 37, 'دحورا ولهم عذاب واصب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 37, 'إلا من خطف الخطفة فأتبعه شهاب ثاقب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 37, 'فاستفتهم أهم أشد خلقا أم من خلقنا إنا خلقناهم من طين لازب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 37, 'بل عجبت ويسخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 37, 'وإذا ذكروا لا يذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 37, 'وإذا رأوا آية يستسخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 37, 'وقالوا إن هذا إلا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 37, 'أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 37, 'أوآباؤنا الأولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 37, 'قل نعم وأنتم داخرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 37, 'فإنما هي زجرة واحدة فإذا هم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 37, 'وقالوا يا ويلنا هذا يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 37, 'هذا يوم الفصل الذي كنتم به تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 37, 'احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 37, 'من دون الله فاهدوهم إلى صراط الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 37, 'وقفوهم إنهم مسئولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 37, 'ما لكم لا تناصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 37, 'بل هم اليوم مستسلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 37, 'وأقبل بعضهم على بعض يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 37, 'قالوا إنكم كنتم تأتوننا عن اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 37, 'قالوا بل لم تكونوا مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 37, 'وما كان لنا عليكم من سلطان بل كنتم قوما طاغين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 37, 'فحق علينا قول ربنا إنا لذائقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 37, 'فأغويناكم إنا كنا غاوين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 37, 'فإنهم يومئذ في العذاب مشتركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 37, 'إنا كذلك نفعل بالمجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 37, 'إنهم كانوا إذا قيل لهم لا إله إلا الله يستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 37, 'ويقولون أئنا لتاركو آلهتنا لشاعر مجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 37, 'بل جاء بالحق وصدق المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 37, 'إنكم لذائقو العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 37, 'وما تجزون إلا ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 37, 'إلا عباد الله المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 37, 'أولئك لهم رزق معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 37, 'فواكه وهم مكرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 37, 'في جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 37, 'على سرر متقابلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 37, 'يطاف عليهم بكأس من معين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 37, 'بيضاء لذة للشاربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 37, 'لا فيها غول ولا هم عنها ينزفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 37, 'وعندهم قاصرات الطرف عين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 37, 'كأنهن بيض مكنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 37, 'فأقبل بعضهم على بعض يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 37, 'قال قائل منهم إني كان لي قرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 37, 'يقول أإنك لمن المصدقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 37, 'أإذا متنا وكنا ترابا وعظاما أإنا لمدينون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 37, 'قال هل أنتم مطلعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 37, 'فاطلع فرآه في سواء الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 37, 'قال تالله إن كدت لتردين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 37, 'ولولا نعمة ربي لكنت من المحضرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 37, 'أفما نحن بميتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 37, 'إلا موتتنا الأولى وما نحن بمعذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 37, 'إن هذا لهو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 37, 'لمثل هذا فليعمل العاملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 37, 'أذلك خير نزلا أم شجرة الزقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 37, 'إنا جعلناها فتنة للظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 37, 'إنها شجرة تخرج في أصل الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 37, 'طلعها كأنه رءوس الشياطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 37, 'فإنهم لآكلون منها فمالئون منها البطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 37, 'ثم إن لهم عليها لشوبا من حميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 37, 'ثم إن مرجعهم لإلى الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 37, 'إنهم ألفوا آباءهم ضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 37, 'فهم على آثارهم يهرعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 37, 'ولقد ضل قبلهم أكثر الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 37, 'ولقد أرسلنا فيهم منذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 37, 'فانظر كيف كان عاقبة المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 37, 'إلا عباد الله المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 37, 'ولقد نادانا نوح فلنعم المجيبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 37, 'ونجيناه وأهله من الكرب العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 37, 'وجعلنا ذريته هم الباقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 37, 'وتركنا عليه في الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 37, 'سلام على نوح في العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 37, 'إنا كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 37, 'إنه من عبادنا المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 37, 'ثم أغرقنا الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 37, 'وإن من شيعته لإبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 37, 'إذ جاء ربه بقلب سليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 37, 'إذ قال لأبيه وقومه ماذا تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 37, 'أئفكا آلهة دون الله تريدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 37, 'فما ظنكم برب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 37, 'فنظر نظرة في النجوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 37, 'فقال إني سقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 37, 'فتولوا عنه مدبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 37, 'فراغ إلى آلهتهم فقال ألا تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 37, 'ما لكم لا تنطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 37, 'فراغ عليهم ضربا باليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 37, 'فأقبلوا إليه يزفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 37, 'قال أتعبدون ما تنحتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 37, 'والله خلقكم وما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(97, 1, 37, 'قالوا ابنوا له بنيانا فألقوه في الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(98, 1, 37, 'فأرادوا به كيدا فجعلناهم الأسفلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(99, 1, 37, 'وقال إني ذاهب إلى ربي سيهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(100, 1, 37, 'رب هب لي من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(101, 1, 37, 'فبشرناه بغلام حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(102, 1, 37, 'فلما بلغ معه السعي قال يا بني إني أرى في المنام أني أذبحك فانظر ماذا ترى قال يا أبت افعل ما تؤمر ستجدني إن شاء الله من الصابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(103, 1, 37, 'فلما أسلما وتله للجبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(104, 1, 37, 'وناديناه أن يا إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(105, 1, 37, 'قد صدقت الرؤيا إنا كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(106, 1, 37, 'إن هذا لهو البلاء المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(107, 1, 37, 'وفديناه بذبح عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(108, 1, 37, 'وتركنا عليه في الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(109, 1, 37, 'سلام على إبراهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(110, 1, 37, 'كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(111, 1, 37, 'إنه من عبادنا المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(112, 1, 37, 'وبشرناه بإسحاق نبيا من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(113, 1, 37, 'وباركنا عليه وعلى إسحاق ومن ذريتهما محسن وظالم لنفسه مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(114, 1, 37, 'ولقد مننا على موسى وهارون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(115, 1, 37, 'ونجيناهما وقومهما من الكرب العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(116, 1, 37, 'ونصرناهم فكانوا هم الغالبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(117, 1, 37, 'وآتيناهما الكتاب المستبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(118, 1, 37, 'وهديناهما الصراط المستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(119, 1, 37, 'وتركنا عليهما في الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(120, 1, 37, 'سلام على موسى وهارون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(121, 1, 37, 'إنا كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(122, 1, 37, 'إنهما من عبادنا المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(123, 1, 37, 'وإن إلياس لمن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(124, 1, 37, 'إذ قال لقومه ألا تتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(125, 1, 37, 'أتدعون بعلا وتذرون أحسن الخالقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(126, 1, 37, 'الله ربكم ورب آبائكم الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(127, 1, 37, 'فكذبوه فإنهم لمحضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(128, 1, 37, 'إلا عباد الله المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(129, 1, 37, 'وتركنا عليه في الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(130, 1, 37, 'سلام على إل ياسين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(131, 1, 37, 'إنا كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(132, 1, 37, 'إنه من عبادنا المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(133, 1, 37, 'وإن لوطا لمن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(134, 1, 37, 'إذ نجيناه وأهله أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(135, 1, 37, 'إلا عجوزا في الغابرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(136, 1, 37, 'ثم دمرنا الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(137, 1, 37, 'وإنكم لتمرون عليهم مصبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(138, 1, 37, 'وبالليل أفلا تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(139, 1, 37, 'وإن يونس لمن المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(140, 1, 37, 'إذ أبق إلى الفلك المشحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(141, 1, 37, 'فساهم فكان من المدحضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(142, 1, 37, 'فالتقمه الحوت وهو مليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(143, 1, 37, 'فلولا أنه كان من المسبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(144, 1, 37, 'للبث في بطنه إلى يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(145, 1, 37, 'فنبذناه بالعراء وهو سقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(146, 1, 37, 'وأنبتنا عليه شجرة من يقطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(147, 1, 37, 'وأرسلناه إلى مائة ألف أو يزيدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(148, 1, 37, 'فآمنوا فمتعناهم إلى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(149, 1, 37, 'فاستفتهم ألربك البنات ولهم البنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(150, 1, 37, 'أم خلقنا الملائكة إناثا وهم شاهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(151, 1, 37, 'ألا إنهم من إفكهم ليقولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(152, 1, 37, 'ولد الله وإنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(153, 1, 37, 'أصطفى البنات على البنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(154, 1, 37, 'ما لكم كيف تحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(155, 1, 37, 'أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(156, 1, 37, 'أم لكم سلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(157, 1, 37, 'فأتوا بكتابكم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(158, 1, 37, 'وجعلوا بينه وبين الجنة نسبا ولقد علمت الجنة إنهم لمحضرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(159, 1, 37, 'سبحان الله عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(160, 1, 37, 'إلا عباد الله المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(161, 1, 37, 'فإنكم وما تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(162, 1, 37, 'ما أنتم عليه بفاتنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(163, 1, 37, 'إلا من هو صال الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(164, 1, 37, 'وما منا إلا له مقام معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(165, 1, 37, 'وإنا لنحن الصافون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(166, 1, 37, 'وإنا لنحن المسبحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(167, 1, 37, 'وإن كانوا ليقولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(168, 1, 37, 'لو أن عندنا ذكرا من الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(169, 1, 37, 'لكنا عباد الله المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(170, 1, 37, 'فكفروا به فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(171, 1, 37, 'ولقد سبقت كلمتنا لعبادنا المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(172, 1, 37, 'إنهم لهم المنصورون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(173, 1, 37, 'وإن جندنا لهم الغالبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(174, 1, 37, 'فتول عنهم حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(175, 1, 37, 'وأبصرهم فسوف يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(176, 1, 37, 'أفبعذابنا يستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(177, 1, 37, 'فإذا نزل بساحتهم فساء صباح المنذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(178, 1, 37, 'وتول عنهم حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(179, 1, 37, 'وأبصر فسوف يبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(180, 1, 37, 'سبحان ربك رب العزة عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(181, 1, 37, 'وسلام على المرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(182, 1, 37, 'والحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 38, 'بسم الله الرحمن الرحيم ص والقرآن ذي الذكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 38, 'بل الذين كفروا في عزة وشقاق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 38, 'كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 38, 'وعجبوا أن جاءهم منذر منهم وقال الكافرون هذا ساحر كذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 38, 'أجعل الآلهة إلها واحدا إن هذا لشيء عجاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 38, 'وانطلق الملأ منهم أن امشوا واصبروا على آلهتكم إن هذا لشيء يراد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 38, 'ما سمعنا بهذا في الملة الآخرة إن هذا إلا اختلاق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 38, 'أأنزل عليه الذكر من بيننا بل هم في شك من ذكري بل لما يذوقوا عذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 38, 'أم عندهم خزائن رحمة ربك العزيز الوهاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 38, 'أم لهم ملك السماوات والأرض وما بينهما فليرتقوا في الأسباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 38, 'جند ما هنالك مهزوم من الأحزاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 38, 'كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 38, 'وثمود وقوم لوط وأصحاب الأيكة أولئك الأحزاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 38, 'إن كل إلا كذب الرسل فحق عقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 38, 'وما ينظر هؤلاء إلا صيحة واحدة ما لها من فواق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 38, 'وقالوا ربنا عجل لنا قطنا قبل يوم الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 38, 'اصبر على ما يقولون واذكر عبدنا داوود ذا الأيد إنه أواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 38, 'إنا سخرنا الجبال معه يسبحن بالعشي والإشراق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 38, 'والطير محشورة كل له أواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 38, 'وشددنا ملكه وآتيناه الحكمة وفصل الخطاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 38, 'وهل أتاك نبأ الخصم إذ تسوروا المحراب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 38, 'إذ دخلوا على داوود ففزع منهم قالوا لا تخف خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا إلى سواء الصراط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 38, 'إن هذا أخي له تسع وتسعون نعجة ولي نعجة واحدة فقال أكفلنيها وعزني في الخطاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 38, 'قال لقد ظلمك بسؤال نعجتك إلى نعاجه وإن كثيرا من الخلطاء ليبغي بعضهم على بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم وظن داوود أنما فتناه فاستغفر ربه وخر راكعا وأناب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 38, 'فغفرنا له ذلك وإن له عندنا لزلفى وحسن مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 38, 'يا داوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله إن الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 38, 'وما خلقنا السماء والأرض وما بينهما باطلا ذلك ظن الذين كفروا فويل للذين كفروا من النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 38, 'أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 38, 'كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 38, 'ووهبنا لداوود سليمان نعم العبد إنه أواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 38, 'إذ عرض عليه بالعشي الصافنات الجياد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 38, 'فقال إني أحببت حب الخير عن ذكر ربي حتى توارت بالحجاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 38, 'ردوها علي فطفق مسحا بالسوق والأعناق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 38, 'ولقد فتنا سليمان وألقينا على كرسيه جسدا ثم أناب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 38, 'قال رب اغفر لي وهب لي ملكا لا ينبغي لأحد من بعدي إنك أنت الوهاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 38, 'فسخرنا له الريح تجري بأمره رخاء حيث أصاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 38, 'والشياطين كل بناء وغواص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 38, 'وآخرين مقرنين في الأصفاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 38, 'هذا عطاؤنا فامنن أو أمسك بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 38, 'وإن له عندنا لزلفى وحسن مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 38, 'واذكر عبدنا أيوب إذ نادى ربه أني مسني الشيطان بنصب وعذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 38, 'اركض برجلك هذا مغتسل بارد وشراب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 38, 'ووهبنا له أهله ومثلهم معهم رحمة منا وذكرى لأولي الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 38, 'وخذ بيدك ضغثا فاضرب به ولا تحنث إنا وجدناه صابرا نعم العبد إنه أواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 38, 'واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدي والأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 38, 'إنا أخلصناهم بخالصة ذكرى الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 38, 'وإنهم عندنا لمن المصطفين الأخيار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 38, 'واذكر إسماعيل واليسع وذا الكفل وكل من الأخيار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 38, 'هذا ذكر وإن للمتقين لحسن مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 38, 'جنات عدن مفتحة لهم الأبواب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 38, 'متكئين فيها يدعون فيها بفاكهة كثيرة وشراب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 38, 'وعندهم قاصرات الطرف أتراب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 38, 'هذا ما توعدون ليوم الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 38, 'إن هذا لرزقنا ما له من نفاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 38, 'هذا وإن للطاغين لشر مآب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 38, 'جهنم يصلونها فبئس المهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 38, 'هذا فليذوقوه حميم وغساق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 38, 'وآخر من شكله أزواج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 38, 'هذا فوج مقتحم معكم لا مرحبا بهم إنهم صالو النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 38, 'قالوا بل أنتم لا مرحبا بكم أنتم قدمتموه لنا فبئس القرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 38, 'قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 38, 'وقالوا ما لنا لا نرى رجالا كنا نعدهم من الأشرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 38, 'أتخذناهم سخريا أم زاغت عنهم الأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 38, 'إن ذلك لحق تخاصم أهل النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 38, 'قل إنما أنا منذر وما من إله إلا الله الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 38, 'رب السماوات والأرض وما بينهما العزيز الغفار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 38, 'قل هو نبأ عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 38, 'أنتم عنه معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 38, 'ما كان لي من علم بالملإ الأعلى إذ يختصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 38, 'إن يوحى إلي إلا أنما أنا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 38, 'إذ قال ربك للملائكة إني خالق بشرا من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 38, 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 38, 'فسجد الملائكة كلهم أجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 38, 'إلا إبليس استكبر وكان من الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 38, 'قال يا إبليس ما منعك أن تسجد لما خلقت بيدي أستكبرت أم كنت من العالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 38, 'قال أنا خير منه خلقتني من نار وخلقته من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 38, 'قال فاخرج منها فإنك رجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 38, 'وإن عليك لعنتي إلى يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 38, 'قال رب فأنظرني إلى يوم يبعثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 38, 'قال فإنك من المنظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 38, 'إلى يوم الوقت المعلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 38, 'قال فبعزتك لأغوينهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 38, 'إلا عبادك منهم المخلصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 38, 'قال فالحق والحق أقول');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 38, 'لأملأن جهنم منك وممن تبعك منهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 38, 'قل ما أسألكم عليه من أجر وما أنا من المتكلفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 38, 'إن هو إلا ذكر للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 38, 'ولتعلمن نبأه بعد حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 39, 'بسم الله الرحمن الرحيم تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 39, 'إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 39, 'ألا لله الدين الخالص والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون إن الله لا يهدي من هو كاذب كفار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 39, 'لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 39, 'خلق السماوات والأرض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لأجل مسمى ألا هو العزيز الغفار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 39, 'خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ذلكم الله ربكم له الملك لا إله إلا هو فأنى تصرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 39, 'إن تكفروا فإن الله غني عنكم ولا يرضى لعباده الكفر وإن تشكروا يرضه لكم ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 39, 'وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله قل تمتع بكفرك قليلا إنك من أصحاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 39, 'أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه قل هل يستوي الذين يعلمون والذين لا يعلمون إنما يتذكر أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 39, 'قل يا عباد الذين آمنوا اتقوا ربكم للذين أحسنوا في هذه الدنيا حسنة وأرض الله واسعة إنما يوفى الصابرون أجرهم بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 39, 'قل إني أمرت أن أعبد الله مخلصا له الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 39, 'وأمرت لأن أكون أول المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 39, 'قل إني أخاف إن عصيت ربي عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 39, 'قل الله أعبد مخلصا له ديني');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 39, 'فاعبدوا ما شئتم من دونه قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ألا ذلك هو الخسران المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 39, 'لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده يا عباد فاتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 39, 'والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله لهم البشرى فبشر عباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 39, 'الذين يستمعون القول فيتبعون أحسنه أولئك الذين هداهم الله وأولئك هم أولو الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 39, 'أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 39, 'لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار وعد الله لا يخلف الله الميعاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 39, 'ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما إن في ذلك لذكرى لأولي الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 39, 'أفمن شرح الله صدره للإسلام فهو على نور من ربه فويل للقاسية قلوبهم من ذكر الله أولئك في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 39, 'الله نزل أحسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلى ذكر الله ذلك هدى الله يهدي به من يشاء ومن يضلل الله فما له من هاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 39, 'أفمن يتقي بوجهه سوء العذاب يوم القيامة وقيل للظالمين ذوقوا ما كنتم تكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 39, 'كذب الذين من قبلهم فأتاهم العذاب من حيث لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 39, 'فأذاقهم الله الخزي في الحياة الدنيا ولعذاب الآخرة أكبر لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 39, 'ولقد ضربنا للناس في هذا القرآن من كل مثل لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 39, 'قرآنا عربيا غير ذي عوج لعلهم يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 39, 'ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا الحمد لله بل أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 39, 'إنك ميت وإنهم ميتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 39, 'ثم إنكم يوم القيامة عند ربكم تختصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 39, 'فمن أظلم ممن كذب على الله وكذب بالصدق إذ جاءه أليس في جهنم مثوى للكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 39, 'والذي جاء بالصدق وصدق به أولئك هم المتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 39, 'لهم ما يشاءون عند ربهم ذلك جزاء المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 39, 'ليكفر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 39, 'أليس الله بكاف عبده ويخوفونك بالذين من دونه ومن يضلل الله فما له من هاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 39, 'ومن يهد الله فما له من مضل أليس الله بعزيز ذي انتقام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 39, 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته قل حسبي الله عليه يتوكل المتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 39, 'قل يا قوم اعملوا على مكانتكم إني عامل فسوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 39, 'من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 39, 'إنا أنزلنا عليك الكتاب للناس بالحق فمن اهتدى فلنفسه ومن ضل فإنما يضل عليها وما أنت عليهم بوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 39, 'الله يتوفى الأنفس حين موتها والتي لم تمت في منامها فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى إن في ذلك لآيات لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 39, 'أم اتخذوا من دون الله شفعاء قل أولو كانوا لا يملكون شيئا ولا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 39, 'قل لله الشفاعة جميعا له ملك السماوات والأرض ثم إليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 39, 'وإذا ذكر الله وحده اشمأزت قلوب الذين لا يؤمنون بالآخرة وإذا ذكر الذين من دونه إذا هم يستبشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 39, 'قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 39, 'ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة وبدا لهم من الله ما لم يكونوا يحتسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 39, 'وبدا لهم سيئات ما كسبوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 39, 'فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته على علم بل هي فتنة ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 39, 'قد قالها الذين من قبلهم فما أغنى عنهم ما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 39, 'فأصابهم سيئات ما كسبوا والذين ظلموا من هؤلاء سيصيبهم سيئات ما كسبوا وما هم بمعجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 39, 'أولم يعلموا أن الله يبسط الرزق لمن يشاء ويقدر إن في ذلك لآيات لقوم يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 39, 'قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله إن الله يغفر الذنوب جميعا إنه هو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 39, 'وأنيبوا إلى ربكم وأسلموا له من قبل أن يأتيكم العذاب ثم لا تنصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 39, 'واتبعوا أحسن ما أنزل إليكم من ربكم من قبل أن يأتيكم العذاب بغتة وأنتم لا تشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 39, 'أن تقول نفس يا حسرتا على ما فرطت في جنب الله وإن كنت لمن الساخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 39, 'أو تقول لو أن الله هداني لكنت من المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 39, 'أو تقول حين ترى العذاب لو أن لي كرة فأكون من المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 39, 'بلى قد جاءتك آياتي فكذبت بها واستكبرت وكنت من الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 39, 'ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة أليس في جهنم مثوى للمتكبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 39, 'وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 39, 'الله خالق كل شيء وهو على كل شيء وكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 39, 'له مقاليد السماوات والأرض والذين كفروا بآيات الله أولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 39, 'قل أفغير الله تأمروني أعبد أيها الجاهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 39, 'ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك ولتكونن من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 39, 'بل الله فاعبد وكن من الشاكرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 39, 'وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه سبحانه وتعالى عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 39, 'ونفخ في الصور فصعق من في السماوات ومن في الأرض إلا من شاء الله ثم نفخ فيه أخرى فإذا هم قيام ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 39, 'وأشرقت الأرض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 39, 'ووفيت كل نفس ما عملت وهو أعلم بما يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 39, 'وسيق الذين كفروا إلى جهنم زمرا حتى إذا جاءوها فتحت أبوابها وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم وينذرونكم لقاء يومكم هذا قالوا بلى ولكن حقت كلمة العذاب على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 39, 'قيل ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 39, 'وسيق الذين اتقوا ربهم إلى الجنة زمرا حتى إذا جاءوها وفتحت أبوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 39, 'وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء فنعم أجر العاملين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 39, 'وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم وقضي بينهم بالحق وقيل الحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 40, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 40, 'تنزيل الكتاب من الله العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 40, 'غافر الذنب وقابل التوب شديد العقاب ذي الطول لا إله إلا هو إليه المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 40, 'ما يجادل في آيات الله إلا الذين كفروا فلا يغررك تقلبهم في البلاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 40, 'كذبت قبلهم قوم نوح والأحزاب من بعدهم وهمت كل أمة برسولهم ليأخذوه وجادلوا بالباطل ليدحضوا به الحق فأخذتهم فكيف كان عقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 40, 'وكذلك حقت كلمت ربك على الذين كفروا أنهم أصحاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 40, 'الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين آمنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 40, 'ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم إنك أنت العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 40, 'وقهم السيئات ومن تق السيئات يومئذ فقد رحمته وذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 40, 'إن الذين كفروا ينادون لمقت الله أكبر من مقتكم أنفسكم إذ تدعون إلى الإيمان فتكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 40, 'قالوا ربنا أمتنا اثنتين وأحييتنا اثنتين فاعترفنا بذنوبنا فهل إلى خروج من سبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 40, 'ذلكم بأنه إذا دعي الله وحده كفرتم وإن يشرك به تؤمنوا فالحكم لله العلي الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 40, 'هو الذي يريكم آياته وينزل لكم من السماء رزقا وما يتذكر إلا من ينيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 40, 'فادعوا الله مخلصين له الدين ولو كره الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 40, 'رفيع الدرجات ذو العرش يلقي الروح من أمره على من يشاء من عباده لينذر يوم التلاق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 40, 'يوم هم بارزون لا يخفى على الله منهم شيء لمن الملك اليوم لله الواحد القهار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 40, 'اليوم تجزى كل نفس بما كسبت لا ظلم اليوم إن الله سريع الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 40, 'وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين ما للظالمين من حميم ولا شفيع يطاع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 40, 'يعلم خائنة الأعين وما تخفي الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 40, 'والله يقضي بالحق والذين يدعون من دونه لا يقضون بشيء إن الله هو السميع البصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 40, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم كانوا هم أشد منهم قوة وآثارا في الأرض فأخذهم الله بذنوبهم وما كان لهم من الله من واق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 40, 'ذلك بأنهم كانت تأتيهم رسلهم بالبينات فكفروا فأخذهم الله إنه قوي شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 40, 'ولقد أرسلنا موسى بآياتنا وسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 40, 'إلى فرعون وهامان وقارون فقالوا ساحر كذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 40, 'فلما جاءهم بالحق من عندنا قالوا اقتلوا أبناء الذين آمنوا معه واستحيوا نساءهم وما كيد الكافرين إلا في ضلال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 40, 'وقال فرعون ذروني أقتل موسى وليدع ربه إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 40, 'وقال موسى إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 40, 'وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم وإن يك كاذبا فعليه كذبه وإن يك صادقا يصبكم بعض الذي يعدكم إن الله لا يهدي من هو مسرف كذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 40, 'يا قوم لكم الملك اليوم ظاهرين في الأرض فمن ينصرنا من بأس الله إن جاءنا قال فرعون ما أريكم إلا ما أرى وما أهديكم إلا سبيل الرشاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 40, 'وقال الذي آمن يا قوم إني أخاف عليكم مثل يوم الأحزاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 40, 'مثل دأب قوم نوح وعاد وثمود والذين من بعدهم وما الله يريد ظلما للعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 40, 'ويا قوم إني أخاف عليكم يوم التناد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 40, 'يوم تولون مدبرين ما لكم من الله من عاصم ومن يضلل الله فما له من هاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 40, 'ولقد جاءكم يوسف من قبل بالبينات فما زلتم في شك مما جاءكم به حتى إذا هلك قلتم لن يبعث الله من بعده رسولا كذلك يضل الله من هو مسرف مرتاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 40, 'الذين يجادلون في آيات الله بغير سلطان أتاهم كبر مقتا عند الله وعند الذين آمنوا كذلك يطبع الله على كل قلب متكبر جبار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 40, 'وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 40, 'أسباب السماوات فأطلع إلى إله موسى وإني لأظنه كاذبا وكذلك زين لفرعون سوء عمله وصد عن السبيل وما كيد فرعون إلا في تباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 40, 'وقال الذي آمن يا قوم اتبعون أهدكم سبيل الرشاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 40, 'يا قوم إنما هذه الحياة الدنيا متاع وإن الآخرة هي دار القرار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 40, 'من عمل سيئة فلا يجزى إلا مثلها ومن عمل صالحا من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة يرزقون فيها بغير حساب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 40, 'ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 40, 'تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز الغفار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 40, 'لا جرم أنما تدعونني إليه ليس له دعوة في الدنيا ولا في الآخرة وأن مردنا إلى الله وأن المسرفين هم أصحاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 40, 'فستذكرون ما أقول لكم وأفوض أمري إلى الله إن الله بصير بالعباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 40, 'فوقاه الله سيئات ما مكروا وحاق بآل فرعون سوء العذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 40, 'النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة أدخلوا آل فرعون أشد العذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 40, 'وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 40, 'قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 40, 'وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 40, 'قالوا أولم تك تأتيكم رسلكم بالبينات قالوا بلى قالوا فادعوا وما دعاء الكافرين إلا في ضلال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 40, 'إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 40, 'يوم لا ينفع الظالمين معذرتهم ولهم اللعنة ولهم سوء الدار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 40, 'ولقد آتينا موسى الهدى وأورثنا بني إسرائيل الكتاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 40, 'هدى وذكرى لأولي الألباب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 40, 'فاصبر إن وعد الله حق واستغفر لذنبك وسبح بحمد ربك بالعشي والإبكار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 40, 'إن الذين يجادلون في آيات الله بغير سلطان أتاهم إن في صدورهم إلا كبر ما هم ببالغيه فاستعذ بالله إنه هو السميع البصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 40, 'لخلق السماوات والأرض أكبر من خلق الناس ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 40, 'وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء قليلا ما تتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 40, 'إن الساعة لآتية لا ريب فيها ولكن أكثر الناس لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 40, 'وقال ربكم ادعوني أستجب لكم إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 40, 'الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 40, 'ذلكم الله ربكم خالق كل شيء لا إله إلا هو فأنى تؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 40, 'كذلك يؤفك الذين كانوا بآيات الله يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 40, 'الله الذي جعل لكم الأرض قرارا والسماء بناء وصوركم فأحسن صوركم ورزقكم من الطيبات ذلكم الله ربكم فتبارك الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 40, 'هو الحي لا إله إلا هو فادعوه مخلصين له الدين الحمد لله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 40, 'قل إني نهيت أن أعبد الذين تدعون من دون الله لما جاءني البينات من ربي وأمرت أن أسلم لرب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 40, 'هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا أشدكم ثم لتكونوا شيوخا ومنكم من يتوفى من قبل ولتبلغوا أجلا مسمى ولعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 40, 'هو الذي يحيي ويميت فإذا قضى أمرا فإنما يقول له كن فيكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 40, 'ألم تر إلى الذين يجادلون في آيات الله أنى يصرفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 40, 'الذين كذبوا بالكتاب وبما أرسلنا به رسلنا فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 40, 'إذ الأغلال في أعناقهم والسلاسل يسحبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 40, 'في الحميم ثم في النار يسجرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 40, 'ثم قيل لهم أين ما كنتم تشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 40, 'من دون الله قالوا ضلوا عنا بل لم نكن ندعو من قبل شيئا كذلك يضل الله الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 40, 'ذلكم بما كنتم تفرحون في الأرض بغير الحق وبما كنتم تمرحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 40, 'ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 40, 'فاصبر إن وعد الله حق فإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 40, 'ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك وما كان لرسول أن يأتي بآية إلا بإذن الله فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 40, 'الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 40, 'ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 40, 'ويريكم آياته فأي آيات الله تنكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 40, 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أكثر منهم وأشد قوة وآثارا في الأرض فما أغنى عنهم ما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 40, 'فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 40, 'فلما رأوا بأسنا قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 40, 'فلم يك ينفعهم إيمانهم لما رأوا بأسنا سنت الله التي قد خلت في عباده وخسر هنالك الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 41, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 41, 'تنزيل من الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 41, 'كتاب فصلت آياته قرآنا عربيا لقوم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 41, 'بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 41, 'وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب فاعمل إننا عاملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 41, 'قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فاستقيموا إليه واستغفروه وويل للمشركين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 41, 'الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 41, 'إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 41, 'قل أئنكم لتكفرون بالذي خلق الأرض في يومين وتجعلون له أندادا ذلك رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 41, 'وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام سواء للسائلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 41, 'ثم استوى إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 41, 'فقضاهن سبع سماوات في يومين وأوحى في كل سماء أمرها وزينا السماء الدنيا بمصابيح وحفظا ذلك تقدير العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 41, 'فإن أعرضوا فقل أنذرتكم صاعقة مثل صاعقة عاد وثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 41, 'إذ جاءتهم الرسل من بين أيديهم ومن خلفهم ألا تعبدوا إلا الله قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 41, 'فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة أولم يروا أن الله الذي خلقهم هو أشد منهم قوة وكانوا بآياتنا يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 41, 'فأرسلنا عليهم ريحا صرصرا في أيام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ولعذاب الآخرة أخزى وهم لا ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 41, 'وأما ثمود فهديناهم فاستحبوا العمى على الهدى فأخذتهم صاعقة العذاب الهون بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 41, 'ونجينا الذين آمنوا وكانوا يتقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 41, 'ويوم يحشر أعداء الله إلى النار فهم يوزعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 41, 'حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 41, 'وقالوا لجلودهم لم شهدتم علينا قالوا أنطقنا الله الذي أنطق كل شيء وهو خلقكم أول مرة وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 41, 'وما كنتم تستترون أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 41, 'وذلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 41, 'فإن يصبروا فالنار مثوى لهم وإن يستعتبوا فما هم من المعتبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 41, 'وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم وحق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 41, 'وقال الذين كفروا لا تسمعوا لهذا القرآن والغوا فيه لعلكم تغلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 41, 'فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 41, 'ذلك جزاء أعداء الله النار لهم فيها دار الخلد جزاء بما كانوا بآياتنا يجحدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 41, 'وقال الذين كفروا ربنا أرنا اللذين أضلانا من الجن والإنس نجعلهما تحت أقدامنا ليكونا من الأسفلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 41, 'إن الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة ألا تخافوا ولا تحزنوا وأبشروا بالجنة التي كنتم توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 41, 'نحن أولياؤكم في الحياة الدنيا وفي الآخرة ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 41, 'نزلا من غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 41, 'ومن أحسن قولا ممن دعا إلى الله وعمل صالحا وقال إنني من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 41, 'ولا تستوي الحسنة ولا السيئة ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 41, 'وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 41, 'وإما ينزغنك من الشيطان نزغ فاستعذ بالله إنه هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 41, 'ومن آياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن إن كنتم إياه تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 41, 'فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 41, 'ومن آياته أنك ترى الأرض خاشعة فإذا أنزلنا عليها الماء اهتزت وربت إن الذي أحياها لمحيي الموتى إنه على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 41, 'إن الذين يلحدون في آياتنا لا يخفون علينا أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة اعملوا ما شئتم إنه بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 41, 'إن الذين كفروا بالذكر لما جاءهم وإنه لكتاب عزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 41, 'لا يأتيه الباطل من بين يديه ولا من خلفه تنزيل من حكيم حميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 41, 'ما يقال لك إلا ما قد قيل للرسل من قبلك إن ربك لذو مغفرة وذو عقاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 41, 'ولو جعلناه قرآنا أعجميا لقالوا لولا فصلت آياته أأعجمي وعربي قل هو للذين آمنوا هدى وشفاء والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى أولئك ينادون من مكان بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 41, 'ولقد آتينا موسى الكتاب فاختلف فيه ولولا كلمة سبقت من ربك لقضي بينهم وإنهم لفي شك منه مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 41, 'من عمل صالحا فلنفسه ومن أساء فعليها وما ربك بظلام للعبيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 41, 'إليه يرد علم الساعة وما تخرج من ثمرات من أكمامها وما تحمل من أنثى ولا تضع إلا بعلمه ويوم يناديهم أين شركائي قالوا آذناك ما منا من شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 41, 'وضل عنهم ما كانوا يدعون من قبل وظنوا ما لهم من محيص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 41, 'لا يسأم الإنسان من دعاء الخير وإن مسه الشر فيئوس قنوط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 41, 'ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 41, 'وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر فذو دعاء عريض');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 41, 'قل أرأيتم إن كان من عند الله ثم كفرتم به من أضل ممن هو في شقاق بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 41, 'سنريهم آياتنا في الآفاق وفي أنفسهم حتى يتبين لهم أنه الحق أولم يكف بربك أنه على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 41, 'ألا إنهم في مرية من لقاء ربهم ألا إنه بكل شيء محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 42, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 42, 'عسق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 42, 'كذلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 42, 'له ما في السماوات وما في الأرض وهو العلي العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 42, 'تكاد السماوات يتفطرن من فوقهن والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض ألا إن الله هو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 42, 'والذين اتخذوا من دونه أولياء الله حفيظ عليهم وما أنت عليهم بوكيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 42, 'وكذلك أوحينا إليك قرآنا عربيا لتنذر أم القرى ومن حولها وتنذر يوم الجمع لا ريب فيه فريق في الجنة وفريق في السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 42, 'ولو شاء الله لجعلهم أمة واحدة ولكن يدخل من يشاء في رحمته والظالمون ما لهم من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 42, 'أم اتخذوا من دونه أولياء فالله هو الولي وهو يحيي الموتى وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 42, 'وما اختلفتم فيه من شيء فحكمه إلى الله ذلكم الله ربي عليه توكلت وإليه أنيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 42, 'فاطر السماوات والأرض جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا يذرؤكم فيه ليس كمثله شيء وهو السميع البصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 42, 'له مقاليد السماوات والأرض يبسط الرزق لمن يشاء ويقدر إنه بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 42, 'شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين ولا تتفرقوا فيه كبر على المشركين ما تدعوهم إليه الله يجتبي إليه من يشاء ويهدي إليه من ينيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 42, 'وما تفرقوا إلا من بعد ما جاءهم العلم بغيا بينهم ولولا كلمة سبقت من ربك إلى أجل مسمى لقضي بينهم وإن الذين أورثوا الكتاب من بعدهم لفي شك منه مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 42, 'فلذلك فادع واستقم كما أمرت ولا تتبع أهواءهم وقل آمنت بما أنزل الله من كتاب وأمرت لأعدل بينكم الله ربنا وربكم لنا أعمالنا ولكم أعمالكم لا حجة بيننا وبينكم الله يجمع بيننا وإليه المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 42, 'والذين يحاجون في الله من بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 42, 'الله الذي أنزل الكتاب بالحق والميزان وما يدريك لعل الساعة قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 42, 'يستعجل بها الذين لا يؤمنون بها والذين آمنوا مشفقون منها ويعلمون أنها الحق ألا إن الذين يمارون في الساعة لفي ضلال بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 42, 'الله لطيف بعباده يرزق من يشاء وهو القوي العزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 42, 'من كان يريد حرث الآخرة نزد له في حرثه ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 42, 'أم لهم شركاء شرعوا لهم من الدين ما لم يأذن به الله ولولا كلمة الفصل لقضي بينهم وإن الظالمين لهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 42, 'ترى الظالمين مشفقين مما كسبوا وهو واقع بهم والذين آمنوا وعملوا الصالحات في روضات الجنات لهم ما يشاءون عند ربهم ذلك هو الفضل الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 42, 'ذلك الذي يبشر الله عباده الذين آمنوا وعملوا الصالحات قل لا أسألكم عليه أجرا إلا المودة في القربى ومن يقترف حسنة نزد له فيها حسنا إن الله غفور شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 42, 'أم يقولون افترى على الله كذبا فإن يشإ الله يختم على قلبك ويمح الله الباطل ويحق الحق بكلماته إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 42, 'وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات ويعلم ما تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 42, 'ويستجيب الذين آمنوا وعملوا الصالحات ويزيدهم من فضله والكافرون لهم عذاب شديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 42, 'ولو بسط الله الرزق لعباده لبغوا في الأرض ولكن ينزل بقدر ما يشاء إنه بعباده خبير بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 42, 'وهو الذي ينزل الغيث من بعد ما قنطوا وينشر رحمته وهو الولي الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 42, 'ومن آياته خلق السماوات والأرض وما بث فيهما من دابة وهو على جمعهم إذا يشاء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 42, 'وما أصابكم من مصيبة فبما كسبت أيديكم ويعفو عن كثير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 42, 'وما أنتم بمعجزين في الأرض وما لكم من دون الله من ولي ولا نصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 42, 'ومن آياته الجوار في البحر كالأعلام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 42, 'إن يشأ يسكن الريح فيظللن رواكد على ظهره إن في ذلك لآيات لكل صبار شكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 42, 'أو يوبقهن بما كسبوا ويعف عن كثير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 42, 'ويعلم الذين يجادلون في آياتنا ما لهم من محيص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 42, 'فما أوتيتم من شيء فمتاع الحياة الدنيا وما عند الله خير وأبقى للذين آمنوا وعلى ربهم يتوكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 42, 'والذين يجتنبون كبائر الإثم والفواحش وإذا ما غضبوا هم يغفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 42, 'والذين استجابوا لربهم وأقاموا الصلاة وأمرهم شورى بينهم ومما رزقناهم ينفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 42, 'والذين إذا أصابهم البغي هم ينتصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 42, 'وجزاء سيئة سيئة مثلها فمن عفا وأصلح فأجره على الله إنه لا يحب الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 42, 'ولمن انتصر بعد ظلمه فأولئك ما عليهم من سبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 42, 'إنما السبيل على الذين يظلمون الناس ويبغون في الأرض بغير الحق أولئك لهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 42, 'ولمن صبر وغفر إن ذلك لمن عزم الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 42, 'ومن يضلل الله فما له من ولي من بعده وترى الظالمين لما رأوا العذاب يقولون هل إلى مرد من سبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 42, 'وتراهم يعرضون عليها خاشعين من الذل ينظرون من طرف خفي وقال الذين آمنوا إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ألا إن الظالمين في عذاب مقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 42, 'وما كان لهم من أولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 42, 'استجيبوا لربكم من قبل أن يأتي يوم لا مرد له من الله ما لكم من ملجإ يومئذ وما لكم من نكير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 42, 'فإن أعرضوا فما أرسلناك عليهم حفيظا إن عليك إلا البلاغ وإنا إذا أذقنا الإنسان منا رحمة فرح بها وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 42, 'لله ملك السماوات والأرض يخلق ما يشاء يهب لمن يشاء إناثا ويهب لمن يشاء الذكور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 42, 'أو يزوجهم ذكرانا وإناثا ويجعل من يشاء عقيما إنه عليم قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 42, 'وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب أو يرسل رسولا فيوحي بإذنه ما يشاء إنه علي حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 42, 'وكذلك أوحينا إليك روحا من أمرنا ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا وإنك لتهدي إلى صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 42, 'صراط الله الذي له ما في السماوات وما في الأرض ألا إلى الله تصير الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 43, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 43, 'والكتاب المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 43, 'إنا جعلناه قرآنا عربيا لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 43, 'وإنه في أم الكتاب لدينا لعلي حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 43, 'أفنضرب عنكم الذكر صفحا أن كنتم قوما مسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 43, 'وكم أرسلنا من نبي في الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 43, 'وما يأتيهم من نبي إلا كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 43, 'فأهلكنا أشد منهم بطشا ومضى مثل الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 43, 'ولئن سألتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 43, 'الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 43, 'والذي نزل من السماء ماء بقدر فأنشرنا به بلدة ميتا كذلك تخرجون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 43, 'والذي خلق الأزواج كلها وجعل لكم من الفلك والأنعام ما تركبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 43, 'لتستووا على ظهوره ثم تذكروا نعمة ربكم إذا استويتم عليه وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 43, 'وإنا إلى ربنا لمنقلبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 43, 'وجعلوا له من عباده جزءا إن الإنسان لكفور مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 43, 'أم اتخذ مما يخلق بنات وأصفاكم بالبنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 43, 'وإذا بشر أحدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 43, 'أومن ينشأ في الحلية وهو في الخصام غير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 43, 'وجعلوا الملائكة الذين هم عباد الرحمن إناثا أشهدوا خلقهم ستكتب شهادتهم ويسألون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 43, 'وقالوا لو شاء الرحمن ما عبدناهم ما لهم بذلك من علم إن هم إلا يخرصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 43, 'أم آتيناهم كتابا من قبله فهم به مستمسكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 43, 'بل قالوا إنا وجدنا آباءنا على أمة وإنا على آثارهم مهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 43, 'وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 43, 'قال أولو جئتكم بأهدى مما وجدتم عليه آباءكم قالوا إنا بما أرسلتم به كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 43, 'فانتقمنا منهم فانظر كيف كان عاقبة المكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 43, 'وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 43, 'إلا الذي فطرني فإنه سيهدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 43, 'وجعلها كلمة باقية في عقبه لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 43, 'بل متعت هؤلاء وآباءهم حتى جاءهم الحق ورسول مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 43, 'ولما جاءهم الحق قالوا هذا سحر وإنا به كافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 43, 'وقالوا لولا نزل هذا القرآن على رجل من القريتين عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 43, 'أهم يقسمون رحمت ربك نحن قسمنا بينهم معيشتهم في الحياة الدنيا ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ورحمت ربك خير مما يجمعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 43, 'ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 43, 'ولبيوتهم أبوابا وسررا عليها يتكئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 43, 'وزخرفا وإن كل ذلك لما متاع الحياة الدنيا والآخرة عند ربك للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 43, 'ومن يعش عن ذكر الرحمن نقيض له شيطانا فهو له قرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 43, 'وإنهم ليصدونهم عن السبيل ويحسبون أنهم مهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 43, 'حتى إذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 43, 'ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 43, 'أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 43, 'فإما نذهبن بك فإنا منهم منتقمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 43, 'أو نرينك الذي وعدناهم فإنا عليهم مقتدرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 43, 'فاستمسك بالذي أوحي إليك إنك على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 43, 'وإنه لذكر لك ولقومك وسوف تسألون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 43, 'واسأل من أرسلنا من قبلك من رسلنا أجعلنا من دون الرحمن آلهة يعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 43, 'ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه فقال إني رسول رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 43, 'فلما جاءهم بآياتنا إذا هم منها يضحكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 43, 'وما نريهم من آية إلا هي أكبر من أختها وأخذناهم بالعذاب لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 43, 'وقالوا يا أيه الساحر ادع لنا ربك بما عهد عندك إننا لمهتدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 43, 'فلما كشفنا عنهم العذاب إذا هم ينكثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 43, 'ونادى فرعون في قومه قال يا قوم أليس لي ملك مصر وهذه الأنهار تجري من تحتي أفلا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 43, 'أم أنا خير من هذا الذي هو مهين ولا يكاد يبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 43, 'فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 43, 'فاستخف قومه فأطاعوه إنهم كانوا قوما فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 43, 'فلما آسفونا انتقمنا منهم فأغرقناهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 43, 'فجعلناهم سلفا ومثلا للآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 43, 'ولما ضرب ابن مريم مثلا إذا قومك منه يصدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 43, 'وقالوا أآلهتنا خير أم هو ما ضربوه لك إلا جدلا بل هم قوم خصمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 43, 'إن هو إلا عبد أنعمنا عليه وجعلناه مثلا لبني إسرائيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 43, 'ولو نشاء لجعلنا منكم ملائكة في الأرض يخلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 43, 'وإنه لعلم للساعة فلا تمترن بها واتبعون هذا صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 43, 'ولا يصدنكم الشيطان إنه لكم عدو مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 43, 'ولما جاء عيسى بالبينات قال قد جئتكم بالحكمة ولأبين لكم بعض الذي تختلفون فيه فاتقوا الله وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 43, 'إن الله هو ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 43, 'فاختلف الأحزاب من بينهم فويل للذين ظلموا من عذاب يوم أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 43, 'هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 43, 'الأخلاء يومئذ بعضهم لبعض عدو إلا المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 43, 'يا عباد لا خوف عليكم اليوم ولا أنتم تحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 43, 'الذين آمنوا بآياتنا وكانوا مسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 43, 'ادخلوا الجنة أنتم وأزواجكم تحبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 43, 'يطاف عليهم بصحاف من ذهب وأكواب وفيها ما تشتهيه الأنفس وتلذ الأعين وأنتم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 43, 'وتلك الجنة التي أورثتموها بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 43, 'لكم فيها فاكهة كثيرة منها تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 43, 'إن المجرمين في عذاب جهنم خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 43, 'لا يفتر عنهم وهم فيه مبلسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 43, 'وما ظلمناهم ولكن كانوا هم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 43, 'ونادوا يا مالك ليقض علينا ربك قال إنكم ماكثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 43, 'لقد جئناكم بالحق ولكن أكثركم للحق كارهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 43, 'أم أبرموا أمرا فإنا مبرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 43, 'أم يحسبون أنا لا نسمع سرهم ونجواهم بلى ورسلنا لديهم يكتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 43, 'قل إن كان للرحمن ولد فأنا أول العابدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 43, 'سبحان رب السماوات والأرض رب العرش عما يصفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 43, 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 43, 'وهو الذي في السماء إله وفي الأرض إله وهو الحكيم العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 43, 'وتبارك الذي له ملك السماوات والأرض وما بينهما وعنده علم الساعة وإليه ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 43, 'ولا يملك الذين يدعون من دونه الشفاعة إلا من شهد بالحق وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 43, 'ولئن سألتهم من خلقهم ليقولن الله فأنى يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 43, 'وقيله يا رب إن هؤلاء قوم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 43, 'فاصفح عنهم وقل سلام فسوف يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 44, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 44, 'والكتاب المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 44, 'إنا أنزلناه في ليلة مباركة إنا كنا منذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 44, 'فيها يفرق كل أمر حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 44, 'أمرا من عندنا إنا كنا مرسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 44, 'رحمة من ربك إنه هو السميع العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 44, 'رب السماوات والأرض وما بينهما إن كنتم موقنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 44, 'لا إله إلا هو يحيي ويميت ربكم ورب آبائكم الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 44, 'بل هم في شك يلعبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 44, 'فارتقب يوم تأتي السماء بدخان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 44, 'يغشى الناس هذا عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 44, 'ربنا اكشف عنا العذاب إنا مؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 44, 'أنى لهم الذكرى وقد جاءهم رسول مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 44, 'ثم تولوا عنه وقالوا معلم مجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 44, 'إنا كاشفو العذاب قليلا إنكم عائدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 44, 'يوم نبطش البطشة الكبرى إنا منتقمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 44, 'ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 44, 'أن أدوا إلي عباد الله إني لكم رسول أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 44, 'وأن لا تعلوا على الله إني آتيكم بسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 44, 'وإني عذت بربي وربكم أن ترجمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 44, 'وإن لم تؤمنوا لي فاعتزلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 44, 'فدعا ربه أن هؤلاء قوم مجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 44, 'فأسر بعبادي ليلا إنكم متبعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 44, 'واترك البحر رهوا إنهم جند مغرقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 44, 'كم تركوا من جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 44, 'وزروع ومقام كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 44, 'ونعمة كانوا فيها فاكهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 44, 'كذلك وأورثناها قوما آخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 44, 'فما بكت عليهم السماء والأرض وما كانوا منظرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 44, 'ولقد نجينا بني إسرائيل من العذاب المهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 44, 'من فرعون إنه كان عاليا من المسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 44, 'ولقد اخترناهم على علم على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 44, 'وآتيناهم من الآيات ما فيه بلاء مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 44, 'إن هؤلاء ليقولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 44, 'إن هي إلا موتتنا الأولى وما نحن بمنشرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 44, 'فأتوا بآبائنا إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 44, 'أهم خير أم قوم تبع والذين من قبلهم أهلكناهم إنهم كانوا مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 44, 'وما خلقنا السماوات والأرض وما بينهما لاعبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 44, 'ما خلقناهما إلا بالحق ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 44, 'إن يوم الفصل ميقاتهم أجمعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 44, 'يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 44, 'إلا من رحم الله إنه هو العزيز الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 44, 'إن شجرت الزقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 44, 'طعام الأثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 44, 'كالمهل يغلي في البطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 44, 'كغلي الحميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 44, 'خذوه فاعتلوه إلى سواء الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 44, 'ثم صبوا فوق رأسه من عذاب الحميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 44, 'ذق إنك أنت العزيز الكريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 44, 'إن هذا ما كنتم به تمترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 44, 'إن المتقين في مقام أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 44, 'في جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 44, 'يلبسون من سندس وإستبرق متقابلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 44, 'كذلك وزوجناهم بحور عين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 44, 'يدعون فيها بكل فاكهة آمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 44, 'لا يذوقون فيها الموت إلا الموتة الأولى ووقاهم عذاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 44, 'فضلا من ربك ذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 44, 'فإنما يسرناه بلسانك لعلهم يتذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 44, 'فارتقب إنهم مرتقبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 45, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 45, 'تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 45, 'إن في السماوات والأرض لآيات للمؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 45, 'وفي خلقكم وما يبث من دابة آيات لقوم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 45, 'واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 45, 'تلك آيات الله نتلوها عليك بالحق فبأي حديث بعد الله وآياته يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 45, 'ويل لكل أفاك أثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 45, 'يسمع آيات الله تتلى عليه ثم يصر مستكبرا كأن لم يسمعها فبشره بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 45, 'وإذا علم من آياتنا شيئا اتخذها هزوا أولئك لهم عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 45, 'من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ولهم عذاب عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 45, 'هذا هدى والذين كفروا بآيات ربهم لهم عذاب من رجز أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 45, 'الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 45, 'وسخر لكم ما في السماوات وما في الأرض جميعا منه إن في ذلك لآيات لقوم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 45, 'قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 45, 'من عمل صالحا فلنفسه ومن أساء فعليها ثم إلى ربكم ترجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 45, 'ولقد آتينا بني إسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 45, 'وآتيناهم بينات من الأمر فما اختلفوا إلا من بعد ما جاءهم العلم بغيا بينهم إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 45, 'ثم جعلناك على شريعة من الأمر فاتبعها ولا تتبع أهواء الذين لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 45, 'إنهم لن يغنوا عنك من الله شيئا وإن الظالمين بعضهم أولياء بعض والله ولي المتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 45, 'هذا بصائر للناس وهدى ورحمة لقوم يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 45, 'أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ساء ما يحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 45, 'وخلق الله السماوات والأرض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 45, 'أفرأيت من اتخذ إلهه هواه وأضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله أفلا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 45, 'وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا وما يهلكنا إلا الدهر وما لهم بذلك من علم إن هم إلا يظنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 45, 'وإذا تتلى عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 45, 'قل الله يحييكم ثم يميتكم ثم يجمعكم إلى يوم القيامة لا ريب فيه ولكن أكثر الناس لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 45, 'ولله ملك السماوات والأرض ويوم تقوم الساعة يومئذ يخسر المبطلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 45, 'وترى كل أمة جاثية كل أمة تدعى إلى كتابها اليوم تجزون ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 45, 'هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 45, 'فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ذلك هو الفوز المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 45, 'وأما الذين كفروا أفلم تكن آياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 45, 'وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 45, 'وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 45, 'وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا ومأواكم النار وما لكم من ناصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 45, 'ذلكم بأنكم اتخذتم آيات الله هزوا وغرتكم الحياة الدنيا فاليوم لا يخرجون منها ولا هم يستعتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 45, 'فلله الحمد رب السماوات ورب الأرض رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 45, 'وله الكبرياء في السماوات والأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 46, 'بسم الله الرحمن الرحيم حم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 46, 'تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 46, 'ما خلقنا السماوات والأرض وما بينهما إلا بالحق وأجل مسمى والذين كفروا عما أنذروا معرضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 46, 'قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 46, 'ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 46, 'وإذا حشر الناس كانوا لهم أعداء وكانوا بعبادتهم كافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 46, 'وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 46, 'أم يقولون افتراه قل إن افتريته فلا تملكون لي من الله شيئا هو أعلم بما تفيضون فيه كفى به شهيدا بيني وبينكم وهو الغفور الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 46, 'قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم إن أتبع إلا ما يوحى إلي وما أنا إلا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 46, 'قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم إن الله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 46, 'وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه وإذ لم يهتدوا به فسيقولون هذا إفك قديم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 46, 'ومن قبله كتاب موسى إماما ورحمة وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 46, 'إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 46, 'أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 46, 'ووصينا الإنسان بوالديه إحسانا حملته أمه كرها ووضعته كرها وحمله وفصاله ثلاثون شهرا حتى إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي إني تبت إليك وإني من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 46, 'أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة وعد الصدق الذي كانوا يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 46, 'والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هذا إلا أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 46, 'أولئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 46, 'ولكل درجات مما عملوا وليوفيهم أعمالهم وهم لا يظلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 46, 'ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 46, 'واذكر أخا عاد إذ أنذر قومه بالأحقاف وقد خلت النذر من بين يديه ومن خلفه ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 46, 'قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 46, 'قال إنما العلم عند الله وأبلغكم ما أرسلت به ولكني أراكم قوما تجهلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 46, 'فلما رأوه عارضا مستقبل أوديتهم قالوا هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 46, 'تدمر كل شيء بأمر ربها فأصبحوا لا يرى إلا مساكنهم كذلك نجزي القوم المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 46, 'ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 46, 'ولقد أهلكنا ما حولكم من القرى وصرفنا الآيات لعلهم يرجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 46, 'فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة بل ضلوا عنهم وذلك إفكهم وما كانوا يفترون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 46, 'وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا فلما قضي ولوا إلى قومهم منذرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 46, 'قالوا يا قومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقا لما بين يديه يهدي إلى الحق وإلى طريق مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 46, 'يا قومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 46, 'ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء أولئك في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 46, 'أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى بلى إنه على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 46, 'ويوم يعرض الذين كفروا على النار أليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 46, 'فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار بلاغ فهل يهلك إلا القوم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 47, 'بسم الله الرحمن الرحيم الذين كفروا وصدوا عن سبيل الله أضل أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 47, 'والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم وأصلح بالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 47, 'ذلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم كذلك يضرب الله للناس أمثالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 47, 'فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض والذين قتلوا في سبيل الله فلن يضل أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 47, 'سيهديهم ويصلح بالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 47, 'ويدخلهم الجنة عرفها لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 47, 'يا أيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 47, 'والذين كفروا فتعسا لهم وأضل أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 47, 'ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 47, 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين أمثالها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 47, 'ذلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولى لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 47, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار والذين كفروا يتمتعون ويأكلون كما تأكل الأنعام والنار مثوى لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 47, 'وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 47, 'أفمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 47, 'مثل الجنة التي وعد المتقون فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ولهم فيها من كل الثمرات ومغفرة من ربهم كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 47, 'ومنهم من يستمع إليك حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا أولئك الذين طبع الله على قلوبهم واتبعوا أهواءهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 47, 'والذين اهتدوا زادهم هدى وآتاهم تقواهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 47, 'فهل ينظرون إلا الساعة أن تأتيهم بغتة فقد جاء أشراطها فأنى لهم إذا جاءتهم ذكراهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 47, 'فاعلم أنه لا إله إلا الله واستغفر لذنبك وللمؤمنين والمؤمنات والله يعلم متقلبكم ومثواكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 47, 'ويقول الذين آمنوا لولا نزلت سورة فإذا أنزلت سورة محكمة وذكر فيها القتال رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت فأولى لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 47, 'طاعة وقول معروف فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 47, 'فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 47, 'أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 47, 'أفلا يتدبرون القرآن أم على قلوب أقفالها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 47, 'إن الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى الشيطان سول لهم وأملى لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 47, 'ذلك بأنهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الأمر والله يعلم إسرارهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 47, 'فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 47, 'ذلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 47, 'أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 47, 'ولو نشاء لأريناكهم فلعرفتهم بسيماهم ولتعرفنهم في لحن القول والله يعلم أعمالكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 47, 'ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو أخباركم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 47, 'إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 47, 'يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 47, 'إن الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 47, 'فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون والله معكم ولن يتركم أعمالكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 47, 'إنما الحياة الدنيا لعب ولهو وإن تؤمنوا وتتقوا يؤتكم أجوركم ولا يسألكم أموالكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 47, 'إن يسألكموها فيحفكم تبخلوا ويخرج أضغانكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 47, 'ها أنتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ومن يبخل فإنما يبخل عن نفسه والله الغني وأنتم الفقراء وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 48, 'بسم الله الرحمن الرحيم إنا فتحنا لك فتحا مبينا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 48, 'ليغفر لك الله ما تقدم من ذنبك وما تأخر ويتم نعمته عليك ويهديك صراطا مستقيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 48, 'وينصرك الله نصرا عزيزا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 48, 'هو الذي أنزل السكينة في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم ولله جنود السماوات والأرض وكان الله عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 48, 'ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 48, 'ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم وأعد لهم جهنم وساءت مصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 48, 'ولله جنود السماوات والأرض وكان الله عزيزا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 48, 'إنا أرسلناك شاهدا ومبشرا ونذيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 48, 'لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 48, 'إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم فمن نكث فإنما ينكث على نفسه ومن أوفى بما عاهد عليه الله فسيؤتيه أجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 48, 'سيقول لك المخلفون من الأعراب شغلتنا أموالنا وأهلونا فاستغفر لنا يقولون بألسنتهم ما ليس في قلوبهم قل فمن يملك لكم من الله شيئا إن أراد بكم ضرا أو أراد بكم نفعا بل كان الله بما تعملون خبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 48, 'بل ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبدا وزين ذلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 48, 'ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 48, 'ولله ملك السماوات والأرض يغفر لمن يشاء ويعذب من يشاء وكان الله غفورا رحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 48, 'سيقول المخلفون إذا انطلقتم إلى مغانم لتأخذوها ذرونا نتبعكم يريدون أن يبدلوا كلام الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا لا يفقهون إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 48, 'قل للمخلفين من الأعراب ستدعون إلى قوم أولي بأس شديد تقاتلونهم أو يسلمون فإن تطيعوا يؤتكم الله أجرا حسنا وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 48, 'ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار ومن يتول يعذبه عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 48, 'لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 48, 'ومغانم كثيرة يأخذونها وكان الله عزيزا حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 48, 'وعدكم الله مغانم كثيرة تأخذونها فعجل لكم هذه وكف أيدي الناس عنكم ولتكون آية للمؤمنين ويهديكم صراطا مستقيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 48, 'وأخرى لم تقدروا عليها قد أحاط الله بها وكان الله على كل شيء قديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 48, 'ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 48, 'سنة الله التي قد خلت من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 48, 'وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم وكان الله بما تعملون بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 48, 'هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ليدخل الله في رحمته من يشاء لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 48, 'إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته على رسوله وعلى المؤمنين وألزمهم كلمة التقوى وكانوا أحق بها وأهلها وكان الله بكل شيء عليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 48, 'لقد صدق الله رسوله الرؤيا بالحق لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 48, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 48, 'محمد رسول الله والذين معه أشداء على الكفار رحماء بينهم تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا سيماهم في وجوههم من أثر السجود ذلك مثلهم في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 49, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله واتقوا الله إن الله سميع عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 49, 'يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 49, 'إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة وأجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 49, 'إن الذين ينادونك من وراء الحجرات أكثرهم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 49, 'ولو أنهم صبروا حتى تخرج إليهم لكان خيرا لهم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 49, 'يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 49, 'واعلموا أن فيكم رسول الله لو يطيعكم في كثير من الأمر لعنتم ولكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان أولئك هم الراشدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 49, 'فضلا من الله ونعمة والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 49, 'وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا إن الله يحب المقسطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 49, 'إنما المؤمنون إخوة فأصلحوا بين أخويكم واتقوا الله لعلكم ترحمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 49, 'يا أيها الذين آمنوا لا يسخر قوم من قوم عسى أن يكونوا خيرا منهم ولا نساء من نساء عسى أن يكن خيرا منهن ولا تلمزوا أنفسكم ولا تنابزوا بالألقاب بئس الاسم الفسوق بعد الإيمان ومن لم يتب فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 49, 'يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ولا تجسسوا ولا يغتب بعضكم بعضا أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه واتقوا الله إن الله تواب رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 49, 'يا أيها الناس إنا خلقناكم من ذكر وأنثى وجعلناكم شعوبا وقبائل لتعارفوا إن أكرمكم عند الله أتقاكم إن الله عليم خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 49, 'قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 49, 'إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله أولئك هم الصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 49, 'قل أتعلمون الله بدينكم والله يعلم ما في السماوات وما في الأرض والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 49, 'يمنون عليك أن أسلموا قل لا تمنوا علي إسلامكم بل الله يمن عليكم أن هداكم للإيمان إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 49, 'إن الله يعلم غيب السماوات والأرض والله بصير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 50, 'بسم الله الرحمن الرحيم ق والقرآن المجيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 50, 'بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 50, 'أإذا متنا وكنا ترابا ذلك رجع بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 50, 'قد علمنا ما تنقص الأرض منهم وعندنا كتاب حفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 50, 'بل كذبوا بالحق لما جاءهم فهم في أمر مريج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 50, 'أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 50, 'والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 50, 'تبصرة وذكرى لكل عبد منيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 50, 'ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 50, 'والنخل باسقات لها طلع نضيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 50, 'رزقا للعباد وأحيينا به بلدة ميتا كذلك الخروج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 50, 'كذبت قبلهم قوم نوح وأصحاب الرس وثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 50, 'وعاد وفرعون وإخوان لوط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 50, 'وأصحاب الأيكة وقوم تبع كل كذب الرسل فحق وعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 50, 'أفعيينا بالخلق الأول بل هم في لبس من خلق جديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 50, 'ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ونحن أقرب إليه من حبل الوريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 50, 'إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 50, 'ما يلفظ من قول إلا لديه رقيب عتيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 50, 'وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 50, 'ونفخ في الصور ذلك يوم الوعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 50, 'وجاءت كل نفس معها سائق وشهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 50, 'لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 50, 'وقال قرينه هذا ما لدي عتيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 50, 'ألقيا في جهنم كل كفار عنيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 50, 'مناع للخير معتد مريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 50, 'الذي جعل مع الله إلها آخر فألقياه في العذاب الشديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 50, 'قال قرينه ربنا ما أطغيته ولكن كان في ضلال بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 50, 'قال لا تختصموا لدي وقد قدمت إليكم بالوعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 50, 'ما يبدل القول لدي وما أنا بظلام للعبيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 50, 'يوم نقول لجهنم هل امتلأت وتقول هل من مزيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 50, 'وأزلفت الجنة للمتقين غير بعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 50, 'هذا ما توعدون لكل أواب حفيظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 50, 'من خشي الرحمن بالغيب وجاء بقلب منيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 50, 'ادخلوها بسلام ذلك يوم الخلود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 50, 'لهم ما يشاءون فيها ولدينا مزيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 50, 'وكم أهلكنا قبلهم من قرن هم أشد منهم بطشا فنقبوا في البلاد هل من محيص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 50, 'إن في ذلك لذكرى لمن كان له قلب أو ألقى السمع وهو شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 50, 'ولقد خلقنا السماوات والأرض وما بينهما في ستة أيام وما مسنا من لغوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 50, 'فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 50, 'ومن الليل فسبحه وأدبار السجود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 50, 'واستمع يوم يناد المناد من مكان قريب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 50, 'يوم يسمعون الصيحة بالحق ذلك يوم الخروج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 50, 'إنا نحن نحيي ونميت وإلينا المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 50, 'يوم تشقق الأرض عنهم سراعا ذلك حشر علينا يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 50, 'نحن أعلم بما يقولون وما أنت عليهم بجبار فذكر بالقرآن من يخاف وعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 51, 'بسم الله الرحمن الرحيم والذاريات ذروا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 51, 'فالحاملات وقرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 51, 'فالجاريات يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 51, 'فالمقسمات أمرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 51, 'إنما توعدون لصادق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 51, 'وإن الدين لواقع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 51, 'والسماء ذات الحبك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 51, 'إنكم لفي قول مختلف');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 51, 'يؤفك عنه من أفك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 51, 'قتل الخراصون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 51, 'الذين هم في غمرة ساهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 51, 'يسألون أيان يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 51, 'يوم هم على النار يفتنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 51, 'ذوقوا فتنتكم هذا الذي كنتم به تستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 51, 'إن المتقين في جنات وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 51, 'آخذين ما آتاهم ربهم إنهم كانوا قبل ذلك محسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 51, 'كانوا قليلا من الليل ما يهجعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 51, 'وبالأسحار هم يستغفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 51, 'وفي أموالهم حق للسائل والمحروم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 51, 'وفي الأرض آيات للموقنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 51, 'وفي أنفسكم أفلا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 51, 'وفي السماء رزقكم وما توعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 51, 'فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 51, 'هل أتاك حديث ضيف إبراهيم المكرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 51, 'إذ دخلوا عليه فقالوا سلاما قال سلام قوم منكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 51, 'فراغ إلى أهله فجاء بعجل سمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 51, 'فقربه إليهم قال ألا تأكلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 51, 'فأوجس منهم خيفة قالوا لا تخف وبشروه بغلام عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 51, 'فأقبلت امرأته في صرة فصكت وجهها وقالت عجوز عقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 51, 'قالوا كذلك قال ربك إنه هو الحكيم العليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 51, 'قال فما خطبكم أيها المرسلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 51, 'قالوا إنا أرسلنا إلى قوم مجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 51, 'لنرسل عليهم حجارة من طين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 51, 'مسومة عند ربك للمسرفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 51, 'فأخرجنا من كان فيها من المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 51, 'فما وجدنا فيها غير بيت من المسلمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 51, 'وتركنا فيها آية للذين يخافون العذاب الأليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 51, 'وفي موسى إذ أرسلناه إلى فرعون بسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 51, 'فتولى بركنه وقال ساحر أو مجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 51, 'فأخذناه وجنوده فنبذناهم في اليم وهو مليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 51, 'وفي عاد إذ أرسلنا عليهم الريح العقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 51, 'ما تذر من شيء أتت عليه إلا جعلته كالرميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 51, 'وفي ثمود إذ قيل لهم تمتعوا حتى حين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 51, 'فعتوا عن أمر ربهم فأخذتهم الصاعقة وهم ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 51, 'فما استطاعوا من قيام وما كانوا منتصرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 51, 'وقوم نوح من قبل إنهم كانوا قوما فاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 51, 'والسماء بنيناها بأيد وإنا لموسعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 51, 'والأرض فرشناها فنعم الماهدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 51, 'ومن كل شيء خلقنا زوجين لعلكم تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 51, 'ففروا إلى الله إني لكم منه نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 51, 'ولا تجعلوا مع الله إلها آخر إني لكم منه نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 51, 'كذلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 51, 'أتواصوا به بل هم قوم طاغون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 51, 'فتول عنهم فما أنت بملوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 51, 'وذكر فإن الذكرى تنفع المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 51, 'وما خلقت الجن والإنس إلا ليعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 51, 'ما أريد منهم من رزق وما أريد أن يطعمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 51, 'إن الله هو الرزاق ذو القوة المتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 51, 'فإن للذين ظلموا ذنوبا مثل ذنوب أصحابهم فلا يستعجلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 51, 'فويل للذين كفروا من يومهم الذي يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 52, 'بسم الله الرحمن الرحيم والطور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 52, 'وكتاب مسطور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 52, 'في رق منشور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 52, 'والبيت المعمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 52, 'والسقف المرفوع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 52, 'والبحر المسجور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 52, 'إن عذاب ربك لواقع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 52, 'ما له من دافع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 52, 'يوم تمور السماء مورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 52, 'وتسير الجبال سيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 52, 'فويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 52, 'الذين هم في خوض يلعبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 52, 'يوم يدعون إلى نار جهنم دعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 52, 'هذه النار التي كنتم بها تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 52, 'أفسحر هذا أم أنتم لا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 52, 'اصلوها فاصبروا أو لا تصبروا سواء عليكم إنما تجزون ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 52, 'إن المتقين في جنات ونعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 52, 'فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 52, 'كلوا واشربوا هنيئا بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 52, 'متكئين على سرر مصفوفة وزوجناهم بحور عين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 52, 'والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء كل امرئ بما كسب رهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 52, 'وأمددناهم بفاكهة ولحم مما يشتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 52, 'يتنازعون فيها كأسا لا لغو فيها ولا تأثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 52, 'ويطوف عليهم غلمان لهم كأنهم لؤلؤ مكنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 52, 'وأقبل بعضهم على بعض يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 52, 'قالوا إنا كنا قبل في أهلنا مشفقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 52, 'فمن الله علينا ووقانا عذاب السموم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 52, 'إنا كنا من قبل ندعوه إنه هو البر الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 52, 'فذكر فما أنت بنعمت ربك بكاهن ولا مجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 52, 'أم يقولون شاعر نتربص به ريب المنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 52, 'قل تربصوا فإني معكم من المتربصين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 52, 'أم تأمرهم أحلامهم بهذا أم هم قوم طاغون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 52, 'أم يقولون تقوله بل لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 52, 'فليأتوا بحديث مثله إن كانوا صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 52, 'أم خلقوا من غير شيء أم هم الخالقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 52, 'أم خلقوا السماوات والأرض بل لا يوقنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 52, 'أم عندهم خزائن ربك أم هم المصيطرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 52, 'أم لهم سلم يستمعون فيه فليأت مستمعهم بسلطان مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 52, 'أم له البنات ولكم البنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 52, 'أم تسألهم أجرا فهم من مغرم مثقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 52, 'أم عندهم الغيب فهم يكتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 52, 'أم يريدون كيدا فالذين كفروا هم المكيدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 52, 'أم لهم إله غير الله سبحان الله عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 52, 'وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 52, 'فذرهم حتى يلاقوا يومهم الذي فيه يصعقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 52, 'يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 52, 'وإن للذين ظلموا عذابا دون ذلك ولكن أكثرهم لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 52, 'واصبر لحكم ربك فإنك بأعيننا وسبح بحمد ربك حين تقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 52, 'ومن الليل فسبحه وإدبار النجوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 53, 'بسم الله الرحمن الرحيم والنجم إذا هوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 53, 'ما ضل صاحبكم وما غوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 53, 'وما ينطق عن الهوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 53, 'إن هو إلا وحي يوحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 53, 'علمه شديد القوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 53, 'ذو مرة فاستوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 53, 'وهو بالأفق الأعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 53, 'ثم دنا فتدلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 53, 'فكان قاب قوسين أو أدنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 53, 'فأوحى إلى عبده ما أوحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 53, 'ما كذب الفؤاد ما رأى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 53, 'أفتمارونه على ما يرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 53, 'ولقد رآه نزلة أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 53, 'عند سدرة المنتهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 53, 'عندها جنة المأوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 53, 'إذ يغشى السدرة ما يغشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 53, 'ما زاغ البصر وما طغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 53, 'لقد رأى من آيات ربه الكبرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 53, 'أفرأيتم اللات والعزى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 53, 'ومناة الثالثة الأخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 53, 'ألكم الذكر وله الأنثى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 53, 'تلك إذا قسمة ضيزى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 53, 'إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان إن يتبعون إلا الظن وما تهوى الأنفس ولقد جاءهم من ربهم الهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 53, 'أم للإنسان ما تمنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 53, 'فلله الآخرة والأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 53, 'وكم من ملك في السماوات لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله لمن يشاء ويرضى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 53, 'إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 53, 'وما لهم به من علم إن يتبعون إلا الظن وإن الظن لا يغني من الحق شيئا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 53, 'فأعرض عن من تولى عن ذكرنا ولم يرد إلا الحياة الدنيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 53, 'ذلك مبلغهم من العلم إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 53, 'ولله ما في السماوات وما في الأرض ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 53, 'الذين يجتنبون كبائر الإثم والفواحش إلا اللمم إن ربك واسع المغفرة هو أعلم بكم إذ أنشأكم من الأرض وإذ أنتم أجنة في بطون أمهاتكم فلا تزكوا أنفسكم هو أعلم بمن اتقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 53, 'أفرأيت الذي تولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 53, 'وأعطى قليلا وأكدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 53, 'أعنده علم الغيب فهو يرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 53, 'أم لم ينبأ بما في صحف موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 53, 'وإبراهيم الذي وفى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 53, 'ألا تزر وازرة وزر أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 53, 'وأن ليس للإنسان إلا ما سعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 53, 'وأن سعيه سوف يرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 53, 'ثم يجزاه الجزاء الأوفى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 53, 'وأن إلى ربك المنتهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 53, 'وأنه هو أضحك وأبكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 53, 'وأنه هو أمات وأحيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 53, 'وأنه خلق الزوجين الذكر والأنثى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 53, 'من نطفة إذا تمنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 53, 'وأن عليه النشأة الأخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 53, 'وأنه هو أغنى وأقنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 53, 'وأنه هو رب الشعرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 53, 'وأنه أهلك عادا الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 53, 'وثمود فما أبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 53, 'وقوم نوح من قبل إنهم كانوا هم أظلم وأطغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 53, 'والمؤتفكة أهوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 53, 'فغشاها ما غشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 53, 'فبأي آلاء ربك تتمارى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 53, 'هذا نذير من النذر الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 53, 'أزفت الآزفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 53, 'ليس لها من دون الله كاشفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 53, 'أفمن هذا الحديث تعجبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 53, 'وتضحكون ولا تبكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 53, 'وأنتم سامدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 53, 'فاسجدوا لله واعبدوا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 54, 'بسم الله الرحمن الرحيم اقتربت الساعة وانشق القمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 54, 'وإن يروا آية يعرضوا ويقولوا سحر مستمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 54, 'وكذبوا واتبعوا أهواءهم وكل أمر مستقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 54, 'ولقد جاءهم من الأنباء ما فيه مزدجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 54, 'حكمة بالغة فما تغن النذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 54, 'فتول عنهم يوم يدع الداع إلى شيء نكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 54, 'خشعا أبصارهم يخرجون من الأجداث كأنهم جراد منتشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 54, 'مهطعين إلى الداع يقول الكافرون هذا يوم عسر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 54, 'كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 54, 'فدعا ربه أني مغلوب فانتصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 54, 'ففتحنا أبواب السماء بماء منهمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 54, 'وفجرنا الأرض عيونا فالتقى الماء على أمر قد قدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 54, 'وحملناه على ذات ألواح ودسر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 54, 'تجري بأعيننا جزاء لمن كان كفر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 54, 'ولقد تركناها آية فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 54, 'فكيف كان عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 54, 'ولقد يسرنا القرآن للذكر فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 54, 'كذبت عاد فكيف كان عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 54, 'إنا أرسلنا عليهم ريحا صرصرا في يوم نحس مستمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 54, 'تنزع الناس كأنهم أعجاز نخل منقعر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 54, 'فكيف كان عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 54, 'ولقد يسرنا القرآن للذكر فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 54, 'كذبت ثمود بالنذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 54, 'فقالوا أبشرا منا واحدا نتبعه إنا إذا لفي ضلال وسعر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 54, 'أألقي الذكر عليه من بيننا بل هو كذاب أشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 54, 'سيعلمون غدا من الكذاب الأشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 54, 'إنا مرسلو الناقة فتنة لهم فارتقبهم واصطبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 54, 'ونبئهم أن الماء قسمة بينهم كل شرب محتضر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 54, 'فنادوا صاحبهم فتعاطى فعقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 54, 'فكيف كان عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 54, 'إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 54, 'ولقد يسرنا القرآن للذكر فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 54, 'كذبت قوم لوط بالنذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 54, 'إنا أرسلنا عليهم حاصبا إلا آل لوط نجيناهم بسحر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 54, 'نعمة من عندنا كذلك نجزي من شكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 54, 'ولقد أنذرهم بطشتنا فتماروا بالنذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 54, 'ولقد راودوه عن ضيفه فطمسنا أعينهم فذوقوا عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 54, 'ولقد صبحهم بكرة عذاب مستقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 54, 'فذوقوا عذابي ونذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 54, 'ولقد يسرنا القرآن للذكر فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 54, 'ولقد جاء آل فرعون النذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 54, 'كذبوا بآياتنا كلها فأخذناهم أخذ عزيز مقتدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 54, 'أكفاركم خير من أولئكم أم لكم براءة في الزبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 54, 'أم يقولون نحن جميع منتصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 54, 'سيهزم الجمع ويولون الدبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 54, 'بل الساعة موعدهم والساعة أدهى وأمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 54, 'إن المجرمين في ضلال وسعر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 54, 'يوم يسحبون في النار على وجوههم ذوقوا مس سقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 54, 'إنا كل شيء خلقناه بقدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 54, 'وما أمرنا إلا واحدة كلمح بالبصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 54, 'ولقد أهلكنا أشياعكم فهل من مدكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 54, 'وكل شيء فعلوه في الزبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 54, 'وكل صغير وكبير مستطر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 54, 'إن المتقين في جنات ونهر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 54, 'في مقعد صدق عند مليك مقتدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 55, 'بسم الله الرحمن الرحيم الرحمن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 55, 'علم القرآن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 55, 'خلق الإنسان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 55, 'علمه البيان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 55, 'الشمس والقمر بحسبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 55, 'والنجم والشجر يسجدان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 55, 'والسماء رفعها ووضع الميزان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 55, 'ألا تطغوا في الميزان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 55, 'وأقيموا الوزن بالقسط ولا تخسروا الميزان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 55, 'والأرض وضعها للأنام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 55, 'فيها فاكهة والنخل ذات الأكمام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 55, 'والحب ذو العصف والريحان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 55, 'خلق الإنسان من صلصال كالفخار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 55, 'وخلق الجان من مارج من نار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 55, 'رب المشرقين ورب المغربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 55, 'مرج البحرين يلتقيان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 55, 'بينهما برزخ لا يبغيان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 55, 'يخرج منهما اللؤلؤ والمرجان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 55, 'وله الجوار المنشآت في البحر كالأعلام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 55, 'كل من عليها فان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 55, 'ويبقى وجه ربك ذو الجلال والإكرام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 55, 'يسأله من في السماوات والأرض كل يوم هو في شأن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 55, 'سنفرغ لكم أيه الثقلان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 55, 'يا معشر الجن والإنس إن استطعتم أن تنفذوا من أقطار السماوات والأرض فانفذوا لا تنفذون إلا بسلطان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 55, 'يرسل عليكما شواظ من نار ونحاس فلا تنتصران');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 55, 'فإذا انشقت السماء فكانت وردة كالدهان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 55, 'فيومئذ لا يسأل عن ذنبه إنس ولا جان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 55, 'يعرف المجرمون بسيماهم فيؤخذ بالنواصي والأقدام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 55, 'هذه جهنم التي يكذب بها المجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 55, 'يطوفون بينها وبين حميم آن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 55, 'ولمن خاف مقام ربه جنتان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 55, 'ذواتا أفنان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 55, 'فيهما عينان تجريان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 55, 'فيهما من كل فاكهة زوجان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 55, 'متكئين على فرش بطائنها من إستبرق وجنى الجنتين دان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 55, 'فيهن قاصرات الطرف لم يطمثهن إنس قبلهم ولا جان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 55, 'كأنهن الياقوت والمرجان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 55, 'هل جزاء الإحسان إلا الإحسان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 55, 'ومن دونهما جنتان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 55, 'مدهامتان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 55, 'فيهما عينان نضاختان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 55, 'فيهما فاكهة ونخل ورمان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 55, 'فيهن خيرات حسان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 55, 'حور مقصورات في الخيام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 55, 'لم يطمثهن إنس قبلهم ولا جان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 55, 'متكئين على رفرف خضر وعبقري حسان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 55, 'فبأي آلاء ربكما تكذبان');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 55, 'تبارك اسم ربك ذي الجلال والإكرام');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 56, 'بسم الله الرحمن الرحيم إذا وقعت الواقعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 56, 'ليس لوقعتها كاذبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 56, 'خافضة رافعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 56, 'إذا رجت الأرض رجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 56, 'وبست الجبال بسا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 56, 'فكانت هباء منبثا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 56, 'وكنتم أزواجا ثلاثة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 56, 'فأصحاب الميمنة ما أصحاب الميمنة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 56, 'وأصحاب المشأمة ما أصحاب المشأمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 56, 'والسابقون السابقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 56, 'أولئك المقربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 56, 'في جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 56, 'ثلة من الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 56, 'وقليل من الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 56, 'على سرر موضونة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 56, 'متكئين عليها متقابلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 56, 'يطوف عليهم ولدان مخلدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 56, 'بأكواب وأباريق وكأس من معين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 56, 'لا يصدعون عنها ولا ينزفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 56, 'وفاكهة مما يتخيرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 56, 'ولحم طير مما يشتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 56, 'وحور عين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 56, 'كأمثال اللؤلؤ المكنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 56, 'جزاء بما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 56, 'لا يسمعون فيها لغوا ولا تأثيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 56, 'إلا قيلا سلاما سلاما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 56, 'وأصحاب اليمين ما أصحاب اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 56, 'في سدر مخضود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 56, 'وطلح منضود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 56, 'وظل ممدود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 56, 'وماء مسكوب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 56, 'وفاكهة كثيرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 56, 'لا مقطوعة ولا ممنوعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 56, 'وفرش مرفوعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 56, 'إنا أنشأناهن إنشاء');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 56, 'فجعلناهن أبكارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 56, 'عربا أترابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 56, 'لأصحاب اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 56, 'ثلة من الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 56, 'وثلة من الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 56, 'وأصحاب الشمال ما أصحاب الشمال');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 56, 'في سموم وحميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 56, 'وظل من يحموم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 56, 'لا بارد ولا كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 56, 'إنهم كانوا قبل ذلك مترفين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 56, 'وكانوا يصرون على الحنث العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 56, 'وكانوا يقولون أئذا متنا وكنا ترابا وعظاما أإنا لمبعوثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 56, 'أوآباؤنا الأولون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 56, 'قل إن الأولين والآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 56, 'لمجموعون إلى ميقات يوم معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 56, 'ثم إنكم أيها الضالون المكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 56, 'لآكلون من شجر من زقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 56, 'فمالئون منها البطون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 56, 'فشاربون عليه من الحميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 56, 'فشاربون شرب الهيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 56, 'هذا نزلهم يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(57, 1, 56, 'نحن خلقناكم فلولا تصدقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(58, 1, 56, 'أفرأيتم ما تمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(59, 1, 56, 'أأنتم تخلقونه أم نحن الخالقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(60, 1, 56, 'نحن قدرنا بينكم الموت وما نحن بمسبوقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(61, 1, 56, 'على أن نبدل أمثالكم وننشئكم في ما لا تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(62, 1, 56, 'ولقد علمتم النشأة الأولى فلولا تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(63, 1, 56, 'أفرأيتم ما تحرثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(64, 1, 56, 'أأنتم تزرعونه أم نحن الزارعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(65, 1, 56, 'لو نشاء لجعلناه حطاما فظلتم تفكهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(66, 1, 56, 'إنا لمغرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(67, 1, 56, 'بل نحن محرومون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(68, 1, 56, 'أفرأيتم الماء الذي تشربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(69, 1, 56, 'أأنتم أنزلتموه من المزن أم نحن المنزلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(70, 1, 56, 'لو نشاء جعلناه أجاجا فلولا تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(71, 1, 56, 'أفرأيتم النار التي تورون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(72, 1, 56, 'أأنتم أنشأتم شجرتها أم نحن المنشئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(73, 1, 56, 'نحن جعلناها تذكرة ومتاعا للمقوين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(74, 1, 56, 'فسبح باسم ربك العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(75, 1, 56, 'فلا أقسم بمواقع النجوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(76, 1, 56, 'وإنه لقسم لو تعلمون عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(77, 1, 56, 'إنه لقرآن كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(78, 1, 56, 'في كتاب مكنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(79, 1, 56, 'لا يمسه إلا المطهرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(80, 1, 56, 'تنزيل من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(81, 1, 56, 'أفبهذا الحديث أنتم مدهنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(82, 1, 56, 'وتجعلون رزقكم أنكم تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(83, 1, 56, 'فلولا إذا بلغت الحلقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(84, 1, 56, 'وأنتم حينئذ تنظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(85, 1, 56, 'ونحن أقرب إليه منكم ولكن لا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(86, 1, 56, 'فلولا إن كنتم غير مدينين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(87, 1, 56, 'ترجعونها إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(88, 1, 56, 'فأما إن كان من المقربين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(89, 1, 56, 'فروح وريحان وجنت نعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(90, 1, 56, 'وأما إن كان من أصحاب اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(91, 1, 56, 'فسلام لك من أصحاب اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(92, 1, 56, 'وأما إن كان من المكذبين الضالين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(93, 1, 56, 'فنزل من حميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(94, 1, 56, 'وتصلية جحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(95, 1, 56, 'إن هذا لهو حق اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(96, 1, 56, 'فسبح باسم ربك العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 57, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات والأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 57, 'له ملك السماوات والأرض يحيي ويميت وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 57, 'هو الأول والآخر والظاهر والباطن وهو بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 57, 'هو الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو معكم أين ما كنتم والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 57, 'له ملك السماوات والأرض وإلى الله ترجع الأمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 57, 'يولج الليل في النهار ويولج النهار في الليل وهو عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 57, 'آمنوا بالله ورسوله وأنفقوا مما جعلكم مستخلفين فيه فالذين آمنوا منكم وأنفقوا لهم أجر كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 57, 'وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا بربكم وقد أخذ ميثاقكم إن كنتم مؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 57, 'هو الذي ينزل على عبده آيات بينات ليخرجكم من الظلمات إلى النور وإن الله بكم لرءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 57, 'وما لكم ألا تنفقوا في سبيل الله ولله ميراث السماوات والأرض لا يستوي منكم من أنفق من قبل الفتح وقاتل أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا وكلا وعد الله الحسنى والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 57, 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله أجر كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 57, 'يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ذلك هو الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 57, 'يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 57, 'ينادونهم ألم نكن معكم قالوا بلى ولكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتى جاء أمر الله وغركم بالله الغرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 57, 'فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا مأواكم النار هي مولاكم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 57, 'ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم وكثير منهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 57, 'اعلموا أن الله يحيي الأرض بعد موتها قد بينا لكم الآيات لعلكم تعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 57, 'إن المصدقين والمصدقات وأقرضوا الله قرضا حسنا يضاعف لهم ولهم أجر كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 57, 'والذين آمنوا بالله ورسله أولئك هم الصديقون والشهداء عند ربهم لهم أجرهم ونورهم والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 57, 'اعلموا أنما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الأموال والأولاد كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما وفي الآخرة عذاب شديد ومغفرة من الله ورضوان وما الحياة الدنيا إلا متاع الغرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 57, 'سابقوا إلى مغفرة من ربكم وجنة عرضها كعرض السماء والأرض أعدت للذين آمنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 57, 'ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها إن ذلك على الله يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 57, 'لكيلا تأسوا على ما فاتكم ولا تفرحوا بما آتاكم والله لا يحب كل مختال فخور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 57, 'الذين يبخلون ويأمرون الناس بالبخل ومن يتول فإن الله هو الغني الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 57, 'لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط وأنزلنا الحديد فيه بأس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب إن الله قوي عزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 57, 'ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب فمنهم مهتد وكثير منهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 57, 'ثم قفينا على آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها فآتينا الذين آمنوا منهم أجرهم وكثير منهم فاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 57, 'يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 57, 'لئلا يعلم أهل الكتاب ألا يقدرون على شيء من فضل الله وأن الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 58, 'بسم الله الرحمن الرحيم قد سمع الله قول التي تجادلك في زوجها وتشتكي إلى الله والله يسمع تحاوركما إن الله سميع بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 58, 'الذين يظاهرون منكم من نسائهم ما هن أمهاتهم إن أمهاتهم إلا اللائي ولدنهم وإنهم ليقولون منكرا من القول وزورا وإن الله لعفو غفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 58, 'والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل أن يتماسا ذلكم توعظون به والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 58, 'فمن لم يجد فصيام شهرين متتابعين من قبل أن يتماسا فمن لم يستطع فإطعام ستين مسكينا ذلك لتؤمنوا بالله ورسوله وتلك حدود الله وللكافرين عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 58, 'إن الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم وقد أنزلنا آيات بينات وللكافرين عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 58, 'يوم يبعثهم الله جميعا فينبئهم بما عملوا أحصاه الله ونسوه والله على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 58, 'ألم تر أن الله يعلم ما في السماوات وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم ولا أدنى من ذلك ولا أكثر إلا هو معهم أين ما كانوا ثم ينبئهم بما عملوا يوم القيامة إن الله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 58, 'ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصيت الرسول وإذا جاءوك حيوك بما لم يحيك به الله ويقولون في أنفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 58, 'يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوى واتقوا الله الذي إليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 58, 'إنما النجوى من الشيطان ليحزن الذين آمنوا وليس بضارهم شيئا إلا بإذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 58, 'يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم وإذا قيل انشزوا فانشزوا يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 58, 'يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ذلك خير لكم وأطهر فإن لم تجدوا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 58, 'أأشفقتم أن تقدموا بين يدي نجواكم صدقات فإذ لم تفعلوا وتاب الله عليكم فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله والله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 58, 'ألم تر إلى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 58, 'أعد الله لهم عذابا شديدا إنهم ساء ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 58, 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 58, 'لن تغني عنهم أموالهم ولا أولادهم من الله شيئا أولئك أصحاب النار هم فيها خالدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 58, 'يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ويحسبون أنهم على شيء ألا إنهم هم الكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 58, 'استحوذ عليهم الشيطان فأنساهم ذكر الله أولئك حزب الشيطان ألا إن حزب الشيطان هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 58, 'إن الذين يحادون الله ورسوله أولئك في الأذلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 58, 'كتب الله لأغلبن أنا ورسلي إن الله قوي عزيز');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 58, 'لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم أولئك كتب في قلوبهم الإيمان وأيدهم بروح منه ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها رضي الله عنهم ورضوا عنه أولئك حزب الله ألا إن حزب الله هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 59, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات وما في الأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 59, 'هو الذي أخرج الذين كفروا من أهل الكتاب من ديارهم لأول الحشر ما ظننتم أن يخرجوا وظنوا أنهم مانعتهم حصونهم من الله فأتاهم الله من حيث لم يحتسبوا وقذف في قلوبهم الرعب يخربون بيوتهم بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 59, 'ولولا أن كتب الله عليهم الجلاء لعذبهم في الدنيا ولهم في الآخرة عذاب النار');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 59, 'ذلك بأنهم شاقوا الله ورسوله ومن يشاق الله فإن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 59, 'ما قطعتم من لينة أو تركتموها قائمة على أصولها فبإذن الله وليخزي الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 59, 'وما أفاء الله على رسوله منهم فما أوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 59, 'ما أفاء الله على رسوله من أهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الأغنياء منكم وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا واتقوا الله إن الله شديد العقاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 59, 'للفقراء المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله أولئك هم الصادقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 59, 'والذين تبوءوا الدار والإيمان من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة مما أوتوا ويؤثرون على أنفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 59, 'والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان ولا تجعل في قلوبنا غلا للذين آمنوا ربنا إنك رءوف رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 59, 'ألم تر إلى الذين نافقوا يقولون لإخوانهم الذين كفروا من أهل الكتاب لئن أخرجتم لنخرجن معكم ولا نطيع فيكم أحدا أبدا وإن قوتلتم لننصرنكم والله يشهد إنهم لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 59, 'لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الأدبار ثم لا ينصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 59, 'لأنتم أشد رهبة في صدورهم من الله ذلك بأنهم قوم لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 59, 'لا يقاتلونكم جميعا إلا في قرى محصنة أو من وراء جدر بأسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بأنهم قوم لا يعقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 59, 'كمثل الذين من قبلهم قريبا ذاقوا وبال أمرهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 59, 'كمثل الشيطان إذ قال للإنسان اكفر فلما كفر قال إني بريء منك إني أخاف الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 59, 'فكان عاقبتهما أنهما في النار خالدين فيها وذلك جزاء الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 59, 'يا أيها الذين آمنوا اتقوا الله ولتنظر نفس ما قدمت لغد واتقوا الله إن الله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 59, 'ولا تكونوا كالذين نسوا الله فأنساهم أنفسهم أولئك هم الفاسقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 59, 'لا يستوي أصحاب النار وأصحاب الجنة أصحاب الجنة هم الفائزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 59, 'لو أنزلنا هذا القرآن على جبل لرأيته خاشعا متصدعا من خشية الله وتلك الأمثال نضربها للناس لعلهم يتفكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 59, 'هو الله الذي لا إله إلا هو عالم الغيب والشهادة هو الرحمن الرحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 59, 'هو الله الذي لا إله إلا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر سبحان الله عما يشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 59, 'هو الله الخالق البارئ المصور له الأسماء الحسنى يسبح له ما في السماوات والأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 60, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء تلقون إليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول وإياكم أن تؤمنوا بالله ربكم إن كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي تسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ومن يفعله منكم فقد ضل سواء السبيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 60, 'إن يثقفوكم يكونوا لكم أعداء ويبسطوا إليكم أيديهم وألسنتهم بالسوء وودوا لو تكفرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 60, 'لن تنفعكم أرحامكم ولا أولادكم يوم القيامة يفصل بينكم والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 60, 'قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا برآء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتى تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك وما أملك لك من الله من شيء ربنا عليك توكلنا وإليك أنبنا وإليك المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 60, 'ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا إنك أنت العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 60, 'لقد كان لكم فيهم أسوة حسنة لمن كان يرجو الله واليوم الآخر ومن يتول فإن الله هو الغني الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 60, 'عسى الله أن يجعل بينكم وبين الذين عاديتم منهم مودة والله قدير والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 60, 'لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم وتقسطوا إليهم إن الله يحب المقسطين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 60, 'إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا على إخراجكم أن تولوهم ومن يتولهم فأولئك هم الظالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 60, 'يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات فامتحنوهن الله أعلم بإيمانهن فإن علمتموهن مؤمنات فلا ترجعوهن إلى الكفار لا هن حل لهم ولا هم يحلون لهن وآتوهم ما أنفقوا ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهن ولا تمسكوا بعصم الكوافر واسألوا ما أنفقتم وليسألوا ما أنفقوا ذلكم حكم الله يحكم بينكم والله عليم حكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 60, 'وإن فاتكم شيء من أزواجكم إلى الكفار فعاقبتم فآتوا الذين ذهبت أزواجهم مثل ما أنفقوا واتقوا الله الذي أنتم به مؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 60, 'يا أيها النبي إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن أولادهن ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن ولا يعصينك في معروف فبايعهن واستغفر لهن الله إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 60, 'يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 61, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات وما في الأرض وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 61, 'يا أيها الذين آمنوا لم تقولون ما لا تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 61, 'كبر مقتا عند الله أن تقولوا ما لا تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 61, 'إن الله يحب الذين يقاتلون في سبيله صفا كأنهم بنيان مرصوص');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 61, 'وإذ قال موسى لقومه يا قوم لم تؤذونني وقد تعلمون أني رسول الله إليكم فلما زاغوا أزاغ الله قلوبهم والله لا يهدي القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 61, 'وإذ قال عيسى ابن مريم يا بني إسرائيل إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد فلما جاءهم بالبينات قالوا هذا سحر مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 61, 'ومن أظلم ممن افترى على الله الكذب وهو يدعى إلى الإسلام والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 61, 'يريدون ليطفئوا نور الله بأفواههم والله متم نوره ولو كره الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 61, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 61, 'يا أيها الذين آمنوا هل أدلكم على تجارة تنجيكم من عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 61, 'تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ذلكم خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 61, 'يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن ذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 61, 'وأخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 61, 'يا أيها الذين آمنوا كونوا أنصار الله كما قال عيسى ابن مريم للحواريين من أنصاري إلى الله قال الحواريون نحن أنصار الله فآمنت طائفة من بني إسرائيل وكفرت طائفة فأيدنا الذين آمنوا على عدوهم فأصبحوا ظاهرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 62, 'بسم الله الرحمن الرحيم يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 62, 'هو الذي بعث في الأميين رسولا منهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 62, 'وآخرين منهم لما يلحقوا بهم وهو العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 62, 'ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 62, 'مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل أسفارا بئس مثل القوم الذين كذبوا بآيات الله والله لا يهدي القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 62, 'قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله من دون الناس فتمنوا الموت إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 62, 'ولا يتمنونه أبدا بما قدمت أيديهم والله عليم بالظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 62, 'قل إن الموت الذي تفرون منه فإنه ملاقيكم ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 62, 'يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع ذلكم خير لكم إن كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 62, 'فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 62, 'وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما قل ما عند الله خير من اللهو ومن التجارة والله خير الرازقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 63, 'بسم الله الرحمن الرحيم إذا جاءك المنافقون قالوا نشهد إنك لرسول الله والله يعلم إنك لرسوله والله يشهد إن المنافقين لكاذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 63, 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله إنهم ساء ما كانوا يعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 63, 'ذلك بأنهم آمنوا ثم كفروا فطبع على قلوبهم فهم لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 63, 'وإذا رأيتهم تعجبك أجسامهم وإن يقولوا تسمع لقولهم كأنهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قاتلهم الله أنى يؤفكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 63, 'وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورأيتهم يصدون وهم مستكبرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 63, 'سواء عليهم أستغفرت لهم أم لم تستغفر لهم لن يغفر الله لهم إن الله لا يهدي القوم الفاسقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 63, 'هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا ولله خزائن السماوات والأرض ولكن المنافقين لا يفقهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 63, 'يقولون لئن رجعنا إلى المدينة ليخرجن الأعز منها الأذل ولله العزة ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 63, 'يا أيها الذين آمنوا لا تلهكم أموالكم ولا أولادكم عن ذكر الله ومن يفعل ذلك فأولئك هم الخاسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 63, 'وأنفقوا من ما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا أخرتني إلى أجل قريب فأصدق وأكن من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 63, 'ولن يؤخر الله نفسا إذا جاء أجلها والله خبير بما تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 64, 'بسم الله الرحمن الرحيم يسبح لله ما في السماوات وما في الأرض له الملك وله الحمد وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 64, 'هو الذي خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 64, 'خلق السماوات والأرض بالحق وصوركم فأحسن صوركم وإليه المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 64, 'يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون والله عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 64, 'ألم يأتكم نبأ الذين كفروا من قبل فذاقوا وبال أمرهم ولهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 64, 'ذلك بأنه كانت تأتيهم رسلهم بالبينات فقالوا أبشر يهدوننا فكفروا وتولوا واستغنى الله والله غني حميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 64, 'زعم الذين كفروا أن لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 64, 'فآمنوا بالله ورسوله والنور الذي أنزلنا والله بما تعملون خبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 64, 'يوم يجمعكم ليوم الجمع ذلك يوم التغابن ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 64, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار خالدين فيها وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 64, 'ما أصاب من مصيبة إلا بإذن الله ومن يؤمن بالله يهد قلبه والله بكل شيء عليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 64, 'وأطيعوا الله وأطيعوا الرسول فإن توليتم فإنما على رسولنا البلاغ المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 64, 'الله لا إله إلا هو وعلى الله فليتوكل المؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 64, 'يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذروهم وإن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 64, 'إنما أموالكم وأولادكم فتنة والله عنده أجر عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 64, 'فاتقوا الله ما استطعتم واسمعوا وأطيعوا وأنفقوا خيرا لأنفسكم ومن يوق شح نفسه فأولئك هم المفلحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 64, 'إن تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم والله شكور حليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 64, 'عالم الغيب والشهادة العزيز الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 65, 'بسم الله الرحمن الرحيم يا أيها النبي إذا طلقتم النساء فطلقوهن لعدتهن وأحصوا العدة واتقوا الله ربكم لا تخرجوهن من بيوتهن ولا يخرجن إلا أن يأتين بفاحشة مبينة وتلك حدود الله ومن يتعد حدود الله فقد ظلم نفسه لا تدري لعل الله يحدث بعد ذلك أمرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 65, 'فإذا بلغن أجلهن فأمسكوهن بمعروف أو فارقوهن بمعروف وأشهدوا ذوي عدل منكم وأقيموا الشهادة لله ذلكم يوعظ به من كان يؤمن بالله واليوم الآخر ومن يتق الله يجعل له مخرجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 65, 'ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه إن الله بالغ أمره قد جعل الله لكل شيء قدرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 65, 'واللائي يئسن من المحيض من نسائكم إن ارتبتم فعدتهن ثلاثة أشهر واللائي لم يحضن وأولات الأحمال أجلهن أن يضعن حملهن ومن يتق الله يجعل له من أمره يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 65, 'ذلك أمر الله أنزله إليكم ومن يتق الله يكفر عنه سيئاته ويعظم له أجرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 65, 'أسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن وإن كن أولات حمل فأنفقوا عليهن حتى يضعن حملهن فإن أرضعن لكم فآتوهن أجورهن وأتمروا بينكم بمعروف وإن تعاسرتم فسترضع له أخرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 65, 'لينفق ذو سعة من سعته ومن قدر عليه رزقه فلينفق مما آتاه الله لا يكلف الله نفسا إلا ما آتاها سيجعل الله بعد عسر يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 65, 'وكأين من قرية عتت عن أمر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 65, 'فذاقت وبال أمرها وكان عاقبة أمرها خسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 65, 'أعد الله لهم عذابا شديدا فاتقوا الله يا أولي الألباب الذين آمنوا قد أنزل الله إليكم ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 65, 'رسولا يتلو عليكم آيات الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا قد أحسن الله له رزقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 65, 'الله الذي خلق سبع سماوات ومن الأرض مثلهن يتنزل الأمر بينهن لتعلموا أن الله على كل شيء قدير وأن الله قد أحاط بكل شيء علما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 66, 'بسم الله الرحمن الرحيم يا أيها النبي لم تحرم ما أحل الله لك تبتغي مرضات أزواجك والله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 66, 'قد فرض الله لكم تحلة أيمانكم والله مولاكم وهو العليم الحكيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 66, 'وإذ أسر النبي إلى بعض أزواجه حديثا فلما نبأت به وأظهره الله عليه عرف بعضه وأعرض عن بعض فلما نبأها به قالت من أنبأك هذا قال نبأني العليم الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 66, 'إن تتوبا إلى الله فقد صغت قلوبكما وإن تظاهرا عليه فإن الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 66, 'عسى ربه إن طلقكن أن يبدله أزواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وأبكارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 66, 'يا أيها الذين آمنوا قوا أنفسكم وأهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 66, 'يا أيها الذين كفروا لا تعتذروا اليوم إنما تجزون ما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 66, 'يا أيها الذين آمنوا توبوا إلى الله توبة نصوحا عسى ربكم أن يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الأنهار يوم لا يخزي الله النبي والذين آمنوا معه نورهم يسعى بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا واغفر لنا إنك على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 66, 'يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ومأواهم جهنم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 66, 'ضرب الله مثلا للذين كفروا امرأت نوح وامرأت لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 66, 'وضرب الله مثلا للذين آمنوا امرأت فرعون إذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 66, 'ومريم ابنت عمران التي أحصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 67, 'بسم الله الرحمن الرحيم تبارك الذي بيده الملك وهو على كل شيء قدير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 67, 'الذي خلق الموت والحياة ليبلوكم أيكم أحسن عملا وهو العزيز الغفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 67, 'الذي خلق سبع سماوات طباقا ما ترى في خلق الرحمن من تفاوت فارجع البصر هل ترى من فطور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 67, 'ثم ارجع البصر كرتين ينقلب إليك البصر خاسئا وهو حسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 67, 'ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين وأعتدنا لهم عذاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 67, 'وللذين كفروا بربهم عذاب جهنم وبئس المصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 67, 'إذا ألقوا فيها سمعوا لها شهيقا وهي تفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 67, 'تكاد تميز من الغيظ كلما ألقي فيها فوج سألهم خزنتها ألم يأتكم نذير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 67, 'قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن أنتم إلا في ضلال كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 67, 'وقالوا لو كنا نسمع أو نعقل ما كنا في أصحاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 67, 'فاعترفوا بذنبهم فسحقا لأصحاب السعير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 67, 'إن الذين يخشون ربهم بالغيب لهم مغفرة وأجر كبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 67, 'وأسروا قولكم أو اجهروا به إنه عليم بذات الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 67, 'ألا يعلم من خلق وهو اللطيف الخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 67, 'هو الذي جعل لكم الأرض ذلولا فامشوا في مناكبها وكلوا من رزقه وإليه النشور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 67, 'أأمنتم من في السماء أن يخسف بكم الأرض فإذا هي تمور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 67, 'أم أمنتم من في السماء أن يرسل عليكم حاصبا فستعلمون كيف نذير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 67, 'ولقد كذب الذين من قبلهم فكيف كان نكير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 67, 'أولم يروا إلى الطير فوقهم صافات ويقبضن ما يمسكهن إلا الرحمن إنه بكل شيء بصير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 67, 'أمن هذا الذي هو جند لكم ينصركم من دون الرحمن إن الكافرون إلا في غرور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 67, 'أمن هذا الذي يرزقكم إن أمسك رزقه بل لجوا في عتو ونفور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 67, 'أفمن يمشي مكبا على وجهه أهدى أمن يمشي سويا على صراط مستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 67, 'قل هو الذي أنشأكم وجعل لكم السمع والأبصار والأفئدة قليلا ما تشكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 67, 'قل هو الذي ذرأكم في الأرض وإليه تحشرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 67, 'ويقولون متى هذا الوعد إن كنتم صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 67, 'قل إنما العلم عند الله وإنما أنا نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 67, 'فلما رأوه زلفة سيئت وجوه الذين كفروا وقيل هذا الذي كنتم به تدعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 67, 'قل أرأيتم إن أهلكني الله ومن معي أو رحمنا فمن يجير الكافرين من عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 67, 'قل هو الرحمن آمنا به وعليه توكلنا فستعلمون من هو في ضلال مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 67, 'قل أرأيتم إن أصبح ماؤكم غورا فمن يأتيكم بماء معين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 68, 'بسم الله الرحمن الرحيم ن والقلم وما يسطرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 68, 'ما أنت بنعمة ربك بمجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 68, 'وإن لك لأجرا غير ممنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 68, 'وإنك لعلى خلق عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 68, 'فستبصر ويبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 68, 'بأيكم المفتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 68, 'إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 68, 'فلا تطع المكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 68, 'ودوا لو تدهن فيدهنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 68, 'ولا تطع كل حلاف مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 68, 'هماز مشاء بنميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 68, 'مناع للخير معتد أثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 68, 'عتل بعد ذلك زنيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 68, 'أن كان ذا مال وبنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 68, 'إذا تتلى عليه آياتنا قال أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 68, 'سنسمه على الخرطوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 68, 'إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 68, 'ولا يستثنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 68, 'فطاف عليها طائف من ربك وهم نائمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 68, 'فأصبحت كالصريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 68, 'فتنادوا مصبحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 68, 'أن اغدوا على حرثكم إن كنتم صارمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 68, 'فانطلقوا وهم يتخافتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 68, 'أن لا يدخلنها اليوم عليكم مسكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 68, 'وغدوا على حرد قادرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 68, 'فلما رأوها قالوا إنا لضالون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 68, 'بل نحن محرومون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 68, 'قال أوسطهم ألم أقل لكم لولا تسبحون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 68, 'قالوا سبحان ربنا إنا كنا ظالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 68, 'فأقبل بعضهم على بعض يتلاومون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 68, 'قالوا يا ويلنا إنا كنا طاغين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 68, 'عسى ربنا أن يبدلنا خيرا منها إنا إلى ربنا راغبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 68, 'كذلك العذاب ولعذاب الآخرة أكبر لو كانوا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 68, 'إن للمتقين عند ربهم جنات النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 68, 'أفنجعل المسلمين كالمجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 68, 'ما لكم كيف تحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 68, 'أم لكم كتاب فيه تدرسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 68, 'إن لكم فيه لما تخيرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 68, 'أم لكم أيمان علينا بالغة إلى يوم القيامة إن لكم لما تحكمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 68, 'سلهم أيهم بذلك زعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 68, 'أم لهم شركاء فليأتوا بشركائهم إن كانوا صادقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 68, 'يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 68, 'خاشعة أبصارهم ترهقهم ذلة وقد كانوا يدعون إلى السجود وهم سالمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 68, 'فذرني ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 68, 'وأملي لهم إن كيدي متين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 68, 'أم تسألهم أجرا فهم من مغرم مثقلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 68, 'أم عندهم الغيب فهم يكتبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 68, 'فاصبر لحكم ربك ولا تكن كصاحب الحوت إذ نادى وهو مكظوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 68, 'لولا أن تداركه نعمة من ربه لنبذ بالعراء وهو مذموم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 68, 'فاجتباه ربه فجعله من الصالحين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 68, 'وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر ويقولون إنه لمجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 68, 'وما هو إلا ذكر للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 69, 'بسم الله الرحمن الرحيم الحاقة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 69, 'ما الحاقة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 69, 'وما أدراك ما الحاقة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 69, 'كذبت ثمود وعاد بالقارعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 69, 'فأما ثمود فأهلكوا بالطاغية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 69, 'وأما عاد فأهلكوا بريح صرصر عاتية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 69, 'سخرها عليهم سبع ليال وثمانية أيام حسوما فترى القوم فيها صرعى كأنهم أعجاز نخل خاوية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 69, 'فهل ترى لهم من باقية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 69, 'وجاء فرعون ومن قبله والمؤتفكات بالخاطئة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 69, 'فعصوا رسول ربهم فأخذهم أخذة رابية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 69, 'إنا لما طغى الماء حملناكم في الجارية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 69, 'لنجعلها لكم تذكرة وتعيها أذن واعية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 69, 'فإذا نفخ في الصور نفخة واحدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 69, 'وحملت الأرض والجبال فدكتا دكة واحدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 69, 'فيومئذ وقعت الواقعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 69, 'وانشقت السماء فهي يومئذ واهية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 69, 'والملك على أرجائها ويحمل عرش ربك فوقهم يومئذ ثمانية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 69, 'يومئذ تعرضون لا تخفى منكم خافية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 69, 'فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 69, 'إني ظننت أني ملاق حسابيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 69, 'فهو في عيشة راضية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 69, 'في جنة عالية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 69, 'قطوفها دانية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 69, 'كلوا واشربوا هنيئا بما أسلفتم في الأيام الخالية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 69, 'وأما من أوتي كتابه بشماله فيقول يا ليتني لم أوت كتابيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 69, 'ولم أدر ما حسابيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 69, 'يا ليتها كانت القاضية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 69, 'ما أغنى عني ماليه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 69, 'هلك عني سلطانيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 69, 'خذوه فغلوه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 69, 'ثم الجحيم صلوه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 69, 'ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 69, 'إنه كان لا يؤمن بالله العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 69, 'ولا يحض على طعام المسكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 69, 'فليس له اليوم هاهنا حميم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 69, 'ولا طعام إلا من غسلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 69, 'لا يأكله إلا الخاطئون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 69, 'فلا أقسم بما تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 69, 'وما لا تبصرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 69, 'إنه لقول رسول كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 69, 'وما هو بقول شاعر قليلا ما تؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 69, 'ولا بقول كاهن قليلا ما تذكرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 69, 'تنزيل من رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 69, 'ولو تقول علينا بعض الأقاويل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 69, 'لأخذنا منه باليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 69, 'ثم لقطعنا منه الوتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 69, 'فما منكم من أحد عنه حاجزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 69, 'وإنه لتذكرة للمتقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 69, 'وإنا لنعلم أن منكم مكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 69, 'وإنه لحسرة على الكافرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 69, 'وإنه لحق اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 69, 'فسبح باسم ربك العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 70, 'بسم الله الرحمن الرحيم سأل سائل بعذاب واقع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 70, 'للكافرين ليس له دافع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 70, 'من الله ذي المعارج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 70, 'تعرج الملائكة والروح إليه في يوم كان مقداره خمسين ألف سنة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 70, 'فاصبر صبرا جميلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 70, 'إنهم يرونه بعيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 70, 'ونراه قريبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 70, 'يوم تكون السماء كالمهل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 70, 'وتكون الجبال كالعهن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 70, 'ولا يسأل حميم حميما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 70, 'يبصرونهم يود المجرم لو يفتدي من عذاب يومئذ ببنيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 70, 'وصاحبته وأخيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 70, 'وفصيلته التي تؤويه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 70, 'ومن في الأرض جميعا ثم ينجيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 70, 'كلا إنها لظى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 70, 'نزاعة للشوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 70, 'تدعو من أدبر وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 70, 'وجمع فأوعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 70, 'إن الإنسان خلق هلوعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 70, 'إذا مسه الشر جزوعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 70, 'وإذا مسه الخير منوعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 70, 'إلا المصلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 70, 'الذين هم على صلاتهم دائمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 70, 'والذين في أموالهم حق معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 70, 'للسائل والمحروم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 70, 'والذين يصدقون بيوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 70, 'والذين هم من عذاب ربهم مشفقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 70, 'إن عذاب ربهم غير مأمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 70, 'والذين هم لفروجهم حافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 70, 'إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 70, 'فمن ابتغى وراء ذلك فأولئك هم العادون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 70, 'والذين هم لأماناتهم وعهدهم راعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 70, 'والذين هم بشهاداتهم قائمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 70, 'والذين هم على صلاتهم يحافظون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 70, 'أولئك في جنات مكرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 70, 'فمال الذين كفروا قبلك مهطعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 70, 'عن اليمين وعن الشمال عزين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 70, 'أيطمع كل امرئ منهم أن يدخل جنة نعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 70, 'كلا إنا خلقناهم مما يعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 70, 'فلا أقسم برب المشارق والمغارب إنا لقادرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 70, 'على أن نبدل خيرا منهم وما نحن بمسبوقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 70, 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 70, 'يوم يخرجون من الأجداث سراعا كأنهم إلى نصب يوفضون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 70, 'خاشعة أبصارهم ترهقهم ذلة ذلك اليوم الذي كانوا يوعدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 71, 'بسم الله الرحمن الرحيم إنا أرسلنا نوحا إلى قومه أن أنذر قومك من قبل أن يأتيهم عذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 71, 'قال يا قوم إني لكم نذير مبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 71, 'أن اعبدوا الله واتقوه وأطيعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 71, 'يغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى إن أجل الله إذا جاء لا يؤخر لو كنتم تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 71, 'قال رب إني دعوت قومي ليلا ونهارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 71, 'فلم يزدهم دعائي إلا فرارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 71, 'وإني كلما دعوتهم لتغفر لهم جعلوا أصابعهم في آذانهم واستغشوا ثيابهم وأصروا واستكبروا استكبارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 71, 'ثم إني دعوتهم جهارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 71, 'ثم إني أعلنت لهم وأسررت لهم إسرارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 71, 'فقلت استغفروا ربكم إنه كان غفارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 71, 'يرسل السماء عليكم مدرارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 71, 'ويمددكم بأموال وبنين ويجعل لكم جنات ويجعل لكم أنهارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 71, 'ما لكم لا ترجون لله وقارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 71, 'وقد خلقكم أطوارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 71, 'ألم تروا كيف خلق الله سبع سماوات طباقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 71, 'وجعل القمر فيهن نورا وجعل الشمس سراجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 71, 'والله أنبتكم من الأرض نباتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 71, 'ثم يعيدكم فيها ويخرجكم إخراجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 71, 'والله جعل لكم الأرض بساطا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 71, 'لتسلكوا منها سبلا فجاجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 71, 'قال نوح رب إنهم عصوني واتبعوا من لم يزده ماله وولده إلا خسارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 71, 'ومكروا مكرا كبارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 71, 'وقالوا لا تذرن آلهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 71, 'وقد أضلوا كثيرا ولا تزد الظالمين إلا ضلالا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 71, 'مما خطيئاتهم أغرقوا فأدخلوا نارا فلم يجدوا لهم من دون الله أنصارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 71, 'وقال نوح رب لا تذر على الأرض من الكافرين ديارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 71, 'إنك إن تذرهم يضلوا عبادك ولا يلدوا إلا فاجرا كفارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 71, 'رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين إلا تبارا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 72, 'بسم الله الرحمن الرحيم قل أوحي إلي أنه استمع نفر من الجن فقالوا إنا سمعنا قرآنا عجبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 72, 'يهدي إلى الرشد فآمنا به ولن نشرك بربنا أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 72, 'وأنه تعالى جد ربنا ما اتخذ صاحبة ولا ولدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 72, 'وأنه كان يقول سفيهنا على الله شططا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 72, 'وأنا ظننا أن لن تقول الإنس والجن على الله كذبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 72, 'وأنه كان رجال من الإنس يعوذون برجال من الجن فزادوهم رهقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 72, 'وأنهم ظنوا كما ظننتم أن لن يبعث الله أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 72, 'وأنا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 72, 'وأنا كنا نقعد منها مقاعد للسمع فمن يستمع الآن يجد له شهابا رصدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 72, 'وأنا لا ندري أشر أريد بمن في الأرض أم أراد بهم ربهم رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 72, 'وأنا منا الصالحون ومنا دون ذلك كنا طرائق قددا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 72, 'وأنا ظننا أن لن نعجز الله في الأرض ولن نعجزه هربا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 72, 'وأنا لما سمعنا الهدى آمنا به فمن يؤمن بربه فلا يخاف بخسا ولا رهقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 72, 'وأنا منا المسلمون ومنا القاسطون فمن أسلم فأولئك تحروا رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 72, 'وأما القاسطون فكانوا لجهنم حطبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 72, 'وألو استقاموا على الطريقة لأسقيناهم ماء غدقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 72, 'لنفتنهم فيه ومن يعرض عن ذكر ربه يسلكه عذابا صعدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 72, 'وأن المساجد لله فلا تدعوا مع الله أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 72, 'وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 72, 'قل إنما أدعو ربي ولا أشرك به أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 72, 'قل إني لا أملك لكم ضرا ولا رشدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 72, 'قل إني لن يجيرني من الله أحد ولن أجد من دونه ملتحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 72, 'إلا بلاغا من الله ورسالاته ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 72, 'حتى إذا رأوا ما يوعدون فسيعلمون من أضعف ناصرا وأقل عددا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 72, 'قل إن أدري أقريب ما توعدون أم يجعل له ربي أمدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 72, 'عالم الغيب فلا يظهر على غيبه أحدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 72, 'إلا من ارتضى من رسول فإنه يسلك من بين يديه ومن خلفه رصدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 72, 'ليعلم أن قد أبلغوا رسالات ربهم وأحاط بما لديهم وأحصى كل شيء عددا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 73, 'بسم الله الرحمن الرحيم يا أيها المزمل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 73, 'قم الليل إلا قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 73, 'نصفه أو انقص منه قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 73, 'أو زد عليه ورتل القرآن ترتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 73, 'إنا سنلقي عليك قولا ثقيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 73, 'إن ناشئة الليل هي أشد وطئا وأقوم قيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 73, 'إن لك في النهار سبحا طويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 73, 'واذكر اسم ربك وتبتل إليه تبتيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 73, 'رب المشرق والمغرب لا إله إلا هو فاتخذه وكيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 73, 'واصبر على ما يقولون واهجرهم هجرا جميلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 73, 'وذرني والمكذبين أولي النعمة ومهلهم قليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 73, 'إن لدينا أنكالا وجحيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 73, 'وطعاما ذا غصة وعذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 73, 'يوم ترجف الأرض والجبال وكانت الجبال كثيبا مهيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 73, 'إنا أرسلنا إليكم رسولا شاهدا عليكم كما أرسلنا إلى فرعون رسولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 73, 'فعصى فرعون الرسول فأخذناه أخذا وبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 73, 'فكيف تتقون إن كفرتم يوما يجعل الولدان شيبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 73, 'السماء منفطر به كان وعده مفعولا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 73, 'إن هذه تذكرة فمن شاء اتخذ إلى ربه سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 73, 'إن ربك يعلم أنك تقوم أدنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك والله يقدر الليل والنهار علم أن لن تحصوه فتاب عليكم فاقرءوا ما تيسر من القرآن علم أن سيكون منكم مرضى وآخرون يضربون في الأرض يبتغون من فضل الله وآخرون يقاتلون في سبيل الله فاقرءوا ما تيسر منه وأقيموا الصلاة وآتوا الزكاة وأقرضوا الله قرضا حسنا وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا وأعظم أجرا واستغفروا الله إن الله غفور رحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 74, 'بسم الله الرحمن الرحيم يا أيها المدثر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 74, 'قم فأنذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 74, 'وربك فكبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 74, 'وثيابك فطهر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 74, 'والرجز فاهجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 74, 'ولا تمنن تستكثر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 74, 'ولربك فاصبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 74, 'فإذا نقر في الناقور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 74, 'فذلك يومئذ يوم عسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 74, 'على الكافرين غير يسير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 74, 'ذرني ومن خلقت وحيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 74, 'وجعلت له مالا ممدودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 74, 'وبنين شهودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 74, 'ومهدت له تمهيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 74, 'ثم يطمع أن أزيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 74, 'كلا إنه كان لآياتنا عنيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 74, 'سأرهقه صعودا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 74, 'إنه فكر وقدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 74, 'فقتل كيف قدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 74, 'ثم قتل كيف قدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 74, 'ثم نظر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 74, 'ثم عبس وبسر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 74, 'ثم أدبر واستكبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 74, 'فقال إن هذا إلا سحر يؤثر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 74, 'إن هذا إلا قول البشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 74, 'سأصليه سقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 74, 'وما أدراك ما سقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 74, 'لا تبقي ولا تذر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 74, 'لواحة للبشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 74, 'عليها تسعة عشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 74, 'وما جعلنا أصحاب النار إلا ملائكة وما جعلنا عدتهم إلا فتنة للذين كفروا ليستيقن الذين أوتوا الكتاب ويزداد الذين آمنوا إيمانا ولا يرتاب الذين أوتوا الكتاب والمؤمنون وليقول الذين في قلوبهم مرض والكافرون ماذا أراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك إلا هو وما هي إلا ذكرى للبشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 74, 'كلا والقمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 74, 'والليل إذ أدبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 74, 'والصبح إذا أسفر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 74, 'إنها لإحدى الكبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 74, 'نذيرا للبشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 74, 'لمن شاء منكم أن يتقدم أو يتأخر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 74, 'كل نفس بما كسبت رهينة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 74, 'إلا أصحاب اليمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 74, 'في جنات يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 74, 'عن المجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 74, 'ما سلككم في سقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 74, 'قالوا لم نك من المصلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 74, 'ولم نك نطعم المسكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 74, 'وكنا نخوض مع الخائضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 74, 'وكنا نكذب بيوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 74, 'حتى أتانا اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 74, 'فما تنفعهم شفاعة الشافعين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 74, 'فما لهم عن التذكرة معرضين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 74, 'كأنهم حمر مستنفرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(51, 1, 74, 'فرت من قسورة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(52, 1, 74, 'بل يريد كل امرئ منهم أن يؤتى صحفا منشرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(53, 1, 74, 'كلا بل لا يخافون الآخرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(54, 1, 74, 'كلا إنه تذكرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(55, 1, 74, 'فمن شاء ذكره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(56, 1, 74, 'وما يذكرون إلا أن يشاء الله هو أهل التقوى وأهل المغفرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 75, 'بسم الله الرحمن الرحيم لا أقسم بيوم القيامة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 75, 'ولا أقسم بالنفس اللوامة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 75, 'أيحسب الإنسان ألن نجمع عظامه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 75, 'بلى قادرين على أن نسوي بنانه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 75, 'بل يريد الإنسان ليفجر أمامه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 75, 'يسأل أيان يوم القيامة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 75, 'فإذا برق البصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 75, 'وخسف القمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 75, 'وجمع الشمس والقمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 75, 'يقول الإنسان يومئذ أين المفر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 75, 'كلا لا وزر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 75, 'إلى ربك يومئذ المستقر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 75, 'ينبأ الإنسان يومئذ بما قدم وأخر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 75, 'بل الإنسان على نفسه بصيرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 75, 'ولو ألقى معاذيره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 75, 'لا تحرك به لسانك لتعجل به');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 75, 'إن علينا جمعه وقرآنه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 75, 'فإذا قرأناه فاتبع قرآنه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 75, 'ثم إن علينا بيانه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 75, 'كلا بل تحبون العاجلة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 75, 'وتذرون الآخرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 75, 'وجوه يومئذ ناضرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 75, 'إلى ربها ناظرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 75, 'ووجوه يومئذ باسرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 75, 'تظن أن يفعل بها فاقرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 75, 'كلا إذا بلغت التراقي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 75, 'وقيل من راق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 75, 'وظن أنه الفراق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 75, 'والتفت الساق بالساق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 75, 'إلى ربك يومئذ المساق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 75, 'فلا صدق ولا صلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 75, 'ولكن كذب وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 75, 'ثم ذهب إلى أهله يتمطى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 75, 'أولى لك فأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 75, 'ثم أولى لك فأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 75, 'أيحسب الإنسان أن يترك سدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 75, 'ألم يك نطفة من مني يمنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 75, 'ثم كان علقة فخلق فسوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 75, 'فجعل منه الزوجين الذكر والأنثى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 75, 'أليس ذلك بقادر على أن يحيي الموتى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 76, 'بسم الله الرحمن الرحيم هل أتى على الإنسان حين من الدهر لم يكن شيئا مذكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 76, 'إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 76, 'إنا هديناه السبيل إما شاكرا وإما كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 76, 'إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 76, 'إن الأبرار يشربون من كأس كان مزاجها كافورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 76, 'عينا يشرب بها عباد الله يفجرونها تفجيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 76, 'يوفون بالنذر ويخافون يوما كان شره مستطيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 76, 'ويطعمون الطعام على حبه مسكينا ويتيما وأسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 76, 'إنما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 76, 'إنا نخاف من ربنا يوما عبوسا قمطريرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 76, 'فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 76, 'وجزاهم بما صبروا جنة وحريرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 76, 'متكئين فيها على الأرائك لا يرون فيها شمسا ولا زمهريرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 76, 'ودانية عليهم ظلالها وذللت قطوفها تذليلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 76, 'ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 76, 'قوارير من فضة قدروها تقديرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 76, 'ويسقون فيها كأسا كان مزاجها زنجبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 76, 'عينا فيها تسمى سلسبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 76, 'ويطوف عليهم ولدان مخلدون إذا رأيتهم حسبتهم لؤلؤا منثورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 76, 'وإذا رأيت ثم رأيت نعيما وملكا كبيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 76, 'عاليهم ثياب سندس خضر وإستبرق وحلوا أساور من فضة وسقاهم ربهم شرابا طهورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 76, 'إن هذا كان لكم جزاء وكان سعيكم مشكورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 76, 'إنا نحن نزلنا عليك القرآن تنزيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 76, 'فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 76, 'واذكر اسم ربك بكرة وأصيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 76, 'ومن الليل فاسجد له وسبحه ليلا طويلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 76, 'إن هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 76, 'نحن خلقناهم وشددنا أسرهم وإذا شئنا بدلنا أمثالهم تبديلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 76, 'إن هذه تذكرة فمن شاء اتخذ إلى ربه سبيلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 76, 'وما تشاءون إلا أن يشاء الله إن الله كان عليما حكيما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 76, 'يدخل من يشاء في رحمته والظالمين أعد لهم عذابا أليما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 77, 'بسم الله الرحمن الرحيم والمرسلات عرفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 77, 'فالعاصفات عصفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 77, 'والناشرات نشرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 77, 'فالفارقات فرقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 77, 'فالملقيات ذكرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 77, 'عذرا أو نذرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 77, 'إنما توعدون لواقع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 77, 'فإذا النجوم طمست');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 77, 'وإذا السماء فرجت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 77, 'وإذا الجبال نسفت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 77, 'وإذا الرسل أقتت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 77, 'لأي يوم أجلت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 77, 'ليوم الفصل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 77, 'وما أدراك ما يوم الفصل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 77, 'ألم نهلك الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 77, 'ثم نتبعهم الآخرين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 77, 'كذلك نفعل بالمجرمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 77, 'ألم نخلقكم من ماء مهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 77, 'فجعلناه في قرار مكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 77, 'إلى قدر معلوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 77, 'فقدرنا فنعم القادرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 77, 'ألم نجعل الأرض كفاتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 77, 'أحياء وأمواتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 77, 'وجعلنا فيها رواسي شامخات وأسقيناكم ماء فراتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 77, 'انطلقوا إلى ما كنتم به تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 77, 'انطلقوا إلى ظل ذي ثلاث شعب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 77, 'لا ظليل ولا يغني من اللهب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 77, 'إنها ترمي بشرر كالقصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 77, 'كأنه جمالت صفر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 77, 'هذا يوم لا ينطقون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 77, 'ولا يؤذن لهم فيعتذرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 77, 'هذا يوم الفصل جمعناكم والأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 77, 'فإن كان لكم كيد فكيدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 77, 'إن المتقين في ظلال وعيون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 77, 'وفواكه مما يشتهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 77, 'كلوا واشربوا هنيئا بما كنتم تعملون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 77, 'إنا كذلك نجزي المحسنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 77, 'كلوا وتمتعوا قليلا إنكم مجرمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(47, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(48, 1, 77, 'وإذا قيل لهم اركعوا لا يركعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(49, 1, 77, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(50, 1, 77, 'فبأي حديث بعده يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 78, 'بسم الله الرحمن الرحيم عم يتساءلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 78, 'عن النبإ العظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 78, 'الذي هم فيه مختلفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 78, 'كلا سيعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 78, 'ثم كلا سيعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 78, 'ألم نجعل الأرض مهادا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 78, 'والجبال أوتادا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 78, 'وخلقناكم أزواجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 78, 'وجعلنا نومكم سباتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 78, 'وجعلنا الليل لباسا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 78, 'وجعلنا النهار معاشا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 78, 'وبنينا فوقكم سبعا شدادا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 78, 'وجعلنا سراجا وهاجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 78, 'وأنزلنا من المعصرات ماء ثجاجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 78, 'لنخرج به حبا ونباتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 78, 'وجنات ألفافا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 78, 'إن يوم الفصل كان ميقاتا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 78, 'يوم ينفخ في الصور فتأتون أفواجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 78, 'وفتحت السماء فكانت أبوابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 78, 'وسيرت الجبال فكانت سرابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 78, 'إن جهنم كانت مرصادا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 78, 'للطاغين مآبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 78, 'لابثين فيها أحقابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 78, 'لا يذوقون فيها بردا ولا شرابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 78, 'إلا حميما وغساقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 78, 'جزاء وفاقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 78, 'إنهم كانوا لا يرجون حسابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 78, 'وكذبوا بآياتنا كذابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 78, 'وكل شيء أحصيناه كتابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 78, 'فذوقوا فلن نزيدكم إلا عذابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 78, 'إن للمتقين مفازا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 78, 'حدائق وأعنابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 78, 'وكواعب أترابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 78, 'وكأسا دهاقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 78, 'لا يسمعون فيها لغوا ولا كذابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 78, 'جزاء من ربك عطاء حسابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 78, 'رب السماوات والأرض وما بينهما الرحمن لا يملكون منه خطابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 78, 'يوم يقوم الروح والملائكة صفا لا يتكلمون إلا من أذن له الرحمن وقال صوابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 78, 'ذلك اليوم الحق فمن شاء اتخذ إلى ربه مآبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 78, 'إنا أنذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يا ليتني كنت ترابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 79, 'بسم الله الرحمن الرحيم والنازعات غرقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 79, 'والناشطات نشطا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 79, 'والسابحات سبحا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 79, 'فالسابقات سبقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 79, 'فالمدبرات أمرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 79, 'يوم ترجف الراجفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 79, 'تتبعها الرادفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 79, 'قلوب يومئذ واجفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 79, 'أبصارها خاشعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 79, 'يقولون أإنا لمردودون في الحافرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 79, 'أإذا كنا عظاما نخرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 79, 'قالوا تلك إذا كرة خاسرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 79, 'فإنما هي زجرة واحدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 79, 'فإذا هم بالساهرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 79, 'هل أتاك حديث موسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 79, 'إذ ناداه ربه بالواد المقدس طوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 79, 'اذهب إلى فرعون إنه طغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 79, 'فقل هل لك إلى أن تزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 79, 'وأهديك إلى ربك فتخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 79, 'فأراه الآية الكبرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 79, 'فكذب وعصى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 79, 'ثم أدبر يسعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 79, 'فحشر فنادى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 79, 'فقال أنا ربكم الأعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 79, 'فأخذه الله نكال الآخرة والأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 79, 'إن في ذلك لعبرة لمن يخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 79, 'أأنتم أشد خلقا أم السماء بناها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 79, 'رفع سمكها فسواها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 79, 'وأغطش ليلها وأخرج ضحاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 79, 'والأرض بعد ذلك دحاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 79, 'أخرج منها ماءها ومرعاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 79, 'والجبال أرساها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 79, 'متاعا لكم ولأنعامكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 79, 'فإذا جاءت الطامة الكبرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 79, 'يوم يتذكر الإنسان ما سعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 79, 'وبرزت الجحيم لمن يرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 79, 'فأما من طغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 79, 'وآثر الحياة الدنيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 79, 'فإن الجحيم هي المأوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 79, 'وأما من خاف مقام ربه ونهى النفس عن الهوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 79, 'فإن الجنة هي المأوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 79, 'يسألونك عن الساعة أيان مرساها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(43, 1, 79, 'فيم أنت من ذكراها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(44, 1, 79, 'إلى ربك منتهاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(45, 1, 79, 'إنما أنت منذر من يخشاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(46, 1, 79, 'كأنهم يوم يرونها لم يلبثوا إلا عشية أو ضحاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 80, 'بسم الله الرحمن الرحيم عبس وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 80, 'أن جاءه الأعمى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 80, 'وما يدريك لعله يزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 80, 'أو يذكر فتنفعه الذكرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 80, 'أما من استغنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 80, 'فأنت له تصدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 80, 'وما عليك ألا يزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 80, 'وأما من جاءك يسعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 80, 'وهو يخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 80, 'فأنت عنه تلهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 80, 'كلا إنها تذكرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 80, 'فمن شاء ذكره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 80, 'في صحف مكرمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 80, 'مرفوعة مطهرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 80, 'بأيدي سفرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 80, 'كرام بررة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 80, 'قتل الإنسان ما أكفره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 80, 'من أي شيء خلقه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 80, 'من نطفة خلقه فقدره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 80, 'ثم السبيل يسره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 80, 'ثم أماته فأقبره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 80, 'ثم إذا شاء أنشره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 80, 'كلا لما يقض ما أمره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 80, 'فلينظر الإنسان إلى طعامه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 80, 'أنا صببنا الماء صبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 80, 'ثم شققنا الأرض شقا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 80, 'فأنبتنا فيها حبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 80, 'وعنبا وقضبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 80, 'وزيتونا ونخلا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 80, 'وحدائق غلبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 80, 'وفاكهة وأبا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 80, 'متاعا لكم ولأنعامكم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 80, 'فإذا جاءت الصاخة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 80, 'يوم يفر المرء من أخيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 80, 'وأمه وأبيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 80, 'وصاحبته وبنيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(37, 1, 80, 'لكل امرئ منهم يومئذ شأن يغنيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(38, 1, 80, 'وجوه يومئذ مسفرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(39, 1, 80, 'ضاحكة مستبشرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(40, 1, 80, 'ووجوه يومئذ عليها غبرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(41, 1, 80, 'ترهقها قترة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(42, 1, 80, 'أولئك هم الكفرة الفجرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 81, 'بسم الله الرحمن الرحيم إذا الشمس كورت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 81, 'وإذا النجوم انكدرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 81, 'وإذا الجبال سيرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 81, 'وإذا العشار عطلت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 81, 'وإذا الوحوش حشرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 81, 'وإذا البحار سجرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 81, 'وإذا النفوس زوجت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 81, 'وإذا الموءودة سئلت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 81, 'بأي ذنب قتلت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 81, 'وإذا الصحف نشرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 81, 'وإذا السماء كشطت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 81, 'وإذا الجحيم سعرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 81, 'وإذا الجنة أزلفت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 81, 'علمت نفس ما أحضرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 81, 'فلا أقسم بالخنس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 81, 'الجوار الكنس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 81, 'والليل إذا عسعس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 81, 'والصبح إذا تنفس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 81, 'إنه لقول رسول كريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 81, 'ذي قوة عند ذي العرش مكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 81, 'مطاع ثم أمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 81, 'وما صاحبكم بمجنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 81, 'ولقد رآه بالأفق المبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 81, 'وما هو على الغيب بضنين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 81, 'وما هو بقول شيطان رجيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 81, 'فأين تذهبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 81, 'إن هو إلا ذكر للعالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 81, 'لمن شاء منكم أن يستقيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 81, 'وما تشاءون إلا أن يشاء الله رب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 82, 'بسم الله الرحمن الرحيم إذا السماء انفطرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 82, 'وإذا الكواكب انتثرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 82, 'وإذا البحار فجرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 82, 'وإذا القبور بعثرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 82, 'علمت نفس ما قدمت وأخرت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 82, 'يا أيها الإنسان ما غرك بربك الكريم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 82, 'الذي خلقك فسواك فعدلك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 82, 'في أي صورة ما شاء ركبك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 82, 'كلا بل تكذبون بالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 82, 'وإن عليكم لحافظين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 82, 'كراما كاتبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 82, 'يعلمون ما تفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 82, 'إن الأبرار لفي نعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 82, 'وإن الفجار لفي جحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 82, 'يصلونها يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 82, 'وما هم عنها بغائبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 82, 'وما أدراك ما يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 82, 'ثم ما أدراك ما يوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 82, 'يوم لا تملك نفس لنفس شيئا والأمر يومئذ لله');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 83, 'بسم الله الرحمن الرحيم ويل للمطففين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 83, 'الذين إذا اكتالوا على الناس يستوفون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 83, 'وإذا كالوهم أو وزنوهم يخسرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 83, 'ألا يظن أولئك أنهم مبعوثون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 83, 'ليوم عظيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 83, 'يوم يقوم الناس لرب العالمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 83, 'كلا إن كتاب الفجار لفي سجين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 83, 'وما أدراك ما سجين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 83, 'كتاب مرقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 83, 'ويل يومئذ للمكذبين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 83, 'الذين يكذبون بيوم الدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 83, 'وما يكذب به إلا كل معتد أثيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 83, 'إذا تتلى عليه آياتنا قال أساطير الأولين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 83, 'كلا بل ران على قلوبهم ما كانوا يكسبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 83, 'كلا إنهم عن ربهم يومئذ لمحجوبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 83, 'ثم إنهم لصالو الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 83, 'ثم يقال هذا الذي كنتم به تكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 83, 'كلا إن كتاب الأبرار لفي عليين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 83, 'وما أدراك ما عليون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 83, 'كتاب مرقوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 83, 'يشهده المقربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 83, 'إن الأبرار لفي نعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 83, 'على الأرائك ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 83, 'تعرف في وجوههم نضرة النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 83, 'يسقون من رحيق مختوم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 83, 'ختامه مسك وفي ذلك فليتنافس المتنافسون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 83, 'ومزاجه من تسنيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 83, 'عينا يشرب بها المقربون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 83, 'إن الذين أجرموا كانوا من الذين آمنوا يضحكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 83, 'وإذا مروا بهم يتغامزون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(31, 1, 83, 'وإذا انقلبوا إلى أهلهم انقلبوا فكهين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(32, 1, 83, 'وإذا رأوهم قالوا إن هؤلاء لضالون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(33, 1, 83, 'وما أرسلوا عليهم حافظين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(34, 1, 83, 'فاليوم الذين آمنوا من الكفار يضحكون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(35, 1, 83, 'على الأرائك ينظرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(36, 1, 83, 'هل ثوب الكفار ما كانوا يفعلون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 84, 'بسم الله الرحمن الرحيم إذا السماء انشقت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 84, 'وأذنت لربها وحقت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 84, 'وإذا الأرض مدت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 84, 'وألقت ما فيها وتخلت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 84, 'وأذنت لربها وحقت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 84, 'يا أيها الإنسان إنك كادح إلى ربك كدحا فملاقيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 84, 'فأما من أوتي كتابه بيمينه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 84, 'فسوف يحاسب حسابا يسيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 84, 'وينقلب إلى أهله مسرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 84, 'وأما من أوتي كتابه وراء ظهره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 84, 'فسوف يدعو ثبورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 84, 'ويصلى سعيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 84, 'إنه كان في أهله مسرورا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 84, 'إنه ظن أن لن يحور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 84, 'بلى إن ربه كان به بصيرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 84, 'فلا أقسم بالشفق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 84, 'والليل وما وسق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 84, 'والقمر إذا اتسق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 84, 'لتركبن طبقا عن طبق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 84, 'فما لهم لا يؤمنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 84, 'وإذا قرئ عليهم القرآن لا يسجدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 84, 'بل الذين كفروا يكذبون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 84, 'والله أعلم بما يوعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 84, 'فبشرهم بعذاب أليم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 84, 'إلا الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 85, 'بسم الله الرحمن الرحيم والسماء ذات البروج');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 85, 'واليوم الموعود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 85, 'وشاهد ومشهود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 85, 'قتل أصحاب الأخدود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 85, 'النار ذات الوقود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 85, 'إذ هم عليها قعود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 85, 'وهم على ما يفعلون بالمؤمنين شهود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 85, 'وما نقموا منهم إلا أن يؤمنوا بالله العزيز الحميد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 85, 'الذي له ملك السماوات والأرض والله على كل شيء شهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 85, 'إن الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 85, 'إن الذين آمنوا وعملوا الصالحات لهم جنات تجري من تحتها الأنهار ذلك الفوز الكبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 85, 'إن بطش ربك لشديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 85, 'إنه هو يبدئ ويعيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 85, 'وهو الغفور الودود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 85, 'ذو العرش المجيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 85, 'فعال لما يريد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 85, 'هل أتاك حديث الجنود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 85, 'فرعون وثمود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 85, 'بل الذين كفروا في تكذيب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 85, 'والله من ورائهم محيط');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 85, 'بل هو قرآن مجيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 85, 'في لوح محفوظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 86, 'بسم الله الرحمن الرحيم والسماء والطارق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 86, 'وما أدراك ما الطارق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 86, 'النجم الثاقب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 86, 'إن كل نفس لما عليها حافظ');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 86, 'فلينظر الإنسان مم خلق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 86, 'خلق من ماء دافق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 86, 'يخرج من بين الصلب والترائب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 86, 'إنه على رجعه لقادر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 86, 'يوم تبلى السرائر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 86, 'فما له من قوة ولا ناصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 86, 'والسماء ذات الرجع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 86, 'والأرض ذات الصدع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 86, 'إنه لقول فصل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 86, 'وما هو بالهزل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 86, 'إنهم يكيدون كيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 86, 'وأكيد كيدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 86, 'فمهل الكافرين أمهلهم رويدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 87, 'بسم الله الرحمن الرحيم سبح اسم ربك الأعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 87, 'الذي خلق فسوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 87, 'والذي قدر فهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 87, 'والذي أخرج المرعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 87, 'فجعله غثاء أحوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 87, 'سنقرئك فلا تنسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 87, 'إلا ما شاء الله إنه يعلم الجهر وما يخفى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 87, 'ونيسرك لليسرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 87, 'فذكر إن نفعت الذكرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 87, 'سيذكر من يخشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 87, 'ويتجنبها الأشقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 87, 'الذي يصلى النار الكبرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 87, 'ثم لا يموت فيها ولا يحيى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 87, 'قد أفلح من تزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 87, 'وذكر اسم ربه فصلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 87, 'بل تؤثرون الحياة الدنيا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 87, 'والآخرة خير وأبقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 87, 'إن هذا لفي الصحف الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 87, 'صحف إبراهيم وموسى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 88, 'بسم الله الرحمن الرحيم هل أتاك حديث الغاشية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 88, 'وجوه يومئذ خاشعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 88, 'عاملة ناصبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 88, 'تصلى نارا حامية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 88, 'تسقى من عين آنية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 88, 'ليس لهم طعام إلا من ضريع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 88, 'لا يسمن ولا يغني من جوع');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 88, 'وجوه يومئذ ناعمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 88, 'لسعيها راضية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 88, 'في جنة عالية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 88, 'لا تسمع فيها لاغية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 88, 'فيها عين جارية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 88, 'فيها سرر مرفوعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 88, 'وأكواب موضوعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 88, 'ونمارق مصفوفة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 88, 'وزرابي مبثوثة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 88, 'أفلا ينظرون إلى الإبل كيف خلقت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 88, 'وإلى السماء كيف رفعت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 88, 'وإلى الجبال كيف نصبت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 88, 'وإلى الأرض كيف سطحت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 88, 'فذكر إنما أنت مذكر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 88, 'لست عليهم بمصيطر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 88, 'إلا من تولى وكفر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 88, 'فيعذبه الله العذاب الأكبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 88, 'إن إلينا إيابهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 88, 'ثم إن علينا حسابهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 89, 'بسم الله الرحمن الرحيم والفجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 89, 'وليال عشر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 89, 'والشفع والوتر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 89, 'والليل إذا يسر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 89, 'هل في ذلك قسم لذي حجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 89, 'ألم تر كيف فعل ربك بعاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 89, 'إرم ذات العماد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 89, 'التي لم يخلق مثلها في البلاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 89, 'وثمود الذين جابوا الصخر بالواد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 89, 'وفرعون ذي الأوتاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 89, 'الذين طغوا في البلاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 89, 'فأكثروا فيها الفساد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 89, 'فصب عليهم ربك سوط عذاب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 89, 'إن ربك لبالمرصاد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 89, 'فأما الإنسان إذا ما ابتلاه ربه فأكرمه ونعمه فيقول ربي أكرمن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 89, 'وأما إذا ما ابتلاه فقدر عليه رزقه فيقول ربي أهانن');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 89, 'كلا بل لا تكرمون اليتيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 89, 'ولا تحاضون على طعام المسكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 89, 'وتأكلون التراث أكلا لما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 89, 'وتحبون المال حبا جما');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 89, 'كلا إذا دكت الأرض دكا دكا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(22, 1, 89, 'وجاء ربك والملك صفا صفا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(23, 1, 89, 'وجيء يومئذ بجهنم يومئذ يتذكر الإنسان وأنى له الذكرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(24, 1, 89, 'يقول يا ليتني قدمت لحياتي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(25, 1, 89, 'فيومئذ لا يعذب عذابه أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(26, 1, 89, 'ولا يوثق وثاقه أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(27, 1, 89, 'يا أيتها النفس المطمئنة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(28, 1, 89, 'ارجعي إلى ربك راضية مرضية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(29, 1, 89, 'فادخلي في عبادي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(30, 1, 89, 'وادخلي جنتي');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 90, 'بسم الله الرحمن الرحيم لا أقسم بهذا البلد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 90, 'وأنت حل بهذا البلد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 90, 'ووالد وما ولد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 90, 'لقد خلقنا الإنسان في كبد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 90, 'أيحسب أن لن يقدر عليه أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 90, 'يقول أهلكت مالا لبدا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 90, 'أيحسب أن لم يره أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 90, 'ألم نجعل له عينين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 90, 'ولسانا وشفتين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 90, 'وهديناه النجدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 90, 'فلا اقتحم العقبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 90, 'وما أدراك ما العقبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 90, 'فك رقبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 90, 'أو إطعام في يوم ذي مسغبة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 90, 'يتيما ذا مقربة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 90, 'أو مسكينا ذا متربة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 90, 'ثم كان من الذين آمنوا وتواصوا بالصبر وتواصوا بالمرحمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 90, 'أولئك أصحاب الميمنة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 90, 'والذين كفروا بآياتنا هم أصحاب المشأمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 90, 'عليهم نار مؤصدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 91, 'بسم الله الرحمن الرحيم والشمس وضحاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 91, 'والقمر إذا تلاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 91, 'والنهار إذا جلاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 91, 'والليل إذا يغشاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 91, 'والسماء وما بناها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 91, 'والأرض وما طحاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 91, 'ونفس وما سواها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 91, 'فألهمها فجورها وتقواها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 91, 'قد أفلح من زكاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 91, 'وقد خاب من دساها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 91, 'كذبت ثمود بطغواها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 91, 'إذ انبعث أشقاها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 91, 'فقال لهم رسول الله ناقة الله وسقياها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 91, 'فكذبوه فعقروها فدمدم عليهم ربهم بذنبهم فسواها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 91, 'ولا يخاف عقباها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 92, 'بسم الله الرحمن الرحيم والليل إذا يغشى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 92, 'والنهار إذا تجلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 92, 'وما خلق الذكر والأنثى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 92, 'إن سعيكم لشتى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 92, 'فأما من أعطى واتقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 92, 'وصدق بالحسنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 92, 'فسنيسره لليسرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 92, 'وأما من بخل واستغنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 92, 'وكذب بالحسنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 92, 'فسنيسره للعسرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 92, 'وما يغني عنه ماله إذا تردى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 92, 'إن علينا للهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 92, 'وإن لنا للآخرة والأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 92, 'فأنذرتكم نارا تلظى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 92, 'لا يصلاها إلا الأشقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 92, 'الذي كذب وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 92, 'وسيجنبها الأتقى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 92, 'الذي يؤتي ماله يتزكى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 92, 'وما لأحد عنده من نعمة تجزى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(20, 1, 92, 'إلا ابتغاء وجه ربه الأعلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(21, 1, 92, 'ولسوف يرضى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 93, 'بسم الله الرحمن الرحيم والضحى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 93, 'والليل إذا سجى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 93, 'ما ودعك ربك وما قلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 93, 'وللآخرة خير لك من الأولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 93, 'ولسوف يعطيك ربك فترضى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 93, 'ألم يجدك يتيما فآوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 93, 'ووجدك ضالا فهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 93, 'ووجدك عائلا فأغنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 93, 'فأما اليتيم فلا تقهر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 93, 'وأما السائل فلا تنهر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 93, 'وأما بنعمة ربك فحدث');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 94, 'بسم الله الرحمن الرحيم ألم نشرح لك صدرك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 94, 'ووضعنا عنك وزرك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 94, 'الذي أنقض ظهرك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 94, 'ورفعنا لك ذكرك');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 94, 'فإن مع العسر يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 94, 'إن مع العسر يسرا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 94, 'فإذا فرغت فانصب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 94, 'وإلى ربك فارغب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 95, 'بسم الله الرحمن الرحيم والتين والزيتون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 95, 'وطور سينين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 95, 'وهذا البلد الأمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 95, 'لقد خلقنا الإنسان في أحسن تقويم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 95, 'ثم رددناه أسفل سافلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 95, 'إلا الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 95, 'فما يكذبك بعد بالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 95, 'أليس الله بأحكم الحاكمين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 96, 'بسم الله الرحمن الرحيم اقرأ باسم ربك الذي خلق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 96, 'خلق الإنسان من علق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 96, 'اقرأ وربك الأكرم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 96, 'الذي علم بالقلم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 96, 'علم الإنسان ما لم يعلم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 96, 'كلا إن الإنسان ليطغى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 96, 'أن رآه استغنى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 96, 'إن إلى ربك الرجعى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 96, 'أرأيت الذي ينهى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 96, 'عبدا إذا صلى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 96, 'أرأيت إن كان على الهدى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(12, 1, 96, 'أو أمر بالتقوى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(13, 1, 96, 'أرأيت إن كذب وتولى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(14, 1, 96, 'ألم يعلم بأن الله يرى');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(15, 1, 96, 'كلا لئن لم ينته لنسفعا بالناصية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(16, 1, 96, 'ناصية كاذبة خاطئة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(17, 1, 96, 'فليدع ناديه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(18, 1, 96, 'سندع الزبانية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(19, 1, 96, 'كلا لا تطعه واسجد واقترب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 97, 'بسم الله الرحمن الرحيم إنا أنزلناه في ليلة القدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 97, 'وما أدراك ما ليلة القدر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 97, 'ليلة القدر خير من ألف شهر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 97, 'تنزل الملائكة والروح فيها بإذن ربهم من كل أمر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 97, 'سلام هي حتى مطلع الفجر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 98, 'بسم الله الرحمن الرحيم لم يكن الذين كفروا من أهل الكتاب والمشركين منفكين حتى تأتيهم البينة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 98, 'رسول من الله يتلو صحفا مطهرة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 98, 'فيها كتب قيمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 98, 'وما تفرق الذين أوتوا الكتاب إلا من بعد ما جاءتهم البينة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 98, 'وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة وذلك دين القيمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 98, 'إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها أولئك هم شر البرية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 98, 'إن الذين آمنوا وعملوا الصالحات أولئك هم خير البرية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 98, 'جزاؤهم عند ربهم جنات عدن تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم ورضوا عنه ذلك لمن خشي ربه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 99, 'بسم الله الرحمن الرحيم إذا زلزلت الأرض زلزالها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 99, 'وأخرجت الأرض أثقالها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 99, 'وقال الإنسان ما لها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 99, 'يومئذ تحدث أخبارها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 99, 'بأن ربك أوحى لها');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 99, 'يومئذ يصدر الناس أشتاتا ليروا أعمالهم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 99, 'فمن يعمل مثقال ذرة خيرا يره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 99, 'ومن يعمل مثقال ذرة شرا يره');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 100, 'بسم الله الرحمن الرحيم والعاديات ضبحا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 100, 'فالموريات قدحا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 100, 'فالمغيرات صبحا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 100, 'فأثرن به نقعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 100, 'فوسطن به جمعا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 100, 'إن الإنسان لربه لكنود');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 100, 'وإنه على ذلك لشهيد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 100, 'وإنه لحب الخير لشديد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 100, 'أفلا يعلم إذا بعثر ما في القبور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 100, 'وحصل ما في الصدور');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 100, 'إن ربهم بهم يومئذ لخبير');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 101, 'بسم الله الرحمن الرحيم القارعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 101, 'ما القارعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 101, 'وما أدراك ما القارعة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 101, 'يوم يكون الناس كالفراش المبثوث');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 101, 'وتكون الجبال كالعهن المنفوش');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 101, 'فأما من ثقلت موازينه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 101, 'فهو في عيشة راضية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 101, 'وأما من خفت موازينه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 101, 'فأمه هاوية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(10, 1, 101, 'وما أدراك ما هيه');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(11, 1, 101, 'نار حامية');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 102, 'بسم الله الرحمن الرحيم ألهاكم التكاثر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 102, 'حتى زرتم المقابر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 102, 'كلا سوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 102, 'ثم كلا سوف تعلمون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 102, 'كلا لو تعلمون علم اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 102, 'لترون الجحيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 102, 'ثم لترونها عين اليقين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 102, 'ثم لتسألن يومئذ عن النعيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 103, 'بسم الله الرحمن الرحيم والعصر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 103, 'إن الإنسان لفي خسر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 103, 'إلا الذين آمنوا وعملوا الصالحات وتواصوا بالحق وتواصوا بالصبر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 104, 'ويل لكل همزة لمزة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 104, 'الذي جمع مالا وعدده');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 104, 'يحسب أن ماله أخلده');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 104, 'كلا لينبذن في الحطمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 104, 'وما أدراك ما الحطمة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 104, 'نار الله الموقدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 104, 'التي تطلع على الأفئدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(8, 1, 104, 'إنها عليهم مؤصدة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(9, 1, 104, 'في عمد ممددة');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 105, 'بسم الله الرحمن الرحيم ألم تر كيف فعل ربك بأصحاب الفيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 105, 'ألم يجعل كيدهم في تضليل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 105, 'وأرسل عليهم طيرا أبابيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 105, 'ترميهم بحجارة من سجيل');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 105, 'فجعلهم كعصف مأكول');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 106, 'بسم الله الرحمن الرحيم لإيلاف قريش');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 106, 'إيلافهم رحلة الشتاء والصيف');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 106, 'فليعبدوا رب هذا البيت');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 106, 'الذي أطعمهم من جوع وآمنهم من خوف');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 107, 'بسم الله الرحمن الرحيم أرأيت الذي يكذب بالدين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 107, 'فذلك الذي يدع اليتيم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 107, 'ولا يحض على طعام المسكين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 107, 'فويل للمصلين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 107, 'الذين هم عن صلاتهم ساهون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 107, 'الذين هم يراءون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(7, 1, 107, 'ويمنعون الماعون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 108, 'بسم الله الرحمن الرحيم إنا أعطيناك الكوثر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 108, 'فصل لربك وانحر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 108, 'إن شانئك هو الأبتر');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 109, 'بسم الله الرحمن الرحيم قل يا أيها الكافرون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 109, 'لا أعبد ما تعبدون');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 109, 'ولا أنتم عابدون ما أعبد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 109, 'ولا أنا عابد ما عبدتم');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 109, 'ولا أنتم عابدون ما أعبد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 109, 'لكم دينكم ولي دين');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 110, 'بسم الله الرحمن الرحيم إذا جاء نصر الله والفتح');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 110, 'ورأيت الناس يدخلون في دين الله أفواجا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 110, 'فسبح بحمد ربك واستغفره إنه كان توابا');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 111, 'بسم الله الرحمن الرحيم تبت يدا أبي لهب وتب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 111, 'ما أغنى عنه ماله وما كسب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 111, 'سيصلى نارا ذات لهب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 111, 'وامرأته حمالة الحطب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 111, 'في جيدها حبل من مسد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 112, 'بسم الله الرحمن الرحيم قل هو الله أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 112, 'الله الصمد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 112, 'لم يلد ولم يولد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 112, 'ولم يكن له كفوا أحد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 113, 'بسم الله الرحمن الرحيم قل أعوذ برب الفلق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 113, 'من شر ما خلق');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 113, 'ومن شر غاسق إذا وقب');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 113, 'ومن شر النفاثات في العقد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 113, 'ومن شر حاسد إذا حسد');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(1, 1, 114, 'بسم الله الرحمن الرحيم قل أعوذ برب الناس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(2, 1, 114, 'ملك الناس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(3, 1, 114, 'إله الناس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(4, 1, 114, 'من شر الوسواس الخناس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(5, 1, 114, 'الذي يوسوس في صدور الناس');
INSERT INTO Verse(Id, ReadingId, ChapterId, Content) VALUES(6, 1, 114, 'من الجنة والناس');