DROP TABLE IF EXISTS Verse;
DROP TABLE IF EXISTS Chapter;
DROP TABLE IF EXISTS Reading;
CREATE TABLE "Chapter" ("Id" INTEGER NOT NULL, "Name" TEXT NOT NULL, PRIMARY KEY("Id"));
CREATE TABLE "Reading" ("Id" INTEGER NOT NULL, "Name" TEXT NOT NULL, PRIMARY KEY("Id"));
CREATE TABLE "Verse" ("Id" INTEGER NOT NULL, "ReadingId" INTEGER NOT NULL, "ChapterId" INTEGER NOT NULL, "Content" TEXT NOT NULL, FOREIGN KEY("ReadingId") REFERENCES "Reading"("Id"), FOREIGN KEY("ChapterId") REFERENCES "Chapter"("Id"), PRIMARY KEY("Id","ReadingId","ChapterId"));
